INSERT INTO `article` VALUES ('1', '宴山亭', '裁剪冰绡，轻叠数重，淡着燕脂匀注。新样靓妆，艳溢香融，羞杀蕊珠宫女。易得凋零，更多少、无情风雨。愁苦，问院落凄凉，几番春暮？凭寄离恨重重，者双燕何曾，会人言语？天遥地远，万水千山，知他故宫何处？怎不思量？除梦里有曾去。无据，和梦也新来不做。', '赵佶', null, '1', 'Z', null);
INSERT INTO `article` VALUES ('2', '木兰花', '城上风光莺语乱，城下烟波春拍岸。绿杨芳草几时休？泪眼愁肠先已断。情怀渐觉成衰晚，鸾镜朱颜惊暗换。昔年多病厌芳尊，今日芳尊惟恐浅。', '钱惟演', null, '1', 'Q', null);
INSERT INTO `article` VALUES ('3', '苏幕遮', '碧云天，黄叶地，秋色连波，波上寒烟翠。山映斜阳天接水，芳草无情，更在斜阳外。黯乡魂，追旅思。夜夜除非，好梦留人睡。明月楼高休独倚，酒入愁肠，化作相思泪。', '范仲淹', null, '1', 'F', null);
INSERT INTO `article` VALUES ('4', '御街行', '纷纷坠叶飘香砌。夜寂静，寒声碎。真珠帘卷玉楼空，天淡银河垂地。年年今夜，月华如练，长是人千里。　愁肠已断无由醉，酒未到，先成泪。残灯明灭枕头攲，谙尽孤眠滋味。都来此事，眉间心上，无计相回避。　', '范仲淹', null, '1', 'F', null);
INSERT INTO `article` VALUES ('5', '千秋岁', '数声鶗鳺，又报芳菲歇。惜春更选残红折，雨轻风色暴，梅子青时节。永丰柳，无人尽日花飞雪。　莫把幺弦拨，怨极弦能说。天不老，情难绝，心似双丝网，中有千千结。夜过也，东窗未白孤灯灭。', '张先', null, '1', 'Z', null);
INSERT INTO `article` VALUES ('6', '菩萨蛮', '哀筝一弄湘江曲，声声写尽湘波绿。纤指十三弦，细将幽恨传。当筵秋水慢，玉柱斜飞雁。弹到断肠时，春山眉黛低。醉垂鞭，张先双蝶绣罗裙，东池宴初相见。朱粉不深匀，闲花淡淡春。细看诸处好，人人道柳腰身。昨日乱山昏，来时衣上云。', '张先', null, '1', 'Z', null);
INSERT INTO `article` VALUES ('7', '一丛花', '伤高怀远几时穷？无物似情浓。离愁正引千丝乱，更东陌，飞絮蒙蒙。嘶骑渐遥，征尘不断，何处认郎踪？　双鸳池沼水溶溶，南北小桡通。梯横画阁黄昏后，又还是，斜月帘栊。沉恨细思，不如桃杏，犹解嫁东风。　', '张先', null, '1', 'Z', null);
INSERT INTO `article` VALUES ('8', '天仙子', '水调数声持酒听，午醉醒来愁未醒。送春春去几时回？临晚镜，伤流景，往事后期空记省。沙上并禽池上暝，云破月来花弄影。重重帘幕密遮灯，风不定，人初静，明月落红应满径。', '张先', null, '1', 'Z', null);
INSERT INTO `article` VALUES ('9', '青门引', '乍暖还轻冷，风雨晚来方定。庭轩寂寞近清明，残花中酒，又是去年病。楼头画角风吹醒，入夜重门静。那堪更被明月，隔墙送过秋千影。　', '张先', null, '1', 'Z', null);
INSERT INTO `article` VALUES ('10', '浣溪沙', '一曲新词酒一杯，去年天气旧池台，夕阳西下几时回？无可奈何花落去，似曾相识燕归来，小园香径独徘徊。', '晏殊', null, '1', 'Y', null);
INSERT INTO `article` VALUES ('11', '浣溪沙', '一向年光有限身，等闲离别易消魂，酒筵歌席莫辞频。满目山河空念远，落花风雨更伤春，不如怜取眼前人。', '晏殊', null, '1', 'Y', null);
INSERT INTO `article` VALUES ('12', '清乐平', '　红笺小字，说尽平生意。鸿雁在云鱼在水，惆怅此情难寄。　斜阳独倚西楼，遥山恰对帘钩。人面不知何处，绿波依旧东流。', '晏殊', null, '1', 'Y', null);
INSERT INTO `article` VALUES ('13', '清乐平', '　金风细细，叶叶梧桐坠。绿酒初尝人易醉，一枕小窗浓睡。　紫薇朱槿花残，斜阳却照阑干。双燕欲归时节，银屏昨夜微寒。', '晏殊', null, '1', 'Y', null);
INSERT INTO `article` VALUES ('14', '木兰花', '燕鸿过后莺归去，细算浮生千万绪。长于春梦几多时，散似秋云无觅处。闻琴解佩神仙侣，挽断罗衣留不住。劝君莫作独醒人，烂醉花间应有数。', '晏殊', null, '1', 'Y', null);
INSERT INTO `article` VALUES ('15', '木兰花', '池塘水绿风微暖，记得玉真初见面。重头歌韵响琤琮，入破舞腰红乱旋。玉钩栏下香阶畔，醉后不知斜日晚。当时共我赏花人，点检如今无一半。', '晏殊', null, '1', 'Y', null);
INSERT INTO `article` VALUES ('16', '木兰花', '绿杨芳草长亭路，年少抛人容易去。楼头残梦五更钟，花底离愁三月雨。无情不似多情苦，一寸还成千万缕。天涯地角有穷时，只有相思无尽处。', '晏殊', null, '1', 'Y', null);
INSERT INTO `article` VALUES ('17', '踏莎行', '祖席离歌，长亭别宴，香尘已隔犹回面。居人匹马映林嘶，行人去棹依波转。画阁魂消，高楼目断，斜阳只送平波远。无穷无尽是离愁，天涯地角寻思遍。', '晏殊', null, '1', 'Y', null);
INSERT INTO `article` VALUES ('18', '踏莎行', '小径红稀，芳郊绿遍，高台树色阴阴见。春风不解禁杨花，蒙蒙乱扑行人面。翠叶藏莺，朱帘隔燕，炉香静逐游丝转。一场愁梦酒醒时，斜阳却照深深院。', '晏殊', null, '1', 'Y', null);
INSERT INTO `article` VALUES ('19', '蝶恋花', '六曲阑干偎碧树,杨柳风轻,展尽黄金缕。谁把钿筝移玉柱，穿帘海燕双飞去。满眼游丝兼落絮,红杏开时,一霎清明雨。浓睡觉来莺乱语，惊残好梦无寻处。', '晏殊', null, '1', 'Y', null);
INSERT INTO `article` VALUES ('20', '凤箫吟', '锁离愁连绵无际，来时陌上初熏，绣帏人念远，暗垂珠露，泣送征轮。长行长在眼，更重重、远水孤云。但望极楼高，尽日目断王孙。　消魂池塘别后，曾行处、绿妒轻裙。恁时携素手,乱花飞絮里,缓步香茵。朱颜自改，向年年、芳意长新。遍绿野、嬉游醉眼，莫负青春。', '韩缜', null, '1', 'H', null);
INSERT INTO `article` VALUES ('21', '木兰花', '东城渐觉风光好，彀皱波纹迎客棹。绿杨烟外晓云轻，红杏枝头春意闹。浮生长恨欢娱少，肯爱千金轻一笑？为君持酒劝斜阳，且向花间留晚照。', '宋祁', null, '1', 'S', null);
INSERT INTO `article` VALUES ('22', '采桑子', '群芳过后西湖好，狼籍残红，飞絮蒙蒙，垂柳阑干尽日风。笙歌散尽游人去，始觉春空，垂下帘栊，双燕归来细雨中。', '欧阳修', null, '1', 'O', null);
INSERT INTO `article` VALUES ('23', '诉衷情', '清晨帘幕卷轻霜，呵手试梅妆。都缘自有离恨，故画作远山长。思往事，惜流芳，易成伤。拟歌先敛，欲笑还颦，最断人肠。', '欧阳修', null, '1', 'O', null);
INSERT INTO `article` VALUES ('24', '踏莎行', '候馆梅残，溪桥柳细，草薰风暖摇征辔。离愁渐远渐无穷，迢迢不断如春水。寸寸柔肠，盈盈粉泪，楼高莫近危阑倚。平芜尽处是春山，行人更在春山外。', '欧阳修', null, '1', 'O', null);
INSERT INTO `article` VALUES ('25', '蝶恋花', '庭院深深深几许？杨柳堆烟,帘幕无重数。玉勒雕鞍游冶处,楼高不见章台路。雨横风狂三月暮，门掩黄昏,无计留春住。泪眼问花花不语,乱红飞过秋千去。', '欧阳修', null, '1', 'O', null);
INSERT INTO `article` VALUES ('26', '蝶恋花', '谁道闲情抛弃久？每到春来,惆怅还依旧。日日花前常病酒,不辞镜里朱颜瘦。河畔青芜堤上柳，为问新愁,何事年年有？独立小桥风满袖,平林新月人归后。', '欧阳修', null, '1', 'O', null);
INSERT INTO `article` VALUES ('27', '蝶恋花', '几日行云何处去？忘了归来,不道春将暮。百草千花寒食路,香车系在谁家树？泪眼倚楼频独语。双燕来时,陌上相逢否？撩乱春愁如柳絮,依依梦里无寻处。', '欧阳修', null, '1', 'O', null);
INSERT INTO `article` VALUES ('28', '木兰花', '别后不知君远近，触目凄凉多少闷！渐行渐远渐无书，水阔鱼沉何处问？夜深风竹敲秋韵，万叶千声皆是恨。故攲单枕梦中寻，梦又不成灯双烬。', '欧阳修', null, '1', 'O', null);
INSERT INTO `article` VALUES ('29', '浪淘沙', '把酒祝东风，且共从容。垂杨紫陌洛城东，总是当时携手处，游遍芳丝。聚散苦匆匆，此恨无穷。今年花胜去年红，可惜明年花更好，知与谁同？', '欧阳修', null, '1', 'O', null);
INSERT INTO `article` VALUES ('30', '青玉案', '一年春事都来几？早过了、三之二。绿暗红嫣浑可事。绿杨庭院，暖风帘幕，有个人憔悴。买花载酒长安市，又争似，家山见桃李？不枉东风吹客泪，相思难表，梦魂无据，惟有归来是。', '欧阳修', null, '1', 'O', null);
INSERT INTO `article` VALUES ('31', '曲玉管', '陇首云飞，江边日晚，烟波满目凭阑久。一望关河萧索，千里清秋，忍凝眸。杳杳神京，盈盈仙子，别来锦字终难偶。断雁无凭，冉冉飞下汀洲、思悠悠。暗想当初，有多少、幽欢佳会；岂知聚散难期，翻成雨恨云愁。阻追游，每登山临水，惹起平生心事，一场消黯，永日无言，却下层楼。', '柳永', null, '1', 'L', null);
INSERT INTO `article` VALUES ('32', '雨霖铃', '寒蝉凄切，对长亭晚，骤雨初歇。都门帐饮无绪，留恋处、兰舟催发。执手相看泪眼，竟无语凝噎。念去去、千里烟波，暮霭沉沉楚天阔。　多情自古伤离别，更那堪、冷落清秋节！今宵酒醒何处？杨柳岸、晓风残月。此去经年，应是良辰好景虚设。便纵有千种风情，更与何人说？', '柳永', null, '1', 'L', null);
INSERT INTO `article` VALUES ('33', '蝶恋花', '伫倚危楼风细细，望极春愁，黯黯生天际。草色烟光残照里，无言谁会凭阑意？　拟把疏狂图一醉，对酒当歌，强乐还无味。衣带渐宽终不悔，为伊消得人憔悴。　', '柳永', null, '1', 'L', null);
INSERT INTO `article` VALUES ('34', '采莲令', '月华收,云淡霜天曙。西征客、此时情苦。翠娥手,送临歧、轧轧开朱户。千娇面、盈盈伫立,无言有泪,断肠争忍回顾？一叶兰舟,便凭急桨凌波去。贪行色、岂知离绪,万般方寸，但饮恨、脉脉同谁语？更回首、重城不见，寒江天外，隐隐两三烟树。　', '柳永', null, '1', 'L', null);
INSERT INTO `article` VALUES ('35', '浪淘沙慢', '梦觉透窗风一线,寒流灯吹息。那堪酒醒,又闻空阶夜雨频滴。嗟因循、久作天涯客。负佳人、几许盟言，便忍把、从前欢会，陡顿翻成忧戚。愁极，再三追思,洞房深处,几度饮散歌阑，香暖鸳鸯被。岂暂时疏散，费伊心力。殢云尤雨，有万般千种，相怜相惜。恰到如今,天长漏永,无端自家疏隔。知何时、却拥秦云态？愿低帏昵枕，轻轻细说与，江乡夜夜，数寒更思忆。　', '柳永', null, '1', 'L', null);
INSERT INTO `article` VALUES ('36', '定风波', '自春来、惨绿愁红，芳心是事可可。日上花梢，莺穿柳带，犹压香衾卧。暖酥消，腻云亸，终日厌厌倦梳裹。无那。恨薄情一去，音书无个。早知恁么。悔当初、不把雕鞍锁。向鸡窗、只与蛮笺象管，拘束教吟课。镇相随，莫抛躲。针线闲拈伴伊坐。和我，免使年少光阴虚过。', '柳永', null, '1', 'L', null);
INSERT INTO `article` VALUES ('38', '少年游', '晚秋天。一霎微雨洒庭轩。槛菊萧疏,井梧零乱,惹残烟。凄然。望江关，飞云黯淡夕阳间。当时宋玉悲感，向此临水与登山。远道迢递,行人凄楚,倦听陇水潺湲。正蝉吟败叶，蛩响衰草，相应喧喧。　孤馆度日如年。风露渐变，悄悄至更阑。长天净，绛河清浅，皓月婵娟。思绵绵，夜永对景，那堪屈指，暗想从前。未名未禄，绮陌红楼，往往经岁迁延。帝里风光好,当年少日,暮宴朝欢。况有狂朋怪侣，遇当歌、对酒竞留连。别来迅景如梭，旧游似梦，烟水程何限。念利名、憔悴长萦绊。追往事、空惨愁颜。漏箭移、稍觉轻寒。渐呜咽画角数声残。对闲窗畔，停灯向晓，抱影无眠。', '柳永', null, '1', 'L', null);
INSERT INTO `article` VALUES ('39', '夜半乐', '冻云黯淡天气，扁舟一叶，乘兴离江渚。渡万壑千岩，越溪深处。怒涛渐息，樵风乍起，更闻商旅相呼，片帆高举。泛画鹢、翩翩过南浦。　望中洒旆闪闪，一簇烟村，数行霜树。残日下渔人鸣榔归去。败荷零落，衰杨掩映，岸边两两三三，浣纱游女。避行客，含羞笑相语。　到此因念，绣阁轻抛，浪萍难驻。叹后约、丁宁竟何据。惨离怀、空恨岁晚归期阻。凝泪眼、杳杳神京路。断鸿声远长天暮。　', '柳永', null, '1', 'L', null);
INSERT INTO `article` VALUES ('40', '玉胡蝶', '望处雨收云断,凭栏悄悄,目送秋光。晚景萧疏，堪动宋玉悲凉。水风轻、蘋花渐老，月露冷、梧叶飘黄。遗情伤。故人何在？烟水茫茫。难忘。文期洒会，几孤风月，屡变星霜。海阔山遥，未知何处是潇湘？念双燕、难凭远信，指暮天、空识归航。黯相望。断鸿声里，立尽斜阳。　', '柳永', null, '1', 'L', null);
INSERT INTO `article` VALUES ('41', '八声甘州', '对潇潇暮雨洒江天，一番洗清秋。渐霜风凄紧，关河冷落，残照当楼。是处红衰翠减，苒苒物华休。惟有长江水，无语东流。　不忍登高临远，望故乡渺邈，归思难收。叹年来踪迹，何事苦淹留？想佳人妆楼凝望，误几回天际识归舟？争知我、倚阑干处，正恁凝愁？　', '柳永', null, '1', 'L', null);
INSERT INTO `article` VALUES ('42', '迷神引', '一叶扁舟轻帆卷,暂泊楚江南岸。孤城暮角,引胡笳怨。水茫茫，平沙雁，旋惊散。烟敛寒林簇，画屏展。天际遥山小，黛眉浅。旧赏轻抛，到此成游宦。觉客程劳,年光晚。异乡风物,忍萧索、当愁眼。帝城赊，秦楼阻，旅魂乱。芳草连空阔，残照满。佳人无消息，断云远。', '柳永', null, '1', 'L', null);
INSERT INTO `article` VALUES ('43', '竹马子', '登孤垒荒凉，危亭旷望，静临烟渚。对雌霓挂雨，雄风拂槛，微收残暮。渐觉一叶惊秋，残蝉噪晚，素商时序。览景想肖欢，指神京、非零非烟深处。向此成追感，新愁易积，故人难聚。凭高尽日凝伫，赢得消魂无语。极目霁霭霏，暝鸦零乱，萧索江城暮。南楼画角，又送残阳去。', '柳永', null, '1', 'L', null);
INSERT INTO `article` VALUES ('44', '桂枝香', '登临送目，正故国晚秋，天气初肃。千里澄江似练，翠峰如簇。归帆去棹斜阳里，背西风，酒旗斜矗。彩舟云淡，星河鹭起，画图难足。念往昔、繁华竞逐，叹门外楼头，悲恨相续。千古凭高，对此漫嗟荣辱。六朝旧事如流水，但寒烟、衰草凝绿。至今商女，时时犹唱，后庭遗曲。', '王安石', null, '1', 'W', null);
INSERT INTO `article` VALUES ('45', '千秋岁引', '别馆寒砧，孤城画角，一派秋声入寥廓。东归燕从海上去，南来雁向沙头落。楚台风，庚楼月，宛如昨。缧缧无奈，被些名利缚，无奈被他情担阁，可惜风流总闲却。当初漫留华表语，而今误我秦楼约。梦阑时，酒醒后，思量着。　', '王安石', null, '1', 'W', null);
INSERT INTO `article` VALUES ('50', '鹧鸪天', '彩袖殷勤捧玉钟，当年拚却醉颜红。舞低杨柳楼心月，歌尽桃花扇底风。从别后，忆相逢，几回魂梦与君同。今宵剩把银釭照，犹恐相逢是梦中。', '晏几道', null, '1', 'Y', null);
INSERT INTO `article` VALUES ('55', '阮郎归', '旧香残粉似当初，人情恨不如。一春犹有数行书，秋来书更疏。衾凤冷，枕鸳孤，愁肠待酒舒。梦魂纵有也成虚，那堪和梦无。', '晏几道', null, '1', 'Y', null);
INSERT INTO `article` VALUES ('56', '阮郎归', '天边金掌露成霜，云随雁字长。　绿杯红袖趁重阳，人情似故乡。兰佩紫，菊簪黄，殷勤理旧狂。　欲将沉醉换悲凉，清歌莫断肠。', '晏几道', null, '1', 'Y', null);
INSERT INTO `article` VALUES ('82', '水龙吟', '次韵林圣予《惜春》　问春何苦勿勿,带风伴雨如驰骤。幽葩细萼,小园低槛，壅培未就。吹尽繁红，占春长久，不如垂柳。算春长不老,人愁春老,愁只是、人间有。春恨十常八九，忍轻孤、芳醪经口。那知自是、桃花结子，不因春瘦。世上功名，老来风味，春归时候。最多情犹有，尊前青眼，相逢依旧。情愁忧患都说尽，烟柳浮云始作闲。', '晁补之', null, '1', 'C', null);
INSERT INTO `article` VALUES ('83', '临江仙', '梦后楼台高锁，酒醒帘暮低垂。去年春恨却来时，落花人独立，微雨燕双飞。记得小蘋初见，两重心字罗衣。琵琶弦上说相思，当时明月在，曾照彩云归。', '晏几道', null, '1', 'Y', null);
INSERT INTO `article` VALUES ('84', '蝶恋花', '醉别西楼醒不记,春梦秋云,聚散真容易。斜月半窗还少睡，画屏闲展吴山翠。衣上酒痕诗里字,点点行行,总是凄凉意。红烛自怜无好计，夜寒空替人垂泪。', '晏几道', null, '1', 'Y', null);
INSERT INTO `article` VALUES ('87', '生查子', '关山魂梦长，塞雁音书少。两鬓可怜青，只为相思老。归傍碧纱窗，说与人人道：真个别离难，不似相逢好。', '晏几道', null, '1', 'Y', null);
INSERT INTO `article` VALUES ('88', '木兰花', '东风又作无情计，艳粉娇红吹满地。碧楼帘影不遮愁，还似去年今日意。谁知错管春残事，到处登临曾费泪。此时金盏直须深，看尽落花能几醉。', '晏几道', null, '1', 'Y', null);
INSERT INTO `article` VALUES ('89', '木兰花', '秋千院落重帘暮，彩笔闲来题绣户。墙头丹杏雨馀花，门外绿杨风后絮。朝云信断知何处？应作襄王春梦去。紫骝认得旧游踪，嘶过画桥东畔路。', '晏几道', null, '1', 'Y', null);
INSERT INTO `article` VALUES ('90', '清平乐', '留人不住，醉解兰舟去。一棹碧涛春水路，过尽晓莺啼处。渡头杨柳青青，枝枝叶叶离情。此后锦书休寄，画楼云雨无凭。', '晏几道', null, '1', 'Y', null);
INSERT INTO `article` VALUES ('93', '六么令', '绿阴春尽，飞絮绕香阁。晚来翠眉宫样，巧把远山学。一寸狂心未说，已向横波觉。　画帘遮匝，新翻曲妙，暗许闲人带偷掐。前度书多隐语，意浅愁难答。不消红蜡，闲云归后，月在庭花旧阑角。　', '晏几道', null, '1', 'Y', null);
INSERT INTO `article` VALUES ('94', '御街行', '街南绿树春饶絮，雪满游春路。树头花艳杂娇云，树底人家朱户。北楼闲上，疏帘高卷，直见街南树。　阑干倚尽犹慵去，几度黄昏雨。晚春盘马踏青苔，曾傍绿阴深驻。落花犹在，香屏空掩，人面知何处？　', '晏几道', null, '1', 'Y', null);
INSERT INTO `article` VALUES ('95', '虞美人', '曲阑干外天如水，昨夜还曾倚。初将明月比佳期，长向月圆时候、望人归。罗衣著破前香在，旧意谁教攻。一春离恨懒调弦，犹有两行闲泪、宝筝前。', '晏几道', null, '1', 'Y', null);
INSERT INTO `article` VALUES ('96', '留春令', '画屏天畔，梦回依约，十洲云水。手捻红笺寄人书，写无限、伤春事。别浦高楼曾漫倚，对江南千里。楼下分流水声中，有当日、凭高泪。', '晏几道', null, '1', 'Y', null);
INSERT INTO `article` VALUES ('97', '思远人', '丙辰中秋，欢饮达旦，作此篇兼怀子由明月几时有，把酒问青天。不知天上宫阙，今夕是何年。我欲乘风归去。惟恐琼楼玉宇，高处不胜寒，起舞弄清影，何似在人间。　转朱阁，低绮户，照无眠。不应有恨，何事长向别时圆？人有悲欢离合，月有阴睛圆缺，此事古难全。但愿人长久，千里共婵娟。', '晏几道', null, '1', 'Y', null);
INSERT INTO `article` VALUES ('99', '水龙吟', '次韵章质夫《杨花词》似花还似非花，也无人惜从教坠。抛家傍路，思量却是，无情有思。萦损柔肠，困酣娇眼，欲开还闭。梦随风万里，寻郎去处，又还被莺呼起。不恨此花飞尽，恨西园、落红难缀。晓还雨过，遗踪何在？一池萍碎。春色三分，二分尘土，一分流水。细看来不是杨花，点点是离人泪。', '苏轼', null, '1', 'S', null);
INSERT INTO `article` VALUES ('100', '永遇乐', '彭城夜宿燕子楼，梦盼盼，因作此词明月如霜，好风如水，清景无限。曲港跳鱼，圆荷泻璐，寂寞无人见。紞如三鼓，铿然一叶，黯黯梦云惊断。夜茫茫、重寻无处，觉来小园行遍。天涯倦客,山中归路,望断故园心眼。燕子楼空，佳人何在？空锁楼中燕。古今如梦，何曾梦觉，但有旧欢新怨。异时对、黄楼夜景，为余浩叹。', '苏轼', null, '1', 'S', null);
INSERT INTO `article` VALUES ('101', '洞仙歌', '冰肌玉骨，自清凉无汗。水殿风来暗香满。绣帘开，一点明月窥人，人未寝，倚枕钗横鬓乱。　起来携素手，庭户无声，时见疏星渡河汉。试问夜如何？夜已三更。金波淡，玉绳低转。但屈指西风几时来？又不道流年暗中偷换。', '苏轼', null, '1', 'S', null);
INSERT INTO `article` VALUES ('102', '卜算子', '缺月挂疏桐，漏断人初静。　谁见幽人独住来，飘渺孤鸿影。惊起却回头，有恨无人省。　拣尽寒枝不肯栖，寂寞沙洲冷。', '苏轼', null, '1', 'S', null);
INSERT INTO `article` VALUES ('103', '青玉案', '三年枕上吴中路，遣黄犬，随君去。若到松江呼小渡，莫惊鸳鹭，四桥尽是、老子经行处。辋川图上看春暮，常记高人右丞句。作个归期天定许，春衫犹是，小蛮针线，曾湿西湖雨。', '苏轼', null, '1', 'S', null);
INSERT INTO `article` VALUES ('104', '临江仙', '夜饮东坡醒复醉,归来仿佛三更。家童鼻息已雷鸣,敲门都不应，倚杖听江声。长恨此身非我有,何时忘却营营。夜阑风静彀纹平,小舟从此逝，江海寄余生。', '苏轼', null, '1', 'S', null);
INSERT INTO `article` VALUES ('105', '定风波', '莫听穿林打叶声，何妨吟啸且徐行。竹杖芒鞋轻胜马，谁怕！一蓑烟雨任平生。　料峭春风吹酒醒，微冷，山头斜照却相迎。回首向来萧瑟处，归去，也无风雨也无晴。', '苏轼', null, '1', 'S', null);
INSERT INTO `article` VALUES ('106', '贺新郎', '乳燕飞华屋。悄无人、桐阴转午，晚凉新浴。手弄生绡白团扇，扇手一时似玉。渐困倚、孤眠清熟。帘外谁来推绣户？枉教人梦断瑶台曲。又却是，风敲竹。　石榴半吐红巾蹙。待浮花浪蕊都尽，伴君幽独。秾艳一枝细看取，芳心千重似束。又恐被、秋风惊绿。若待得君来向此，花前对酒不忍触。共粉泪，两簌簌。　', '苏轼', null, '1', 'S', null);
INSERT INTO `article` VALUES ('107', '望海潮', '梅英疏淡，冰澌溶泄，东风暗换年华。金谷俊游，铜驼巷陌，新晴细履平沙。长忆误随车，正絮翻蝶舞，芳思交加。柳下桃蹊，乱分春色到人家。西园夜饮鸣笳。有华灯碍月，飞盖妨花。兰苑未空，行人渐老，重来是事堪喈。烟暝酒旗斜，但倚楼极目，时见栖鸦。无奈归心，暗随流水到天涯。　', '秦观', null, '1', 'Q', null);
INSERT INTO `article` VALUES ('108', '八六子', '倚危亭，恨如芳草，萋萋剗尽还生。念柳外青骢别后，水边红袂分时，怆然暗惊。无端天与娉婷。夜月一帘幽梦，春风十里柔情。　怎奈何欢娱渐随流水，素弦声断，翠绡香减；那堪片片，飞花弄晚。蒙蒙残雨笼晴。正销凝，黄鹂又啼数声。', '秦观', null, '1', 'Q', null);
INSERT INTO `article` VALUES ('109', '满庭芳', '山抹微云，天粘衰草，画角声断谯门。暂停征棹，聊共引离尊。多少蓬莱旧事，空回首，烟霭纷纷。斜阳外，寒鸦万点，流水绕孤村。　销魂、当此际，香囊暗解，罗带轻分。谩赢得青楼、薄倖名存。此去何时见也？襟袖上、空惹啼痕。伤情处，高楼望断，灯火已黄昏。　', '秦观', null, '1', 'Q', null);
INSERT INTO `article` VALUES ('110', '满庭芳', '晓色云开，春随人意，骤雨才过还晴。古台芳榭，飞燕蹴红英。舞困榆钱自落，秋千外、绿水桥平。东风里，朱门映柳，低按小秦筝。　多情。行乐处，珠钿翠盖，玉辔红樱。渐酒空金榼，花困蓬瀛。豆蔻梢头旧恨，十年梦、屈指堪惊。凭阑久，疏烟淡日，寂寞下芜城。　', '秦观', null, '1', 'Q', null);
INSERT INTO `article` VALUES ('111', '减安木兰花', '天涯旧恨，独自凄凉人不问。　欲见回肠，断尽金炉小篆香。黛蛾长敛，任是春风吹不展。　困倚危楼，过尽飞鸿字字愁。', '秦观', null, '1', 'Q', null);
INSERT INTO `article` VALUES ('112', '浣溪沙', '漠漠轻寒上小楼，晓阴无赖似穷秋，淡烟流水画屏幽。自在飞花轻似梦，无边丝雨细如愁，宝帘闲挂小银钩。', '秦观', null, '1', 'Q', null);
INSERT INTO `article` VALUES ('113', '阮郎归', '湘天风雨破寒初，深沉庭院虚。　丽谯吹罢小单于，迢迢清夜徂。乡梦断，旅魂孤，峥嵘岁又除。　衡阳犹有雁传书，郴阳和雁无。', '秦观', null, '1', 'Q', null);
INSERT INTO `article` VALUES ('114', '绿头鸭', '晚云收，淡天一片琉璃。烂银盘、来从海底，皓色千里澄辉。莹无尘、素娥淡伫,静可数、丹桂参差。玉露初零,金风未澶，一年无似此佳时。露坐久、疏萤时度，乌鹊正南飞。瑶台玲，阑干凭暖，欲下迟迟。　念佳人、音尘别后,对此应解相思。最关情漏声正永,暗断肠、花阴偷移。料得来宵，清光未减，阴睛天气又争知。共凝恋、如今别后，还是隔年期。人强健，清尊素影，长愿相随。　', '晁元礼', null, '1', 'C', null);
INSERT INTO `article` VALUES ('115', '蝶恋花', '欲减罗衣寒未去,不卷珠帘,人在深深处。红杏枝头花几许？啼痕止恨清明雨。尽日沉烟香一缕,宿酒醒迟,恼破春情绪。飞燕又将归信误，小屏风上西江路。', '赵令畤', null, '1', 'Z', null);
INSERT INTO `article` VALUES ('116', '蝶恋花', '卷絮风头寒欲尽,坠粉飘香,日日红成阵。新酒又添残酒困，今春不减前春恨。蝶去莺飞无处问,隔水高楼,望断双鱼信。恼乱横波秋一寸，斜阳只与黄昏近。', '赵令畤', null, '1', 'Z', null);
INSERT INTO `article` VALUES ('117', '清平乐', '春风依旧，着意隋堤柳。搓得鹅几黄欲就，天气清明时候。去年紫陌青门，今宵雨云魂。断送一生憔悴，只消几个黄昏。', '赵令畤', null, '1', 'Z', null);
INSERT INTO `article` VALUES ('118', '水龙吟', '次韵林圣予《惜春》　问春何苦勿勿,带风伴雨如驰骤。幽葩细萼,小园低槛，壅培未就。吹尽繁红，占春长久，不如垂柳。算春长不老,人愁春老,愁只是、人间有。春恨十常八九，忍轻孤、芳醪经口。那知自是、桃花结子，不因春瘦。世上功名，老来风味，春归时候。最多情犹有，尊前青眼，相逢依旧。', '晁补之', null, '1', 'C', null);
INSERT INTO `article` VALUES ('119', '夜游宫', '叶下斜阳照水，卷轻浪、沉沉千里。桥上酸风射眸子。立多时，看黄昏灯火市。　古屋寒窗底，听几片、井桐飞坠。不恋单衾再三起。有谁知，为萧娘书一纸？', '周邦彦', null, '1', 'Z', null);
INSERT INTO `article` VALUES ('120', '青玉案', '凌波不过横塘路，但目送、芳尘去。锦瑟华年谁与度？月桥花院，琐窗朱户，只有春知处。飞云冉冉蘅皋暮，彩笔新题断肠句。试问闲愁都几许？一川烟草，满城风絮，梅子黄时雨。', '贺铸', null, '1', 'H', null);
INSERT INTO `article` VALUES ('122', '感皇恩', '淡妆多态，更的的、频回眄睐。便认得琴心先许，欲绾合双带。记画堂、风月逢迎，轻颦浅笑娇无奈。向睡鸭炉边，翔鸳屏里，羞把香罗暗解。　自过了烧灯后，都不见踏青挑菜。几回凭双燕，丁宁深意，往来却恨重帘碍。约何时再，正春浓酒困，人闲昼永无聊赖。厌厌睡起，犹有花梢日在。　', '贺铸', null, '1', 'H', null);
INSERT INTO `article` VALUES ('123', '浣溪沙', '不信芳春厌老人，老人几度送馀春，惜春行乐莫辞频。巧笑艳歌皆我意，恼花颠酒拼君瞋，物情惟有醉中真。', '贺铸', null, '1', 'H', null);
INSERT INTO `article` VALUES ('124', '浣溪沙', '楼角初消一缕霞，淡黄杨柳暗栖鸦，玉人和月摘梅花。笑捻粉香归洞户，更垂帘幕护窗纱，东风寒似夜来些。', '贺铸', null, '1', 'H', null);
INSERT INTO `article` VALUES ('125', '石州慢', '薄雨收寒，斜照弄晴，春意空阔。长亭柳色才黄，远客一枝先折。烟横水漫，映带几点归鸿，平沙消尽龙沙雪。犹记出关来，恰而今时节。　将发，画楼芳酒，红泪清歌，便成轻别。回首经年，杳杳音尘都绝。欲知方寸，共有几许新愁？芭蕉不展丁香结。枉望断天涯，两厌厌风月。', '贺铸', null, '1', 'H', null);
INSERT INTO `article` VALUES ('126', '蝶恋花', '几许伤春春复暮,杨柳清阴,偏碍游丝度。天际小山桃叶步，白蘋花满湔裙处。竟日微吟长短句,帘影灯昏,心寄胡琴语。数点雨声风约住，朦胧淡月云来去。', '贺铸', null, '1', 'H', null);
INSERT INTO `article` VALUES ('128', '天门谣', '烟络横林，山沉远照，迤逦黄昏钟鼓。烛映帘栊，蛩催机杼，共苦清秋风露。不眠思妇，齐应和、几声砧杵。惊动天涯倦宦，锓囗囗岁华行暮。　当年酒狂自负，谓东君、以春相付。流浪征骖北道，客樯南浦，幽恨无人晤语。赖明月曾知旧游处，好伴云来，还将梦去。', '贺铸', null, '1', 'H', null);
INSERT INTO `article` VALUES ('129', '望湘人', '厌莺声到枕，花气动帘，醉魂愁相半。被惜余熏，带惊剩眼，几许伤春春晚。泪竹痕鲜，佩兰香老，湘天浓暖。记小江风月佳时，屡约非烟游伴。须信鸾弦易断，奈云和再鼓，曲中人远。认罗袜无踪，旧处弄波清浅。青翰棹舣，白蘋洲畔，尽目临皋飞观。不解寄、一字相思，幸有归来双燕。', '贺铸', null, '1', 'H', null);
INSERT INTO `article` VALUES ('130', '绿头鸭', '玉人家，画楼珠箔临津。托微风彩箫流怨，断肠马上曾闻。宴堂开、艳妆丝里，调琴思、认歌颦。麝蜡烟浓，玉莲漏短，更衣不待酒初醺。绣屏掩、枕鸳相就，香气渐暾暾。回廊影、疏钟淡月，几许消魂？翠钗分、银笺封泪，舞鞋从此生尘。任兰舟载将离恨，转南浦、北西醺。记取明年，蔷薇谢后，佳期应未误行云。凤城远、楚梅香嫩，先寄一枝春。青门外，只凭芳草，寻访郎君。　', '贺铸', null, '1', 'H', null);
INSERT INTO `article` VALUES ('131', '石州慢', '寒水依痕，春意渐回，沙际烟阔。溪梅睛照生香，冷蕊数枝争发。天涯旧恨，试看几许消魂？长亭门外山重叠。不尽眼中青，是愁来时节。　情切，画楼深闭，想见东风，暗消肌雪。孤负枕前云雨，尊前花月。心期切处，更有多少凄凉，殷勤留在归时说。到得再相逢，恰经年离别。', '张元干', null, '1', 'Z', null);
INSERT INTO `article` VALUES ('132', '兰陵王', '卷珠箔，朝雨轻阴乍阁。阑干外、烟柳弄睛，芳草侵阶映红药。东风妒花恶，吹落梢头嫩萼。屏山掩、沉水倦熏，中酒心情怯杯勺。寻思旧京洛，正年少疏狂，歌笑迷著。障泥油壁催梳掠，曾驰道同载，上林携手，灯夜初过早共约，又争信飘泊。寂寞，念行乐。甚粉淡衣襟，音断弦索，琼枝壁月春如昨。怅别华表，那回双鹤。相思除是，向醉里、暂忘却。　', '张元干', null, '1', 'Z', null);
INSERT INTO `article` VALUES ('133', '贺新郎', '睡起流莺语，掩苍苔，房栊向晚，乱红无数。吹尽残花无人见，惟有垂杨自舞。渐暖霭、初回轻暑，宝扇重寻明月影，暗尘侵、上有乘鸾女。惊旧恨，遽如许。江南梦断横江渚。浪粘天、葡萄涨绿，半空烟雨。无限楼前沧波意，谁采频花寄取？但怅望、兰舟容与，万里云帆何时到？送孤鸿、目断千山阻。谁为我，唱金缕。', '叶梦得', null, '1', 'Y', null);
INSERT INTO `article` VALUES ('134', '虞美人', '落花已作风前舞，又送黄昏雨。晓来庭院半残红，惟有游丝，千丈袅晴空。殷勤花下同携手，更尽杯中酒。美人不用敛蛾眉，我亦多情，无奈酒阑时。', '叶梦得', null, '1', 'Y', null);
INSERT INTO `article` VALUES ('135', '点绛唇', '新月娟娟，夜寒江静山衔斗。起来搔首，梅影横窗瘦。好个霜天，闲却传杯手。君知否？乱鸦啼后，归兴浓如酒。', '汪藻', null, '1', 'W', null);
INSERT INTO `article` VALUES ('136', '喜迁莺', '晓光催角，听宿鸟未惊，邻鸡先觉。迤逦烟村，马嘶人起，残月尚穿林薄。泪痕带霜微凝，酒力冲寒犹弱。叹倦客，悄不禁重染，风尘京洛。　追念人别后，心事万重，难觅孤鸿托。翠幌娇深，曲屏香暖，争念岁华飘泊。怨月恨花烦恼，不是不曾经著。者情味、望一成消减，新来还恶。　', '刘一生', null, '1', 'L', null);
INSERT INTO `article` VALUES ('137', '高阳台·除夜', '频听银签，重然绛蜡，年华衮衮惊心。饯旧迎新，能消几刻光阴？老来可惯通宵饮？待不眠、还怕寒侵。掩清尊、多谢梅花，伴我微吟。邻娃已试春妆了，更蜂腰簇翠，燕股横金。句引东风，也知芳思难禁。朱颜那有年年好，逞艳游、赢取如今。恣登临、残雪楼台，迟日园林。', '韩疁', null, '1', 'H', null);
INSERT INTO `article` VALUES ('138', '汉宫春', '潇洒江梅,向竹梢疏处,横两三枝。东君也不爱惜，雪压霜欺。无情燕子，怕春寒、轻失花期。却是有、年年塞雁，归来曾见开时。　清浅小溪如练，问玉堂何似，茅舍疏篱？伤心故人去后、冷落新诗。微云淡月，对江天、分付他谁。空白忆、清香未减，风流不在人知。', '李邴', null, '1', 'L', null);
INSERT INTO `article` VALUES ('139', '临江仙', '高咏楚词酬午日，天涯节序匆匆。榴花不似舞裙红,无人知此意,歌罢满帘风。万事一身伤老矣，戎葵凝笑墙东。酒杯深浅去年同,试浇桥下水,今夕到湘中。', '陈与义', null, '1', 'C', null);
INSERT INTO `article` VALUES ('140', '临江仙', '忆昔午桥桥上饮,坐中多是豪英。长沟流月去无声，杏花疏影里,吹笛到天明。二十馀年如一梦,此身虽在堪惊，头登小阁看新晴。古今多少事,渔唱起三更。', '陈与义', null, '1', 'C', null);
INSERT INTO `article` VALUES ('141', '苏武慢', '雁落平沙，烟笼寒冰，古垒鸣笳声断。青山隐隐，败叶萧萧，天际暝鸦零乱。楼上黄昏,片帆千里归程,年华将晚。望碧云空暮，佳人何处？梦魂俱远。忆旧游，邃馆朱扉，小园香径，尚想桃花人面。书盈锦轴，恨满金徽，难写寸心幽怨。两地离愁，一尊芳酒，凄凉危栏倚遍。尽迟留，凭仗西风，吹干泪眼。', '蔡伸', null, '1', 'C', null);
INSERT INTO `article` VALUES ('142', '柳梢青', '数声鶗鳺，可怜又是、春归时节。满面东风，海棠铺绣，梨花飘雪。丁香露泣残枝，算未比、愁肠寸结。自是休文，多情多感，不干风月。', '蔡伸', null, '1', 'C', null);
INSERT INTO `article` VALUES ('143', '鹧鸪天', '一点残红欲尽时，乍凉秋气满屏帏。梧桐叶上三更雨，叶叶声声是别离。调宝瑟，拨金猊，那时同唱鹧鸪词。如今风雨西楼夜，不听清歌也垂泪。', '周紫芝', null, '1', 'Z', null);
INSERT INTO `article` VALUES ('144', '踏沙行', '情似游丝，人如飞絮，泪珠阁定空相觑。一溪烟柳万丝垂，无因系得兰舟住。烟过斜阳，草迷烟渚，如今已是愁无数。明朝且做莫思量，如何过得今宵去。', '周紫芝', null, '1', 'Z', null);
INSERT INTO `article` VALUES ('145', '帝台春', '芳草碧色，萋萋便南陌。暖絮乱红，也似知人，春愁无力。忆得——盈盈拾发言翠侣，共携赏、凤城寒食。　到今来，海角逢春，天涯为客。愁旋释，还似织；泪暗则，而今已拚了，忘则怎生便忘得。又还问鳞鸿，试重寻消息。', '李甲', null, '1', 'L', null);
INSERT INTO `article` VALUES ('146', '忆王孙', '萋萋芳草忆王孙，柳外楼高空断魂，杜宇声声不忍闻。欲黄昏，雨打梨花深闭门。', '李重元', null, '1', 'L', null);
INSERT INTO `article` VALUES ('147', '忆秦娥', '千里草，萋萋尽处遥山小。遥山小，行人远似，此山多少？天若有情天亦老，此情说便说不了。说不了，一声唤起，又惊春晓。', '万俟咏', null, '1', 'W', null);
INSERT INTO `article` VALUES ('148', '三台', '见梨花初带夜月，海棠半含朝雨。内苑春、不禁过青门，御沟涨、潜通南浦。东风静，细柳垂金缕，望凤阙非烟非雾。好时代、朝野多欢，遍九陌、太平箫鼓。乍莺儿百啭断续，燕子飞来飞去。近绿水、台榭映秋千，斗草聚、双双游女。饧香更、酒冷踏青路；会暗识、夭桃朱户。向晚骤、宝马雕鞍，醉襟惹、乱花飞絮。正轻寒轻暖漏水，半阴半晴云暮。禁火天、已是试新妆，岁华到、三分佳处。清明看、汉蜡传宫炬，散翠烟、飞入槐府。敛兵卫、阊阖门开，住传宣、又还休务。', '万俟咏', null, '1', 'W', null);
INSERT INTO `article` VALUES ('149', '二郎神', '闷来弹鹊，又搅碎、一帘花影。漫试著春衫，还思纤手，熏彻金猊烬冷。动是愁端如何向？但怪得新来多病。嗟旧日沈腰，如今潘鬓，怎堪临镜？重省别时泪湿，罗衣犹凝。料为我厌厌，日高慵起，账托春醒未醒。雁足不来，马蹄难驻，门掩一庭芳景。空伫立，尽日阑干，倚遍昼长人静。　', '徐伸', null, '1', 'X', null);
INSERT INTO `article` VALUES ('150', '江神子慢', '玉台挂秋月，铅素浅、梅花傅香雪。冰姿洁，金莲衬、小小凌波罗袜。雨初歇，楼外孤鸿声渐远，远山外行人音信绝。此恨对语犹难，那堪更寄书说。教人红消翠减，觉衣宽金缕，都为轻别。太情切，消魂处画角黄昏时节。声呜咽。落尽庭花草，春去也。银蟾迥，无情圆又缺。恨伊不似馀香，惹鸳鸯结。', '田为', null, '1', 'T', null);
INSERT INTO `article` VALUES ('151', '蓦山溪', '洗妆真态，不作铅花御。竹外一枝斜，想佳人、天寒日暮。黄昏院落，无处着清香，风细细，雪垂垂，何况江头路。　月边疏影，梦到消魂处。结子欲黄时，又须作、廉纤细雨。孤芳一世，供断有情愁，消瘦损，东阳也，试问花知否？　', '曹组', null, '1', 'C', null);
INSERT INTO `article` VALUES ('152', '蓦山溪', '浮烟冷雨，今日还重九。秋去又秋来，但黄花、年年如旧。平台戏马，无处问英雄；茅舍底，竹篱东，伫立时搔首。　客来何有？草草三杯酒。一醉万缘空，莫贪伊、金印如斗。病翁老矣，谁共赋归来？芟垅麦，网溪鱼，未落他人后。　', '间子翚', null, '1', 'J', null);
INSERT INTO `article` VALUES ('153', '贺新郎', '篆缕消金鼎。醉沉沉、庭阴转午，画堂人静。芳草王孙知何处？惟有杨花糁径。渐玉枕、腾腾春醒，帘外残红春已透，镇无聊、殢酒厌厌病。云鬓乱，未忺整。江南旧事休重省，遍天涯寻消问息，断鸿难倩。月满西楼凭阑久，依旧归期未定。又只恐、瓶沉金井。嘶骑不来银烛暗，枉教人立尽梧桐影。谁伴我，对鸾镜。', '李玉', null, '1', 'L', null);
INSERT INTO `article` VALUES ('155', '烛影摇红', '青楼春晚，昼寂寂、梳匀又懒。乍听得、鸦啼莺哢，惹起新愁无限。记年时偷掷春心，花前隔雾遥相见。便角枕题诗，宝钗贳酒，共醉青苔深院。怎忘得、回廊下，携手处、花明月满。如今但暮雨，蜂愁蝶恨，小窗闲对芭蕉展。却谁拘管？尽无言、闲品秦筝，泪满参差雁。腰肢渐小，心与杨花共远。　', '廖世美', null, '1', 'L', null);
INSERT INTO `article` VALUES ('156', '南浦', '风悲画角，听单于、三弄落谯门。投宿囗囗征骑，飞雪满孤村。酒市渐阑灯火，正敲窗、乱叶舞纷纷。送数声惊雁，乍离烟水，嘹唳度寒云。好在半胧淡月，到如今、无处不消魂。故国梅花归梦，愁损绿罗裙。为问暗香闲艳，也相思、万点付啼痕。算翠屏应是，两眉馀恨倚黄昏。　', '鲁逸仲', null, '1', 'L', null);
INSERT INTO `article` VALUES ('157', '满江红', '怒发冲寇，凭阑处、潇潇雨歇。抬望眼、仰天长啸，壮怀激烈。三十功名尘与土，八千里路云和月。莫等闲、白了少年头，空悲切。靖康耻，犹未雪；臣子憾，何时灭。驾长车踏破、贺兰山缺。壮志饥餐胡虏肉，笑谈渴饮匈奴血。待从头、收拾旧山河，朝天阙。　', '岳飞', null, '1', 'Y', null);
INSERT INTO `article` VALUES ('158', '烛影摇红', '双阙中天，凤楼十二春寒浅。去年元夜奉宸游，曾侍瑶池宴。玉殿珠帘尽卷，拥群仙、蓬壶阆苑。五云深处，万烛光中，揭天丝管。驰隙流年，恍如一瞬星霜换。今宵谁念泣孤臣，回首长安远。可是尘缘未断，漫惆怅、华胥梦短。满怀幽恨，数点寒灯，几声归雁。', '张抡', null, '1', 'Z', null);
INSERT INTO `article` VALUES ('159', '水龙吟', '夜来风雨匆匆，故园定是花无几。愁多怨极，等闲孤负，一年芳意。柳困桃慵，杏青梅小，对人容易。算好春长在，好花长见，原只是、人憔悴。回首池南旧事，恨星星、不堪重记。如今但有，看花老眼，伤时清泪。不怕逢花瘦，只愁怕、老来风味。待繁红乱处，留云借月，也须拚醉。', '程垓', null, '1', 'C', null);
INSERT INTO `article` VALUES ('160', '六州歌头', '　长淮望断，关塞莽然平。征尘暗，霜风劲，悄边声。黯消凝，追想当年事，殆天数，非人力；洙泗上，弦歌地，亦膻腥。隔水毡乡，落日牛羊下，区脱纵横。看名王宵猎，骑火一川明，笳鼓悲鸣，遣人惊。　　念腰间箭，匣中剑，空埃蠹，竟何成！时易失，心徒壮，岁将零，渺神京。干羽方怀远，静烽燧，且休兵。冠盖使，纷驰鹜，若为情？闻道中原遗老，常南望翠葆霓旌。使行人到此，忠愤气填膺，有泪如倾。', '张孝祥', null, '1', 'Z', null);
INSERT INTO `article` VALUES ('161', '念奴娇', '洞庭青草，近中秋、更无一点风色。玉界琼田三万顷，著我扁舟一叶。素月分辉，银河共影，表里俱澄澈。怡然心会，妙处难与君说。　应念岭海经年,孤光自照,肝胆皆冰雪。短发萧骚襟袖冷，稳泛沧浪空阔。尽挹西江，细斟北斗，万象为宾客。扣舷独啸，不知今夕何夕！', '张孝祥', null, '1', 'Z', null);
INSERT INTO `article` VALUES ('162', '六州歌头', '东风著意，先上小桃枝。红粉腻，娇如醉，倚朱扉。记年时，隐映新生妆面，临水岸，春将半，云日暖，斜桥转，夹城西。草软莎平，跋马垂杨渡，玉勒争嘶。认蛾眉，凝笑脸，薄拂燕脂，绣户曾窥，恨依依。共携手处，香如雾，红随步，怨春迟。消瘦损，凭谁问？只花知，泪空垂。旧日堂前燕，和烟雨又双飞。人自老，春长暮霭，目断武陵溪，往事难追。', '韩元吉', null, '1', 'H', null);
INSERT INTO `article` VALUES ('163', '好事近', '凝碧旧池头，一听管弦凄切。多少梨园声在，总不堪华发。杏花无处避春愁，也傍野烟发。惟有御沟声断，似知人呜咽。', '韩元吉', null, '1', 'H', null);
INSERT INTO `article` VALUES ('164', '瑞鹤仙', '郊原初过雨，风数叶零乱，风定犹舞。斜阳挂深树，映浓愁浅黛。遥山媚妩。来时旧路，尚岩花、娇黄半吐。到今日惟有、溪边流水，见人如故。无语，邮亭深静，下马还寻，旧曾题处。无聊倦旅，伤离恨，最愁苦。纵收香藏镜，他年重到，人面桃花在否？念沉沉小阁幽窗，有时梦去。', '袁去华', null, '1', 'Y', null);
INSERT INTO `article` VALUES ('165', '剑器近', '夜来雨，赖倩得东风吹住。海棠正妖绕处，且留取。悄庭户，试细听莺啼燕语，分明共人愁绪，怕春去。佳树翠阴初转午。重帘未卷，乍睡起，寂寞看风絮。偷弹清泪寄烟波，见江头故人，为言憔悴如许。彩笺无数，去却寒暄，到了浑无定据。断肠落日千山暮。　', '袁去华', null, '1', 'Y', null);
INSERT INTO `article` VALUES ('166', '安公子', '弱柳千丝缕，嫩黄匀遍鸦啼处。寒入罗衣春尚浅，过一番风雨。问燕子来时，绿水桥边路，曾画楼、见个人人否？料静掩云窗，尘满哀弦危柱。　庚信愁如许，为谁都著眉端聚。独立东风弹泪眼，寄烟波东去。念永昼春闲，人倦如何度？闲傍枕、百转黄鹂语。唤觉来厌厌，残照依然花坞。　', '袁去华', null, '1', 'Y', null);
INSERT INTO `article` VALUES ('167', '瑞鹤仙', '脸霞红印枕，睡觉来、冠儿还是不整。屏闲麝煤冷，但眉峰压翠，泪珠弹粉。堂深昼永，燕交飞、风帘露井。恨无人说与，相思近日，带围宽尽。重省，残灯朱幌，淡月纱窗，那时风景。阳台路迥，云雨梦，便无准。待归来，先指花梢教看，欲把心期细问。问因循过了青春，怎生意稳？', '陆淞', null, '1', 'L', null);
INSERT INTO `article` VALUES ('168', '卜算子', '驿外断桥边，寂寞开无主。已是黄昏独自愁，更著风和雨。无意苦争春，一任群芳妒。零落成泥碾作尘，只有香如故。', '陆游', null, '1', 'L', null);
INSERT INTO `article` VALUES ('169', '水龙吟', '闹花深处楼台，画帘半卷东风软。春归翠陌，平莎茸嫩，垂杨金浅。迟日催花，淡云阁雨，轻寒轻暖。恨芳菲世界，游人未赏，都付与莺和燕。　寂寞凭高念远，向南楼、一声归雁。金钗斗草，青丝勒马，风流云散。罗绶分香，翠绡封泪，几多幽怨？正消魂又是，疏烟淡月，子规声断。', '陈亮', null, '1', 'C', null);
INSERT INTO `article` VALUES ('170', '忆秦娥', '楼阴缺，阑于影卧东厢月。东厢月，一天风露，杏花如雪。隔烟催漏金叫咽，罗帏黯淡灯花结。灯花结，片时春梦，江南天阔。', '范成大', null, '1', 'F', null);
INSERT INTO `article` VALUES ('171', '眼儿媚', '萍乡道中乍晴，卧舆中困甚，小憩柳塘。酣酣日脚紫烟浮，妍暖破轻裘。困人天色，醉人花气，午梦扶头。春慵恰似春塘水，一片彀纹愁。溶溶曳曳，东风无力，欲避还休。', '范成大', null, '1', 'F', null);
INSERT INTO `article` VALUES ('173', '霜天晓角', '绿树听鹈鴂，更那堪、鹧鸪声住，杜鹃声切！啼到春归无寻处，苦恨芳菲都歇。算未抵人间离别：马上琵琶关塞黑，更长门、翠辇辞金阙。看燕燕，送归妾。　将军百战声名裂，向河梁、回头万里，故人长绝。易水萧萧西风冷，满座衣冠似雪，正壮士悲歌未徹。啼鸟还知如许恨，料不啼清泪长啼血。谁共我，醉明月！', '范成大', null, '1', 'F', null);
INSERT INTO `article` VALUES ('174', '念奴娇', '野棠花落，又匆匆、过了清明时节。剗地东风期客梦，一枕云屏寒怯。曲岸持觞，垂杨系马，此地曾轻别。楼空人去，旧游飞燕能说。　闻道绮陌东头,行人长见,帘底纤纤月。旧恨春江流不尽，新恨云山千叠。料得明朝，尊前重见，镜花难折。也应惊问，近来多少华发？　', '辛弃疾', null, '1', 'X', null);
INSERT INTO `article` VALUES ('175', '汉宫春·立春日', '春已归来,看美人头上,袅袅春幡。无端风雨，未肯收尽馀寒。年时燕子，料今宵梦到西园。浑未辨、黄柑荐酒，更传青韭堆盘。却笑东风,从此便熏梅染柳,更传些闲。闲时又来镜里，转变朱颜。清愁不断，问何人会解连环。生怕见、花开花落，朝来塞雁先还。', '辛弃疾', null, '1', 'X', null);
INSERT INTO `article` VALUES ('176', '贺新郎', '凤尾龙香拔。自开元、霓裳曲罢，几番风月。最苦浔阳江头客，画舸亭亭待发。记出塞、黄云堆雪。马上离愁三万里，望昭阳、宫殿孤鸿没，弦解语，恨难说。辽阳驿使音尘绝，琐窗寒、轻拢慢捻，泪珠盈睫。推手含情还却手，一抹梁州哀彻。千古事云飞烟灭。贺老定场无消息，想沉香亭北繁华歇，弹到此，为呜咽。', '辛弃疾', null, '1', 'X', null);
INSERT INTO `article` VALUES ('177', '水龙吟', '楚天千里清秋，水随天去秋无际。遥岑远目，献愁供恨，玉簪螺髻。落日楼头，断鸿声里，江南游子。把吴钩看了，阑干拍遍，无人会、登临意。休说鲈鱼堪脍，尽西风，季鹰归未？求田问舍，怕应羞见，刘郎才气。可惜流年，忧愁风雨，树犹如此。倩何人唤取，红巾翠袖，揾英雄泪！', '辛弃疾', null, '1', 'X', null);
INSERT INTO `article` VALUES ('178', '摸鱼儿', '更能消、几番风雨，匆匆春又归去。惜春长怕花开早，何况落红无数。春且住！见说道天涯芳草无归路。怨春不语，算只有殷勤，画檐蛛网，终日惹飞絮。长门事,准拟佳期又误,蛾眉曾有人妒。千金纵买相如赋，脉脉此情谁诉？君莫舞！君不见、玉环飞燕皆尘土。闲愁最苦，休去倚危阑，斜阳正在，烟柳断肠处。', '辛弃疾', null, '1', 'X', null);
INSERT INTO `article` VALUES ('179', '永遇乐', '千古江山，英雄无觅、孙仲谋处。舞榭歌台，风流总被、雨打风吹去。斜阳草树，寻常巷陌，人道寄奴曾住。想当年，金戈铁马，气吞万里如虎。元嘉草草,封狼居胥,赢得仓皇北顾。四十三年，望中犹记、灯火扬州路。可堪回首、佛狸祠下，一片神鸦社鼓。凭谁问，廉颇老矣，尚能饭否？　', '辛弃疾', null, '1', 'X', null);
INSERT INTO `article` VALUES ('180', '木兰花慢', '老来情味减，对别酒，怯流年。况屈指中秋，十分好月，不照人圆。无情水，都不管，共西风、只管送归船。秋晚莼鲈江上，夜深儿女灯前。征衫，便好去朝天。玉殿正思贤。想夜半承明，留教视草，却遣筹边。长安故人问我，道愁肠殢酒只依然。目断秋霄落雁，醉来时响空弦。　', '辛弃疾', null, '1', 'X', null);
INSERT INTO `article` VALUES ('181', '祝英台近·晚春', '宝钗分，桃叶渡，烟柳暗南浦。怕上层楼，十日九风雨。断肠片片飞红，都无人管，更谁劝、啼莺声住？髻边觑，应把花卜归期，才簪又重数。罗帐灯昏，哽咽梦中语。是他春带愁来，春归何处？却不解、带将愁去。　', '辛弃疾', null, '1', 'X', null);
INSERT INTO `article` VALUES ('182', '青玉案·元夕', '东风夜放花千树，更吹落，星如雨。宝马雕车香满路，凤箫声动，玉壶光转，一夜鱼龙舞。蛾儿雪柳黄金缕，笑语盈盈暗香去。众里寻他千百度，蓦然回首，那人却在，灯火阑珊处。', '辛弃疾', null, '1', 'X', null);
INSERT INTO `article` VALUES ('183', '鹧鸪天', '枕箪溪堂冷欲秋，断云依水晚来收。红莲相倚浑如醉，白鸟无言定自愁。书咄咄，且休休，一丘一壑也风流。不知筋力衰多少，但觉新来懒上楼。', '辛弃疾', null, '1', 'X', null);
INSERT INTO `article` VALUES ('184', '菩萨蛮', '郁孤台下清江水，中间多少行人泪。西北是长安，可怜无数山。青山遮不住，毕竟东流去。江晚正愁余，山深闻鹧鸪。', '辛弃疾', null, '1', 'X', null);
INSERT INTO `article` VALUES ('185', '点绛唇', '丁未冬，过吴松作燕雁无心，太湖西畔随云去。数峰清苦，商略黄昏雨。第四桥边，拟共天随住。今何许？凭栏怀古，残柳参差舞。', '姜夔', null, '1', 'J', null);
INSERT INTO `article` VALUES ('186', '鹧鸪天', '肥水东流无尽期，当初不合种相思。梦中未比丹青见，暗里忽惊山鸟啼。春未绿，鬓先丝，人间别久不成悲。准教岁岁红莲夜，两处沉吟各自知。', '姜夔', null, '1', 'J', null);
INSERT INTO `article` VALUES ('187', '踏莎行', '燕燕轻盈，莺莺娇软，分明又向华胥见。夜长争得薄情知？春初早被相识染。别后书辞，别时针线，离魂暗逐郎行远。淮南皓月冷千山，冥冥归去无人管。', '姜夔', null, '1', 'J', null);
INSERT INTO `article` VALUES ('188', '庆宫春', '双桨莼波，一蓑松雨，暮愁渐满空阔。呼我盟鸥，翩翩欲下，背人还过木末。那回归去，荡云雪、孤舟夜发。伤心重见，依约眉山，黛痕低压。　采香径里春寒，老子婆娑，自歌谁答？垂虹西望，飘然引去，此兴平生难遏。酒醒波远，正凝想、明珰素袜。如今安在？惟有阑干，伴人一霎。　', '姜夔', null, '1', 'J', null);
INSERT INTO `article` VALUES ('189', '齐天乐', '庚郎先自吟愁赋，凄凄更闻私语。露湿铜铺，苔侵石井，都是曾听伊处。正思妇无眠，起寻机杼。曲曲屏山，夜凉独自甚情绪？西窗又吹暗雨，为谁频断续，相和砧杵？候馆迎秋，离宫吊月，别有伤心无数。豳诗漫与，笑篱落呼灯，世间儿女。写入琴丝，一声声更苦。', '姜夔', null, '1', 'J', null);
INSERT INTO `article` VALUES ('191', '琵琶仙', '芳莲坠粉，疏桐吹绿，庭院暗雨乍歇。无端抱影销魂处，还见篠墙萤暗，藓阶蛩切。送客重寻西去路，问水面、琵琶谁拔？最可惜一片江山，总付与啼鴂。长恨相逢未款，而今何事，又对西风离别？渚寒烟淡，棹移人远，漂渺行舟如叶。想文君望久，倚竹愁生步罗袜。归来后翠尊双饮,下了珠帘,玲珑闲看月。', '姜夔', null, '1', 'J', null);
INSERT INTO `article` VALUES ('192', '念奴娇', '闹红一舸，记来时尝与鸳鸯为侣。三十六陂人未到，水佩风裳无数。翠叶吹凉，玉容销酒，更洒菰蒲雨。嫣然摇动，冷香飞上诗句。日暮青盖亭亭,情人不见,争忍凌波去？只恐舞衣寒易落，愁入西凤南浦。高柳垂阴，老鱼吹浪，留我花间住。田田多少，几回沙际归路。', '姜夔', null, '1', 'J', null);
INSERT INTO `article` VALUES ('193', '扬州慢', '淮左名都，竹西佳处，解鞍少驻初程。过春风十里，尽荠麦青青。自胡马窥江去后，废池乔木，犹厌言兵。渐黄昏、清角吹寒，都在空城。　杜郎俊赏，算而今、重到须惊。纵豆蔻词工，青楼梦好，难赋深情。二十四桥仍在，波心荡、冷月无声。念桥边红药，年年知为谁生？　', '姜夔', null, '1', 'J', null);
INSERT INTO `article` VALUES ('194', '长亭怨慢', '予颇喜自制曲，初率意为长短句，然后协以律，故前後阕多不同。桓大司马云「昔年种柳，依依汉南，今看摇落，凄怆江潭，树犹如此，人何以堪」。此语予深爱之。　渐吹尽，枝头香絮，是处人家，绿深门户。远浦萦回，暮帆零乱向何许？阅人多矣，谁得似长亭树？树若有情时，不会得青青如此！日暮，望高城不见，只见乱山无数。韦郎去也，怎忘得玉箫分付。第一是早早归来，怕红萼无人为主。算空有并刀，难剪离愁千缕。', '姜夔', null, '1', 'J', null);
INSERT INTO `article` VALUES ('197', '淡黄柳', '苔枝缀玉，有翠禽小小，枝上同宿。客里相逢,篱角黄昏,无言自倚修竹。昭君不惯胡沙远，但暗忆、江南江北。想佩环、月夜归来，化作此花幽独。　犹记深宫旧事，那人正睡里，飞近蛾绿。莫似春风，不管盈盈，早与安排金屋。还教一片随波去，又却怨、玉龙哀曲。等恁时、重觅幽香，已入小窗横幅。', '姜夔', null, '1', 'J', null);
INSERT INTO `article` VALUES ('198', '翠楼吟', '月冷龙沙，尘清虎落，今年汉酺初赐。新翻胡部曲，听毡幕元戎歌吹。层楼高峙，看槛曲萦红，檐牙飞翠。人姝丽，粉香吹下，夜寒风细。　此地宜有词仙，拥素云黄鹤，与君游戏。玉梯凝望久，但芳草萋萋千里。天涯情味，仗酒祓清愁，花消英气。西山外，晚来还卷，一帘秋霁。', '姜夔', null, '1', 'J', null);
INSERT INTO `article` VALUES ('199', '杏花天', '绿丝低拂鸳鸯浦，想桃叶，当时唤渡。又将愁眼与春风,待去,倚兰桡更少驻。金陵路，莺吟燕舞。算潮水知人最苦。满汀芳草不成归,日暮,更移舟向甚处？', '姜夔', null, '1', 'J', null);
INSERT INTO `article` VALUES ('200', '一萼红', '古城阴，有官梅几许，红萼未宜簪。池面冰胶,墙腰雪老,云意还又沉沉。翠藤共闲穿径竹，渐笑语、惊起卧沙禽。野老林泉，故王台榭，呼唤登临。　南去北来何事，荡湘云楚水，目极伤心。朱户黏鸡，金盘簇燕，空叹时序侵寻。记曾共西楼雅集，想垂柳、还袅万丝金。待得归鞍到时，只怕春深。　', '姜夔', null, '1', 'J', null);
INSERT INTO `article` VALUES ('201', '霓裳中序第一', '亭皋正望极，乱落江莲归未得。多病却无气力，况纨扇渐疏，罗衣初索。流光过隙，叹杏梁双燕如客。　人何在？一帘淡月，仿佛照颜色。　幽寂，乱蛩吟壁，动庚信清愁似织。沉思年少浪迹,笛里关山,柳下坊陌。坠红无信息，漫暗水、涓涓溜碧。飘零久，而今何意，醉卧酒垆侧！', '姜夔', null, '1', 'J', null);
INSERT INTO `article` VALUES ('202', '小重山', '柳暗花明春事深,小阑红芍药,已抽簪。雨馀风软碎鸣禽,迟迟日,犹带一分阴。往事莫沉吟，身闲时序好、且凳临。旧游无处不堪寻，无寻处、惟有少年心。', '章良能', null, '1', 'Z', null);
INSERT INTO `article` VALUES ('203', '唐多令', '芦叶满汀洲，寒沙带浅流。二十年重过南楼。柳下系船犹未稳，能几日，又中秋。黄鹤断矶头，故人曾到否？旧江山浑是新愁。欲买桂花同载酒，终不似，少年游。', '刘过', null, '1', 'L', null);
INSERT INTO `article` VALUES ('204', '木兰花', '春风只在园西畔，荠菜花繁胡蝶乱。冰池晴绿照还空，香径落红吹已断。意长翻恨游丝短，尽日相思罗带缓。宝奁如月不欺人，明日归来君试看。', '严仁', null, '1', 'Y', null);
INSERT INTO `article` VALUES ('205', '风入松', '一春长费买花钱，日日醉湖边。玉骢惯识西湖路，骄嘶过、沽酒楼前。红杏香中箫鼓，绿杨影里秋千。暖风十里丽人天，花压鬓云偏。画船载取春归去，余情付、湖水湖烟。明日重扶残醉，来寻陌上花钿。', '俞国宝', null, '1', 'Y', null);
INSERT INTO `article` VALUES ('206', '满庭芳', '月洗高梧，露溥幽草，宝钗楼外秋深。土花沿翠，萤火坠墙阴。静听寒声断续，微韵转，凄咽悲沉。争求侣、殷勤劝织，促破晓机心。　儿时曾记得，呼灯灌穴，敛步随音。任满身花影，独自追寻。携向华堂戏斗。亭台小笼巧妆金。今休说，从渠床下，凉夜伴孤吟。　', '张镃', null, '1', 'Z', null);
INSERT INTO `article` VALUES ('207', '宴山亭', '幽梦初回，重阴未开，晓色催成疏雨。竹槛气寒，蕙畹声摇，新绿暗通南浦。未有人行，才半启回廊朱户。无绪，空望极霓旌，锦书难据。　苔径追忆曾游，念谁伴秋千，彩绳芳柱。犀帘黛卷，凤枕云孤，应也几番凝伫。怎得伊来，花雾绕、小堂深处。留住，直到老不教归去。', '张镃', null, '1', 'Z', null);
INSERT INTO `article` VALUES ('208', '绮罗香', '做冷欺花，将烟困柳，千里偷催春暮。尽日冥迷，愁里欲飞还住。惊粉重蝶宿西园，喜泥润、燕归南浦。最妨她佳约风流，钿车不到杜陵路。沉沉江上望极，还被春潮晚急，难寻官渡。隐约遥峰，和泪谢娘眉妩。临断岸、新绿生时，是落红、带愁流处。记当日门掩梨花，剪灯深夜语。　', '史达祖', null, '1', 'S', null);
INSERT INTO `article` VALUES ('209', '双双燕', '过春社了，度帘幕中间，去年尘冷。差池欲住，试入旧巢相并。还相雕梁藻井，又软语商量不定。飘然快拂花梢。翠尾分开红影。　芳径，芹泥雨润。爱贴地争飞，竞夸轻俊。红楼归晚，看足柳昏花暝。应自栖香正稳，便忘了天涯芳信。愁损翠黛双蛾，日日画阑独凭。', '史达祖', null, '1', 'S', null);
INSERT INTO `article` VALUES ('210', '东风第一枝', '巧沁兰心，偷粘草甲，东风欲障新暖。漫疑碧瓦难留，信知暮寒犹浅。行天入境，做弄出、轻松纤软。料故园、不卷重帘，误了乍来双燕。　青未了、柳回白眼，红欲断、杏开素面。旧游忆著山阴，后盟遂妨上苑。寒炉重熨，便放漫春衫针线。怕凤靴挑菜归来，万一灞桥相见。', '史达祖', null, '1', 'S', null);
INSERT INTO `article` VALUES ('211', '喜迁莺', '月波疑滴，望玉壶天近，了无尘隔。翠眼圈花,冰丝织练,黄道宝光相直。自怜诗酒瘦，难应接许多春色。最无赖，是随香趁烛，曾伴狂客。　踪迹，漫记忆，老了杜郎，忍听东风笛。柳院灯疏，梅厅雪在，谁与细倾克碧？旧情拘未定，犹自学当年游历，怕万一，误玉人夜寒帘隙。　', '史达祖', null, '1', 'S', null);
INSERT INTO `article` VALUES ('212', '三姝媚', '烟光摇缥瓦，望晴檐多风，柳花如洒。锦瑟横床,想泪痕尘影,凤弦常下。倦出犀帷，频梦见、王孙骄马。讳道相思，偷理绡衩。惆怅南楼遥夜，记翠箔张灯，枕肩歌罢。又入铜驼，遍旧家门巷，首询声价。可惜东风，将恨与闲花俱谢。记取崔徵模样。归来暗写。　', '史达祖', null, '1', 'S', null);
INSERT INTO `article` VALUES ('213', '秋霁', '江水苍苍，望倦柳愁荷，共感秋色。废阁先凉,古帘空暮,雁程最嫌风力。故园信息，爱渠入眼南山碧。念上国，谁是、脍鲈江汉未归客。还又岁晚、瘦骨临风，夜闻秋声，吹动岑寂。露蛩悲、青灯冷屋，翻书愁上鬓毛白。年少俊游浑断得，但可怜处，无奈苒苒魂惊,采香南浦,剪梅烟驿。', '史达祖', null, '1', 'S', null);
INSERT INTO `article` VALUES ('214', '夜合花', '柳锁莺魂,花翻蝶梦,自知愁染潘郎。轻衫未揽，犹将泪点偷藏。忘前事，怯流光，早春窥、酥雨池塘。向消凝里，梅开半面，情满徐妆。风丝一寸柔肠，曾在歌边惹恨，烛底萦香。芳机瑞锦，如何未织鸳鸯。人扶醉，月依墙，是当初、谁敢疏狂！把闲言语，花房夜久，各自思量。　', '史达祖', null, '1', 'S', null);
INSERT INTO `article` VALUES ('216', '玉胡蝶', '秋江带雨，寒沙萦水，人瞰画阁愁独。烟蓑散响惊诗思，还被乱鸥飞去，秀句难续。冷眼尽归图画上,认隔岸微茫云屋；想半属渔市樵村,欲暮竞然竹。须信风流未老，凭持尊酒，慰此凄凉心目。一鞭南陌，几篙官渡，赖有歌眉舒绿。只匆匆残照，早觉闲愁挂乔木。应难奈故人天际，望彻淮山，相思无雁足。', '史达祖', null, '1', 'S', null);
INSERT INTO `article` VALUES ('217', '生查子', '繁灯夺霁华，戏鼓侵明发。物色旧时同，情味中年别。浅画镜中眉，深拜楼中月。人散市声收，渐入愁时节。', '刘克庄', null, '1', 'L', null);
INSERT INTO `article` VALUES ('218', '贺新郎', '深院榴花吐，画帘开、綀衣纨扇，午风清暑。儿女纷纷夸结束，新样钗符艾虎。早已有游人观渡。老大逢场慵作戏，任陌头年少争旗鼓，溪雨急，浪花舞。灵均标致高如许，忆生平、既纫兰佩，更怀椒醑。谁信骚魂千载后，波底垂涎角黍。又说是、蛟馋龙怒。把似而今醒到了，料当年，醉死差无苦，聊一笑。', '刘克庄', null, '1', 'L', null);
INSERT INTO `article` VALUES ('219', '贺新郎', '湛湛长空黑，更那堪、斜风细雨，乱愁如织。老眼平生空四海，赖有高楼百尺。看浩荡千崖秋色。白发书生神州泪，尽凄凉不向牛山滴。追往事，去无迹。少年自负凌云笔，到而今春华落尽，满怀萧瑟。常恨世人新意少，爱说南朝狂客。把破帽年年拈出。若对黄花孤负酒,怕黄花也笑人岑寂。鸿去北,日西匿。', '刘克庄', null, '1', 'L', null);
INSERT INTO `article` VALUES ('220', '木兰花', '年年跃马长安市，客舍似家家似寄。青钱换酒日无何，红烛呼卢宵不寐。易挑锦妇机中字，难得玉人心下事。男儿西北有神州，莫滴水西桥畔泪。', '刘克庄', null, '1', 'L', null);
INSERT INTO `article` VALUES ('221', '江城子', '画楼帘暮卷新晴，掩银屏，晓寒轻。坠粉飘香，日日唤愁生。暗数十年湖上路，能几度，着娉婷。　年华空自感漂零，拥春醒？天阔云闲，无处觅萧声。载酒买花年少事，浑不似、旧心情。　', '卢祖皋', null, '1', 'L', null);
INSERT INTO `article` VALUES ('222', '宴清都', '春讯飞琼管，风日薄，度墙啼鸟声乱。江城次第，笙歌翠合，绮罗香暖。溶溶涧渌冰泮，醉梦里，年华暗换。料黛眉，重锁隋堤，芳心还动梁苑。新来雁阔云音，鸾分鉴影，无计重见。春啼细雨，笼愁淡月，恁时庭院。离肠未语先断，算犹有凭高望眼。更那堪衰草边天，飞梅弄晚。', '卢祖皋', null, '1', 'L', null);
INSERT INTO `article` VALUES ('223', '南乡子', '生怕倚阑干，阁下溪声阁外山。惟有旧时山共水，依然，暮雨朝云去不还。应是蹑飞鸾，月下时时整佩环。月又渐低霜又下，更阑，折得梅花独自看。', '潘牥', null, '1', 'P', null);
INSERT INTO `article` VALUES ('224', '瑞鹤仙', '湿云粘雁影，望征唱片，愁迷离绪难整。千金买光景，但疏钟催晓，乱鸦啼螟。花悰暗省，许多情，相逢梦境。便行云都不归来，也合寄将音信。孤迥。盟鸾心在，跨鹤程高，后期无准。情丝等剪，翻若得旧时恨。怕天教何处，参差双燕，还染残朱剩粉。对菱花与说相思，看谁瘦损？', '陆壑', null, '1', 'L', null);
INSERT INTO `article` VALUES ('225', '渡江云', '羞经颦浅恨，晚风未落，片绣点重茵。旧堤分燕尾，桂棹轻鸥，宝勒倚残云。千丝怨碧，渐路入、仙坞迷津。肠漫回，隔花时见、背面楚腰身。逡巡，题门惆怅，堕履牵萦。数幽期难准，还始觉留情缘眼，宽带因春。明朝事与孤冷，做满湖风雨愁人。山黛暝，尘波澹绿无痕。', '吴文英', null, '1', 'W', null);
INSERT INTO `article` VALUES ('226', '夜合花', '白鹤江入京，泊葑门，有感。柳暝河桥,莺清台苑,短策频惹春香。当时夜泊，温柔便入深乡。词韵窄，酒杯长，剪蜡花、壶箭催忙。共追游处，凌波翠陌，连棹横塘。十年一梦凄凉，似西湖燕去，吴馆巢荒。重来万感，依前唤酒银罂。溪雨急，岸花狂，趁残鸦飞过苍茫。故人楼上，凭谁指与芳草斜阳？', '吴文英', null, '1', 'W', null);
INSERT INTO `article` VALUES ('227', '霜叶飞', '断烟离绪，关心事，斜阳红隐霜树。半壶秋水荐黄花，香噀西风雨。纵玉勒、轻飞迅羽，凄凉谁吊荒台古。记醉踏南屏，彩扇咽寒蝉，倦梦不知蛮素。聊对旧节传杯，尘管，断阕经岁慵赋。小蟾斜影转东篱，夜冷残蛩语。早白发、缘愁万缕，惊从卷乌纱去，漫细将、茱萸看，但约明年，翠微高处。　', '吴文英', null, '1', 'W', null);
INSERT INTO `article` VALUES ('228', '宴清都', '绣幄鸳鸯柱，红情密、腻云低护秦树。芳草兼倚，花梢钿合，锦屏人妒。东风睡足交枝，正梦枕瑶钗燕股。障滟蜡、满照欢丛，蟾冷落羞度。人间万感幽单，华清惯浴，春盎风露。连鬟并暖，同心共结，向承恩处。凭谁为歌长恨？暗殿锁、秋灯夜语。叙旧期、不负春盟，红朝翠暮。', '吴文英', null, '1', 'W', null);
INSERT INTO `article` VALUES ('230', '齐天乐', '小娉婷清铅素靥，蜂黄暗偷晕，翠翘攲鬓。昨夜冷中庭，月下相认，睡浓更苦凄风紧。惊回心未稳，送晓色、一壶葱茜，才知花梦准。湘娥化作此幽芳，凌波路，古岸云沙遗恨。临砌影，寒香乱、冻梅藏韵。熏炉畔、旋移傍枕，还又见、玉人垂绀鬓。料唤赏、清华池馆，台杯须满引。', '吴文英', null, '1', 'W', null);
INSERT INTO `article` VALUES ('231', '浣溪沙', '门隔花深旧梦游，夕阳无语燕归愁，玉纤香动小帘钩。落絮无声春堕泪，行云有影月含羞，东风临夜冷于秋。', '吴文英', null, '1', 'W', null);
INSERT INTO `article` VALUES ('232', '浣溪沙', '波面铜花冷不收，玉人垂钓理纤钩，月明池阁夜来秋。江燕话归成晓别，水花红减似春休，西风梧井叶先愁。', '吴文英', null, '1', 'W', null);
INSERT INTO `article` VALUES ('233', '点绛唇', '卷尽愁云，素娥临夜新梳洗。暗尘不起，酥润凌波地。辇路重来，仿佛灯前事。情如水，小楼熏被，春梦笙歌里。', '吴文英', null, '1', 'W', null);
INSERT INTO `article` VALUES ('234', '祝英台', '采幽香，巡古苑，竹冷翠微路。斗草溪根，沙印小莲步。自怜两鬓清霜，一年寒食，又身在云山深处。昼闲度，因甚天也悭春，轻阴便成雨？绿暗长亭，归梦趁风絮。有情花影阑干，莺声门径，解留我霎时凝伫。', '吴文英', null, '1', 'W', null);
INSERT INTO `article` VALUES ('235', '祝英台', '剪红情，裁绿意，花信上钗股。残日东风，不放岁华去。有人添烛西窗，不眠侵晓，笑声转新年莺语。旧尊俎，玉纤曾擘黄柑，柔香系幽素。归梦湖边，还迷镜中路。可怜千点吴霜，寒消不尽，又相对落梅如雨。', '吴文英', null, '1', 'W', null);
INSERT INTO `article` VALUES ('236', '澡兰香', '盘丝系腕，巧篆垂簪，玉隐绀纱睡觉。银瓶露井，彩囗云窗，往事少年依约。为当时曾写榴裙，伤心红绡褪萼。黍梦光阴，渐老汀洲烟箬。莫唱江南古调，怨抑难招，楚江沉魄。薰风燕乳，暗雨槐黄，午镜澡兰帘幕。念秦楼、也拟人归，应剪菖蒲自酌。但怅望一缕新蟾，随人天角。', '吴文英', null, '1', 'W', null);
INSERT INTO `article` VALUES ('237', '风入松', '听风听雨过清明，愁草瘗花铭。楼前绿暗分携路，一丝柳、一寸柔情。料峭春寒中酒，交加晓梦啼莺。西园日日扫林亭，依旧赏新晴。黄蜂频扑秋千索，有当时、纤手香凝。惆怅双鸳不到，幽阶一夜苔生。', '吴文英', null, '1', 'W', null);
INSERT INTO `article` VALUES ('238', '莺啼序', '残寒正欺病酒,掩沉香绣户。燕来晚、飞入西城,似说春事迟暮。画船载、清明过却，晴烟冉冉吴宫树。念羁情、游荡随风，化为轻絮。十载西湖，傍柳系马,趁娇尘软雾。溯红渐、招入仙溪,锦儿偷寄幽素。倚银屏春宽梦窄，断红湿、歌纨金缕。螟堤空,轻把斜阳,总还鸥鹭。幽兰旋老，杜若还生，水乡尚寄旅。别后访、六桥无信,事往花委,瘗玉埋香，几番风雨。长波妒盼，遥山羞黛,渔灯分影克江宿。记当时、短楫桃根渡,青楼仿佛。临分败壁题诗，泪墨惨淡尘土。危亭望极，草色天涯，叹鬓侵半苧。暗点检、离痕欢唾，尚染鲛绡，嚲凤迷归，破鸾慵舞。殷勤待写，书中长恨，蓝霞辽海沉过雁。漫相思、弹入哀筝柱。伤心千里江南，怨曲重招，断魂在否？', '吴文英', null, '1', 'W', null);
INSERT INTO `article` VALUES ('239', '惜黄花慢', '送客吴皋，正试霜夜冷，枫落长桥。望天不尽，背城渐杳，离亭黯黯，恨水迢迢。翠香零落红衣老，暮愁锁，残柳眉梢。念瘦腰,沈郎旧日,曾系兰桡。仙人凤咽琼箫，怅断魂送远，九辩难招。醉鬟留盼，小窗剪烛,歌云载恨,飞上银霄。素秋不解随船去，败红趁一叶寒涛。梦翠翘，怨鸿料过南谯。', '吴文英', null, '1', 'W', null);
INSERT INTO `article` VALUES ('240', '高阳台', '宫粉雕痕，仙云堕影，无人野水荒湾。西石埋香，金沙锁骨连环。南楼不恨吹横笛，恨晓风千里关山。半飘零、庭上黄昏，月冷阑干。寿阳空理愁鸾，问谁调玉髓，暗补香瘢？细雨归鸿，孤山无限春寒。离魂难倩招清些，梦缟衣解溪边。最愁人、啼鸟晴明，叶底清圆。', '吴文英', null, '1', 'W', null);
INSERT INTO `article` VALUES ('241', '高阳台', '丰乐楼分韵得“如”字修竹凝妆，垂杨驻马，凭阑浅画成图。山色谁题？楼前有雁斜书。东风紧送斜阳下，弄旧寒、晚酒醒馀。自消凝，能几花前，顿老相如？伤春有在高楼上，在灯前攲枕，雨外熏炉。怕舣船，临流可奈清臞？飞红若到西湖底，搅翠澜、总是愁鱼。莫重来、吹尽香绵，泪满平芜。', '吴文英', null, '1', 'W', null);
INSERT INTO `article` VALUES ('242', '三姝媚', '湖山经醉惯,渍春衫,啼痕酒痕无限。又客长安，叹断襟零袂，涴尘谁浣。紫曲门荒，沿败井、风摇青蔓。对语东邻，犹是曾巢，谢堂双燕。春梦人间须断，但怪得当年，梦缘能短。绣屋秦筝，傍海棠偏爱，夜深开宴。舞歇歌沉，花未减、红颜先变。伫久河桥欲去，斜阳泪满。', '吴文英', null, '1', 'W', null);
INSERT INTO `article` VALUES ('243', '八声甘州', '渺空烟四远，是何年、青天坠长星。幻苍崖云树，名娃金屋，残霸宫城。箭径酸风射眼，腻水染花腥。时靸双鸳响，廊叶秋声。宫里吴王沉醉，倩五湖倦客，独钓醒醒。问苍波无语，华发奈山青。水涵空、阑干高处，送乱鸦、斜日落渔汀。连呼酒、上琴台去，秋与云平。', '吴文英', null, '1', 'W', null);
INSERT INTO `article` VALUES ('244', '踏莎行', '润玉笼绡，檀樱倚扇，绣圈犹带脂香浅。榴心空叠舞裙红，艾枝应愁鬟乱。午梦千山,窗阴一箭,香凝瘢新褪红丝腕。隔江人在雨声中，晚风菰叶生秋怨。', '吴文英', null, '1', 'W', null);
INSERT INTO `article` VALUES ('245', '瑞鹤仙', '晴丝牵绪乱，对沧江斜日，花飞人远。垂杨暗吴苑，正旗亭烟冷，河桥风暖。兰情蕙盼，惹相思、春根酒畔。又争知、吟骨萦消，渐把旧衫重剪。凄断流红千浪，缺月孤楼，总难留燕。歌尘凝扇，待凭信，拚分钿。试挑灯欲写，还依不忍，笺幅偷和泪卷。寄残云剩雨蓬莱，也应梦见。', '吴文英', null, '1', 'W', null);
INSERT INTO `article` VALUES ('246', '鹧鸪天', '池上红衣伴倚阑，栖鸦常带夕阳还。殷云度雨疏桐落，明月生凉宝扇闲。乡梦窄，水天宽，小窗愁黛淡秋山。吴鸿好为传归信，杨柳阊门屋数间。', '吴文英', null, '1', 'W', null);
INSERT INTO `article` VALUES ('247', '夜游宫', '人去西楼雁杳,叙别梦、扬州一觉。云淡星疏楚山晓。听啼乌,立河桥，话未了。雨外蛩声早，细织就霜丝多少？说与萧娘未知道。向长安，对秋灯，几人老？', '吴文英', null, '1', 'W', null);
INSERT INTO `article` VALUES ('248', '贺新郎', '乔木生云气。访中兴、英雄陈迹，暗追前事。战舰东风悭借便，梦断神州故里。旋小筑、吴宫闲地，华表月明归夜鹤，叹当时、花竹今如此，枝上露，溅清泪。遨头小簇行春队，步苍苔、寻幽别墅，问梅开未？重唱梅边新度曲，催发寒梢冻蕊。此心与东君同意，后不如今今非昔，两无言相对沧浪水，怀此恨，寄残醉。', '吴文英', null, '1', 'W', null);
INSERT INTO `article` VALUES ('249', '唐多令', '何处合成愁？离人心上秋，纵芭蕉、不雨也飕飕。都道晚凉天气好，有明月，怕登楼。年事梦中休，花空烟水流，燕辞归、客尚淹留。垂柳不萦裙带住，漫长是系行舟。', '吴文英', null, '1', 'W', null);
INSERT INTO `article` VALUES ('250', '湘春夜月', '近清明，翠禽枝上消魂。可惜一片清歌，都付与黄昏。欲共柳花低诉。怕柳花轻薄，不解伤春。衾楚乡旅宿，柔情别绪，谁与温存？空尊夜泣，青山不语，残照当门。翠玉楼前，惟是有、一陂湘水，摇荡湘云。天长梦短，问甚时重见桃根？这次第、算人间没个并刀，剪断心上愁痕。', '黄孝迈', null, '1', 'H', null);
INSERT INTO `article` VALUES ('251', '大有·九日', '戏马台前，采花篱下，问岁华、还是重九。恰归来、南山翠色依旧。帘栊昨夜听风雨，都不似登临时候。一片宋玉情怀，十分卫郎清瘦。红萸佩，空对酒。砧杵动微寒，暗欺罗袖。秋已无多，早是败荷衰柳。强整帽檐攲侧，曾经向天涯搔首。几回忆、故国莼鲈，霜前雁后。', '潘希白', null, '1', 'P', null);
INSERT INTO `article` VALUES ('252', '青玉案', '年年社日停针线，怎忍见、双飞燕？今日江城春已半，一身犹在，乱山深处，寂寞溪桥畔。春衫著破谁针线？点点行行泪痕满。落日解鞍芳草岸，花无人戴，酒无人劝，醉也无人管。', '黄公绍', null, '1', 'H', null);
INSERT INTO `article` VALUES ('253', '摸鱼儿', '对西风、鬓摇烟碧，参差事流水。紫丝罗带鸳鸯结，的的镜盟钗誓。浑不记，漫手织回文，几度欲心碎。安花著叶，奈雨覆云翻，情宽分窄，石上玉簪脆。朱楼外,愁压空云欲坠,月痕犹照无寐。阴晴也只随天意，枉了玉消香碎。君且醉，君不见、长门青草春风泪。一时左计，悔不早荆钗，暮天修竹，头白倚寒翠。', '朱嗣发', null, '1', 'Z', null);
INSERT INTO `article` VALUES ('254', '兰陵王', '送春去，春去人间无路。秋千外，芳草连天，谁谴风沙暗南浦。依依甚意绪？漫忆海门飞絮。乱鸦过、斗转城荒，不见来时试灯处。春去谁最苦？但箭雁沉边，梁燕无主，杜鹃声里长门暮。想玉树凋土，泪盘如露。咸阳送客屡回顾，斜日未能度。春去，尚来否？正江令恨别，庚信愁赋，苏堤尽日风和雨。叹神游故国，花记前度。人生流落，顾孺子，共夜语。', '刘辰翁', null, '1', 'L', null);
INSERT INTO `article` VALUES ('255', '宝鼎现', '红妆春骑，踏月影竿旗穿市。望不尽、楼台歌舞，习习香尘莲步底。箫声断、约彩鸾归去，未怕金吾呵醉。甚辇路、喧阗且止，听得念奴歌起。父老犹记宣和事，抱铜仙、清泪如水。还转盼、沙河多丽。滉漾明光连邸第，帘影冻、散红光成绮。月浸葡萄十里，看往来，神仙才子，肯把菱花扑碎。肠断竹马儿童，空见说、三千乐指。等多时春不归来，到春时欲睡。又说向灯前拥髻，暗滴鲛珠坠。便当日亲见霓裳，天上人间梦里。', '刘辰翁', null, '1', 'L', null);
INSERT INTO `article` VALUES ('256', '永遇乐', '壁月初晴，黛云远淡，春事谁主？禁苑娇寒，湖堤倦暖，前度遽如许。香尘暗陌，华灯明昼，长是懒携去。谁知道、断烟禁夜，满城似愁风雨。宣和旧日，临安南渡，芳景犹自如故。细帙离离,风鬟三五,能赋词最苦。江南无路，鄜州今夜，此苦又谁知否？空相对公、残釭无寐，满村社鼓。', '刘辰翁', null, '1', 'L', null);
INSERT INTO `article` VALUES ('257', '摸鱼儿', '怎知他、春归何处？相逢且尽尊酒。少年袅袅天涯恨，长结西湖烟柳。休回首，但细雨断桥，憔悴人归后。东风似旧，向前度桃花，刘郎能记，花复认郎否？君且住,草草留君剪韭,前宵正恁时候。深杯欲共歌声滑，翻湿春衫半袖。空眉皱，看白发尊前，已似人人有。临分把手，叹一笑论文，清狂顾曲，此会几时又？', '刘辰翁', null, '1', 'L', null);
INSERT INTO `article` VALUES ('258', '高阳台', '照野旌旗，朝天车马，平沙万里天低。宝带金章，尊前茸风欺。秦关汴水经行地，想登临都付新诗。纵英游、叠鼓清笳，骏马名姬。酒酣应对燕山雪，正冰河月冻，晓陇云飞。投老残年，江南谁念方回？东风渐绿西湖岸，雁已还人未南归。归关情、折尽梅花，难寄相思。　', '周密', null, '1', 'Z', null);
INSERT INTO `article` VALUES ('259', '瑶华', '朱钿宝囗，天上飞琼，比人间春别。江南北上，终未见、漫拟梨云梅雪。淮山春晚，问谁识、芳心高洁？消几番、花落花开，老了玉关豪杰。金壶剪送琼枝，看一骑红尘，香度瑶阙。韶华正好，应自喜、初乱长安蜂蝶。杜郎老矣，想旧事花须能说。记少年一梦扬州，二十四桥明月。', '周密', null, '1', 'Z', null);
INSERT INTO `article` VALUES ('260', '玉京秋', '烟水阔，高林弄残照，晚蜩凄切。碧砧度韵，银床飘叶。衣湿桐阴露冷，采凉花时赋秋雪。叹轻别，一襟幽事，砌虫能说。客思吟商还怯，怨歌长、琼壶暗缺。翠扇恩疏，红衣香褪，翻成消歇。玉骨西风，恨最恨、闲却新凉时节。楚箫咽，谁寄西楼淡月。', '周密', null, '1', 'Z', null);
INSERT INTO `article` VALUES ('262', '曲游春', '楚江湄，湘娥再见，无言洒清泪，淡然春意。空独倚东风，芳思谁寄？凌波路冷秋无际。　香云随步起，漫记得、汉宫仙掌，亭亭明月底。冰丝写怨更多情，骚人恨枉赋芳兰幽芷。春思远，谁叹赏国香风味？相露，一枝灯影里。', '周密', null, '1', 'Z', null);
INSERT INTO `article` VALUES ('263', '瑞鹤仙', '绀烟迷雁迹，渐碎鼓零钟，街喧初息。风檠背寒壁，放冰蟾，飞到蛛丝帘隙。琼瑰暗泣，念乡关、霜华似织。漫将身、化鹤归来，忘却旧游端的。欢极蓬壶蕖浸，花院梨溶，醉连春夕。柯云罢弈，樱桃在，梦难觅。劝清光、乍可幽窗相照，休照红楼夜笛。怕人间、换谱伊凉，素娥未识。', '蒋捷', null, '1', 'J', null);
INSERT INTO `article` VALUES ('264', '贺新郎', '梦冷黄金屋。叹秦筝、斜鸿阵里，素弦尘扑。化作娇莺飞归去，犹认纱窗旧绿。正过雨、荆桃如菽。此恨难平君知否？似琼台、涌起弹棋局。消瘦影，嫌明烛。鸳楼碎泻东西玉。问芳踪、何时再展？翠钗难卜。待把宫眉横云样，描上生绡画幅。怕不是新来妆束。彩扇红牙今都在，恨无人解听开元曲。空掩袖，倚寒竹。', '蒋捷', null, '1', 'J', null);
INSERT INTO `article` VALUES ('265', '女冠子', '蕙花香也，雪晴池馆如画。春风飞到，宝钗楼上，一片笙箫，琉璃光射。而今灯漫挂，不是暗尘明月，那时元夜。况年来、心懒意怯，羞与蛾儿争耍。江城人悄初更打，问繁华谁解，再向天公借？剔残红灺，但梦里隐隐，钿车罗帕。吴笺银粉砑，待把旧家风景，写成闲话。笑绿鬟邻女，倚窗犹唱，夕阳西下。', '蒋捷', null, '1', 'J', null);
INSERT INTO `article` VALUES ('266', '高阳台', '接叶巢莺，平波卷絮，断桥斜日归船。能几番游？看花又是明年。东风且伴蔷薇住，到蔷薇、春已堪怜。更凄然，万绿西冷，一抹荒烟。当年燕子知何处？但苔深韦曲，草暗斜川。见说新愁，如今也到鸥边。无心再续笙歌梦，掩重门、浅醉闲眠。莫开帘，怕见飞花，怕听啼鹃。', '张炎', null, '1', 'Z', null);
INSERT INTO `article` VALUES ('267', '渡江云', '山空天入海，倚楼望极，风急暮潮初。一帘鸠外雨，几处闲田，隔水动春锄。新烟禁柳，想如今、绿到西湖。犹记得、当年深隐，门掩两三株。　愁余，荒洲古溆，断梗疏萍，更漂流何？空自觉围羞带减，影怯烟孤。长疑即见桃花面，甚近来翻无书。书纵远，如何梦也都无。　', '张炎', null, '1', 'Z', null);
INSERT INTO `article` VALUES ('268', '八声甘州', '记玉关、踏雪事清游，寒气脆貂裘。傍枯林古道，长河饮马，此意悠悠。短梦依然江表，老泪洒西州。一字列题处，落叶都愁。载取白云归去，问谁留楚佩，弄影中洲？折芦花赠远，零落一身秋。向寻常、野桥流水，待招来、不是旧沙鸥。空怀感，有斜阳处，却怕登楼。　', '张炎', null, '1', 'Z', null);
INSERT INTO `article` VALUES ('269', '解连环', '楚江空晚，恨离群万里，恍然惊散。自顾影、却下寒塘，正沙净草枯，水平天远。写不成书，只寄得相思一点。料因循误了，残毡拥雪，故人心眼。　谁怜旅愁荏苒，漫长门夜悄,锦筝弹怨,想伴侣、犹宿芦花，也曾念春前，去程应转。暮雨相呼，怕蓦地、玉关重见。未羞他、双燕归来，画帘半卷。　', '张炎', null, '1', 'Z', null);
INSERT INTO `article` VALUES ('270', '疏影', '碧圆自洁，向浅洲远浦，亭亭清绝。犹有遗簪,不展秋心,能卷几多炎热？鸳鸯密语同倾盖，且莫与、浣纱人说。恐怨歌、忽断花风，碎却翠云千叠。　回首当年汉舞，怕飞去漫皱，留仙裙折。恋恋青衫，犹染枯香，还叹鬓丝飘雪。盘心清露如铅水,又一夜西风吹折。喜净看、匹练飞光,倒泻半湖明月。', '张炎', null, '1', 'Z', null);
INSERT INTO `article` VALUES ('271', '月下笛', '万里孤云，清游渐远，故人何处？寒窗梦里，犹记经行旧时路。连昌约略无多柳，第一是难听夜雨。漫惊回凄悄，相看烛影，拥衾无语。张绪归何暮？半零落依依，断桥鸥鹭。天涯倦旅，此时心事良苦。只愁重洒西州泪，问杜曲人家在否？恐翠袖天寒，犹倚梅花那树。', '张炎', null, '1', 'Z', null);
INSERT INTO `article` VALUES ('273', '天香', '渐新痕悬柳，淡彩穿花，依约破初暝。便有团圆意，深深拜，相逢谁在香径？画眉未稳，料素娥、犹带离恨。最堪爱、一曲银钩小，宝奁挂秋冷。千古盈亏休问，叹慢磨玉斧，难补金镜。太液池犹在，凄凉处、何人重赋清景？故山夜水，试待他、窥户端正。看云外山河，还老桂花旧影。', '王沂孙', null, '1', 'W', null);
INSERT INTO `article` VALUES ('274', '齐天乐', '一襟馀恨宫魂断，年年翠阴庭树。乍咽凉柯，还移暗叶，重把离愁深诉。西窗过雨，怪瑶佩流空，玉筝调柱。镜暗妆残，为谁娇鬓尚如许？铜仙铅泪，似斜阳几度？馀音更苦，甚独抱清商，顿成凄楚。漫想熏风，柳丝千万缕。　', '王沂孙', null, '1', 'W', null);
INSERT INTO `article` VALUES ('275', '长亭怨慢', '泛孤艇东皋过遍，尚记当日，绿阴门掩。屐齿莓苔，酒痕罗袖事何限？欲寻前迹，空惆怅成秋苑。自约赏花人，别后总风流云散。　水远，怎知流水外，却是乱山尤远。天涯梦短，想忘了绮疏雕槛。望不尽冉冉斜阳，抚乔木年华将晚。但数点红英，犹识西园凄婉。', '王沂孙', null, '1', 'W', null);
INSERT INTO `article` VALUES ('276', '高阳台', '残雪庭阴，轻寒帘影，霏霏玉管春葭。小帖金泥，不知春是谁家？相思一夜窗前梦，奈个人、水隔天遮。但凄然，满树幽香，满地横斜。江南自是离愁苦，况游骢古道，归雁平沙。怎得银笺，殷勤说与年华。如今处处生芳草，纵凭高、不见天涯。更消他，几度东风，几度飞花。', '王沂孙', null, '1', 'W', null);
INSERT INTO `article` VALUES ('278', '法曲献仙音', '江空不渡,恨蘼芜杜若,零落无数。远道荒寒，婉娩流年，望望美人迟暮。风烟雨雪阴晴晚，更何须春风千树。尽孤城、落木萧萧，日夜江声流去。日晏山深闻笛,恐他年流落,与子同赋。事阔心违，交淡媒劳，蔓草沾衣多露。汀洲窈窕馀醒寐，遗佩环、浮沉澧浦。有白鸥淡月微波，寄语逍遥容与。', '王沂孙', null, '1', 'W', null);
INSERT INTO `article` VALUES ('279', '六丑', '似东风老大，那复有当时风气。有情不收，江山身是寄，浩荡何世？但忆临官道，暂来不住，便出门千里。痴心指望回风坠，扇底相逢，钗头微缀。他家万条千缕，解遮亭障驿，不隔江水。瓜洲曾檥，等行人岁岁。日下长秋，城乌夜起。帐庐好在春睡，共飞归湖上，草青无地。愔愔雨、春心如腻、欲待化、丰乐楼前帐饮，青门都废。何人念、流落无几。点点抟作雪绵松润，为君囗泪。紫萸香慢姚云文　近重阳、偏多风雨，绝怜此日暄明。问秋香浓未，待携客、出西城。正自羁怀多感，怕荒台高处，更不胜情。向尊前又忆、漉酒插花人，只座上已无老兵。凄清，浅醉还醒。愁不肯、与诗平。记长楸走马，雕弓笮柳，前事休评。紫萸一枝传赐，梦谁到、汉家陵。尽乌纱、便随风去，要天知道，华发如此星星，歌罢涕零。　', '彭元逊', null, '1', 'P', null);
INSERT INTO `article` VALUES ('280', '金明池', '天阔云高，溪横水远，晚日寒生轻晕。闲阶静、杨花渐少，朱门掩、莺声犹嫩。悔匆匆、过却清明，旋占得、馀芳已成幽恨。却几日阴沉，连宵慵困，起来韶华都尽。　怨入双眉闲斗损，乍品得情怀，看承全近。深深态、无非自许，厌厌意、终羞人问。争知道、梦里蓬莱，待忘了馀香，时传音信。纵留得莺花，东风不住，也则眼前愁闷。　', '僧挥', null, '1', 'S', null);
INSERT INTO `article` VALUES ('281', '凤凰台上忆吹箫', '香冷金猊，被翻红浪，起来慵自梳头。任宝奁尘满，日上帘钩。生怕离怀别苦，多少事、欲说还休。新来瘦，非干病酒，不是悲秋。休休，这回去也，千万遍阳关，也则难留。念武陵人远，烟锁秦楼。惟有楼前流水，应念我、终日凝眸。凝眸处，从今又添，一段新愁。', '李清照', null, '1', 'L', null);
INSERT INTO `article` VALUES ('282', '醉花阴', '薄雾浓云愁水昼，瑞脑消金兽。佳节又重阳，玉枕纱厨，半夜凉初透。东篱把酒黄昏后，有暗香盈袖。莫道不消魂？薄雾浓云愁水昼，瑞脑消金。', '李清照', null, '1', 'L', null);
INSERT INTO `article` VALUES ('283', '声声慢', '寻寻觅觅，冷冷清清，凄凄惨惨戚戚。乍暖还寒时侯，最难将息。三杯两盏淡酒，怎敌他、晚来风急。雁过也，最伤心，却是旧时相识。满地黄花堆积，惟悴损、如今有谁堪摘。守着窗儿，独自怎生得黑？梧桐更兼细雨，到黄昏、点点滴滴。这次第，怎一个愁字了得。', '李清照', null, '1', 'L', null);
INSERT INTO `article` VALUES ('284', '念奴娇', '萧条庭院，有斜风细雨，重门须闭。宠柳娇花寒食近，种种恼人天气。险韵诗成，扶头酒醒，别是闲滋味。征鸿过尽，万千心事难寄。　楼上几日春寒,帘垂四面,玉阑干慵倚。被冷香消新梦觉，不许愁人不起。清露晨流，新桐初引，多少游春意。日高烟敛，更看今日晴未。', '李清照', null, '1', 'L', null);
INSERT INTO `article` VALUES ('285', '永遇乐', '落日溶金，暮云合璧，人在何处？染柳烟浓，吹梅笛怨，春意知几许？元宵佳期节，融和天气，次第岂无风雨。来相召、香车宝马，谢他酒朋诗侣。　中州盛日，闺门多暇，记得偏重三五。铺翟冠儿,捻金雪柳,簇带争济楚。如今憔悴，风鬟雾鬓，怕见夜间出去。不如向帘儿底下，听人笑语。', '李清照', null, '1', 'L', null);
INSERT INTO `article` VALUES ('286', '佳人', '绝代有佳人，幽居在空谷。自云良家子，零落依草木。关中昔丧乱，兄弟遭杀戮。官高何足论，不得收骨肉。世情恶衰歇，万事随转烛。夫婿轻薄儿，新人美如玉。合昏尚知时，鸳鸯不独宿。但见新人笑，那闻旧人哭！在山泉水清，出山泉水浊。侍婢卖珠回，牵萝补茅屋。摘花不插发，采柏动盈掬。天寒翠袖薄，日暮倚修竹。', '杜甫', null, '1', 'D', null);
INSERT INTO `article` VALUES ('287', '梦李白二首之一', '死别已吞声，生别常恻恻。江南瘴疠地，逐客无消息。故人入我梦，明我长相忆。君今在罗网，何以有羽翼？恐非平生魂，路远不可测。魂来枫林青，魂返关塞黑。落月满屋梁，犹疑照颜色。水深波浪阔，无使蛟龙得。', '杜甫', null, '1', 'D', null);
INSERT INTO `article` VALUES ('288', '梦李白二首之二', '浮云终日行，游子久不至。三夜频梦君，情亲见君意。告归常局促，苦道来不易。江湖多风波，舟楫恐失坠。出门搔白首，若负平生志。冠盖满京华，斯人独憔悴。孰云网恢恢，将老身反累。千秋万岁名，寂寞身后事。', '杜甫', null, '1', 'D', null);
INSERT INTO `article` VALUES ('289', '送别', '下马饮君酒，问君何所之。君言不得意，归卧南山陲。但去莫复闻，白云无尽时。', '王维', null, '1', 'W', null);
INSERT INTO `article` VALUES ('290', '送綦毋潜落第还乡', '圣代无隐者，英灵尽来归。遂令东山客，不得顾采薇。既至金门远，孰云吾道非？江淮度寒食，京洛缝春衣。置酒长安道，同心与我违。行当浮桂棹，未几拂荆扉。远树带行客，孤城当落晖。吾谋适不用，勿谓知音稀。', '王维', null, '1', 'W', null);
INSERT INTO `article` VALUES ('291', '青溪', '言入黄花川，每逐青溪水。随山将万转，趣途无百里。声喧乱石中，色静深松里。漾漾泛菱荇，澄澄映葭苇。我心素已闲，清川澹如此。请留盘石上，垂钓将已矣。', '王维', null, '1', 'W', null);
INSERT INTO `article` VALUES ('292', '渭川田家', '斜光照墟落，穷巷牛羊归。野老念牧童，倚杖候荆扉。雉［句隹］麦苗秀，蚕眠桑叶稀。田夫荷锄立，相见语依依。即此羡闲逸，怅然吟式微。', '王维', null, '1', 'W', null);
INSERT INTO `article` VALUES ('293', '西施咏', '艳色天下重，西施宁久微。朝为越溪女，暮作吴宫妃。贱日岂殊众，贵来方悟稀。邀人傅脂粉，不自著罗衣。君宠益娇态，君怜无是非。当时浣纱伴，莫得同车归。持谢邻家子，效颦安可希！', '王维', null, '1', 'W', null);
INSERT INTO `article` VALUES ('294', '秋登兰山寄张五', '北山白云里，隐者自怡悦。相望始登高，心随雁飞灭。愁因薄暮起，兴是清秋发。时见归村人，沙行渡头歇。天边树若荠，江畔洲如月。何当载酒来，共醉重阳节。', '孟浩然', null, '1', 'M', null);
INSERT INTO `article` VALUES ('295', '夏日南亭怀辛大', '山光忽西落，池月渐东上。散发乘夜凉，开轩卧闲敞。荷风送香气，竹露滴清响。欲取鸣琴弹，恨无知音赏。感此怀故人，中宵劳梦想。', '孟浩然', null, '1', 'M', null);
INSERT INTO `article` VALUES ('296', '宿业师山房待丁大不至', '夕阳度西岭，群壑倏已暝。松月生夜凉，风泉满清听。樵人归欲尽，烟鸟栖初定。之子期宿来，孤琴候萝径。', '孟浩然', null, '1', 'M', null);
INSERT INTO `article` VALUES ('297', '同从弟南斋玩月忆山阴崔少府', '高卧南斋时，开帷月初吐。清辉淡水木，演漾在窗户。苒苒几盈虚，澄澄变今古。美人清江畔，是夜越吟苦。千里其如何，微风吹兰杜。', '王昌龄', null, '1', 'W', null);
INSERT INTO `article` VALUES ('298', '寻西山隐者不遇', '绝顶一茅茨，直上三十里。扣关无僮仆，窥室惟案几。若非巾柴车，应是钓秋水。差池不相见，黾勉空仰止。草色新雨中，松声晚窗里。及兹契幽绝，自足荡心耳。虽无宾主意，颇得清净理。兴尽方下山，何必待之子。', '邱为', null, '1', 'Q', null);
INSERT INTO `article` VALUES ('299', '春泛若耶溪', '幽意无断绝，此去随所偶。晚风吹行舟，花路入溪口。际夜转西壑，隔山望南斗。潭烟飞溶溶，林月低向后。生事且弥漫，愿为持竿叟。', '綦毋潜', null, '1', 'Q', null);
INSERT INTO `article` VALUES ('300', '宿王昌龄隐居', '清溪深不测，隐处唯孤云。松际露微月，清光犹为君。茅亭宿花影，药院滋苔纹。余亦谢时去，西山鸾鹤群。', '常建', null, '1', 'C', null);
INSERT INTO `article` VALUES ('301', '与高适薛据登慈恩寺浮图', '塔势如涌出，孤高耸天宫。登临出世界，磴道盘虚空。突兀压神州，峥嵘如鬼工。四角碍白日，七层摩苍穹。下窥指高鸟，俯听闻惊风。连山若波涛，奔凑如朝东。青槐夹驰道，宫馆何玲珑！秋色从西来，苍然满关中。五陵北原上，万古青蒙蒙。净理了可悟，胜因夙所宗。誓将挂冠去，觉道资无穷。', '岑参', null, '1', 'C', null);
INSERT INTO `article` VALUES ('302', '贼退示官吏并序', '癸卯岁，西原贼入道州，焚烧杀掠，几尽而去。明年，贼又攻永州，破邵，不犯此州边鄙而退，岂力能制敌欤？盖蒙其伤怜而已！诸史何为忍苦征敛！故作诗一篇以示官吏。昔岁逢太平，山林二十年。泉源在庭户，洞壑当门前。井税有常期，日晏犹得眠。忽然遭时变，数岁亲戎旃。今来典斯郡，山夷又纷然。城小贼不屠，人贫伤可怜。是以陷邻境，此州独见全。使臣将王命，岂不如贼焉！令彼征敛者，迫之如火煎。谁能绝人命，以作时世贤。思欲委符节，引竿自刺船。将家就鱼麦，归老江湖边。', '元结', null, '1', 'Y', null);
INSERT INTO `article` VALUES ('303', '郡斋雨中与诸文士燕集', '兵卫森画戟，宴寝凝清香。海上风雨至，逍遥池阁凉。烦疴近消散，嘉宾复满堂。自惭居处崇，未睹斯民康。理会是非遣，性达形迹忘。鲜肥属时禁，蔬果幸见尝。俯饮一杯酒，仰聆金玉章。神欢体自轻，意欲凌风翔。吴中盛文史，群彦今汪洋。方知大蕃地，岂曰财赋强。', '韦应物', null, '1', 'W', null);
INSERT INTO `article` VALUES ('304', '初发扬子寄元大校书', '凄凄去亲爱，泛泛入烟雾。归棹洛阳人，残钟广陵树。今朝为此别，何处还相遇。世事波上舟，沿洄安得住。', '韦应物', null, '1', 'W', null);
INSERT INTO `article` VALUES ('305', '寄全椒山中道士', '今朝郡斋冷，忽念山中客。涧底束荆薪，归来煮白石。欲持一瓢酒，远慰风雨夕。落叶满空山，何处寻行迹。', '韦应物', null, '1', 'W', null);
INSERT INTO `article` VALUES ('306', '长安遇冯著', '客从东方来，衣上灞陵雨。问客何为来，采山因买斧。冥冥花正开，扬扬燕新乳。昨别今已春，鬓丝生几缕。', '韦应物', null, '1', 'W', null);
INSERT INTO `article` VALUES ('307', '夕次盱眙县', '落帆逗淮镇，停舫临孤驿。浩浩风起波，冥冥日沈夕。人归山郭暗，雁下芦洲白。独夜忆秦关，听钟未眠客。', '韦应物', null, '1', 'W', null);
INSERT INTO `article` VALUES ('308', '东郊', '吏舍局终年，出郊旷清曙。杨柳散和风，青山澹吾虑。依丛适自憩，缘涧还复去。微雨霭芳原，春鸠鸣何处？乐幽心屡止，遵事迹犹遽。终罢斯结庐，慕陶真可庶。', '韦应物', null, '1', 'W', null);
INSERT INTO `article` VALUES ('309', '送杨氏女', '永日方戚戚，出行复悠悠。女子今有行，大江溯轻舟。尔辈苦无恃，抚念益慈柔。幼为长所育，两别泣不休。对此结中肠，义往难复留！自小阙内训，事姑贻我忧。赖兹托令门，仁恤庶无尤。贫俭诚所尚，资从岂待周？孝恭遵妇道，容止顺其猷。别离在今晨，见尔当何秋。居闲始自遣，临感忽难收。归来视幼女，零泪缘缨流。', '韦应物', null, '1', 'W', null);
INSERT INTO `article` VALUES ('310', '晨诣超师院读禅经', '汲井漱寒齿，清心拂尘服。闲持贝叶书，步出东斋读。真源了无取，忘迹世所逐。遗言冀可冥，缮性何由熟？道人庭宇静，苔色连深竹。日出雾露馀，青松如膏沐。澹然离言说，悟悦心自足。', '柳宗元', null, '1', 'L', null);
INSERT INTO `article` VALUES ('311', '溪居', '久为簪组累，幸此南夷谪。闲依农圃邻，偶似山林客。晓耕翻露草，夜榜响溪石。来往不逢人，长歌楚天碧。', '柳宗元', null, '1', 'L', null);
INSERT INTO `article` VALUES ('312', '塞上曲', '蝉鸣空桑林，八月萧关道。出塞复入塞，处处黄芦草。从来幽并客，皆向沙场老。莫学游侠儿，矜夸紫骝好。', '王昌龄', null, '1', 'W', null);
INSERT INTO `article` VALUES ('313', '塞下曲', '饮马渡秋水，水寒风似刀。平沙日未没，黯黯见临洮。昔日长城战，咸言意气高。黄尘足今古，白骨乱蓬蒿。', '王昌龄', null, '1', 'W', null);
INSERT INTO `article` VALUES ('314', '关山月', '明月出天山，苍茫云海间。长风几万里，吹度玉门关。汉下白登道，胡窥青海湾。由来征战地，不见有人还。戍客望边色，思归多苦颜。高楼当此夜，叹息未应闲。', '李白', null, '1', 'L', null);
INSERT INTO `article` VALUES ('315', '子夜四时歌', '秦地罗敷女，采桑绿水边。素手青条上，红妆白日鲜。蚕饥妾欲去，五马莫留连。', '李白', null, '1', 'L', null);
INSERT INTO `article` VALUES ('316', '子夜四时歌', '镜湖三百里，菡萏发荷花。五月西施采，人看隘若耶。回舟不待月，归去越王家。', '李白', null, '1', 'L', null);
INSERT INTO `article` VALUES ('317', '子夜四时歌', '长安一片月，万户捣衣声。秋风吹不尽，总是玉关情。何日平胡虏，良人罢远征？', '李白', null, '1', 'L', null);
INSERT INTO `article` VALUES ('318', '子夜四时歌', '明朝驿使发，一夜絮征袍。素手抽针冷，那堪把剪刀。裁缝寄远道，几日到临洮？', '李白', null, '1', 'L', null);
INSERT INTO `article` VALUES ('319', '长干行', '妾发初覆额，折花门前剧。郎骑竹马来，绕床弄青梅。同居长干里，两小无嫌猜。十四为君妇，羞颜未尝开。低头向暗壁，千唤不一回。十五始展眉，愿同尘与灰。常存抱柱信，岂上望夫台！十六君远行，瞿塘滟预堆。五月不可触，猿鸣天上哀。门前迟行迹，一一生绿苔。苔深不能扫，落叶秋风早。八月蝴蝶来，双飞西园草。感此伤妾心，坐愁红颜老。早晚下三巴，预将书报家。相迎不道远，直至长风沙。', '李白', null, '1', 'L', null);
INSERT INTO `article` VALUES ('320', '烈女操', '梧桐相待老，鸳鸯会双死。贞妇贵殉夫，舍生亦如此。波澜誓不起，妾心井中水。', '孟郊', null, '1', 'M', null);
INSERT INTO `article` VALUES ('321', '游子吟', '慈母手中线，游子身上衣。临行密密缝，意恐迟迟归。谁言寸草心，报得三春辉？', '孟郊', null, '1', 'M', null);
INSERT INTO `article` VALUES ('322', '登幽州台歌', '前不见古人，后不见来者。念天地之悠悠，独怆然而涕下！', '陈子昂', null, '1', 'C', null);
INSERT INTO `article` VALUES ('323', '古意', '男儿事长征，少小幽燕客。赌胜马蹄下，由来轻七尺。杀人莫敢前，须如猬毛磔。黄云陇底白雪飞，未得报恩不能归。辽东小妇年十五，惯弹琵琶解歌舞。今为羌笛出塞声，使我三军泪如雨！', '李颀', null, '1', 'L', null);
INSERT INTO `article` VALUES ('324', '送陈章甫', '四月南风大麦黄，枣花未落桐叶长。青山朝别暮还见，嘶马出门思故乡。陈侯立身何坦荡，虬须虎眉仍大颡。腹中贮书一万卷，不肯低头在草莽。东门酤酒饮我曹，心轻万事皆鸿毛。醉卧不知白日暮，有时空望孤云高。长河浪头连天黑，津口停舟渡不得。郑国游人未及家，洛阳行子空叹息。闻道故林相识多，罢官昨日今如何？', '李颀', null, '1', 'L', null);
INSERT INTO `article` VALUES ('325', '琴歌', '主人有酒欢今夕，请奏鸣琴广陵客。月照城头乌半飞，霜凄万树风入衣。铜炉华烛烛增辉，初弹渌水后楚妃。一声已动物皆静，四座无言星欲稀。清淮奉使千馀里，敢告云山从此始？', '李颀', null, '1', 'L', null);
INSERT INTO `article` VALUES ('326', '听董大弹胡笳声兼寄语弄房给事', '蔡女昔造胡笳声，一弹一十有八拍。胡人落泪沾边草，汉使断肠对归客。古戍苍苍烽火寒，大荒沈沈飞雪白。先拂声弦后角羽，四郊秋叶惊［扌戚］［扌戚］。董夫子，通神明，深山窃听来妖精。言迟更速皆应手，将往复旋如有情。空山百鸟散还合，万里浮云阴且晴。嘶酸雏雁失群夜，断绝胡儿恋母声。川为静其波，鸟亦罢其鸣。乌孙部落家乡远，逻娑沙尘哀怨生。幽音变调忽飘洒，长风吹林雨堕瓦。迸泉飒飒飞木末，野鹿呦呦走堂下。长安城连东掖垣，凤凰池对青琐门。高才脱略名与利，日夕望君抱琴至。更多精彩图书、手机资源尽在飞库电脑访问:www.feiku.com手机访问:wap.feiku.com第二章?唐诗300首 51-10', '李颀', null, '1', 'L', null);
INSERT INTO `article` VALUES ('327', '听安万善吹筚篥歌', '南山截竹为筚篥，此乐本自龟兹出。流传汉地曲转奇，凉州胡人为我吹。傍邻闻者多叹息，远客思乡皆泪垂。世人解听不解赏，长飙风中自来往。枯桑老柏寒飕［风留］，九雏鸣凤乱啾啾。龙吟虎啸一时发，万籁百泉相与秋。忽然更作渔阳掺，黄云萧条白日暗。变调如闻杨柳春，上林繁花照眼新。岁夜高堂列明烛，美酒一杯声一曲。', '李颀', null, '1', 'L', null);
INSERT INTO `article` VALUES ('328', '夜归鹿门山歌', '山寺钟鸣昼已昏，渔梁渡头争渡喧。人随沙路向江村，余亦乘舟归鹿门。鹿门月照开烟树，忽到庞公栖隐处。岩扉松径长寂寥，惟有幽人自来去。', '孟浩然', null, '1', 'M', null);
INSERT INTO `article` VALUES ('329', '庐山谣寄卢侍御虚舟', '我本楚狂人，凤歌笑孔丘。手持绿玉杖，朝别黄鹤楼。五岳寻仙不辞远，一生好入名山游。庐山秀出南斗傍，屏风九叠云锦张。影落明湖青黛光，金阙前开二峰长。银河倒挂三石梁，香炉瀑布遥相望。回崖沓障凌苍苍。翠影红霞映朝日，鸟飞不到吴天长。登高壮观天地间，大江茫茫去不黄。黄云万里动风色，白波九道流雪山。好为庐山谣，兴因庐山发。闲窥石镜清我心，谢公行处苍苔没。早服还丹无世情，琴心三叠道初成。遥见仙人彩云里，手把芙蓉朝玉京。先期汗漫九垓上，愿接卢敖游太清。', '李白', null, '1', 'L', null);
INSERT INTO `article` VALUES ('330', '梦游天姥吟留别', '海客谈瀛洲，烟涛微茫信难求。越人语天姥，云霓明灭或可睹。天姥连天向天横，势拔五岳掩赤城。天台四万八千丈，对此欲倒东南倾。我欲因之梦吴越，一夜飞渡镜湖月。湖月照我影，送我至剡溪。谢公宿处今尚在，渌水荡漾清猿啼。脚著谢公屐，身登青云梯。半壁见海日，空中闻天鸡。千岩万壑路不定，迷花倚石忽已暝。熊咆龙吟殷岩泉，栗深林兮惊层巅。云青青兮欲雨，水澹澹兮生烟。裂缺霹雳，丘峦崩摧。洞天石扇，訇然中开。青冥浩荡不见底，日月照耀金银台。霓为衣兮风为马，云之君兮纷纷而来下。虎鼓瑟兮鸾回车，仙之人兮列如麻。忽魂悸以魄动，恍惊起而长嗟。惟觉时之枕席，失向来之烟霞。世间行乐亦如此，古来万事东流水。别君去兮何时还？且放白鹿青崖间。须行即骑访名山。安能摧眉折腰事权贵，使我不得开心颜！', '李白', null, '1', 'L', null);
INSERT INTO `article` VALUES ('331', '金陵酒肆留别', '风吹柳花满店香，吴姬压酒唤客尝。金陵子弟来相送，欲行不行各尽觞。请君试问东流水，别意与之谁短长？', '李白', null, '1', 'L', null);
INSERT INTO `article` VALUES ('332', '宣州谢［月兆］楼饯别校书叔云', '弃我去者，昨日之日不可留。乱我心者，今日之日多烦忧！长风万里送秋雁，对此可以酣高楼。蓬莱文章建安骨，中间小谢又清发。俱怀逸兴壮思飞，欲上青天览明月。抽刀断水水更流，举杯销愁愁更愁。人生在世不称意，明朝散发弄扁舟。', '李白', null, '1', 'L', null);
INSERT INTO `article` VALUES ('333', '走马川行奉送封大夫出师西征', '君不见走马川行雪海边，平沙莽莽黄入天。轮台九月风夜吼，一川碎石大如斗。随风满地石乱走，匈奴草黄马正肥。金山西见烟尘飞，汉家大将西出师。将军金甲夜不脱，半夜军行戈相拨。风头如刀面如割，马毛带雪汗气蒸。五花连钱旋作冰，幕中草檄砚水凝。虏骑闻之应胆慑，料知短兵不敢接。车师西门伫献捷！', '岑参', null, '1', 'C', null);
INSERT INTO `article` VALUES ('334', '轮台歌奉送封大夫出师西征', '轮台城头夜吹角，轮台城北旄头落。羽书昨夜过渠黎，单于已在金山西。戍楼西望烟尘黑，汉兵屯在轮台北。上将拥旄西出征，平明吹笛大军行。四边伐鼓雪海涌，三军大呼阴山动。虏塞兵气连云屯，战场白骨缠草根。剑河风急雪片阔，沙口石冻马蹄脱。亚相勤王甘苦辛，誓将报主静边尘。古来青史谁不见，今见功名胜古人。', '岑参', null, '1', 'C', null);
INSERT INTO `article` VALUES ('335', '白雪歌送武判官归京', '北风卷地白草折，胡天八月即飞雪。忽如一夜春风来，千树万树梨花开。散入珠帘湿罗幕，狐裘不暖锦衾薄。将军角弓不得控，都护铁衣冷犹著。瀚海阑干百丈冰，愁云黪淡万里凝。中军置酒饮归客，胡琴琵琶与羌笛。纷纷暮雪下辕门，风掣红旗冻不翻。轮台东门送君去，去时雪满天山路。山回路转不见君，雪上空留马行处。', '岑参', null, '1', 'C', null);
INSERT INTO `article` VALUES ('336', '韦讽录事宅观曹将军画马图', '国初以来画鞍马，神妙独数江都王。将军得名三十载，人间又见真乘黄。曾貌先帝照夜白，龙池十日飞霹雳。内府殷红玛瑙盘，婕妤传诏才人索。盘赐将军拜舞归，轻纨细绮相追飞。贵戚权门得笔迹，始觉屏障生光辉。昔日太宗拳毛［马呙］，近时郭家狮子花。今之新图有二马。复令识者久叹嗟。此皆骑战一敌万，缟素漠漠开风沙。其余七匹亦殊绝，迥若寒空杂烟雪。霜蹄蹴踏长楸间，马官厮养森成列。可怜九马争神骏，顾视清高气深稳。借问苦心爱者谁，后有韦讽前支盾。忆昔巡幸新丰宫，翠花拂天来向东。腾骧磊落三万匹，皆与此图筋骨同。自从献宝朝河宗，无复射蛟江水中。君不见，金粟堆前松柏里。龙媒去尽鸟呼风。', '杜甫', null, '1', 'D', null);
INSERT INTO `article` VALUES ('337', '丹青引赠曹霸将军', '将军魏武之子孙，于今为庶为青门。英雄割据虽已矣，文采风流今尚存。学书初学卫夫人，但恨无过王右军。丹青不知老将至，富贵于我如浮云。开元之中常引见，承恩数上南熏殿。凌烟功臣少颜色，将军下笔开生面。良相头上进贤冠，猛将腰间大羽箭。褒公鄂公毛发动，英姿飒爽犹酣战。先帝天马玉花骢，画工如山貌不同。是日牵来赤墀下，迥立阊阖生长风。诏谓将军拂绢素，意匠惨淡经营中。斯须九重真龙出，一洗万古凡马空。玉花却在御榻上，榻上庭前屹相向。至尊含笑催赐金，圉人太仆皆惆怅。弟子韩干早入室，亦能画马穷殊相。干惟画肉不画骨，忍使骅骝气凋丧。将军画善盖有神，偶逢佳士亦写真。即今漂泊干戈际，屡貌寻常行路人。涂穷反遭俗眼白，世上未有如公贫。但看古来盛名下，终日坎［土禀］缠其身！', '杜甫', null, '1', 'D', null);
INSERT INTO `article` VALUES ('338', '寄韩谏议', '今我不乐思岳阳，身欲奋飞病在床。美人娟娟隔秋水，濯足洞庭望八荒。鸿飞冥冥日月白，青枫叶赤天雨霜。玉京群帝集北斗，或骑麒麟翳凤凰。芙蓉旌旗烟雾落，影动倒景摇潇湘。星宫之君醉琼浆，羽人稀少不在旁。似闻昨者赤松子，恐是汉代韩张良。昔随刘氏定长安，帷幄未改神惨伤。国家成败吾岂敢，色难腥腐餐枫香。周南留滞古所惜，南极老人应寿昌。美人胡为隔秋水，焉得置之贡玉堂？', '杜甫', null, '1', 'D', null);
INSERT INTO `article` VALUES ('339', '古柏行', '孔明庙前有老柏，柯如青铜根如石。双皮溜雨四十围，黛色参天二千尺。君臣已与时际会，树木犹为人爱惜。云来气接巫峡长，月出寒通雪山白。忆昨路绕锦亭东，先主武侯同［门必］宫。崔嵬枝干郊原古，窈窕丹青户牖空。落落盘踞虽得地，冥冥孤高多烈风。扶持自是神明力，正直元因造化功。大厦如倾要梁栋，万牛回首丘山重。不露文章世已惊，未辞剪伐谁能送？苦心岂免容蝼蚁？香叶终经宿鸾凤。志士幽人莫怨嗟，古来材大难为用！', '杜甫', null, '1', 'D', null);
INSERT INTO `article` VALUES ('340', '观公孙大娘弟子舞剑器行并序', '大历二年十月十九日夔府别驾元持宅见临颍李十二娘舞剑器，壮其蔚［足支］。问其所师，曰：余公孙大娘弟子也。开元三载，余尚童稚，记于郾城观公孙氏舞剑器浑脱。浏漓顿挫，独出冠时。自高头宜春梨园二伎坊内人，洎外供奉，晓是舞者，圣文神武皇帝初，公孙一人而已。玉貌锦衣，况余白首！今兹弟子亦匪盛颜。既辨其由来，知波澜莫二。抚事慷慨，聊为剑器行。昔者吴人张旭善草书书帖，数尝於邺县见公孙大娘舞西河剑器，自此草书长进，豪荡感激。即公孙可知矣！昔有佳人公孙氏，一舞剑器动四方。观者如山色沮丧，天地为之久低昂。霍如羿射九日落，矫如群帝骖龙翔。来如雷霆收震怒，罢如江海凝清光。绛唇珠袖两寂寞，晚有弟子传芬芳。临颍美人在白帝，妙舞此曲神扬扬。与余问答既有以，感时抚事增惋伤。先帝侍女八千人，公孙剑器初第一。五十年间似反掌，风尘［氵项］洞昏王室。梨园子弟散如烟，女乐馀姿映寒日。金粟堆前木已拱，瞿塘石城草萧瑟。玳筵急管曲复终，乐极哀来月东出。老夫不知其所往，足茧荒山转愁疾。', '杜甫', null, '1', 'D', null);
INSERT INTO `article` VALUES ('341', '石鱼湖上醉歌并序', '漫叟以公田米酿酒，因休暇，则载酒于湖上，时取一醉；欢醉中，据湖岸，引臂向鱼取酒，使舫载之，遍饮坐者。意疑倚巴丘，酌於君山之上，诸子环洞庭而坐，酒舫泛泛然，触波涛而往来者，乃作歌以长之。石鱼湖，似洞庭，夏水欲满君山青。山为樽，水为沼，酒徒历历坐洲鸟。长风连日作大浪，不能废人运酒舫。我持长瓢坐巴丘，酌饮四座以散愁。', '元结', null, '1', 'Y', null);
INSERT INTO `article` VALUES ('342', '山石', '山石荦确行径微，黄昏到寺蝙蝠飞。升堂坐阶新雨足，芭蕉叶大栀子肥。僧言古壁佛画好，以火来照所见稀。铺床拂席置羹饭，疏粝亦足饱我饥。夜深静卧百虫绝，清月出岭光入扉。天明独去无道路，出入高下穷烟霏。山红涧碧纷烂漫，时见松枥皆十围。当流赤足蹋涧石，水声激激风吹衣。人生如此自可乐，岂必局束为人［革几］！嗟哉吾党二三子，安得至老不更归！', '韩愈', null, '1', 'H', null);
INSERT INTO `article` VALUES ('343', '八月十五夜赠张功曹', '纤云四卷天无河，清风吹空月舒波。沙平水息声影绝，一杯相属君当歌。君歌声酸辞且苦，不能听终泪如雨。洞庭连天九嶷高，蛟龙出没猩鼯号。十生九死到官所，幽居默默如藏逃。下床畏蛇食畏药，海气湿蛰熏腥臊。昨者州前槌大鼓，嗣皇继圣登夔皋。赦书一日行万里，罪从大辟皆除死。迁者追回流者还，涤瑕荡垢清朝班。州家申名使家抑，坎轲只得移荆蛮。判司卑官不堪说，未免捶楚尘埃间。同时辈流多上道，天路幽险难追攀。君歌且休听我歌，我歌今与君殊科。一年明月今宵多，人生由命非由他。有酒不饮奈明何！', '韩愈', null, '1', 'H', null);
INSERT INTO `article` VALUES ('344', '谒衡岳庙遂宿岳寺题门楼', '五岳祭秩皆三公，四方环镇嵩当中。火维地荒足妖怪，天假神柄专其雄。喷云泄雾藏半腹，虽有绝顶谁能穷？我来正逢秋雨节，阴气晦昧无清风。潜心默祷若有应，岂非正直能感通！须臾静扫众峰出，仰见突兀撑青空。紫盖连延接天柱，石廪腾掷堆祝融。森然魄动下马拜，松柏一迳趋灵宫。纷墙丹柱动光彩，鬼物图画填青红。升阶伛偻荐脯酒，欲以菲薄明其衷。庙内老人识神意，睢盱侦伺能鞠躬。手持杯［王交］导我掷，云此最吉馀难同。窜逐蛮荒幸不死，衣食才足甘长终。侯王将相望久绝，神纵欲福难为功！夜投佛寺上高阁，星月掩映云［日童］［日龙］。猿鸣钟动不知曙，杲杲寒日生于东。', '韩愈', null, '1', 'H', null);
INSERT INTO `article` VALUES ('345', '石鼓歌', '张生手持石鼓文，劝我识作石鼓歌。少陵无人谪仙死，才薄将奈石鼓何！周纲凌迟四海沸，宣王愤起挥天戈。大开明堂受朝贺，诸侯剑佩鸣相磨。搜于岐阳骋雄俊，万里禽兽皆遮罗。镌功勒成告万世，凿石作鼓隳嵯峨。从臣才艺咸第一，拣选撰刻留山阿。雨淋日炙野火燎，鬼物守护烦［扌为］呵。公从何处得纸本？毫发尽备无差讹。辞严义密读难晓，字体不类隶与蝌。年深岂免有缺画，快剑砍断生蛟鼍。鸾翔凤翥众仙下，珊瑚碧树交枝柯。金绳铁索锁钮壮，古鼎跃水龙腾梭。陋儒编诗不收入，二雅褊迫无委蛇。孔子西行不到秦，掎摭星宿遗羲娥。嗟予好古生苦晚，对此涕泪双滂沱。忆昔初蒙博士征，其年始改称元和。故人从军在右辅，为我度量掘臼科。濯冠沐浴告祭酒，如此至宝存岂多！毡包席裹可立致，十鼓只载数骆驼。荐诸太庙比郜鼎，光价岂止百倍过！圣恩若许留太学，诸生讲解得切磋。观经鸿都尚填咽，坐见举国来奔波。剜苔剔藓露节角，安置妥帖平不颇。大厦深檐与盖覆，经历久远期无佗。中朝大官老于事，讵肯感激徒□（“妍”右上一横改为“合”）婀。牧童敲火牛砺角，谁复著手为摩挲？日销月铄就埋没，六年西顾空吟哦。羲之俗书趁姿媚，数纸尚可博白鹅。继周八代争战罢，无人收拾理则那。方今太平日无事，柄任儒术崇丘轲。安能以此上论列，愿借辩口如悬河。石鼓之歌止于此，呜呼吾意其蹉跎！', '韩愈', null, '1', 'H', null);
INSERT INTO `article` VALUES ('346', '渔翁', '渔翁夜傍西岩宿，晓汲清湘燃楚烛。烟销日出不见人，［矣欠］乃一声山水绿。回看天际下中流，岩上无心云相逐。', '柳宗元', null, '1', 'L', null);
INSERT INTO `article` VALUES ('347', '长恨歌', '汉皇重色思倾国，御宇多年求不得。杨家有女初长成，养在深闺人未识。天生丽质难自弃，一朝选在君王侧。回眸一笑百媚生，六宫粉黛无颜色。春寒赐浴华清池，温泉水滑洗凝脂。侍儿扶起娇无力，始是新承恩泽时。云鬓花颜金步摇，芙蓉帐暖度春宵。春宵苦短日高起，从此君王不早朝。承欢侍宴无闲暇，春从春游夜专夜。后宫佳丽三千人，三千宠爱在一身。金星妆成娇侍夜，玉楼宴罢醉和春。姊妹弟兄皆列士，可怜光彩生门户。遂令天下父母心，不重生男重生女。骊宫高处入青云，仙乐风飘处处闻。缓歌慢舞凝丝竹，尽日君王看不足。渔阳鼙鼓动地来，惊破霓裳羽衣曲。九重城阙烟尘生，千乘万骑西南行。翠华摇摇行复止，西出都门百馀里。六军不发无奈何，宛转蛾眉马前死。花钿委地无人收，翠翘金雀玉搔头。君王掩面救不得，回看血泪相和流。黄埃散漫风萧索，云栈萦纡登剑阁。峨嵋山下少人行，旌旗无光日色薄。蜀江水碧蜀山青，圣主朝朝暮暮情。行宫见月伤心色，夜雨闻铃肠断声。天旋地转回龙驭，到此踌躇不能去。马嵬坡下泥土中，不见玉颜空死处。君臣相顾尽沾衣，东望都门信马归。归来池苑皆依旧，太液芙蓉未央柳。芙蓉如面柳如眉，对此如何不泪垂！春风桃李花开日，秋雨梧桐叶落时。西宫南内多秋草，落叶满阶红不扫。梨园子弟白发新，椒房阿监青娥老。夕殿萤飞思悄然，孤灯挑尽未成眠。迟迟钟鼓初长夜，耿耿星河欲曙天。鸳鸯瓦冷霜华重，翡翠衾寒谁与共？悠悠生死别经年，魂魄不曾来入梦。临邛道士鸿都客，能以精诚致魂魄。为感君王辗转思，遂教方士殷勤觅。排空驭气奔如电，升天入地求之遍。上穷碧落下黄泉，两处茫茫皆不见。忽闻海上有仙山，山在虚无缥缈间。楼阁玲珑五云起，其中绰约多仙子。中有一人字太真，雪肤花貌参差是。金阙西厢叩玉扃，转教小玉报双成。闻道汉家天子使，九华帐里梦魂惊。揽衣推枕起徘徊，珠箔银屏迤逦开。云鬓半偏新睡觉，花冠不整下堂来。风吹仙袂飘飘举，犹似霓裳羽衣舞。玉容寂寞泪阑干，梨花一枝春带雨。含情凝睇谢君王，一别音容两渺茫。昭阳殿里恩爱绝，蓬莱宫中日月长。回头下望人寰处，不见长安见尘雾。唯将旧物表深情，钿合金钗寄将去。钗留一股合一扇，钗擘黄金合分钿。但教心似金钿坚，天上人间会相见。临别殷勤重寄词，词中有誓两心知。七月七日长生殿，夜半无人私语时。在天愿作比翼鸟，在地愿为连理枝。天长地久有时尽，此恨绵绵无绝期！', '白居易', null, '1', 'B', null);
INSERT INTO `article` VALUES ('348', '琵琶行并序', '元和十年，予左迁九江郡司马。明年秋，送客湓浦口，闻船中夜弹琵琶者，听其音，铮铮然有京都声；问其人，本长安倡女，尝学琵琶於穆曹二善才。年长色衰，委身为贾人妇。遂命酒，使快弹数曲，曲罢悯然。自叙少小时欢乐事，今漂沦憔悴，转徙於江湖间。予出官二年恬然自安，感斯人言，是夕，始觉有迁谪意，因为长句歌以赠之，凡六百一十六言，命曰琵琶行。浔言江头夜送客，枫叶荻花秋瑟瑟。主人下马客在船，举酒欲饮无管弦。醉不成欢惨将别，别时茫茫江浸月。忽闻水上琵琶声，主人忘归客不发。寻声暗问弹者谁，琵琶声停欲语迟。移船相近邀相见，添酒回灯重开宴。千呼万唤始出来，犹抱琵琶半遮面。转轴拨弦三两声，未成曲调先有情。弦弦掩抑声声思，似诉平生不得志。低眉信手续续弹，说尽心中无限事。轻拢慢捻抹复挑，初为霓裳后六么。大弦嘈嘈如急雨，小弦切切如私语。嘈嘈切切错杂弹，大珠小珠落玉盘。间关莺语花底滑，幽咽泉流水下滩。水泉冷涩弦凝绝，凝绝不通声渐歇。别有幽愁暗恨生，此时无声胜有声。银瓶乍破水浆迸，铁骑突出刀枪鸣。曲终收拨当心画，四弦一声如裂帛。东船西舫悄无言，唯见江心秋月白。沈吟放拨插弦中，整顿衣裳起敛容。自言本是京城女，家在虾蟆陵下住。十三学得琵琶成，名属教坊第一部。曲罢曾教善才服，妆成每被秋娘妒。五陵年少争缠头，一曲红绡不知数。钿头银篦击节碎，血色罗裙翻酒污。今年欢笑复明年，秋月春风等闲度。弟走从军阿姨死，暮去朝来颜色故。门前冷落车马稀，老大嫁作商人妇。商人重利轻别离，前月浮梁买茶去。去来江口守空船，绕船月明江水寒。夜深忽梦少年事，梦啼妆泪红阑干。我闻琵琶已叹息，又闻此语重唧唧。同是天涯沦落人，相逢何必曾相识！我从去年辞帝京，谪居卧病浔阳城。浔阳地僻无音乐，终岁不闻丝竹声。住近湓江地低湿，黄芦苦竹绕宅生。其间旦暮闻何物？杜鹃啼血猿哀鸣。春江花朝秋月夜，往往取酒还独倾。岂无山歌与村笛，呕哑嘲哳难为听！今夜闻君琵琶语，如听仙乐耳暂明。莫辞更坐弹一曲，为君翻作琵琶行。感我此言良久立，却坐促弦弦转急。凄凄不似向前声，满座重闻皆掩泣。座中泣下谁最多，江州司马青衫湿！', '白居易', null, '1', 'B', null);
INSERT INTO `article` VALUES ('349', '韩碑', '元和天子神武姿，彼何人哉轩与羲。誓将上雪列圣耻，坐法宫中朝四夷。淮西有贼五十载，封狼生［豸区］［豸区］生罴。不据山河据平地，长戈利矛日可麾。帝得圣相相曰度，贼斫不死神扶持。腰悬相印作都统，阴风惨澹天王旗。□（上朔下心］武古通作牙爪，仪曹外郎载笔随。行军司马智且勇，十四万众犹虎貔。入蔡缚贼献太庙，功无与让恩不訾。帝曰汝度功第一，汝从事愈宜为辞。愈拜稽首蹈且舞，金石刻画臣能为。古者世称大手笔，此事不系于职司。当仁自古有不让，言讫屡颔天子颐。公退斋戒坐小阁，濡染大笔何淋漓！点窜尧典舜典字，涂改清庙生民诗。文成破体书在纸，清晨再拜铺丹墀。表曰臣愈昧死上，咏神圣功书之碑。碑高三丈字如斗，负以灵鳌蟠以螭。句奇语重喻者少，谗之天子言其私。长绳百尺拽碑倒，粗沙大石相磨治。公之斯文若元气，先时已入人肝脾。汤盘孔鼎有述作，今无其器存其辞。呜呼圣皇及圣相，相与［火亘］赫流淳熙。公之斯文不示后，曷与三五相攀追。愿书万本诵万过，口角流沫右手胝。传之七十有二代，以为封禅玉检明堂基。', '李商隐', null, '1', 'L', null);
INSERT INTO `article` VALUES ('350', '燕歌行并序', '开元二十六年，客有从御史大夫张公出塞而还者，作燕歌行以示适，感征戍之事，因而和焉。汉家烟尘在东北，汉将辞家破残贼。男儿本自重横行，天子非常赐颜色。［扌从］金伐鼓下榆关，旌旆逶迤碣石间。校尉羽书飞瀚海，单于猎火照狼山。山川萧条极边土，胡骑凭陵杂风雨。战士军前半死生，美人帐下犹歌舞。大漠穷秋塞草衰，孤城落日斗兵稀。身当恩遇常轻敌，力尽关山未解围。铁衣远戍辛勤久，玉筋应啼别离后。少妇城南欲断肠，征人蓟北空回首。边庭飘摇那可度，绝域苍茫更何有！杀气三时作阵云，寒声一夜传刁斗。相看白刃血纷纷，死节从来岂顾勋？君不见沙场征战苦，至今犹忆李将军！', '高适', null, '1', 'G', null);
INSERT INTO `article` VALUES ('351', '古从军行', '白日登山望烽火，黄昏饮马傍交河。行人刁斗风沙暗，公主琵琶幽怨多。野云万里无城郭，雨雪纷纷连大漠。胡雁哀鸣夜夜飞，胡儿眼泪双双落。闻道玉门犹被遮，应将性命逐轻车。年年战骨埋荒外，空见葡萄入汉家。', '李颀', null, '1', 'L', null);
INSERT INTO `article` VALUES ('352', '洛阳女儿行', '洛阳女儿对门居，才可容颜十五馀。良人玉勒乘骢马，侍女金盘脍鲤鱼。画阁朱楼尽相望，红桃绿柳垂檐向。罗帷送上七香车，宝扇迎归九华帐。狂夫富贵在青春，意气骄奢剧季伦。自怜碧玉亲教舞，不惜珊瑚持与人。春窗曙灭九微火，九微片片飞花琐。戏罢曾无理曲时，妆成只是薰香坐。城中相识尽繁华，日夜经过赵李家。谁怜越女颜如玉，贫贱江头自浣纱！', '王维', null, '1', 'W', null);
INSERT INTO `article` VALUES ('353', '老将行', '少年十五二十时，步行夺得胡马骑。射杀山中白额虎，肯数邺下黄须儿！一身转战三千里，一剑曾当百万师。汉兵奋迅如霹雳，虏骑崩腾畏蒺藜。卫青不败由天幸，李广无功缘数奇。自从弃置便衰朽，世事蹉跎成白首。昔时飞箭无全目，今日垂杨生左肘。路旁时卖故侯瓜，门前学种先生柳。苍茫古木连穷巷，寥落寒山对虚牖。誓令疏勒出飞泉，不似颍川空使酒。贺兰山下阵如云，羽檄交驰日夕闻。节使三河募年少，诏书五道出将军。试拂铁衣如雪色，聊持宝剑动星文。愿得燕弓射大将，耻令越甲鸣吾君。莫嫌旧日云中守，犹堪一战取功勋！', '王维', null, '1', 'W', null);
INSERT INTO `article` VALUES ('354', '桃源行', '渔舟逐水爱山春，两岸桃花夹古津。坐看红树不知远，行尽青溪不见人。山口潜行始隈［阝奥］，山开旷望旋平陆。遥看一处攒云树，近入千家散花竹。樵客初传汉姓名，居人未改秦衣服。居人共住武陵源，还从物外起田园。月明松下房栊静，日出云中鸡犬喧。惊闻俗客争来集，竞引还家问都邑。平明闾巷扫花开，薄暮渔樵乘水入。初因避地去人间，及至成仙遂不还。峡里谁知有人事？世中遥望空云山。不疑灵境难闻见，尘心未尽思乡县。出洞无论隔山水，辞家终拟长游衍。自谓经过旧不迷，安知峰壑今来变？当时只记入山深，青溪几曲到云林。春来遍是桃花水，不辨仙源何处寻。', '王维', null, '1', 'W', null);
INSERT INTO `article` VALUES ('355', '蜀道难', '噫吁戏，危乎高哉！蜀道之难难于上青天！蚕丛及鱼凫，开国何茫然！尔来四万八千岁，始与秦塞通人烟。西当太白有鸟道，可以横绝峨眉巅。地崩山摧壮士死，然后天梯石栈方钩连。上有六龙回日之高标，下有冲波逆折之回川。黄鹤之飞尚不得，猿猱欲度愁攀援。青泥何盘盘，百步九折萦岩峦。扪参历井仰胁息，以手抚膺坐长叹。问君西游何时还？畏途□（繁体“谗”换山旁）岩不可攀！但见悲鸟号古木，雄飞雌从绕林间。又闻子规啼，夜月愁空山。蜀道之难难于上青天！使人听此凋朱颜。连峰去天不盈尺，枯松倒挂倚绝壁。飞湍瀑流争喧［兀豕］，冰崖转石万壑雷。其险也如此！嗟尔远道之人，胡为乎来哉？剑阁峥嵘而崔嵬。一夫当关，万夫莫开。所守或匪亲，化为狼与豺。朝避猛虎，夕避长蛇。磨牙吮血，杀人如麻。锦城虽云乐，不如早还家。蜀道之难难于上青天！侧身西望常咨嗟！', '李白', null, '1', 'L', null);
INSERT INTO `article` VALUES ('356', '长相思二首之一', '长相思，在长安。络纬秋啼金井阑，微霜凄凄簟色寒。孤灯不明思欲绝，卷帷望月空长叹。美人如花隔云端。上有青冥之长天，下有渌水之波澜。天长路远魂飞苦，梦魂不到关山难。长相思，摧心肝！', '李白', null, '1', 'L', null);
INSERT INTO `article` VALUES ('357', '长相思二首之二', '日色已尽花含烟，月明欲素愁不眠。赵瑟初停凤凰柱，蜀琴欲奏鸳鸯弦。此曲有意无人传，愿随春风寄燕然。忆君迢迢隔青天。昔日横波目，今成流泪泉。不信妾肠断，归来看取明镜前。', '李白', null, '1', 'L', null);
INSERT INTO `article` VALUES ('358', '行路难三首之一', '金樽清酒斗十千，玉盘珍羞值万钱。停杯投箸不能食，拔剑四顾心茫然。欲渡黄河冰塞川，将登太行雪满山。闲来垂钓碧溪上，忽复乘舟梦日边。行路难，行路难！多歧路，今安在？长风破浪会有时，直挂云帆济沧海。', '李白', null, '1', 'L', null);
INSERT INTO `article` VALUES ('359', '行路难三首之二', '大道如青天，我独不得出。羞逐长安社中儿，赤鸡白狗赌梨栗。弹剑作歌奏苦声，曳裾王门不称情。淮阴市井笑韩信，汉朝公卿忌贾生。君不见，昔时燕家重郭隗，拥彗折节无嫌猜剧辛乐毅感恩分，输肝剖胆效英才。昭王白骨萦蔓草，谁人更扫黄金台？行路难，归去来！', '李白', null, '1', 'L', null);
INSERT INTO `article` VALUES ('360', '行路难三首之三', '有耳莫洗颍川水，有口莫食首阳蕨。含光混世贵无名，何用孤高比云月？吾观自古贤达人，功成不退皆殒身。子胥既弃吴江上，屈原终投湘水滨。陆机雄才岂自保？李斯税驾苦不早。华亭鹤唳讵可闻，上蔡苍鹰何足道！君不见，吴中张翰称达生，秋风忽忆江东行。且乐生前一杯酒，何须身后千载名！', '李白', null, '1', 'L', null);
INSERT INTO `article` VALUES ('361', '将进酒', '君不见，黄河之水天上来，奔流到海不复回。君不见，高堂明镜悲白发，朝如青丝暮成雪。人生得意须尽欢，莫使金樽空对月！天生我材必有用，千金散尽还复来。烹羊宰牛且为乐，会须一饮三百杯！岑夫子，丹丘生，将进酒，君莫停！与君歌一曲，请君为我侧耳听！钟鼓馔玉不足贵，但愿长醉不愿醒！古来圣贤皆寂寞，惟有饮者留其名！陈王昔时宴平乐，斗酒十千恣欢谑。主人何为言少钱？径须沽取对君酌。五花马，千金裘，呼儿将出换美酒，与尔同消万古愁！', '李白', null, '1', 'L', null);
INSERT INTO `article` VALUES ('362', '兵车行', '车辚辚，马萧萧，行人弓箭各在腰。耶娘妻子走相送，尘埃不见咸阳桥。牵衣顿足拦道哭，哭声直上干云霄！道旁过者问行人，行人但云点行频。或从十五北防河，便至四十西营田。去时里正与裹头，归来头白还戍边！边亭流血成海水，武皇开边意未已。君不闻，汉家山东二百州，千村万落生荆杞！纵有健妇把锄犁，禾生陇亩无东西。况复秦兵耐苦战，被驱不异犬与鸡。长者虽有问，役夫敢申恨？且如今年冬，未休关西卒。县官急索租，租税从何出？信知生男恶，反是生女好。生女犹得嫁比邻，生男埋没随百草！君不见，青海头，古来白骨无人收。新鬼烦冤旧鬼哭，天阴雨湿声啾啾！', '杜甫', null, '1', 'D', null);
INSERT INTO `article` VALUES ('363', '丽人行', '三月三日天气新，长安水边多丽人。态浓意远淑且真，肌理细腻骨肉匀。绣罗衣裳照暮春，蹙金孔雀银麒麟。头上何所有？翠微盍叶垂鬓唇。背后何所见？珠压腰［衤及］稳称身。就中云幕椒房亲，赐名大国虢与秦。紫驼之峰出翠釜，水精之盘行素鳞。犀箸餍饫久未下，鸾刀缕切空纷纶。黄门飞［革空］不动尘，御厨络绎送八珍。箫鼓哀吟感鬼神，宾从杂沓实要津。后来鞍马何逡巡，当轩下马入锦茵。杨花雪落覆白苹，青鸟飞去衔红巾。炙手可热势绝伦，慎莫近前丞相嗔！', '杜甫', null, '1', 'D', null);
INSERT INTO `article` VALUES ('364', '哀江头', '少陵野老吞生哭，春日潜行曲江曲。江头宫殿锁千门，细柳新蒲为谁绿？忆昔霓旌下南苑，苑中景物生颜色。昭阳殿里第一人，同辇随君侍君侧。辇前才人带弓箭，白马嚼啮黄金勒。翻身向天仰射云，一箭正坠双飞翼。明眸皓齿今何在？血污游魂归不得！清渭东流剑阁深，去住彼此无消息。人生有情泪沾臆，江水江花岂终极？黄昏胡骑尘满城，欲往城南望城北。', '杜甫', null, '1', 'D', null);
INSERT INTO `article` VALUES ('365', '哀王孙', '长安城头头白乌，夜飞延秋门上呼。又向人家啄大屋，屋底达官走避胡。金鞭断折九马死，骨肉不待同驰驱。腰下宝［“决”换王旁］青珊瑚，可怜王孙泣路隅！问之不肯道姓名，但道困苦乞为奴。已经百日窜荆棘，身上无有完肌肤。高帝子孙尽隆准，龙种自与常人殊。豺狼在邑龙在野，王孙善保千金躯。不敢长语临交衢，且为王孙立斯须。昨夜东风吹血腥，东来橐驼满旧都。朔方健儿好身手，昔何勇锐今何愚！窃闻天子已传位，圣德北服南单于。花门□（“嫠”下女换刀）面请雪耻，慎勿出口他人狙！哀哉王孙慎勿疏，五陵佳气无时无。', '杜甫', null, '1', 'D', null);
INSERT INTO `article` VALUES ('366', '经邹鲁祭孔子而叹之', '夫子何为者，栖栖一代中。地犹鄹氏邑，宅即鲁王宫。叹凤嗟身否？伤麟怨道穷。今看两楹奠，当与梦时同。', '唐玄宗', null, '1', 'T', null);
INSERT INTO `article` VALUES ('367', '望月怀远', '海上生明月，天涯共此时。情人怨遥夜，竟夕起相思！灭烛怜光满，披衣觉露滋。不堪盈手赠，还寝梦佳期。', '张九龄', null, '1', 'Z', null);
INSERT INTO `article` VALUES ('368', '送杜少府之任蜀州', '城阙辅三秦，风烟望五津。与君离别意，同是宦游人。海内存知己，天涯若比邻。无为在歧路，儿女共沾巾。', '王勃', null, '1', 'W', null);
INSERT INTO `article` VALUES ('369', '在狱咏蝉并序', '余禁所禁垣西，是法厅事也。有古槐数株焉，虽生意可知，同殷仲文之古树，而听讼斯在，即周召伯之甘棠。每至夕照低阴，秋蝉疏引，发声幽息，有切尝闻；岂人心异於曩时，将虫响悲於前听？嗟乎！声以动容，德以象贤，故洁其身也，禀君子达人之高行；蜕其皮也，有仙都羽化之灵姿。候时而来，顺阴阳之数；应节为变，审藏用之机。有目斯开，不以道昏而昧其视；有翼自薄，不以俗厚而易其真。吟乔树之微风，韵资天纵；饮高秋之坠露，清畏人知。仆失路艰虞，遭时徽［纟墨］，不哀伤而自怨，未摇落而先衰。闻蟪蛄之流声，悟平反之已奏；见螳螂之抱影，怯危机之未安。感而缀诗，贻诸知己。庶情沿物应，哀弱羽之飘零；道寄人知，悯馀声之寂寞。非谓文墨，取代幽忧云尔。西路蝉声唱，南冠客思侵。那堪玄鬓影，来对白头吟！露重飞难进，风多响易沉。无人信高洁，谁为表予心？', '骆宾王', null, '1', 'L', null);
INSERT INTO `article` VALUES ('370', '和晋陵路丞早春游望', '独有宦游人，偏惊物候新。云霞出海曙，梅柳渡江春。淑气催黄鸟，晴光转绿苹。忽闻歌古调，归思欲沾巾。', '杜审言', null, '1', 'D', null);
INSERT INTO `article` VALUES ('371', '杂诗', '闻道黄龙戍，频年不解兵。可怜闺里月，长在汉家营。少妇今春意，良人昨夜情。谁能将旗鼓，一为取龙城？', '沈全期', null, '1', 'S', null);
INSERT INTO `article` VALUES ('372', '题大庾岭北驿', '阳月南飞雁，传闻至此回。我行殊未已，何日复归来？江静潮初落，林昏瘴不开。明朝望乡处，应见陇头梅。', '宋之问', null, '1', 'S', null);
INSERT INTO `article` VALUES ('373', '次北固山下', '客路青山外，行舟绿水前。潮平两岸阔，风正一帆悬。海日生残夜，江春入旧年。乡书何处达？归雁洛阳边。', '王湾', null, '1', 'W', null);
INSERT INTO `article` VALUES ('374', '题破山寺后禅院', '清晨入古寺，初日照高林。曲径通幽处，禅房花木深。山光悦鸟性，潭影空人心。万籁此俱寂，惟馀钟磬音。', '常建', null, '1', 'C', null);
INSERT INTO `article` VALUES ('375', '寄左省杜拾遗', '联步趋丹陛，分曹限紫微。晓随天仗入，暮惹御香归。白发悲花落，青云羡鸟飞。圣朝无阙事，自觉谏书稀。', '岑参', null, '1', 'C', null);
INSERT INTO `article` VALUES ('376', '赠孟浩然', '吾爱孟夫子，风流天下闻。红颜弃轩冕，白首卧松云。醉月频中圣，迷花不事君。高山安可仰，徒此挹清芬。第三章?唐诗300首 101-150', '李白', null, '1', 'L', null);
INSERT INTO `article` VALUES ('377', '渡荆门送别', '渡远荆门外，来从楚国游。山随平野尽，江入大荒流。月下飞天镜，云生结海楼。仍怜故乡水，万里送行舟。', '李白', null, '1', 'L', null);
INSERT INTO `article` VALUES ('378', '送友人', '青山横北郭，白水绕东城。此地一为别，孤蓬万里征。浮云游子意，落日故人情。挥手自兹去，萧萧班马鸣。', '李白', null, '1', 'L', null);
INSERT INTO `article` VALUES ('379', '听蜀僧浚弹琴', '蜀僧抱绿绮，西下峨眉峰。为我一挥手，如听万壑松。客心洗流水，馀响入霜钟。不觉碧山暮，秋云暗几重。', '李白', null, '1', 'L', null);
INSERT INTO `article` VALUES ('380', '夜泊牛渚怀古', '牛渚西江夜，青天无片云。登舟望秋月，空忆谢将军。余亦能高咏，斯人不可闻。明朝挂帆席，枫叶落纷纷。', '李白', null, '1', 'L', null);
INSERT INTO `article` VALUES ('381', '月夜', '今夜［鹿阝］州月，闺中只独看。遥怜小儿女，未解忆长安。香雾云鬟湿，清辉玉臂寒。何时倚虚幌，双照泪痕干？', '杜甫', null, '1', 'D', null);
INSERT INTO `article` VALUES ('382', '春望', '国破山河在，城春草木深。感时花溅泪，恨别鸟惊心。烽火连三月，家书抵万金。白头搔更短，浑欲不胜簪。', '杜甫', null, '1', 'D', null);
INSERT INTO `article` VALUES ('383', '春宿左省', '花隐掖垣暮，啾啾栖鸟过。星临万户动，月傍九霄多。不寝听金钥，因风想玉珂。明朝有封事，数问夜如何？', '杜甫', null, '1', 'D', null);
INSERT INTO `article` VALUES ('384', '至德二载甫自京金光门出，问道归凤翔。乾元初从左拾遗移华州掾。与亲', '故别，因出此门。有悲往事。此道昔归顺，西郊胡正繁。至今残破胆，应有未招魂。近得归京邑，移官岂至尊？无才日衰老，驻马望千门。', '杜甫', null, '1', 'D', null);
INSERT INTO `article` VALUES ('385', '月夜忆舍弟', '戍鼓断人行，秋边一雁声。露从今夜白，月是故乡明。有弟皆分散，无家问死生。寄书长不达，况乃未休兵。', '杜甫', null, '1', 'D', null);
INSERT INTO `article` VALUES ('386', '天末怀李白', '凉风起天末，君子意如何？鸿雁几时到，江湖秋水多。文章憎命达，魑魅喜人过。应共冤魂语，投诗赠汨罗。', '杜甫', null, '1', 'D', null);
INSERT INTO `article` VALUES ('387', '奉济驿重送严公四韵', '远送从此别，青山空复情。几时杯重把，昨夜月同行。列郡讴歌惜，三朝出入荣。将村独归处，寂寞养残生。', '杜甫', null, '1', 'D', null);
INSERT INTO `article` VALUES ('388', '别房太尉墓', '他乡复行役，驻马别孤坟。近泪无干土，低空有断云。对棋陪谢傅，把剑觅徐君。唯见林花落，莺啼送客闻。', '杜甫', null, '1', 'D', null);
INSERT INTO `article` VALUES ('389', '旅夜书怀', '细草微风岸，危樯独夜舟。星垂平野阔，月涌大江流。名岂文章著？官应老病休。飘飘何所似，天地一沙鸥。', '杜甫', null, '1', 'D', null);
INSERT INTO `article` VALUES ('390', '登岳阳楼', '昔闻洞庭水，今上岳阳楼。吴楚东南坼，乾坤日夜浮。亲朋无一字，老病有孤舟。戎马关山北，凭轩涕泗流。', '杜甫', null, '1', 'D', null);
INSERT INTO `article` VALUES ('391', '辋川闲居赠裴秀才迪', '寒山转苍翠，秋水日潺［氵爰］。倚杖柴门外，临风听暮蝉。渡头馀落日，墟里上孤烟。复值接舆醉，狂歌五柳前。', '王维', null, '1', 'W', null);
INSERT INTO `article` VALUES ('392', '山居秋暝', '空山新雨后，天气晚来秋。明月松间照，清泉石上流。竹喧归浣女，莲动下渔舟。随意春芳歇，王孙自可留。', '王维', null, '1', 'W', null);
INSERT INTO `article` VALUES ('393', '归嵩山作', '清川带长薄，车马去闲闲。流水如有意，暮禽相与还。荒城临古渡，落日满秋山。迢递嵩高下，归来且闭关。', '王维', null, '1', 'W', null);
INSERT INTO `article` VALUES ('394', '终南山', '太乙近天都，连山接海隅。白云回望合，青霭入看无。分野中峰变，阴晴众壑殊。欲投人处宿，隔水问樵夫。', '王维', null, '1', 'W', null);
INSERT INTO `article` VALUES ('395', '酬张少府', '晚年惟好静，万事不关心。自顾无长策，空知返旧林。松风吹解带，山月照弹琴。君问穷通理，渔歌入浦深。', '王维', null, '1', 'W', null);
INSERT INTO `article` VALUES ('396', '过香积寺', '不知香积寺，数里入云峰。古木无人径，深山何处钟？泉声咽危石，日色冷青松。薄暮空潭曲，安禅制毒龙。', '王维', null, '1', 'W', null);
INSERT INTO `article` VALUES ('397', '送梓州李使君', '万壑树参天，千山响杜鹃。山中一夜雨，树杪百重泉。汉女输［木童］布，巴人讼芋田。文翁翻教授，不敢倚先贤。', '王维', null, '1', 'W', null);
INSERT INTO `article` VALUES ('398', '汉江临眺', '楚塞三湘接，荆门九派通。江流天地外，山色有无中。郡邑浮前浦，波澜动远空。襄阳好风日，留醉与山翁。', '王维', null, '1', 'W', null);
INSERT INTO `article` VALUES ('399', '终南别业', '中岁颇好道，晚家南山陲。兴来美独往，胜事空自知。行到水穷处，坐看云起时。偶然值林叟，谈笑无还期。', '王维', null, '1', 'W', null);
INSERT INTO `article` VALUES ('400', '望洞庭湖赠张丞相', '八月湖水平，涵虚混太清。气蒸云梦泽，波撼岳阳城。欲济无舟楫，端居耻圣明。坐观垂钓者，空有羡鱼情。', '孟浩然', null, '1', 'M', null);
INSERT INTO `article` VALUES ('401', '与诸子登岘山', '人事有代谢，往来成古今。江山留胜迹，我辈复登临。水落鱼梁浅，天寒梦泽深。羊公碑字在，读罢泪沾襟。', '孟浩然', null, '1', 'M', null);
INSERT INTO `article` VALUES ('402', '清明日宴梅道士房', '林卧愁春尽，开轩览物华。忽逢青鸟使，邀入赤松家。丹灶初开火，仙桃正发花。童颜若可驻，何惜醉流霞！', '孟浩然', null, '1', 'M', null);
INSERT INTO `article` VALUES ('403', '岁暮归南山', '北阙休上书，南山归敝庐。不才明主弃，多病故人疏。白发催年老，青阳逼岁除。永怀愁不寐，松月夜窗墟。', '孟浩然', null, '1', 'M', null);
INSERT INTO `article` VALUES ('404', '过故人庄', '故人具鸡黍，邀我至田家。绿树村边合，青山郭外斜。开轩面场圃，把酒话桑麻。待到重阳日，还来就菊花。', '孟浩然', null, '1', 'M', null);
INSERT INTO `article` VALUES ('405', '秦中感秋寄远上人', '一丘尝欲卧，三径苦无资。北土非吾愿，东林怀我师。黄金燃桂尽，壮志逐年衰。日夕凉风至，闻蝉但益悲。', '孟浩然', null, '1', 'M', null);
INSERT INTO `article` VALUES ('406', '宿桐庐江寄广陵旧游', '山暝听猿愁，沧江急夜流。风鸣两岸叶，月照一孤舟。建德非吾土，维扬忆旧游。还将两行泪，遥寄海西头。', '孟浩然', null, '1', 'M', null);
INSERT INTO `article` VALUES ('407', '留别王侍御维', '寂寂竟何待，朝朝空自归。欲寻芳草去，惜与故人违。当路谁相假，知音世所稀。只应守寂寞，还掩故园扉。', '孟浩然', null, '1', 'M', null);
INSERT INTO `article` VALUES ('408', '早寒江上有怀', '木落雁南渡，北风江上寒。我家襄水曲，遥隔楚云端。乡泪客中尽，孤帆天际看。迷津欲有问，平海夕漫漫。', '孟浩然', null, '1', 'M', null);
INSERT INTO `article` VALUES ('409', '秋日登吴公台上寺远眺', '古台摇落后，秋日望乡心。野寺人来少，云峰水隔深。夕阳依旧垒，寒磬满空林。惆怅南朝事，长江独至今。', '刘长卿', null, '1', 'L', null);
INSERT INTO `article` VALUES ('410', '送李中丞归汉阳别业', '流落征南将，曾驱十万师。罢归无旧业，老去恋明时。独立三边静，轻生一剑知。茫茫江汉上，日暮复何之。', '刘常卿', null, '1', 'L', null);
INSERT INTO `article` VALUES ('411', '饯别王十一南游', '望君烟水阔，挥手泪沾巾。飞鸟没何处，青山空向人。长江一帆远，落日五湖春。谁见汀洲上，相思愁白苹？', '刘长卿', null, '1', 'L', null);
INSERT INTO `article` VALUES ('412', '寻南溪常山道人隐居', '一路经行处，莓苔见履痕。白云依静渚，春草闭闲门。过雨看松色，随山到水源。溪花与禅意，相对亦忘言。', '刘长卿', null, '1', 'L', null);
INSERT INTO `article` VALUES ('413', '新年作', '乡心新岁切，天畔独潸然。老至居人下，春归在客先。岭猿同旦暮，江柳共风烟。已似长沙傅，从今又几年？', '刘长卿', null, '1', 'L', null);
INSERT INTO `article` VALUES ('414', '送僧归日本', '上国随缘住，来途若梦行。浮天沧海远，去世法舟轻。水月通禅寂，鱼龙听梵声。惟怜一灯影，万里眼中明。', '钱起', null, '1', 'Q', null);
INSERT INTO `article` VALUES ('415', '谷口书斋寄杨补阙', '泉壑带茅茨，云霞生薜帷。竹怜新雨后，山爱夕阳时。闲鹭栖常早，秋花落更迟。家童扫萝径，昨与故人期。', '钱起', null, '1', 'Q', null);
INSERT INTO `article` VALUES ('416', '淮上喜会梁川故人', '江汉曾为客，相逢每醉还。浮云一别后，流水十年间。欢笑情如旧，萧疏鬓已斑。何因北归去，淮上对秋山。', '韦应物', null, '1', 'W', null);
INSERT INTO `article` VALUES ('417', '赋得暮雨送李胄', '楚江微雨里，建业暮钟时。漠漠帆来重，冥冥鸟去迟。海门深不见，浦树远含滋。相送情无限，沾襟比散丝。', '韦应物', null, '1', 'W', null);
INSERT INTO `article` VALUES ('418', '酬程延秋夜即事见赠', '长簟迎风早，空城澹月华。星河秋一雁，砧杵夜千家。节候看应晚，心期卧亦赊。向来吟秀句，不觉已鸣鸦。', '韩翃', null, '1', 'H', null);
INSERT INTO `article` VALUES ('419', '阙题', '道由白云尽，春与青溪长。时有落花至，远隋流水香。闲门向山路，深柳读书堂。幽映每白日，清辉照衣裳。', '刘脊虚', null, '1', 'L', null);
INSERT INTO `article` VALUES ('420', '江乡故人偶集客舍', '天秋月又满，城阙夜千重。还作江南会，翻疑梦里逢。风枝惊暗鹊，露草覆寒虫。羁旅长堪醉，相留畏晓钟。', '戴叔伦', null, '1', 'D', null);
INSERT INTO `article` VALUES ('421', '李端公', '故关衰草遍，离别正堪悲！路出寒云外，人归暮雪时。少孤为客早，多难识君迟。掩泪空相向，风尘何处期？', '卢纶', null, '1', 'L', null);
INSERT INTO `article` VALUES ('422', '喜见外弟又言别', '十年离乱后，长大一相逢。问姓惊初见，称名忆旧容。别来沧海事，语罢暮天钟。明日巴陵道，秋山又几重。', '李益', null, '1', 'L', null);
INSERT INTO `article` VALUES ('423', '云阳馆与韩绅宿别', '故人江海别，几度隔山川。乍见翻疑梦，相悲各问年。孤灯寒照雨，深竹暗浮烟。更有明朝恨，离杯惜共传。', '司空曙', null, '1', 'S', null);
INSERT INTO `article` VALUES ('424', '喜外弟卢纶见宿', '静夜四无邻，荒居旧业贫。雨中黄叶树，灯下白头人。以我独沉久，愧君相访频。平生自有分，况是蔡家亲！', '司空曙', null, '1', 'S', null);
INSERT INTO `article` VALUES ('425', '贼平后送人北归', '世乱同南去，时清独北还。他乡生白发，旧国见青山。晓月过残垒，繁星宿故关。寒禽与衰草，处处伴愁颜。', '司空曙', null, '1', 'S', null);
INSERT INTO `article` VALUES ('426', '蜀先主庙', '天地英雄气，千秋尚凛然！势分三足鼎，业复五铢钱。得相能开国，生儿不象贤。凄凉蜀故妓，来舞魏宫前。第四章?唐诗300首 151-200', '刘禹锡', null, '1', 'L', null);
INSERT INTO `article` VALUES ('427', '没蕃故人', '前年伐月支，城下没全师。蕃汉断消息，死生长别离。无人收废帐，归马识残旗。欲祭疑君在，天涯哭此时。', '张籍', null, '1', 'Z', null);
INSERT INTO `article` VALUES ('428', '赋得古原草送别', '离离原上草，一岁一枯荣。野火烧不尽，春风吹又生。远芳侵古道，晴翠接荒城。又送王孙去，萋萋满别情。', '白居易', null, '1', 'B', null);
INSERT INTO `article` VALUES ('429', '旅宿', '旅馆无良伴，凝情自悄然。寒灯思旧事，断雁警愁眠。远梦归侵晓，家书到隔年。沧江好烟月，门系钓鱼船。', '杜牧', null, '1', 'D', null);
INSERT INTO `article` VALUES ('430', '秋日赴阙题潼关驿楼', '红叶晚萧萧，长亭酒一瓢。残云归太华，疏雨过中条。树色随山迥，河声入海遥。帝乡明日到，犹自梦渔樵。', '许浑', null, '1', 'X', null);
INSERT INTO `article` VALUES ('431', '早秋', '遥夜泛清瑟，西风生翠萝。残萤栖玉露，早雁拂银河。高树晓还密，远山晴更多。淮南一叶下，自觉老烟波。', '许浑', null, '1', 'X', null);
INSERT INTO `article` VALUES ('432', '蝉', '本以高难饱，徒劳恨费声。五更疏欲断，一树碧无情。薄宦梗犹泛，故园芜已平。烦君最相警，我亦举家清。', '李商隐', null, '1', 'L', null);
INSERT INTO `article` VALUES ('433', '风雨', '凄凉宝剑篇，羁泊欲穷年。黄叶仍风雨，青楼自管弦。新知遭薄俗，旧好隔良缘。心断新丰酒，销愁斗几千。', '李商隐', null, '1', 'L', null);
INSERT INTO `article` VALUES ('434', '落花', '高阁客竟去，小园花乱飞。参差连曲陌，迢递送斜晖。肠断未忍扫，眼穿仍欲归。芳心向春尽，所得是沾衣。', '李商隐', null, '1', 'L', null);
INSERT INTO `article` VALUES ('435', '凉思', '客去波平槛，蝉休露满枝。永怀当此节，倚立自移时。北斗兼春远，南陵寓使迟。天涯占梦数，疑误有新知。', '李商隐', null, '1', 'L', null);
INSERT INTO `article` VALUES ('436', '北青萝', '残阳西入崦，茅屋访孤僧。落叶人何在？寒云路几层？独敲初夜磬，闲倚一枝藤。世界微尘里，吾宁爱与憎。', '李商隐', null, '1', 'L', null);
INSERT INTO `article` VALUES ('437', '送人东游', '荒戍落黄叶，浩然离故关。高风汉阳渡，初日郢门山。江上几人在？天涯孤棹还。何当重相见，樽酒慰离颜？', '温庭筠', null, '1', 'W', null);
INSERT INTO `article` VALUES ('438', '灞上秋居', '灞原风雨定，晚见雁行频。落叶他乡树，寒灯独夜人。空园白露滴，孤壁野僧邻。寄卧郊扉久，何年致此身？', '马戴', null, '1', 'M', null);
INSERT INTO `article` VALUES ('439', '楚江怀古', '露气寒光集，微阳下楚丘。猿啼洞庭树，人在木兰舟。广泽生明月，苍山夹乱流。云中君不见，竟夕自悲秋。', '马戴', null, '1', 'M', null);
INSERT INTO `article` VALUES ('440', '书边事', '调角断清秋，征人倚戍楼。春风对青冢，白日落梁州。大漠无兵阻，穷边有客游。蕃情似此水，长愿向南流。', '张乔', null, '1', 'Z', null);
INSERT INTO `article` VALUES ('441', '巴山道中除夜有怀', '迢递三巴路，羁危万里身。乱山残雪夜，孤独异乡春。渐与骨肉远，转於僮仆亲。那堪正飘泊，明日岁华新。', '崔涂', null, '1', 'C', null);
INSERT INTO `article` VALUES ('442', '孤雁', '几行归塞尽，片影独何之？暮雨相呼失，寒塘欲下迟。渚云低暗渡，关月冷相随。未必逢［矢曾］缴，孤飞自可疑。', '崔涂', null, '1', 'C', null);
INSERT INTO `article` VALUES ('443', '春宫怨', '早被婵娟误，欲妆临镜慵。承恩不在貌，教妾若为容。风暖鸟声碎，日高花影重。年年越溪女，相忆采芙蓉。', '杜荀鹤', null, '1', 'D', null);
INSERT INTO `article` VALUES ('444', '章台夜思', '清瑟怨遥夜，绕弦风雨哀。孤灯闻楚角，残月下章台。芳草已云暮，故人殊未来。乡书不可寄，秋雁又南回。', '韦庄', null, '1', 'W', null);
INSERT INTO `article` VALUES ('445', '寻陆鸿渐不遇', '移家虽带郭，野径入桑麻。近种篱边菊，秋来未著花。扣门无犬吠，欲去问西家。报到山中去，归来每日斜。', '僧皎然', null, '1', 'S', null);
INSERT INTO `article` VALUES ('446', '黄鹤楼', '昔人已乘黄鹤去，此地空馀黄鹤楼。黄鹤一去不复返，白云千载空悠悠。晴川历历汉阳树，芳草萋萋鹦鹉洲。日暮乡关何处是，烟波江上使人愁。', '崔颢', null, '1', 'C', null);
INSERT INTO `article` VALUES ('447', '行经华阴', '迢□（“绕”换山旁）太华俯咸京，天外三峰削不成。武帝祠前云欲散，仙人掌上雨初晴。河山北枕秦关险，驿树西连汉［田寺］平。借问路傍名利客，无如此处学长生。', '崔颢', null, '1', 'C', null);
INSERT INTO `article` VALUES ('448', '望蓟门', '燕台一去客心惊，箫鼓喧喧汉将营。万里寒光生积雪，三边曙色动危旌。沙场烽火侵胡月，海畔云山拥蓟城。少小虽非投笔吏，论功还欲请长缨。', '祖咏', null, '1', 'Z', null);
INSERT INTO `article` VALUES ('449', '送魏万之京', '朝闻游子唱骊歌，昨夜微霜初度河。鸿雁不堪愁里听，云山况是客中过。关城树色催寒近，御苑砧声向晚多。莫见长安行乐处，空令岁月易蹉跎。', '李颀', null, '1', 'L', null);
INSERT INTO `article` VALUES ('450', '九日登望仙台呈刘明府', '汉文皇帝有高台，此日登临曙色开。三晋云山皆北向，二陵风雨自东来。关门令尹谁能识？河上仙翁去不回。且欲竟寻彭泽宰，陶然共醉菊花杯。', '崔曙', null, '1', 'C', null);
INSERT INTO `article` VALUES ('451', '送李少府贬峡中王少府贬长沙', '嗟君此别意何如？驻马衔杯问谪居。巫峡啼猿数行泪，衡阳归雁几封书。青枫江上秋帆远，白帝城边古木疏。圣代即今多雨露，暂时分手莫踌躇。', '高适', null, '1', 'G', null);
INSERT INTO `article` VALUES ('452', '奉和中书舍人贾至早朝大明宫', '鸡鸣紫陌曙光寒，莺啭皇州春色阑。金阙晓钟开万户，玉阶仙仗拥千官。花迎剑佩星初落，柳拂旌旗露未干。独有凤凰池上客，阳春一曲和皆难。', '岑参', null, '1', 'C', null);
INSERT INTO `article` VALUES ('453', '和贾舍人早朝大明宫之作', '绛帻鸡人送晓筹，尚衣方进翠云裘。九天阊阖开宫殿，万国衣冠拜冕旒。日色才临仙掌动，香烟欲傍衮龙浮。朝罢须裁五色诏，佩声归向凤池头。', '王维', null, '1', 'W', null);
INSERT INTO `article` VALUES ('454', '奉和圣制从蓬莱向兴庆阁道中留春雨中春望之作应制', '渭水自萦秦塞曲，黄山旧绕汉宫斜。銮舆迥出千门柳，阁道回看上苑花。云里帝城双凤阙，雨中春树万人家。为乘阳气行时令，不是宸游玩物华。', '王维', null, '1', 'W', null);
INSERT INTO `article` VALUES ('455', '积雨辋川庄作', '积雨空林烟火迟，蒸藜炊黍饷东□（“淄”去三点水加草头）。漠漠水田飞白鹭，阴阴夏木啭黄鹂。山中习静观朝槿，松下清斋折露葵。野老与人争席罢，海鸥何事更相疑。', '王维', null, '1', 'W', null);
INSERT INTO `article` VALUES ('456', '酬郭给事', '洞门高阁霭馀辉，桃李阴阴柳絮飞。禁里疏钟官舍晚，省中啼鸟吏人稀。晨摇玉佩趋金殿，夕奉天书拜琐闱。强欲从君无那老，将因卧病解朝衣。', '王维', null, '1', 'W', null);
INSERT INTO `article` VALUES ('457', '蜀相', '丞相祠堂何处寻？锦官城外柏森森。映阶碧草自春色，隔叶黄鹂空好音。三顾频烦天下计，两朝开济老臣心。出师未捷身先死，长使英雄泪满襟！', '杜甫', null, '1', 'D', null);
INSERT INTO `article` VALUES ('458', '客至', '舍南舍北皆春水，但见群鸥日日来。花径不曾缘客扫，蓬门今始为君开。盘飧市远无兼味，樽酒家贫只旧醅。肯与邻翁相对饮，隔篱呼取尽馀杯！', '杜甫', null, '1', 'D', null);
INSERT INTO `article` VALUES ('459', '野望', '西山白雪三城戍，南浦清江万里桥。海内风尘诸弟隔，天涯涕泪一身遥。唯将迟暮供多病，未有涓埃答圣朝。跨马出郊时极目，不堪人事日萧条！', '杜甫', null, '1', 'D', null);
INSERT INTO `article` VALUES ('460', '闻官军收河南河北', '剑外忽传收蓟北，初闻涕泪满衣裳。却看妻子愁何在，漫卷诗书喜欲狂。白日放歌须纵酒，青春作伴好还乡！即从巴峡穿巫峡，便下襄阳向洛阳。', '杜甫', null, '1', 'D', null);
INSERT INTO `article` VALUES ('461', '登高', '风急天高猿啸哀，渚清沙白鸟飞回。无边落木萧萧下，不尽长江滚滚来。万里悲秋常作客，百年多病独登台。艰难苦恨繁霜鬓，潦倒新停浊酒杯。', '杜甫', null, '1', 'D', null);
INSERT INTO `article` VALUES ('462', '登楼', '花近高楼伤客心，万方多难此登临。锦江春色来天地，玉垒浮云变古今。北极朝庭终不改，西山寇盗莫相侵！可怜后主还祠庙，日暮聊为梁父吟。', '杜甫', null, '1', 'D', null);
INSERT INTO `article` VALUES ('463', '宿府', '清秋幕府井梧寒，独宿江城蜡炬残。永夜角声悲自语，中天月色好谁看？风尘荏苒音书绝，关塞萧条行陆难。已忍伶俜十年事，强移栖息一枝安。', '杜甫', null, '1', 'D', null);
INSERT INTO `article` VALUES ('464', '阁夜', '岁暮阴阳催短景，天涯霜雪霁寒霄。五更鼓角声悲壮，三峡星河影动摇。野哭千家闻战伐，夷歌数处起渔樵。卧龙跃马终黄土，人事音书漫寂寥。', '杜甫', null, '1', 'D', null);
INSERT INTO `article` VALUES ('465', '咏怀古迹五首之一', '支离东北风尘际，漂泊西南天地间。三峡楼台淹日月，五溪衣服共云山。羯胡事主终无赖，词客哀时且未还。庾信平生最萧瑟，暮年诗赋动江关。', '杜甫', null, '1', 'D', null);
INSERT INTO `article` VALUES ('466', '咏怀古迹五首之二', '摇落深知宋玉悲，风流儒雅亦吾师。怅望千秋一洒泪，萧条异代不同时。江山故宅空文藻，云雨荒台岂梦思！最是楚宫俱泯灭，舟人指点到今疑。', '杜甫', null, '1', 'D', null);
INSERT INTO `article` VALUES ('467', '咏怀古迹五首之三', '群山万壑赴荆门，生长明妃尚有村。一去紫台连朔漠，独留青冢向黄昏。画图省识春风面，环佩空归月下魂。千载琵琶作胡语，分明怨恨曲中论。', '杜甫', null, '1', 'D', null);
INSERT INTO `article` VALUES ('468', '咏怀古迹五首之四', '蜀主征吴幸三峡，崩年亦在永安宫。翠华想像空山里，玉殿虚无野寺中。古庙杉松巢水鹤，岁时伏腊走村翁。武侯祠屋常邻近，一体君臣祭祀同。', '杜甫', null, '1', 'D', null);
INSERT INTO `article` VALUES ('469', '咏怀古迹五首之五', '诸葛大名垂宇宙，宗臣遗像肃清高。三分割据纡筹策，万古云霄一羽毛。伯仲之间见伊吕，指挥若定失萧曹。运移汉祚终难复，志决身歼军务劳。', '杜甫', null, '1', 'D', null);
INSERT INTO `article` VALUES ('470', '江州重别薛六柳八二员外', '生涯岂料承优诏？世事空知学醉歌。江上月明胡雁过，淮南木落楚山多。寄身且喜沧洲近，顾影无如白发何！今日龙钟人共老，愧君犹遣慎风波。', '刘长卿', null, '1', 'L', null);
INSERT INTO `article` VALUES ('471', '长沙过贾谊宅', '三年谪宦此栖迟，万古惟留楚客悲。秋草独寻人去后，寒林空见日斜时。汉文有道恩犹薄，湘水无情吊岂知？寂寂江山摇落处，怜君何事到天涯！', '刘长卿', null, '1', 'L', null);
INSERT INTO `article` VALUES ('472', '自夏口至鹦洲夕望岳阳寄源中丞', '汀洲无浪复无烟，楚客相思益渺然。汉口夕阳斜渡鸟，洞庭秋水远连天。孤城背岭寒吹角，独戍临江夜泊船。贾谊上书忧汉室，长沙谪去古今怜。', '刘长卿', null, '1', 'L', null);
INSERT INTO `article` VALUES ('473', '赠阙下裴舍人', '二月黄鹂飞上林，春城紫禁晓阴阴。长乐钟声花外尽，龙池柳色雨中深。阳和不散穷途恨，霄汉长怀捧日心。献赋十年犹未遇，羞将白发对华簪。', '钱起', null, '1', 'Q', null);
INSERT INTO `article` VALUES ('474', '寄李儋元锡', '去年花里逢君别，今日花开又一年。世事茫茫难自料，春愁黯黯独成眠。身多疾病思田里，邑有流亡愧俸钱。闻道欲来相问讯，西楼望月几回圆？', '韦应物', null, '1', 'W', null);
INSERT INTO `article` VALUES ('475', '同题仙游观', '仙台初见五城楼，风物凄凄宿雨收。山色遥连秦树晚，砧声近报汉宫秋。疏松影落空坛静，细草香闲小洞幽。何用别寻方外去，人间亦自有丹丘！第五章?唐诗300首 201-250', '韩翃', null, '1', 'H', null);
INSERT INTO `article` VALUES ('476', '春思', '莺啼燕语报新年，马邑龙堆路几千。家住层城邻汉苑，心随明月到胡天。机中锦字论长恨，楼上花枝笑独眠。为问天戎窦车骑，何时返旆勒燕然？', '皇甫冉', null, '1', 'H', null);
INSERT INTO `article` VALUES ('477', '晚次鄂州', '云开远见汉阳城，犹是孤帆一日程。估客昼眠知浪静，舟人夜语觉潮生。三湘愁鬓逢秋色，万里归心对月明。旧业已随征战尽，更堪江上鼓鼙声。', '卢纶', null, '1', 'L', null);
INSERT INTO `article` VALUES ('478', '登柳州城楼寄漳汀封连四州刺史', '城上高楼接大荒，海天愁思正茫茫。惊风乱［风占）芙蓉水，密雨斜侵薜荔墙。岭树重遮千里目，江流曲似九回肠。共来百越文身地，犹自音书滞一乡。', '柳宗元', null, '1', 'L', null);
INSERT INTO `article` VALUES ('479', '西塞山怀古', '王浚楼船下益州，金陵王气黯然收。千寻铁锁沈江底，一片降幡出石头。人世几回伤往事？山形依旧枕寒流。从今四海为家日，故垒萧萧芦荻秋。', '刘禹锡', null, '1', 'L', null);
INSERT INTO `article` VALUES ('480', '遣悲怀三首之一', '谢公最小偏怜女，自嫁黔娄百事乖。顾我无衣搜荩箧，泥他沽酒拔金钗。野蔬充膳甘长藿，落叶添薪仰古槐。今日俸钱过十万，与君营奠复营斋。', '元稹', null, '1', 'Y', null);
INSERT INTO `article` VALUES ('481', '遣悲怀三首之二', '昔日戏言身后事，今朝都到眼前来。衣裳已施行看尽，针线犹存未忍开。尚想旧情怜婢仆，也曾因梦送钱财。诚知此恨人人有，贫贱夫妻百事哀。', '元稹', null, '1', 'Y', null);
INSERT INTO `article` VALUES ('482', '遣悲怀三首之三', '闲坐悲君亦自悲，百年都是几多时？邓攸无子寻知命，潘岳悼亡犹费词。同穴□（上“穴”下“目”）冥何所望，他生缘会更难期。惟将终夜长开眼，报答平生未展眉。', '元稹', null, '1', 'Y', null);
INSERT INTO `article` VALUES ('483', '自河南经乱，关内阻饥，兄弟离散，各在一处。因望月有感，聊书所怀', '，寄上浮梁大兄，于潜七兄，乌江十五兄，兼示符离及下［圭阝］弟妹。时难年荒世业空，弟兄羁旅各西东。田园寥落干戈后，骨肉流离道路中。吊影分为千里雁，辞根散作九秋蓬。共看明月应垂泪，一夜乡心五处同。', '白居易', null, '1', 'B', null);
INSERT INTO `article` VALUES ('484', '锦瑟', '锦瑟无端五十弦，一弦一柱思华年。庄生晓梦迷蝴蝶，望帝春心托杜鹃。沧海月明珠有泪，蓝田日暖玉生烟。此情可待成追忆，只是当时已惘然。', '李商隐', null, '1', 'L', null);
INSERT INTO `article` VALUES ('485', '无题', '昨夜星辰昨夜风，画楼西畔桂堂东。身无彩凤双飞翼，心有灵犀一点通。隔座送钩春酒暖，分曹射覆蜡灯红。嗟余听鼓应官去，走马兰台类转蓬。', '李商隐', null, '1', 'L', null);
INSERT INTO `article` VALUES ('486', '隋宫', '紫泉宫殿锁烟霞，欲取芜城作帝家。玉玺不缘归日角，锦帆应是到天涯。於今腐草无萤火，终古垂杨有暮鸦。地下若逢陈后主，岂宜重问后庭花？', '李商隐', null, '1', 'L', null);
INSERT INTO `article` VALUES ('487', '无题二首之一', '来是空言去绝踪，月斜楼上五更钟。梦为远别啼难唤，书被催成墨未浓。蜡照半笼金翡翠，麝熏微度绣芙蓉。刘郎已恨蓬山远，更隔蓬山一万重。', '李商隐', null, '1', 'L', null);
INSERT INTO `article` VALUES ('488', '无题二首之二', '飒飒东风细雨来，芙蓉塘外有轻雷。金蟾啮锁烧香入，玉虎牵丝汲井回。贾氏窥帘韩掾少，宓妃留枕魏王才。春心莫共花争发，一寸相思一寸灰。', '李商隐', null, '1', 'L', null);
INSERT INTO `article` VALUES ('489', '筹笔驿', '猿鸟犹疑畏简书，风云常为护储胥。徒令上将挥神笔，终见降王走传车。管乐有才原不忝，关张无命欲何如。他年锦里经祠庙，梁父吟成恨有馀。', '李商隐', null, '1', 'L', null);
INSERT INTO `article` VALUES ('490', '无题', '相见时难别亦难，东风无力百花残。春蚕到死丝方尽，蜡炬成灰泪始干。晓镜但愁云鬓改，夜吟应觉月光寒。蓬莱此去无多路，青鸟殷勤为探看。', '李商隐', null, '1', 'L', null);
INSERT INTO `article` VALUES ('491', '春雨', '怅卧新春白袷衣，白门寥落意多违。红楼隔雨相望冷，珠箔飘灯独自归。远路应悲春［日宛］晚，残宵犹得梦依稀。玉［王当］缄札何由达？万里云罗一雁飞。', '李商隐', null, '1', 'L', null);
INSERT INTO `article` VALUES ('492', '无题二首之一', '凤尾香罗薄几重，碧文圆顶夜深缝。扇裁月魄羞难掩，车走雷声语未通。曾是寂寥金烬暗，断无消息石榴红。斑骓只系垂杨岸，何处西南任好风？', '李商隐', null, '1', 'L', null);
INSERT INTO `article` VALUES ('493', '无题二首之二', '重帷深下莫愁堂，卧后清宵细细长。神女生涯原是梦，小姑居处本无郎。风波不信菱枝弱，月露谁教桂叶香？直道相思了无益，未妨惆怅是清狂。', '李商隐', null, '1', 'L', null);
INSERT INTO `article` VALUES ('494', '利洲南渡', '澹然空水对斜晖，曲岛苍茫接翠微。波上马嘶看棹去，柳边人歇待船归。数丛沙草群鸥散，万顷江田一鹭飞。谁解乘舟寻范蠡，五湖烟水独忘机？', '温庭筠', null, '1', 'W', null);
INSERT INTO `article` VALUES ('495', '苏武庙', '苏武魂销汉使前，古祠高树两茫然。云边雁断胡天月，陇上羊归塞草烟。回日楼台非甲帐，去时冠剑是丁年。茂陵不见封侯印，空向秋波哭逝川。', '温庭筠', null, '1', 'W', null);
INSERT INTO `article` VALUES ('496', '宫词', '十二楼中尽晓妆，望仙楼上望君王。锁衔金兽连环冷，水滴铜龙昼漏长。云髻罢梳还对镜，罗衣欲换更添香。遥窥正殿帘开处，袍裤宫人扫御床。', '薛逢', null, '1', 'X', null);
INSERT INTO `article` VALUES ('497', '贫女', '蓬门未识绮罗香，拟托良媒益自伤。谁爱风流高格调？共怜时世俭梳妆。敢将十指夸针巧，不把双眉斗画长。苦恨年年压金线，为他人作嫁衣裳。', '秦韬玉', null, '1', 'Q', null);
INSERT INTO `article` VALUES ('498', '古意呈补阙乔知之', '卢家少妇郁金香，海燕双栖玳瑁梁。九月寒砧催木叶，十年征戍忆辽阳。白狼河北音书断，丹凤城南秋夜长。谁为含愁独不见，更教明月照流黄？', '沈全期', null, '1', 'S', null);
INSERT INTO `article` VALUES ('499', '鹿柴', '空山不见人，但闻人语响。返景入深林，复照青苔上。', '王维', null, '1', 'W', null);
INSERT INTO `article` VALUES ('500', '竹里馆', '独坐幽篁里，弹琴复长啸。深林人不知，明月来相照。', '王维', null, '1', 'W', null);
INSERT INTO `article` VALUES ('501', '送别', '山中相送罢，日暮掩柴扉。春草明年绿，王孙归不归？', '王维', null, '1', 'W', null);
INSERT INTO `article` VALUES ('502', '相思', '红豆生南国，春来发几枝？愿君多采撷，此物最相思。', '王维', null, '1', 'W', null);
INSERT INTO `article` VALUES ('503', '杂诗', '君自故乡来，应知故乡事。来日绮窗前，寒梅著花未？', '王维', null, '1', 'W', null);
INSERT INTO `article` VALUES ('504', '送崔九', '归山深浅去，须尽丘壑美。莫学武陵人，暂游桃源里。', '裴迪', null, '1', 'P', null);
INSERT INTO `article` VALUES ('505', '终南望馀雪', '终南阴岭秀，积雪浮云端。林表明霁色，城中增暮寒。', '祖咏', null, '1', 'Z', null);
INSERT INTO `article` VALUES ('506', '宿建德江', '移舟泊烟渚，日暮客愁新。野旷天低树，江清月近人。', '孟浩然', null, '1', 'M', null);
INSERT INTO `article` VALUES ('507', '春晓', '春眠不觉晓，处处闻啼鸟。夜来风雨声，花落知多少？', '孟浩然', null, '1', 'M', null);
INSERT INTO `article` VALUES ('508', '夜思', '床前明月光，疑是地上霜。举头望明月，低头思故乡。', '李白', null, '1', 'L', null);
INSERT INTO `article` VALUES ('509', '怨情', '美人卷珠帘，深坐蹙蛾眉。但见泪痕湿，不知心恨谁？', '李白', null, '1', 'L', null);
INSERT INTO `article` VALUES ('510', '八阵图', '功盖三分国，名成八阵图。江流石不转，遗恨失吞吴。', '杜甫', null, '1', 'D', null);
INSERT INTO `article` VALUES ('511', '登鹳雀楼', '白日依山尽，黄河入海流。欲穷千里目，更上一层楼。', '王之涣', null, '1', 'W', null);
INSERT INTO `article` VALUES ('512', '送灵澈', '苍苍竹林寺，杳杳钟声晚。荷笠带斜阳，青山独归远。', '刘长卿', null, '1', 'L', null);
INSERT INTO `article` VALUES ('513', '弹琴', '泠泠七弦上，静听松风寒。古调虽自爱，今人多不弹。', '刘长卿', null, '1', 'L', null);
INSERT INTO `article` VALUES ('514', '送上人', '孤云将野鹤，岂向人间住！莫买沃洲山，时人已知处。', '刘长卿', null, '1', 'L', null);
INSERT INTO `article` VALUES ('515', '秋夜寄邱员外', '怀君属秋夜，散步咏凉天。空山松子落，幽人应未眠。', '韦应物', null, '1', 'W', null);
INSERT INTO `article` VALUES ('516', '听筝', '鸣筝金粟柱，素手玉房前。欲得周郎顾，时时误拂弦。', '李端', null, '1', 'L', null);
INSERT INTO `article` VALUES ('517', '新嫁娘', '三日入厨下，洗手作羹汤。未谙姑食性，先遣小姑尝。', '王建', null, '1', 'W', null);
INSERT INTO `article` VALUES ('518', '玉台体', '昨夜裙带解，今朝［虫喜］子飞。铅华不可弃，莫是藁砧归。', '权德舆', null, '1', 'Q', null);
INSERT INTO `article` VALUES ('519', '江雪', '千山鸟飞绝，万径人踪灭。孤舟蓑笠翁，独钓寒江雪。', '柳宗元', null, '1', 'L', null);
INSERT INTO `article` VALUES ('520', '行宫', '寥落古行宫，宫花寂寞红。白头宫女在，闲坐说玄宗。', '元稹', null, '1', 'Y', null);
INSERT INTO `article` VALUES ('521', '问刘十九', '绿蚁新醅酒，红泥小火炉。晚来天欲雪，能饮一杯无？', '白居易', null, '1', 'B', null);
INSERT INTO `article` VALUES ('522', '何满子', '故国三千里，深宫二十年。一声何满子，双泪落君前。', '张祜', null, '1', 'Z', null);
INSERT INTO `article` VALUES ('523', '登乐游原', '向晚意不适，驱车登古原。夕阳无限好，只是近黄昏。', '李商隐', null, '1', 'L', null);
INSERT INTO `article` VALUES ('524', '寻隐者不遇', '松下问童子，言师采药去。只在此山中，云深不知处。', '贾岛', null, '1', 'J', null);
INSERT INTO `article` VALUES ('525', '渡汉江', '岭外音书绝，经冬复立春。近乡情更怯，不敢问来人。第六章?唐诗300首 251-300', '李频', null, '1', 'L', null);
INSERT INTO `article` VALUES ('526', '春怨', '打起黄莺儿，莫教枝上啼。啼时惊妾梦，不得到辽西。', '金昌绪', null, '1', 'J', null);
INSERT INTO `article` VALUES ('527', '哥舒歌', '北斗七星高，哥舒夜带刀。至今窥牧马，不敢过临洮。', '西鄙人', null, '1', 'X', null);
INSERT INTO `article` VALUES ('528', '长干行二首之一', '君家何处住，妾住在横塘。停船暂借问，或恐是同乡。', '崔颢', null, '1', 'C', null);
INSERT INTO `article` VALUES ('529', '长干行二首之二', '家临九江水，来去九江侧。同是长干人，生小不相识。', '崔颢', null, '1', 'C', null);
INSERT INTO `article` VALUES ('530', '玉阶怨', '玉阶生白露，夜久侵罗袜。却下水晶帘，玲珑望秋月。', '李白', null, '1', 'L', null);
INSERT INTO `article` VALUES ('531', '塞下曲四首之一', '鹫翎金仆姑，燕尾绣蝥弧。独立扬新令，千营共一呼。', '卢纶', null, '1', 'L', null);
INSERT INTO `article` VALUES ('532', '塞下曲四首之二', '林暗草惊风，将军夜引弓。平明寻白羽，没在石棱中。', '卢纶', null, '1', 'L', null);
INSERT INTO `article` VALUES ('533', '塞下曲四首之三', '月黑雁飞高，单于夜遁逃。欲将轻骑逐，大雪满弓刀。', '卢纶', null, '1', 'L', null);
INSERT INTO `article` VALUES ('534', '塞下曲四首之四', '野幕蔽琼筵，羌戎贺劳旋。醉和金甲舞，雷鼓动山川。', '卢纶', null, '1', 'L', null);
INSERT INTO `article` VALUES ('535', '江南曲', '嫁得瞿塘贾，朝朝误妾期。早知潮有信，嫁与弄潮儿。', '李益', null, '1', 'L', null);
INSERT INTO `article` VALUES ('536', '回乡偶书', '少小离家老大回，乡音无改鬓毛衰。儿童相见不相识，笑问客从何处来？', '贺知章', null, '1', 'H', null);
INSERT INTO `article` VALUES ('537', '桃花溪', '隐隐飞桥隔野烟，石矶西畔问渔船。桃花尽日随流水，洞在清溪何处边？', '张旭', null, '1', 'Z', null);
INSERT INTO `article` VALUES ('538', '九月九日忆山东兄弟', '独在异乡为异客，每逢佳节倍思亲。遥知兄弟登高处，遍插茱萸少一人。', '王维', null, '1', 'W', null);
INSERT INTO `article` VALUES ('539', '芙蓉楼送辛渐', '寒雨连江夜入吴，平明送客楚山孤。洛阳亲友如相问，一片冰心在玉壶。', '王昌龄', null, '1', 'W', null);
INSERT INTO `article` VALUES ('540', '闺怨', '闺中少妇不知愁，春日凝妆上翠楼。忽见陌头杨柳色，悔教夫婿觅封侯。', '王昌龄', null, '1', 'W', null);
INSERT INTO `article` VALUES ('541', '春宫曲', '昨夜风开露井桃，未央前殿月轮高。平阳歌舞新承宠，帘外春寒赐锦袍。', '王昌龄', null, '1', 'W', null);
INSERT INTO `article` VALUES ('542', '凉州词', '葡萄美酒夜光杯，欲饮琵琶马上催。醉卧沙场君莫笑，古来征战几人回！', '王翰', null, '1', 'W', null);
INSERT INTO `article` VALUES ('543', '送孟浩然之广陵', '故人西辞黄鹤楼，烟花三月下扬州。孤帆远影碧空尽，惟见长江天际流。', '李白', null, '1', 'L', null);
INSERT INTO `article` VALUES ('544', '下江陵', '朝辞白帝彩云间，千里江陵一日还。两岸猿声啼不住，轻舟已过万重山。', '李白', null, '1', 'L', null);
INSERT INTO `article` VALUES ('545', '逢入京使', '故园东望路漫漫，双袖龙钟泪不干。马上相逢无纸笔，凭君传语报平安。', '岑参', null, '1', 'C', null);
INSERT INTO `article` VALUES ('546', '江南逢李龟年', '岐王宅里寻常见，崔九堂前几度闻。正是江南好风景，落花时节又逢君。', '杜甫', null, '1', 'D', null);
INSERT INTO `article` VALUES ('547', '滁州西涧', '独怜幽草涧边生，上有黄鹂深树鸣。春潮带雨晚来急，野渡无人舟自横。', '韦应物', null, '1', 'W', null);
INSERT INTO `article` VALUES ('548', '枫桥夜泊', '月落乌啼霜满天，江枫渔火对愁眠。姑苏城外寒山寺，夜半钟声到客船。', '张继', null, '1', 'Z', null);
INSERT INTO `article` VALUES ('549', '寒食', '春城无处不飞花，寒食东风御柳斜。日暮汉宫传蜡烛，轻烟散入五侯家。', '韩翃', null, '1', 'H', null);
INSERT INTO `article` VALUES ('550', '月夜', '更深月色半人家，北斗阑干南斗斜。今夜偏知春气暖，虫声新透绿窗纱。', '刘方平', null, '1', 'L', null);
INSERT INTO `article` VALUES ('551', '春怨', '纱窗日落渐黄昏，金屋无人见泪痕。寂寞空庭春欲晚，梨花满地不开门。', '刘方平', null, '1', 'L', null);
INSERT INTO `article` VALUES ('552', '征人怨', '岁岁金河复玉关，朝朝马策与刀环。三春白雪归青冢，万里黄河绕黑山。', '柳中庸', null, '1', 'L', null);
INSERT INTO `article` VALUES ('553', '宫词', '玉楼天半起笙歌，风送宫嫔笑语和。月殿影开闻夜漏，水晶帘卷近秋河。', '顾况', null, '1', 'G', null);
INSERT INTO `article` VALUES ('554', '夜上受降城闻笛', '回乐峰前沙似雪，受降城外月如霜。不知何处吹芦管，一夜征人尽望乡。', '李益', null, '1', 'L', null);
INSERT INTO `article` VALUES ('555', '乌衣巷', '朱雀桥边野草花，乌衣巷口夕阳斜。旧时王谢堂前燕，飞入寻常百姓家。', '刘禹锡', null, '1', 'L', null);
INSERT INTO `article` VALUES ('556', '春词', '新妆宜面下朱楼，深锁春光一院愁。行到中庭数花朵，蜻蜓飞上玉搔头。', '刘禹锡', null, '1', 'L', null);
INSERT INTO `article` VALUES ('557', '后宫词', '泪湿罗巾梦不成，夜深前殿按歌声。红颜未老恩先断，斜倚薰笼坐到明。', '白居易', null, '1', 'B', null);
INSERT INTO `article` VALUES ('558', '赠内人', '禁门宫树月痕过，媚眼惟看宿鹭窠。斜拔玉钗灯影畔，剔开红焰救飞蛾。', '张祜', null, '1', 'Z', null);
INSERT INTO `article` VALUES ('559', '集灵台二首之一', '日光斜照集灵台，红树花迎晓露开。昨夜上皇新授□（“录”加竹头），太真含笑入帘来。', '张祜', null, '1', 'Z', null);
INSERT INTO `article` VALUES ('560', '集灵台二首之二', '虢国夫人承主恩，平明骑马入宫门。却嫌脂粉污颜色，淡扫蛾眉朝至尊。', '张祜', null, '1', 'Z', null);
INSERT INTO `article` VALUES ('561', '题金陵渡', '金陵津渡小山楼，一宿行人自可愁。潮落夜江斜月里，两三星火是瓜州。', '张祜', null, '1', 'Z', null);
INSERT INTO `article` VALUES ('562', '宫词', '寂寂花时闭院门，美人相并立琼轩。含情欲说宫中事，鹦鹉前头不敢言。', '朱庆馀', null, '1', 'Z', null);
INSERT INTO `article` VALUES ('563', '近试上张水部', '洞房昨夜停红烛，待晓堂前拜舅姑。妆罢低声问夫婿，画眉深浅入时无？', '朱庆馀', null, '1', 'Z', null);
INSERT INTO `article` VALUES ('564', '将赴吴兴登乐游原', '清时有味是无能，闲爱孤云静爱僧。欲把一麾江海去，乐游原上望昭陵。', '杜牧', null, '1', 'D', null);
INSERT INTO `article` VALUES ('565', '赤壁', '折戟沈沙铁未销，自将磨洗认前朝。东风不与周郎便，铜雀春深销二乔。', '杜牧', null, '1', 'D', null);
INSERT INTO `article` VALUES ('566', '泊秦淮', '烟笼寒水月笼沙，夜泊秦淮近酒家。商女不知亡国恨，隔江犹唱《后庭花》。', '杜牧', null, '1', 'D', null);
INSERT INTO `article` VALUES ('567', '寄扬州韩绰判官', '青山隐隐水迢迢，秋尽江南草未凋。二十四桥明月夜，玉人何处教吹箫？', '杜牧', null, '1', 'D', null);
INSERT INTO `article` VALUES ('568', '遣怀', '落魄江湖载酒行，楚腰纤细掌中轻。十年一觉扬州梦，赢得青楼薄幸名。', '杜牧', null, '1', 'D', null);
INSERT INTO `article` VALUES ('569', '秋夕', '银烛秋光冷画屏，轻罗小扇扑流萤。天阶夜色凉如水，坐看牵牛织女星。', '杜牧', null, '1', 'D', null);
INSERT INTO `article` VALUES ('570', '赠别二首之一', '娉娉袅袅十三馀，豆蔻梢头二月初。春风十里扬州路，卷上珠帘总不如。', '杜牧', null, '1', 'D', null);
INSERT INTO `article` VALUES ('571', '赠别二首之二', '多情却似总无情，唯觉樽前笑不成。蜡烛有心还惜别，替人垂泪到天明。', '杜牧', null, '1', 'D', null);
INSERT INTO `article` VALUES ('572', '金谷园', '繁华事散逐香尘，流水无情草自春。日暮东风怨啼鸟，落花犹似坠楼人。', '杜牧', null, '1', 'D', null);
INSERT INTO `article` VALUES ('573', '夜雨寄北', '君问归期未有期，巴山夜雨涨秋池。何当共剪西窗烛，却话巴山夜雨时？', '李商隐', null, '1', 'L', null);
INSERT INTO `article` VALUES ('574', '寄令狐郎中', '嵩云秦树久离居，双鲤迢迢一纸笔。休问梁园旧宾客，茂陵秋雨病相如。', '李商隐', null, '1', 'L', null);
INSERT INTO `article` VALUES ('575', '为有', '为有云屏无限娇，凤城寒尽怕春宵。无端嫁得金龟婿，辜负香衾事早朝。', '李商隐', null, '1', 'L', null);
INSERT INTO `article` VALUES ('576', '隋宫', '乘兴南游不戒严，九重谁省谏书函？春风举国裁宫锦，半作障泥半作帆。', '李商隐', null, '1', 'L', null);
INSERT INTO `article` VALUES ('577', '瑶池', '瑶池阿母绮窗开，黄竹歌声动地哀。八骏日行三万里，穆王何事不重来？', '李商隐', null, '1', 'L', null);
INSERT INTO `article` VALUES ('578', '嫦娥', '云母屏风烛影深，长河渐落晓星沈。嫦娥应悔偷灵药，碧海青天夜夜心。', '李商隐', null, '1', 'L', null);
INSERT INTO `article` VALUES ('579', '贾生', '宣室求贤访逐臣，贾生才调更无伦。可怜夜半虚前席，不问苍生问鬼神！', '李商隐', null, '1', 'L', null);
INSERT INTO `article` VALUES ('580', '瑶瑟怨', '冰簟银床梦不成，碧天如水夜云轻。雁声远过潇湘去，十二楼中月自明。', '温庭筠', null, '1', 'W', null);
INSERT INTO `article` VALUES ('581', '马嵬坡', '玄宗回马杨妃死，云雨难忘日月新。终是圣明天子事，景阳宫井又何人？', '郑畋', null, '1', 'Z', null);
INSERT INTO `article` VALUES ('582', '已凉', '碧阑干外绣帘垂，猩色屏风画折枝。八尺龙须方锦褥，已凉天气未寒时。', '韩偓', null, '1', 'H', null);
INSERT INTO `article` VALUES ('583', '金陵图', '江雨霏霏江草齐，六朝如梦鸟空啼。无情最是台城柳，依旧烟笼十里堤。', '韦庄', null, '1', 'W', null);
INSERT INTO `article` VALUES ('584', '陇西行', '誓扫匈奴不顾身，五千貂锦丧胡尘。可怜无定河边骨，犹是深闺梦里人！', '陈陶', null, '1', 'C', null);
INSERT INTO `article` VALUES ('585', '寄人', '别梦依依到谢家，小廊回合曲阑斜。多情只有春庭月，犹为离人照落花。', '张泌', null, '1', 'Z', null);
INSERT INTO `article` VALUES ('586', '杂诗', '尽寒食雨草萋萋，著麦苗风柳映堤。等是有家归未得，杜鹃休向耳边啼。', '无名氏', null, '1', 'W', null);
INSERT INTO `article` VALUES ('587', '渭城曲', '渭城朝雨［氵邑］轻尘，客舍青青柳色新。劝君更尽一杯酒，西出阳关无故人。', '王维', null, '1', 'W', null);
INSERT INTO `article` VALUES ('588', '秋夜曲', '桂魄初生秋露微，轻罗已薄未更衣。银筝夜久殷勤弄，心怯空房不忍归！', '王维', null, '1', 'W', null);
INSERT INTO `article` VALUES ('589', '长信怨', '奉帚平明金殿开，且将团扇共徘徊。玉颜不及寒鸦色，犹带昭阳日影来。', '王昌龄', null, '1', 'W', null);
INSERT INTO `article` VALUES ('590', '出塞', '秦时明月汉时关，万里长征人未还。但使龙城飞将在，不教胡马渡阴山！', '王昌龄', null, '1', 'W', null);
INSERT INTO `article` VALUES ('591', '出塞', '黄河远上白云间，一片孤城万仞山。羌笛何须怨杨柳？春风不度玉门关。', '王之涣', null, '1', 'W', null);
INSERT INTO `article` VALUES ('592', '清平调三首之一', '云想衣裳花想容，春风拂槛露华浓。若非群玉山头见，会向瑶台月下逢。', '李白', null, '1', 'L', null);
INSERT INTO `article` VALUES ('593', '清平调三首之二', '一枝红艳露凝香，云雨巫山枉断肠。借问汉宫谁得似？可怜飞燕倚新妆。', '李白', null, '1', 'L', null);
INSERT INTO `article` VALUES ('594', '清平调三首之三', '名花倾国两相欢，常得君王带笑看。解释春风无限恨，沈香亭北倚阑干。', '李白', null, '1', 'L', null);
INSERT INTO `article` VALUES ('595', '金缕衣', '劝君莫惜金缕衣，劝君惜取少年时。花开堪折直须折，莫待无花空折枝！', '杜秋娘', null, '1', 'D', null);
INSERT INTO `article` VALUES ('1271056071519121491', '第一章（天地之始）', '道可道，非常道;名可名，非常名。无，名天地之始；有，名万物之母。故常无，欲以观其妙；常有，欲以观其徼（jiao）。此两者，同出而异名，同谓之玄。玄之又玄，众妙之门。', '老子', '4', '2', 'L', null);
INSERT INTO `article` VALUES ('1271056071519121492', '第二章（美之为美）', '天下皆知美之为美，斯恶已。皆知善之为善，斯不善矣。故有无相生，难易相成，长短相较，高下相倾，音声相和（he4），前后相随。是以圣人处无为之事，行不言之教。万物作焉而不辞，生而不有，为而不恃（shi），功成而弗（fu）居，夫唯弗居，是以不去。', '老子', '4', '2', 'L', null);
INSERT INTO `article` VALUES ('1271056071519121493', '第三章（圣人之治）', '不尚贤，使民不争；不贵难得之货，使民不为盗；不见可欲，使民心不乱。是以圣人之治，虚其心，实其腹，弱其志，强其骨，常使民无知无欲，使夫智者不敢为也。为无为，则无不为。', '老子', '4', '2', 'L', null);
INSERT INTO `article` VALUES ('1271056071519121494', '第四章（象帝之先）', '道冲，而用之或不盈。渊兮似万物之宗。挫其锐，解其纷，和其光，同其尘。湛兮似或存，吾不知谁之子，象帝之先。', '老子', '4', '2', 'L', null);
INSERT INTO `article` VALUES ('1271056071519121495', '第五章（天地不仁）', '天地不仁，以万物为刍（chu）狗；圣人不仁，以百姓为刍狗。天地之间，其犹橐（tuo）龠（yue）乎！虚而不屈，动而愈出。多言数穷，不如守中。', '老子', '4', '2', 'L', null);
INSERT INTO `article` VALUES ('1271056071519121496', '第六章（玄牝之门）', '谷神不死，是谓玄牝（pin）。玄牝之门，是谓天地根。绵绵若存，用之不勤。', '老子', '4', '2', 'L', null);
INSERT INTO `article` VALUES ('1271056071519121497', '第七章（天长地久）', '天长地久。天地所以能长且久者，以其不自生，故能长生。是以圣人后其身而身先，外其身而身存。非以其无私邪？故能成其私。', '老子', '4', '2', 'L', null);
INSERT INTO `article` VALUES ('1271056071519121498', '第八章（不争无尤）', '上善若水。水善利万物而不争，处众人之所恶（wu），故几于道。居善地，心善渊，与善仁，言善信，正善治，事善能，动善时。夫唯不争，故无尤（you）。', '老子', '4', '2', 'L', null);
INSERT INTO `article` VALUES ('1271056071519121499', '第九章（功遂身退）', '持而盈之，不如其已；揣而锐之，不可长保；金玉满堂，莫之能守；富贵而骄，自遗（yi）其咎（jiu）。功遂身退，天之道。', '老子', '4', '2', 'L', null);
INSERT INTO `article` VALUES ('1271056071519121500', '第十章（长而不宰）', '载营魄抱一，能无离乎？专气致柔，能婴儿乎？涤（di）除玄览（jian），能如疵（bi）乎？爱国治民，能无为乎？天门开阖（he），能为雌（ci）乎？明白四达，能无知乎？生之畜之，生而不有，为而不恃（shi），长而不宰，是谓玄德。', '老子', '4', '2', 'L', null);
INSERT INTO `article` VALUES ('1271056071519121501', '第十一章（无之为用）', '三十辐共一毂，当其无，有车（ju）之用。埏（shan）埴以为器，当其无，有器之用。凿（zao）户牖（you）以为室，当其无，有室之用；故有之以为利，无之以为用。', '老子', '4', '2', 'L', null);
INSERT INTO `article` VALUES ('1271056071519121502', '第十二章（圣人为腹）', '五色令人目盲；五音令人耳聋；五味令人口爽；驰（chi）骋（cheng）畋（tian）猎，令人心发狂；难得之货，令人行妨。是以圣人为腹不为目。故去彼取此。', '老子', '4', '2', 'L', null);
INSERT INTO `article` VALUES ('1271056071519121503', '第十三章（宠辱两忘）', '宠辱若惊，贵大患若身。何谓宠辱若惊？宠为下，得之若惊，失之若惊，是谓宠辱若惊。何谓贵大患若身？吾所以有大患者，为吾有身，及吾无身，吾有何患？故贵以身为天下，若可寄天下；爱以身为天下，若可托天下。', '老子', '4', '2', 'L', null);
INSERT INTO `article` VALUES ('1271056071519121504', '第十四章（无状之状）', '视之不见名曰夷，听之不闻名曰希，搏之不得名曰微。此三者不可致诘（jie），故混而为一。其上不皦（jiao），其下不昧。绳绳不可名，复归于无物。是谓无状之状，无物之象，是谓惚恍。迎之不见其首，随之不见其后。执古之道以御今之有，能知古始，是谓道纪。', '老子', '4', '2', 'L', null);
INSERT INTO `article` VALUES ('1271056071519121505', '第十五章（善为士者）', '古之善为士者，微妙玄通，深不可识。夫唯不可识，故强为之容：豫（yu）焉若冬涉（she）川，犹兮若畏四邻，俨（yan）兮其若容，涣（huan）兮若冰之将释，敦（dun）兮其若朴，旷兮其若谷，混兮其若浊。孰能浊以静之徐清？孰能安以久，动之徐生？保此道者不欲盈。夫唯不盈，故能蔽不新成。', '老子', '4', '2', 'L', null);
INSERT INTO `article` VALUES ('1271056071519121506', '第十六章（殆身不殆）', '致虚极，守静笃（du）。万物并作，吾以观复。夫物芸芸，各复归其根。归根曰静，是曰复命。复命曰常，知常曰明。不知常，妄（wang）作凶。知常容，容乃公，公乃王，王乃天，天乃道，道乃久。殁（mo）身不殆（dai）。', '老子', '4', '2', 'L', null);
INSERT INTO `article` VALUES ('1271056071519121507', '第十七章（功成事遂）', '太上，下知有之；其次，亲而誉之；其次，畏之；其次，侮（wu）之。信不足焉，有不信焉。悠兮其贵言。功成事遂，百姓皆谓“我自然”。', '老子', '4', '2', 'L', null);
INSERT INTO `article` VALUES ('1271056071519121508', '第十八章（道亡有义）', '大道废，有仁义；智慧出，有大伪；六亲不和，有孝慈；国家昏乱，有忠臣。', '老子', '4', '2', 'L', null);
INSERT INTO `article` VALUES ('1271056071519121509', '第十九章（绝圣弃智）', '绝圣弃智，民利百倍；绝仁弃义，民复孝慈；绝巧弃利，盗贼无有。此三者，以为文不足。故令有所属：见素抱朴，少私寡欲。', '老子', '4', '2', 'L', null);
INSERT INTO `article` VALUES ('1271056071519121510', '第二十章（独异于人）', '绝学无忧。唯之与阿，相去几何？善之与恶，相去若何？人之所畏，不可不畏。荒兮其未央哉！众人熙熙，如享太牢，如春登台；我独泊兮其未兆，如婴儿之未孩。傫傫（lei）兮若无所归！众人皆有余，而我独若遗（yi）。我愚人之心也哉，沌沌兮！俗人昭昭（zhao），我独昏昏。俗人察察，我独闷闷。澹dan兮其若海，飂（liao）兮若无止。众人皆有以，而我独顽且鄙（bi）。我独异于人，而贵食母。', '老子', '4', '2', 'L', null);
INSERT INTO `article` VALUES ('1271056071519121511', '第二十一章（惟道是从）', '孔德之容，惟道是从。道之为物，惟恍惟惚。惚兮恍兮，其中有象；恍兮惚兮，其中有物。窈兮冥（ming）兮，其中有精。其精甚真，其中有信。自今及古，其名不去，以阅众甫（fu）。吾何以知众甫之状哉！以此。', '老子', '4', '2', 'L', null);
INSERT INTO `article` VALUES ('1271056071519121512', '第二十二章（圣人抱一）', '曲则全，枉则直，洼（wa）则盈，敝则新，少则得，多则惑。是以圣人抱一为天下式。不自见（xian），故明；不自是，故彰（zhang）；不自伐，故有功；不自矜（jin），故长。夫唯不争，故天下莫能与之争。古之所谓“曲则全”者，岂虚言哉！诚全而归之。', '老子', '4', '2', 'L', null);
INSERT INTO `article` VALUES ('1271056071519121513', '第二十三章（道亦乐得）', '希言自然。故飘风不终朝，骤雨不终日。孰为此者？天地。天地尚不能久，而况于人乎？故从事于道者，同于道；德者，同于德；失者，同于失。同于道者，道亦乐得之；同于德者，德亦乐得之；同于失者，失亦乐得之。信不足焉，有不信焉。', '老子', '4', '2', 'L', null);
INSERT INTO `article` VALUES ('1271056071519121514', '第二十四章（自是不彰）', '企者不立，跨者不行，自见者不明，自是者不彰，自伐者无功，自矜（jin）者不长。其在道也，曰：余（yu）食赘（zhui）行。物或恶（wu）之，故有道者不处。', '老子', '4', '2', 'L', null);
INSERT INTO `article` VALUES ('1271056071519121515', '第二十五章（道法自然）', '有物混成，先天地生。寂（ji）兮寥（liao）兮，独立而不改，周行而不殆（dai），可以为天地母。吾不知其名，字之曰道，强为之名曰大。大曰逝，逝曰远，远曰反。故道大，天大，地大，王亦大。域中有四大，而王居其一焉。人法地，地法天，天法道，道法自然。', '老子', '4', '2', 'L', null);
INSERT INTO `article` VALUES ('1271056071519121516', '第二十六章（静为躁君）', '重为轻根，静为躁君。是以君子终日行不离辎（zi）重。虽有荣观，燕处超然。奈何万乘之主，而以身轻天下？轻则失本，躁则失君。', '老子', '4', '2', 'L', null);
INSERT INTO `article` VALUES ('1271056071519121517', '第二十七章（善行无痕）', '善行无辙（zhe）迹;善言无瑕（xia）谪（zhe）；善数（shu）不用筹（chou）策（ce）；善闭无关楗（jian）而不可开;善结无绳约而不可解。是以圣人常善救人，故无弃人；常善救物，故无弃物。是谓袭（xi）明。故善人者，不善人之师；不善人者，善人之资。不贵其师，不爱其资，虽智大迷，是谓要妙。', '老子', '4', '2', 'L', null);
INSERT INTO `article` VALUES ('1271056071519121518', '第二十八章（知雄守雌）', '知其雄，守其雌（ci），为天下溪。为天下溪，常德不离，复归于婴儿。知其白，守其黑，为天下式。为天下式，常德不忒（te），复归于无极。知其荣，守其辱。为天下谷。为天下谷，常德乃足，复归于朴（pu）。朴散则为器，圣人用之，则为官长，故大制不割。', '老子', '4', '2', 'L', null);
INSERT INTO `article` VALUES ('1271056071519121519', '第二十九章（圣人无为）', '将欲取天下而为之，吾见其不得已。天下神器，不可为也。为者败之，执者失之。故物或行或随，或歔（xu）或吹，或强或羸（lei），或载或隳（hui）。是以圣人去甚，去奢，去泰。', '老子', '4', '2', 'L', null);
INSERT INTO `article` VALUES ('1271056071519121520', '第三十章（以道佐主）', '以道佐（zuo）人主者，不以兵强天下，其事好还。师之所处，荆（jing）棘（ji）生焉。大军之后，必有凶年。善有果而已，不敢以取强。果而勿矜，果而勿伐，果而勿骄。果而不得已，果而勿强。物壮则老，是谓不道，不道早已。', '老子', '4', '2', 'L', null);
INSERT INTO `article` VALUES ('1271056071519121521', '第三十一章（有道不处）', '夫佳兵者不祥之器，物或恶之，故有道者不处。君子居则贵左，用兵则贵右。兵者不祥之器，非君子之器，不得已而用之，恬淡为上。胜而不美，而美之者，是乐杀人。夫乐杀人者，则不可得志于天下矣。吉事尚左，凶事尚右。偏将军居左，上将军居右，言以丧礼处之。杀人之众，以哀悲涖（li）之，战胜，以丧礼处之。', '老子', '4', '2', 'L', null);
INSERT INTO `article` VALUES ('1271056071519121522', '第三十二章（知止不殆）', '道常无名，朴虽小，天下莫能臣也。侯王若能守之，万物将自宾（bjn）。天地相合，以降甘露，民莫之令而自均。始制有名，名亦既有，夫亦将知止，知止可以不殆。譬（pi）道之在天下，犹川谷之于江海。', '老子', '4', '2', 'L', null);
INSERT INTO `article` VALUES ('1271056071519121523', '第三十三章（知人者智）', '知人者智，自知者明。胜人者有力，自胜者强。知足者富，强行者有志。不失其所者久，死而不亡者寿。', '老子', '4', '2', 'L', null);
INSERT INTO `article` VALUES ('1271056071519121524', '第三十四章（不自为大）', '大道泛兮，其可左右。万物恃（shi）之而生而不辞，功成不名有，衣养万物而不为主。常无欲，可名于小；万物归焉而不为主，可名为大。以其终不自为大，故能成其大。', '老子', '4', '2', 'L', null);
INSERT INTO `article` VALUES ('1271056071519121525', '第三十五章（执道乐往）', '执大象，天下往。往而不害，安平泰。乐（yue）与饵（er），过客止。道之出口。淡乎其无味，视之不足见，听之不足闻，用之不足既。', '老子', '4', '2', 'L', null);
INSERT INTO `article` VALUES ('1271056071519121526', '第三十六章（欲歙固张）', '将欲歙（xi）之，必固张之；将欲弱之，必固强之；将欲废之，必固兴之；将欲夺之，必固与之。是谓微明，柔弱胜刚强。鱼不可脱于渊，国之利器不可以示人。', '老子', '4', '2', 'L', null);
INSERT INTO `article` VALUES ('1271056071519121527', '第三十七章（道恒无为）', '道常无为而无不为，侯王若能守之，万物将自化。化而欲作，吾将镇之以无名之朴。无名之朴，夫亦将不欲。不欲以静，天下将自定。', '老子', '4', '2', 'L', null);
INSERT INTO `article` VALUES ('1271056071519121528', '第三十八章（上德不德）', '上德不德，是以有德；下德不失德，是以无德。上德无为，而无以为；下德为之，而有以为。上仁为之，而无以为；上义为之，而有以为；上礼为之，而莫之应，则攘（rang）臂而扔之。故失道而后德，失德而后仁，失仁而后义，失义而后礼。夫礼者，忠信之薄（bo），而乱之首；前识者，道之华，而愚（yu）之始。是以大丈夫处其厚，不居其薄；处其实，不居其华。故去彼取此。', '老子', '4', '2', 'L', null);
INSERT INTO `article` VALUES ('1271056071519121529', '第三十九章（下为高基）', '昔之得一者：天得一以清，地得一以宁；神得一以灵，谷得一以盈，万物得一以生，侯王得一以为天下贞（zhen）。其致之。天无以清，将恐裂；地无以宁，将恐废；神无以灵，将恐歇；谷无以盈，将恐竭（jie）；万物无以生，将恐灭；侯王无以贵高，将恐蹶（jue）。故贵以贱为本，高以下为基。是以侯王自谓孤、寡、不毂（gu）。此非以贱为本邪？非乎？故致数舆（yu）无舆。是故不欲琭（lu）琭如玉，珞（luo）珞如石。', '老子', '4', '2', 'L', null);
INSERT INTO `article` VALUES ('1271056071519121530', '第四十章（无中生有）', '反者，道之动；弱者，道之用。天下万物生于有，有生于无。', '老子', '4', '2', 'L', null);
INSERT INTO `article` VALUES ('1271056071519121531', '第四十一章（善贷且成）', '上士闻道，勤而行之；中士闻道，若存若亡；下士闻道，大笑之。不笑，不足以为道。故建言有之：明道若昧，进道若退，夷道若颣（lei）。上德若谷，大白若辱，广德若不足，建德若偷，质真若渝（yu）。大方无隅（yu）；大器晚成；大音希声，大象无形，道隐无名。夫唯道，善贷（dai）且成。', '老子', '4', '2', 'L', null);
INSERT INTO `article` VALUES ('1271056071519121532', '第四十二章（物损而益）', '道生一，一生二，二生三，三生万物。万物负阴而抱阳，冲气以为和。人之所恶，唯孤、寡、不毂，而王公以为称。故物或损之而益，或益之而损。人之所教，我亦教之。强梁者不得其死，吾将以为教父。', '老子', '4', '2', 'L', null);
INSERT INTO `article` VALUES ('1271056071519121533', '第四十三章（不言之教）', '天下之至柔，驰骋天下之至坚。无有入无间，吾是以知无为之有益。不言之教，无为之益，天下希及之。', '老子', '4', '2', 'L', null);
INSERT INTO `article` VALUES ('1271056071519121534', '第四十四章（知足不辱）', '名与身孰（shu）亲？身与货孰多？得与亡孰病？是故，甚爱必大费，多藏必厚亡。故知足不辱，知止不殆，可以长久。', '老子', '4', '2', 'L', null);
INSERT INTO `article` VALUES ('1271056071519121535', '第四十五章（大成若缺）', '大成若缺，其用不弊。大盈若冲，其用不穷。大直若屈，大巧若拙，大辩若讷（ne）。躁胜寒，静胜热。清静为天下正。', '老子', '4', '2', 'L', null);
INSERT INTO `article` VALUES ('1271056071519121536', '第四十六章（知足常足）', '天下有道，却走马以粪。天下无道，戎（rong）马生于郊。祸莫大于不知足；咎（jiu）莫大于欲得。故知足之足，常足矣。', '老子', '4', '2', 'L', null);
INSERT INTO `article` VALUES ('1271056071519121537', '第四十七章（不行而知）', '不出户，知天下；不窥（kui）牖（you），见天道。其出弥远，其知弥少。是以圣人不行而知，不见而名，不为而成。', '老子', '4', '2', 'L', null);
INSERT INTO `article` VALUES ('1271056071519121538', '第四十八章（为道日损）', '为学日益，为道日损。损之又损，以至于无为。无为而无不为。取天下常以无事，及其有事，不足以取天下。', '老子', '4', '2', 'L', null);
INSERT INTO `article` VALUES ('1271056071519121539', '第四十九章（善者吾善）', '圣人常无心，以百姓之心为心。善者吾善之，不善者吾亦善之，德善；信者吾信之，不信者，吾亦信之。德信。圣人在天下，歙（xi）歙；为天下，浑其心。圣人皆孩之。', '老子', '4', '2', 'L', null);
INSERT INTO `article` VALUES ('1271056071519121540', '第五十章（出生入死）', '出生入死。生之徒十有三；死之徒十有三；人之生，动之死地，亦十有三。夫何故？以其生生之厚。盖闻善摄（she）生者，陆行不遇兕（si）虎，入军不被甲兵。兕无所投其角，虎无所措其爪，兵无所容其刃。夫何故？以其无死地。', '老子', '4', '2', 'L', null);
INSERT INTO `article` VALUES ('1271056071519121541', '第五十一章（尊道贵德）', '道生之，德畜之，物形之，势成之。是以万物莫不尊道而贵德。道之尊，德之贵，夫莫之命而常自然。故道生之，德畜之，长之育之，亭之毒之，养之覆之。生而不有，为而不恃，长而不宰。是谓玄德。', '老子', '4', '2', 'L', null);
INSERT INTO `article` VALUES ('1271056071519121542', '第五十二章（天下有始）', '天下有始，以为天下母；既得其母，以知其子; 既知其子，复守其母，没身不殆。塞其兑，闭其门，终身不勤；开其兑，济其事，终身不救；见小曰明，守柔曰强。用其光，复归其明，无遗身殃（yang），是为袭常。', '老子', '4', '2', 'L', null);
INSERT INTO `article` VALUES ('1271056071519121543', '第五十三章（盗竽非道）', '使我介然有知，行于大道，唯施是畏。大道甚夷，而民好径。朝甚除，田甚芜，仓甚虚；服文采，带利剑，厌饮食，财货有余，是为盗夸。非道也哉！', '老子', '4', '2', 'L', null);
INSERT INTO `article` VALUES ('1271056071519121544', '第五十四章（善抱不脱）', '善建者不拔，善抱者不脱，子孙以祭祀不辍（chuo）；修之于身，其德乃真；修之于家，其德乃余；修之于乡，其德乃长；修之于国，其德乃丰；修之于天下，其德乃普。故以身观身，以家观家，以乡观乡，以国观国，以天下观天下。吾何以知天下之然哉？以此。', '老子', '4', '2', 'L', null);
INSERT INTO `article` VALUES ('1271056071519121545', '第五十五章（含德之厚）', '含德之厚，比于赤子。蜂虿（chai4）虺（hui3）蛇不螫（shi4），猛兽不据，攫（jue2）鸟不搏。骨弱筋柔而握固。未知牝（pin）牡（mu）之合而朘（zui1）作，精之至也。终日号而不嗄（sha4），和之至也。知和曰常，知常曰明，益生曰祥，心使气曰强。物壮则老，是谓不道，不道早已。', '老子', '4', '2', 'L', null);
INSERT INTO `article` VALUES ('1271056071519121546', '第五十六章（知者不言）', '知者不言，言者不知。塞其兑，闭其门，挫其锐，解其纷，和其光，同其尘，是谓玄同。故不可得而亲，不可得而疏；不可得而利，不可得而害；不可得而贵，不可得而贱。故为天下贵。', '老子', '4', '2', 'L', null);
INSERT INTO `article` VALUES ('1271056071519121547', '第五十七章（以正治国）', '以正治国，以奇用兵，以无事取天下。吾何以知其然哉？以此。天下多忌讳（hui），而民弥贫；民多利器，国家滋（zi）昏；人多伎（ji）巧，奇物滋起；法令滋彰，盗贼多有。故圣人云：“我无为而民自化；我好静而民自正；我无事而民自富；我无欲而民自朴。”', '老子', '4', '2', 'L', null);
INSERT INTO `article` VALUES ('1271056071519121548', '第五十八章（福祸相倚）', '其政闷闷，其民淳淳；其政察察，其民缺缺。祸兮福之所倚， 福兮祸之所伏。孰知其极？其无正也。正复为奇（ji1），善复为妖。人之迷，其日固久。是以圣人方而不割，廉而不刿（gui4），直而不肆，光而不耀。', '老子', '4', '2', 'L', null);
INSERT INTO `article` VALUES ('1271056071519121549', '第五十九章（治人尚啬）', '治人事天，莫若啬（se4）。夫为啬，是谓早服；早服谓之重积德；重积德，则无不克；无不克，则莫知其极；莫知其极，可以有国；有国之母，可以长久。是谓深根固柢（di），长生久视之道。', '老子', '4', '2', 'L', null);
INSERT INTO `article` VALUES ('1271056071519121550', '第六十章（以道治国）', '治大国，若烹（peng）小鲜。以道莅（li）天下，其鬼不神；非其鬼不神，其神不伤人；非其神不伤人，圣人亦不伤人。夫两不相伤，故德交归焉。', '老子', '4', '2', 'L', null);
INSERT INTO `article` VALUES ('1271056071519121551', '第六十一章（大者宜下）', '大国者下流，天下之交，天下之牝，牝常以静胜牡，以静为下。故大国以下小国，则取小国；小国以下大国，则取大国。故或下以取，或下而取。大国不过欲兼畜人，小国不过欲入事人。夫两者各得其所欲，大者宜为下。', '老子', '4', '2', 'L', null);
INSERT INTO `article` VALUES ('1271056071519121552', '第六十二章（万物之奥）', '道者万物之奥，善人之宝，不善人之所保。美言可以市，尊行可以加人。人之不善，何弃之有？故立天子，置三公，虽有拱璧以先驷（si）马，不如坐进此道。古之所以贵此道者何？不曰：求以得，有罪以免邪！故为天下贵。', '老子', '4', '2', 'L', null);
INSERT INTO `article` VALUES ('1271056071519121553', '第六十三章（能成其大）', '为无为，事无事，味无味。大小多少，抱怨以德。图难于其易，为大于其细；天下难事，必作于易; 天下大事，必作于细。是以圣人终不为大，故能成其大。夫轻诺必寡信，多易必多难。是以圣人犹难之，故终无难矣。', '老子', '4', '2', 'L', null);
INSERT INTO `article` VALUES ('1271056071519121554', '第六十四章（无为无败）', '其安易持，其未兆易谋，其脆易泮（pan），其微易散（san）。为之于未有，治之于未乱。合抱之木，生于毫末；九层之台，起于累土；千里之行，始于足下。为者败之，执者失之。是以圣人无为故无败，无执故无失。民之从事，常于几成而败之。慎终如始，则无败事。是以圣人欲不欲，不贵难得之货; 学不学，复众人之所过。以辅万物之自然，而不敢为。', '老子', '4', '2', 'L', null);
INSERT INTO `article` VALUES ('1271056071519121555', '第六十五章（善为道者）', '古之善为道者，非以明民，将以愚之。民之难治，以其智多。故以智治国，国之贼；不以智治国，国之福。知此两者亦稽（qi）式。常知稽式，是谓玄德。玄德深矣远矣，与物反矣！然后乃至大顺。', '老子', '4', '2', 'L', null);
INSERT INTO `article` VALUES ('1271056071519121556', '第六十六章（莫能与争）', '江海之所以能为百谷王者，以其善下之，故能为百谷王。是以欲上民，必以言下之；欲先民，必以身后之。是以圣人处上，而民不重；处前，而民不害。是以天下乐推而不厌。以其不争，故天下莫能与之争。', '老子', '4', '2', 'L', null);
INSERT INTO `article` VALUES ('1271056071519121557', '第六十七章（我有之宝）', '天下皆谓我道大，似不肖。夫唯大，故似不肖（xiao）。若肖，久矣其细也夫！我有三宝，持而保之: 一曰慈，二曰俭，三曰不敢为天下先。慈故能勇，俭故能广，不敢为天下先，故能成器长。今舍慈且勇，舍俭且广，舍后且先，死矣！夫慈，以战则胜，以守则固。天将救之，以慈卫之。', '老子', '4', '2', 'L', null);
INSERT INTO `article` VALUES ('1271056071519121558', '第六十八章（不争之德）', '善为士者不武，善战者不怒，善胜敌者不与，善用人者为之下。是谓不争之德，是谓用人之力，是谓配天之极。', '老子', '4', '2', 'L', null);
INSERT INTO `article` VALUES ('1271056071519121559', '第六十九章（哀者胜矣）', '用兵有言：“ 吾不敢为主而为客，不敢进寸而退尺。” 是谓行（heng）无行（heng），攘（rang）无臂，扔无敌，执无兵。祸莫大于轻敌，轻敌几丧吾宝。故抗兵相加，哀者胜矣。', '老子', '4', '2', 'L', null);
INSERT INTO `article` VALUES ('1271056071519121560', '第七十章（被褐怀玉）', '吾言甚易知，甚易行。天下莫能知，莫能行。言有宗，事有君。夫唯无知，是以不我知。知我者希，则我者贵。是以圣人被褐（he4）而怀玉。', '老子', '4', '2', 'L', null);
INSERT INTO `article` VALUES ('1271056071519121561', '第七十一章（以其病病）', '知，不知，上；不知，知，病。夫唯病病，是以不病。圣人不病，以其病病。是以不病。', '老子', '4', '2', 'L', null);
INSERT INTO `article` VALUES ('1271056071519121562', '第七十二章（自爱不贵）', '民不畏威，则大威至。无狎（xia）其所居，无厌其所生。夫唯不厌，是以不厌。是以圣人自知不自见（xian），自爱不自贵。故去彼取此。', '老子', '4', '2', 'L', null);
INSERT INTO `article` VALUES ('1271056071519121563', '第七十三章（天网恢恢）', '勇于敢则杀，勇于不敢则活。此两者，或利或害。天之所恶，孰知其故？是以圣人犹难之，天之道，不争而善胜，不言而善应，不召而自来，繟（chan）然而善谋。天网恢恢，疏而不失。', '老子', '4', '2', 'L', null);
INSERT INTO `article` VALUES ('1271056071519121564', '第七十四章（民不畏死）', '民不畏死，奈何以死惧之？若使民常畏死，而为奇者，吾得执而杀之，孰敢？常有司杀者杀。夫代司杀者杀，是谓代大匠斫（zhuo）。夫代大匠斫者，希有不伤其手矣。', '老子', '4', '2', 'L', null);
INSERT INTO `article` VALUES ('1271056071519121565', '第七十五章（无以生为）', '民之饥，以其上食税之多，是以饥。民之难治，以其上之有为，是以难治。民之轻死，以其上求生之厚，是以轻死。夫唯无以生为者，是贤于贵生。', '老子', '4', '2', 'L', null);
INSERT INTO `article` VALUES ('1271056071519121566', '第七十六章（强大处下）', '人之生也柔弱，其死也坚强。万物草木之生也柔脆，其死也枯槁（gao）。故坚强者死之徒，柔弱者生之徒。是以兵强则不胜，木强则兵。强大处下，柔弱处上。', '老子', '4', '2', 'L', null);
INSERT INTO `article` VALUES ('1271056071519121567', '第七十七章（不欲见贤）', '天之道，其犹张弓欤（yu）？高者抑（yi）之，下者举之；有余者损之，不足者补之；天之道，损有余而补不足；人之道则不然，损不足以奉有余。孰能有余以奉天下？唯有道者。是以圣人为而不恃，功成而不处，其不欲见贤。', '老子', '4', '2', 'L', null);
INSERT INTO `article` VALUES ('1271056071519121568', '第七十八章（柔之胜刚）', '天下莫柔弱于水，而攻坚强者莫之能胜，以其无以易之。弱之胜强，柔之胜刚，天下莫不知，莫能行。是以圣人云：“ 受国之垢（gou），是谓社稷（ji）主；受国不祥，是为天下王。”正言若反。', '老子', '4', '2', 'L', null);
INSERT INTO `article` VALUES ('1271056071519121569', '第七十九章（道与善人）', '和大怨，必有余怨，安可以为善？是以圣人执左契（qi），而不责于人。有德司契，无德司彻。天道无亲，常与善人。', '老子', '4', '2', 'L', null);
INSERT INTO `article` VALUES ('1271056071519121570', '第八十章（小国寡民）', '小国寡民。使有什（shi）伯之器而不用。使民重死而不远徙（xi3）。虽有舟舆（yu），无所乘之; 虽有甲兵，无所陈之。使民复结绳而用之。甘其食，美其服，安其居，乐其俗。邻国相望，鸡犬之声相闻，民至老死不相往来。', '老子', '4', '2', 'L', null);
INSERT INTO `article` VALUES ('1271056071519121571', '第八十一章（善者不辩）', '信言不美，美言不信。善者不辩，辩者不善。知者不博，博者不知。圣人不积，既以为人，己愈有；既以与人，己愈多。天之道，利而不害；圣人之道，为而不争。', '老子', '4', '2', 'L', null);
INSERT INTO `article` VALUES ('1271056071519121572', '弄权一时，凄凉万古', '栖守道德者，寂寞一时；依阿权势者，凄凉万古。达人观物外之物，思身后之身，守受一时之寂寞，毋取万古之凄凉。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121573', '抱朴守拙，涉世之道', '涉世浅，点染亦浅；历事深，机械亦深。故君子与其练达，不若朴鲁；与其曲谨，不若疏狂。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121574', '心事宜明，才华须韫', '君子之心事，天青日白；不可使人不知；君子之才华，玉韫珠藏，不可使人易知。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121575', '出污泥而不染，明机巧而不用', '势力纷华，不近者为洁，近之而不染者尤洁；智械机巧，不知者为高，知而不用者为尤高。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121576', '良药苦口，忠言逆耳', '耳中常闻逆耳之言，心中常有拂心之事，才是进修德行的砥石。若言言悦耳，事事快心，便把此生埋在鸩毒中矣。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121577', '和气致祥，喜神多瑞', '疾风怒雨，禽鸟戚戚；霁日光风，草木欣欣。可见天地不可一日无和气，人心不可一日无喜神。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121578', '淡中知真味，常里识英奇', '肥辛甘非真味，真味只是淡；神奇卓异非至人，至人只是常。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121579', '闲时吃紧，忙里悠闲', '天地寂然不动，而气机无息稍停；日月尽夜奔驰，而贞明万古不易；故君子闲时要有吃紧的心思，忙处要有悠闲的趣味。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121580', '静中观心，真妄毕见', '夜深人静独坐观心，始觉妄穷而真独露，每于此中得大机趣；既觉真现而妄难逃，又于此中得大惭忸。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121581', '得意须早回头，拂心莫便放手', '恩里由来生害，故快意时须早回首；败后或反成功，故拂心处莫便放手。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121582', '澹泊明志，肥甘丧节', '蔾口苋肠者，多冰清玉洁；衮衣玉食者，甘婢膝奴颜。盖志以澹泊明，而节从肥甘丧也。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121583', '眼前放得宽大，死后恩泽悠长', '面前的田地要放得宽，使人无不平之叹；身后的恩惠要留流得久，使人有不匮之恩。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121584', '路要让一步，味须减三分', '径路窄处，留一步与人行；滋味浓的，减三分让人尝；此是涉世一极安乐法。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121585', '脱俗成名，超凡入圣', '作人无甚高远事业，摆脱得俗情便入名流；为学无甚增益功夫，灭除得物累便超圣境。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121586', '义侠交友，纯心作人', '交友须带三分侠气，作人要存一点素心。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121587', '德在人先，利居人后', '宠利毋居人前，德业毋落人后；受享毋逾分外，修为毋减分中。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121588', '退即是进，与即是得', '处世让一步为高，退步即进步的张本；待人宽一分是福，利人实利己的根基。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121589', '骄矜无功，忏悔灭罪', '盖世功劳，当不得一个矜字；弥天罪过，当不过一个悔字。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121590', '完名让人全身远害，', '归咎于己韬光养德完名美节不宜独任，分些与人可以远害全身；辱行污名，不宜全推，引些归己可以韬光养德。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121591', '天道忌盈，卦终未济', '事事留个有余不尽的意思，便造物不能忌我，鬼神不能损我。若业必求满，功必求盈者，不生内变，必召外忧。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121592', '人能诚心和气，胜于调息观心', '家庭有个真佛，日用有种真道。人能诚心和气、愉色婉言，使父母兄弟间形骸两释、意气交流，胜于调息观心万倍矣！', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121593', '动静合宜，道之真体', '好动者云电风灯，嗜寂者死灰槁木；须定云止水中，有“鸢飞鱼跃”气象，才是有道的心体。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121594', '攻人毋太严，教人毋过高', '攻人之恶毋太严，要思其堪受；教人之善毋太高，当使其可从。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121595', '净从秽生，明从暗出', '粪虫至秽，变为蝉而饮露于秋风；腐草无光，化为萤而跃彩于夏月。因知洁常自污出，明每从晦生也。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121596', '客气伏而正气伸，妄心杀而真心现', '矜高倨傲，无非客气；降服得客气下，而后正气伸。情欲意识，尽属妄心；消杀得妄心尽，而后真心现。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121597', '事悟而痴除，性定而动正', '饱后思味，则浓淡之境都消；色后思淫，则男女之见尽绝。故人常以事后之悔悟，破临事之痴迷，则性定而动无不正。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121598', '轩冕客志在林泉，山林士胸怀廊庙', '居轩冕之中，不可无山林的气味；处林泉之下，须要怀廊庙的经纶。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121599', '无过便是功，无怨便是德', '处世不必邀功，无过便是功；与人不求感德，无怨便是德。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121600', '作事勿太苦，待人勿太枯', '忧勤是美德，太苦则无以适性怡情；澹泊是高风，太枯是无以济人利物。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121601', '原谅失败者之初心，', '注意成功者之末路事穷势蹙之人，当原其初心；功成行满之士，要观其末路。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121602', '富者应多施舍，智者宜不炫耀', '富贵家宜宽厚，而反忌刻，是富贵而贫贱其行矣！如何能享？聪明人宜敛藏，而反炫耀，是聪明而愚懵其病矣！如何不败？', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121603', '居安思危，处乱思治', '居卑而后知登高之为危，处晦而后知向明之太霭；守静而后知好动之过劳，养默而后知多言之为躁。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121604', '人能放得心下，即可入圣超凡', '放得功名富贵之心下，便可脱凡；放得道德仁义之心下，才可入圣。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121605', '我见害于心，聪明障于道', '利欲未尽害心，意见乃害心之蟊贼；声色未必障道，聪明乃障道之藩屏。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121606', '知退一步之法，加让三分之功', '人情反覆，世路崎岖。行不去处，须知退一步之法；行得去处，务加让三分之功。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121607', '对小人不恶，待君子有礼', '待小人不难于严，而难于不恶；待君子不难于恭，而难于有礼。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121608', '留正气给天地，遗清名于乾坤', '宁守浑噩而黜聪明，留些正气还天地；宁谢纷华而甘淡泊，遗个清名在乾坤。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121609', '伏魔先伏自心，驭横先平此气', '降魔者先降自心，心伏则群魔退听；驭横者先驭此气，气平则外横不侵。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121610', '种田地须除草艾，教弟子严谨交游', '教弟子如养闺女，最要严出入谨交游。若一接近匪人，是清静田中下一不净的种子，使终生难植嘉禾矣！', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121611', '欲路上勿染指，理路上勿退步', '欲路上事，毋乐其便而姑为染指，一染指便深入万仞；理路上事，毋惮其难而稍为退步，一退步便远隔千山。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121612', '不流于浓艳，不陷于枯寂', '念头浓者，自待厚待人亦厚，处处皆浓；念头淡者，自待薄待人亦薄，事事皆淡。故君子居常嗜好，不可太浓艳，亦不宜太枯寂。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121613', '超越天地之外，不入名利之中', '彼富我仁，彼爵我义，君子固不为君相所牢笼；人定胜天，志一动气，君子亦不受造化之陶铸。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121614', '立身要高一步，处世须退一步', '立身不高一步立，如尘里振衣，泥中灌足，如何超远；处世不退一步处，如飞鹅投烛，羝羊触藩，如何安乐。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121615', '修德须忘功名，读书定要深心', '学者要收拾精神并归一路；如修德而留意于事功名誉，必无实诣；读书而寄兴于吟咏风雅，定不深心。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121616', '真伪之道，只在一念', '人人有个大慈悲，维摩屠刽无二心也；处处有种真趣咏，金屋茅舍非两地也。只是欲闭情封，当面错过，便咫尺千里矣。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121617', '道者应有木石心，名相须具云水趣', '进德修道，要个木石的念头，若一有欣羡，便趋欲境；济世经邦，要段云水的趣咏，若一有贪著，便坠危机。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121618', '善人和气一团，恶人杀气腾腾', '吉人无论作用安详，即梦寐神魂无非和气；凶人无论行事狼戾，即声音唉语浑是杀机。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121619', '欲无祸于昭昭，勿得罪于冥冥', '肝受病则目不能视，肾受病则耳不能听；受病于人所不见，必发于人所共见；故君子欲无得罪于昭昭，必先无得罪于冥冥。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121620', '多心招祸，少事为福', '福莫福于少事，祸莫祸于多心。唯苦事者，方知少事之为福；唯平心者，始知多心之为祸。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121621', '处世要方圆自在，待人要宽严得宜', '处治世宜方，处乱世宜圆，处叔季之世当方圆并用；待善人宜宽，待恶人宜严，待庸众之人当宽严互存。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121622', '忘功不忘过，忘怨不忘恩', '我有功于人不可念，而过则不可不念；人有恩于我不可忘，而怨则不可不忘。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121623', '无求之施一粒万钟，', '有求之施万金无功施恩者，内不见己，外不见人。则千粟可当万钟之报；利物者，计己之施，贵人之报，虽然百镒难成一文之功。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121624', '推己及人，方便法门', '人之际遇，有齐有不齐，而能使己独齐乎？己之情理，有顺有不顺，而能使人皆顺乎？以此相观对治，亦是一方便法门。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121625', '恶人读书，适以济恶', '心地乾净方可读书学古，不然见一善行窃以济私，闻一善言假以覆短，是又籍寇兵而赍盗粮矣。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121626', '崇俭养廉，守拙全真', '奢者富而不足，何如俭者贫而有余；能者劳而府怨，何如拙者逸而全真。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121627', '读书希圣讲学躬行，', '居官爱民立业重德读书不见圣贤，如铅椠佣；居官不爱子民，如衣冠盗。讲学不尚躬行，为口头禅；立业不思重德，为眼前花。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121628', '读心中之名文，听本真之妙曲', '人心有一部真文章，都被残篇断简封锢了；有一部真鼓吹，都被妖歌艳舞湮没了。学者须扫除外物，直觅本来，才有个真受用。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121629', '苦中有乐，乐中有苦', '苦心中常得悦心之趣，得意时便生失意之悲。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121630', '无胜于有德行之行为，', '无劣于有权力之名誉富贵名益，自道德来者，如山林中花自是舒徐繁衍；自功业来者，如盆槛中花便有迁徙兴废；若以权力得者，如瓶钵中花，其根不植，其萎可立而待矣。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121631', '人死留名，豹死留皮', '春至时和，花尚铺一段好色，鸟且啭几句好音。士君子幸列头角，复遇温饱，不思立好言行好事，虽是在世百年恰似未生一日。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121632', '宽严得宜，勿偏一方', '学者有段兢业的心思，又要有段潇洒的趣味，若一味敛束清苦，是有秋杀无春生，何以发育万物。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121633', '大智若愚，大巧似拙', '真廉无廉名，立名者正所以为贪；大巧无巧术，用术者乃所以为拙。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121634', '谦虚受益，满盈招损', '攲器以满覆，扑满以空全；故君子宁居无不居有，宁处缺不处完。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121635', '名利总堕庸俗，意气终归剩技', '名根未拔者，纵轻千乘甘一瓢，总堕尘情；客气未融者，虽泽四海利万世，终为剩技。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121636', '心地须要光明，念头不可暗昧', '心体光明，暗室中有青天；念头暗昧，白日下有厉鬼。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121637', '勿羡贵显，勿虑饥饿', '人知名位为乐，不知无名无位之乐为最真；人知饥寒为虑，不知不饥不寒之虑更为甚。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121638', '阴恶之恶大，显善之善小', '为恶而畏人知，恶中尤有善路；为善而急人知，善处即是恶根。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121639', '君子居安思危，天亦无用其技', '天之机缄不测，抑而伸，伸而抑，皆是播弄英雄，颠倒豪杰处。君子是逆来顺受，居安思危，天亦无听用其伎俩矣。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121640', '中和为福，偏激为灾', '躁性者火炽，遇物则焚，寡恩者冰清，逢物必杀。凝滞固执者，如死水腐木，生机已绝，俱难建功业而延福祉。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121641', '多喜养福，去杀远祸', '福不可徼，养喜神以为召福之本而已；祸不可避，去杀机以为远祸之方而已。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121642', '谨言慎行，君子之道', '十语九中未必称奇，一语不中则愆尤骈集；十谋九成未必归功，一谋不成则訾议业兴，君子所以宁默毋躁，守拙无巧。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121643', '杀气寒薄，和气福厚', '天地之气，暖则生，寒则杀。故性气清冷者，受享亦凉薄；唯和气热心之人，其福亦厚，其禄也长。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121644', '正义路广，欲情道狭', '天理路上甚宽，稍游心胸中便觉广大宏朗；人欲路上甚窄，才寄迹眼前俱荆棘泥土。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121645', '磨练之福久，参勘之知真', '一苦一乐相磨练，练极而成福者其福始久；一疑一信相参勘，勘极而成知者其知始真。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121646', '虚心明义理，实心却物欲', '心不可不虚，虚则义理来居；心不可不实，实则物欲不入。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121647', '厚德载物，雅量容人', '地之秽者多生物，水之清者常无鱼；故君子当存含垢纳污之量，不可持好洁独行之操。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121648', '忧劳兴国，逸豫亡身', '泛驾之马可就驰驱，跃冶之金终归型范。只一优游不振，便终身无个进步。白沙云：“为人多病未足羞，一生无病是吾忧。”真确论也。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121649', '一念私贪，万劫不复', '人只一念私贪，便销刚为柔、塞智为昏、变恩为惨、染洁为污，坏了一生人品。故古人以不贪为宝，所以度越一世。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121650', '心公不昧，六贼无踪', '耳目见闻为外贼，情欲意识为内贱。只是主人翁惺惺不昧，独坐堂中，贼便化为家人矣！', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121651', '勉励现前之业，图谋未来之功', '图未就之功，不如保已成之业；悔既往之失，不如防将来之非。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121652', '养天地正气，法古今完人', '气象要高旷，而不可疏狂；心思要缜密，而不可琐屑；趣味要冲淡，而不可偏枯；操字要严明，而不可激烈。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121653', '不著色相，不留声影', '风来疏竹，风过而竹不留声；雁度寒潭，雁去而潭不留影。故君子事来而心始现，事去而心随空。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121654', '君子德行，其道中庸', '清能有容，仁能善断，明不伤察，直不过矫，是谓蜜饯不甜，海味不咸，才是懿德。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121655', '君子穷当益工，勿失风雅气度', '贫家净扫地，贫女净梳头，景色虽不艳丽，气度自是风雅。士君子一当穷愁寥落，奈何辄自废弛哉！', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121656', '未雨绸缪，有备无患', '闲中不放过，忙处有受用；静中不落空，动处有受用；暗中不欺隐，明处有受用。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121657', '临崖勒马，起死回生', '念头起处，才觉向欲路上去，便挽从理路上来。一起便觉，一觉便走，此是转祸为福，起死回生的关头，切莫轻易放过。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121658', '宁静淡泊，观心之道', '静中念虑澄澈，见心之真体；闲中气象从容，识心之真机；淡中意趣冲夷，得心之真味。观心证道，无如此三者。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121659', '动中静是真静，苦中乐是真乐', '静中静非真静，动处静得来，才是性天之真境；乐处乐非真乐，苦中乐得来，才是心体之真机。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121660', '舍己毋处疑，施恩勿望报', '舍己毋处其疑，处其疑即所舍之志多愧矣；施人毋责其报，责其报并所施之心俱非矣。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121661', '厚德以积福，逸心以补劳，修道以解厄', '天薄我以福，吾厚吾德以迓之；天劳我以形，吾补吾心以逸之；天厄我以遇，吾亨吾道以通之；天且奈我何哉？', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121662', '天福无欲之贞士，而祸避祸之 人', '贞士无心徼福，天即就无心处牖其衷；人著意避祸，天即就著意中夺其魄。可见天之机权最神，人之智巧何益？', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121663', '人生重结果，种田看收成', '声妓晚景从良，一世之胭花无碍；贞妇白头失守，半生之清苦俱非。语云：“看人只看后半截。”真名言也。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121664', '多种功德，勿贪权位', '平民肯种德施惠，便是无位的公相；士夫徒贪权市宠，竟成有爵的乞人。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121665', '当念积累之难，常思倾覆之易', '问祖宗之德泽，吾身所享者是，当念其积累之难；问子孙之福祉，吾身所贻者是，要思其倾覆之易。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121666', '只畏伪君子，不怕真小人', '君子而诈善，无异小人之肆恶；君子而改节，不及小人之自新。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121667', '春风解冻，和气消冰', '家人有过，不宜暴怒，不宜轻弃；此事难言，借他事隐讽之；今日不悟，俟来日再警之。如春风解冻，如和气消冰，才是家庭的型范。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121668', '能彻见心性，则天下平隐', '此心常看得圆满，天下自无缺陷之世界；此心常放得宽平，天下自然无险侧之人情。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121669', '操履不可少变，锋芒不可太露', '澹泊之士，必为浓艳者所疑；检饰之人，多为放肆者所忌。君子处此，固不可少变其操履，亦不可露其锋芒！', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121670', '顺境不足喜，逆境不足忧', '居逆境中，周身皆针砭药石，砥节砺行而不觉；处顺境中，眼前尽兵刃戈矛，销膏靡骨而不知。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121671', '富贵而恣势弄权，乃自取灭亡之道', '生长富贵家中，嗜欲如猛火，权势似烈炎，若不带些清冷气味，其火炎不至焚人，心将自烁矣。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121672', '精诚所至，金石为开', '人心一真，便霜可飞，城可陨，金石可镂；若伪妄之人，形骸徒具，真宰已亡，对人则面目可憎，独居则形影自愧。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121673', '文章极处无奇巧，人品极处只本然', '文章作到极处，无有他奇，只有恰好；人品做到极处，无有他奇，只是本然。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121674', '明世相之本体，负天下之重任', '以幻境言，无论功名富贵，即肢体亦属委形；以真境言，无论父母兄弟，即万物皆吾一体，人能看得破认得真，才可以任天下之重担，亦可脱世间之缰锁。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121675', '凡事当留余地，五分便无殃悔', '爽口之味皆烂肠腐骨之药，五分便无殃；快心之事悉败身丧德之媒，五分便无悔。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121676', '忠恕待人，养德远害', '不责人小过，不发人阴私，不念人旧恶。三者可以养德，亦可以远害。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121677', '持身不可轻，用心不可重', '士君子持身不可轻，轻则物能扰我，而无悠闲镇定之趣；用意不可重，重则我为物泥，而无潇洒活泼之机。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121678', '人生无常，不可虚度', '天地有万古，此身不再得；人生只百年，此日最易过。幸生其间者，不可不知有生之乐，亦不可不怀虚生之忧。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121679', '德怨两忘，恩仇俱泯', '怨因德彰，故使人德我，不若德怨之两忘；仇因恩立，故使人如恩，不若恩仇之俱泯。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121680', '持盈履满，君子兢兢', '老来疾病，都是壮时招的；衰后罪孽，都是盛时造的。故持盈履满，君子尤兢兢焉。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121681', '却私扶公，修身种德', '市私恩，不如扶公议；结新知，不如敦旧好；立荣名，不如种隐德；尚奇节，不如谨庸行。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121682', '勿犯公论，勿谄权门', '公平正论不可犯手，一犯则贻羞万世；权门私窦不可著脚，一著则沾污终身。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121683', '直躬不畏人忌，无恶不惧人毁', '曲意而使人喜，不若直躬而使人忌；无善而致人誉，不若无恶而致人毁。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121684', '从容处家族之变，剀切规朋友之失', '处父兄骨肉之变，宜从容不宜激烈；遇朋友交游之失，宜剀切不宜优游。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121685', '大处着眼，小处着手', '小处不渗漏，暗处不欺隐，末路不怠荒，才是个真正英雄。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121686', '爱重反为仇，薄极反成喜', '千金难结一时之欢，一饭竞致终身之感，盖爱重反为仇，薄极反成喜也。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121687', '藏巧于拙，寓清于浊', '藏巧于拙，用晦而明，寓清于浊，以屈为伸，真涉世之一壶，藏身之三窟也。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121688', '盛极必衰，剥极必复', '衰飒的景象就在盛满中，发生的机缄即在零落内；故君子居安宜操一片心以虑患，处变当坚百忍以图成。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121689', '奇异无远识，独行无恒操', '惊奇喜异者，无远大之识；苦节独者者行，非恒久之操。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121690', '放下屠刀，立地成佛', '当怒火欲水正在腾沸处，明明知得，又明明犯著。知的是谁，犯的又是谁？此处能猛然转念，邪魔便为真君矣。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121691', '毋偏信自任，毋自满嫉人', '毋偏信而为奸所欺，毋自任而为气所使；毋以己之长而形人之短，毋因己之拙而忌人之能。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121692', '毋以短攻短，毋以顽济顽', '人之短处，要曲为弥缝，如暴而扬之，是以短攻短；人有顽固，要善为化诲，如忿而疾之，是以顽济顽。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121693', '对阴险者勿推心，遇高傲者勿多口', '遇沉沉不语之士，且莫输心；见悻悻自好之人，应该防口。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121694', '震聋启瞆，临深履薄', '念头昏散处要知提醒，念头吃紧时要知放下；不然恐去昏昏之病，又来憧憧扰矣。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121695', '君子之心，雨过天晴', '霁日青天，倏变为迅雷震电，疾风怒雨，倏转为朗月晴空，气机何尝一毫凝滞？太虚何尝一毫障塞？人之心体亦当如是。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121696', '有识有力，魔鬼无踪', '胜私制欲之功，有曰识不早力不易者，有曰识得破忍不过者，盖识是一颗照魔的明珠，力是一把斩魔的慧剑，两不可少也。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121697', '大量能容，不动声色', '觉人之诈不形于言，受人之悔不动于色，此中有无穷意味，亦有无穷受用。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121698', '困苦穷乏，锻炼身心', '横逆困穷是锻炼豪杰的一副炉锤，能受其锻炼则身心交益，不受其锻炼则身心交损。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121699', '人乃天地之缩图，天地乃人之父母', '吾身一小天地也，使喜怒不愆，好恶有则，便是变理的功夫；天地一大父母也，使民无怨咨，物无氛疹，亦是敦睦的气象。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121700', '戒疏于虑，警伤于察', '害人之心不可有，防人之心不可无，此戒疏于虑也；宁受人之欺，毋逆人之诈，此警伤于察也，二语并存精明而浑厚矣。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121701', '辨别是非，认识天体', '毋因群疑而阻独见，毋任己意而废人言，毋私小惠而伤大体，毋借公论以快私情。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121702', '亲近善人须知机杜谗，', '铲除恶人应保密防祸善人未能急亲不宜预扬，恐来谗谮之奸；恶人未能轻去不宜先发，恐遭媒蘖之祸。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121703', '节义来自暗室不欺，', '经纶缲出临深履薄青天白日的节义，自暗屋漏室中培来；旋乾转坤的经纶，自临深履薄处缲出。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121704', '伦常本乎天性，不可认德怀恩', '父慈子孝，兄友弟恭，纵作到极处俱是合当如此，著不得一毫感激的念头。如施者任德，受者怀恩，便是路人，便成市道矣。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121705', '不夸妍好洁，无丑污之辱', '有妍必有丑为之对，我不夸妍，谁能丑我？有洁必有污为之仇，我不好洁，谁能污我？', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121706', '富贵多炎凉，骨肉多妒忌', '炎凉之态，富贵更甚于贫贱；妒忌之心，骨肉尤狠于外人。此处若不当以冷肠，御以平气，鲜不日坐烦恼障中矣。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121707', '功过不可少混，恩仇不可过明', '功过不容少混，混则人怀惰堕之心；恩仇不可太明，明则人起携贰之志。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121708', '位盛危至，德高谤兴', '爵位不宜太盛，太盛则危；能事不宜尽毕，尽毕则衰；行谊不宜过高，过高则谤兴而毁来。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121709', '阴恶祸深，阳善功小', '恶忌阴，善忌阳，故恶之显者祸浅，而隐者祸深；善之显者功小，而隐者功大。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121710', '应以德御才，勿恃才败德', '德者才之王，才者德之奴。有才无德，如家无主而奴用事矣，几何不魍魍猖狂。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121711', '穷寇勿追，投鼠忌器', '锄奸杜幸，要放他一条去路。若使之一无所容，譬如塞鼠穴者，一切去路都塞尽，则一切好物俱咬破矣。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121712', '过归己任，功让他人', '当与人同过，不当与人同功，同功则相忌；可与人共患难，不可与人共安乐，安乐则相仇。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121713', '警世救人，功德无量', '士君子贫不能济物者，遇人痴迷处出一言提醒之，遇人急难处出一言解救之，亦是无量功德。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121714', '趋炎附势，人惰之常', '饥则附，饱则飏，燠则趋，寒则弃，人情通患也。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121715', '须冷眼观物，勿轻动刚肠', '君子宜净试冷眼，慎勿轻动刚肠。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121716', '量弘识高，功德日进', '德随量进，量由识长，故欲厚其德，不可不弘其量，欲弘其量，不可不大其识。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121717', '人生惟危，道心惟微', '一灯萤然，万籁无声，此吾人初入宴寂时也；晓梦初醒，群动未起，此吾人初出混沌处也。乘此而一念回光，炯然返照，始知耳口鼻皆桎梏，而情欲嗜好悉机械矣。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121718', '诸恶莫作，众善奉行', '反己者，触事皆成药石；尤人者，动念即是戈子。一以辟众善之路，一以浚诸恶之源，相去霄壤矣。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121719', '功名一时，气节千载', '事业文章随身消毁，而精神万古如新；功名富贵逐世转移，而气节千载一日：君子信不当以彼易此也。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121720', '自然造化之妙，智巧所不能及', '鱼网之设，鸿则罹其中；螳螂之贪，雀又乘其后。机里藏机，变外生变，智巧何足恃哉。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121721', '真诚为人，圆转涉世', '作人无点真恳念头，便成个花子，事事皆虚；涉世无段圆活机趣，便是个木人，处处有碍。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121722', '云去而本觉之月观，尘拂而真如之镜明', '水不波则自定，鉴不翳则自明。故心无可清，去其混之者而清自现；乐不必寻，去其苦之者而乐自存。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121723', '一念能动鬼神，一行克动天地', '有一念犯鬼神之禁，一言而伤天地之和，一事而酿子孙之祸者，最易切戒。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121724', '情急招损，严厉生恨', '事有急之不白者宽之或自明，毋躁急以速其忿；人有操之不从者纵之或自化，毋躁切以益其顽。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121725', '不能养德，终归末节', '节义傲青云，文章白如雪，若不以德性陶熔之，终为血气之私技能之末。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121726', '急流勇退，与世无争', '谢世当谢于正盛之时，居身宜居于独后之地。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121727', '慎德于小事，施恩于无缘', '谨德须谨于至微之事，施恩务施于不报之人。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121728', '文华不如简素，读今不如述古', '交市人不如友山翁，谒朱人不如亲白屋；听街谈巷语不如闻樵歌牧咏，谈今人失德过举不如述古人嘉言懿行。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121729', '修身种德，事业之基', '德者事业之基，未有基不固而栋宇坚久者。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121730', '心善而子孙盛，根固而枝叶荣', '心者后裔之根，未有根不植而枝叶荣茂者。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121731', '勿妄自菲薄，勿自夸自傲', '前人云：“抛却自家无尽藏，沿门持钵效贫儿。”又云：“暴富贫儿休说梦，谁家灶里火无烟？”一箴自昧所有，一箴自夸所有，可为学问切戒。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121732', '道乃公正无私，学当随时警惕', '道是一重公众物事，当随人而接引；学是一个寻常家饭，当随事而警惕。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121733', '信人示己之诚，疑人显己之诈', '信人者，人未必尽诚，己则独诚矣；疑人者，人未必皆诈，己则先诈矣。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121734', '春风育物，朔雪杀生', '念头宽厚的如春风煦育，万物遭之而生；念头忌刻的如朔雪阴凝，万物遭之而死。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121735', '善根暗长，恶损潜消', '为善不见其益，如草里东瓜自应暗长；为恶不见其损，如庭前春雪，当必潜消。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121736', '厚待故交，礼遇衰朽', '遇故旧之交，意气要愈新；处隐微之事，心迹宜愈显；待衰朽之人，恩礼当愈隆。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121737', '君子以勤俭立德，小人以勤俭图利', '勤者敏于德义，而世人借勤以济其贫；俭者淡于货利，而世人假俭以饰其吝。君子持身之符，反为小人营私之具矣。惜哉！', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121738', '学贵有恒，道在悟真', '凭意兴作为者，随作则随止，岂是不退之轮；从情识解悟者，有悟则有迷，终非常明之灯。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121739', '律己宜严，待人宜宽', '人之过误宜恕，而在己则不可恕；己之困辱宜忍，而在人则不可忍。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121740', '为奇不为异，求清不求激', '能脱俗便是奇，作意尚奇者，不为奇而为异；不合污便是清，绝俗求清者，不为清而为激。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121741', '恩宜自薄而厚，威须先严后宽', '恩宜自淡而浓，先浓后淡者，人忘其惠；威宜自严而宽，先宽后严者，人怨其酷。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121742', '心虚意净，明心见性', '心虚则性现，不息心而求见性，如拨波觅月；意净则心清，不了意而求明心，如索镜增尘。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121743', '人情冷暖，世态炎凉', '我贵而人奉之，奉此峨冠大带也；我贱而人侮之，侮此布衣草履也。然则原非奉我，我胡为喜？原非侮我，我胡为怒？', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121744', '慈悲之心，生生之机', '“为鼠常留饭，怜蛾不点灯”古人此等念头，是吾人一点生生之机，无此，便所谓土木形骸而已。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121745', '勿为欲情所系，便与本体相合', '心体便是天体，一念之喜，景星庆云；一念之怒，震雷暴雨；一念之慈，和风甘露；一念之严，烈日秋霜，何者少得，只要随起随灭，廓然无碍，便与太虚同体。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121746', '无事寂寂以照惺惺，有事惺惺以主寂寂', '无事时心易昏冥，宜寂寂而照以惺惺；有事时心易奔逸，宜惺惺而主以寂寂。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121747', '明利害之情，忘利害之虑', '议事者身在事外，宜悉利害之情；任事者身居事中，当忘利害之虑。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121748', '操持严明，守正不阿', '士君子处权门要路，操履要严明，心气要和易，毋少随而近腥膻之党，亦毋过激而犯蜂虿之毒。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121749', '浑然和气，处事珍宝', '标节义者，必以节义受谤；榜道学者，常因道学招尤。故君子不近恶事，亦不立善名，只浑然和气，才是居身之珍。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121750', '诚心和气陶冶暴恶，名义气节激砺邪曲', '遇欺诈之人，以诚心感动之；遇暴戾之人，以和气薰蒸之；遇倾邪私曲之人，以名义气节激砺之，天下无不入我陶冶中矣。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121751', '和气致祥瑞，洁白留清名', '一念慈祥，可以酝酿两间和气；寸心洁白，可以昭垂百代清芬。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121752', '庸德庸行，和平之基', '阴谋怪习，异行奇能，俱是涉世祸胎。只有一个庸德庸行，便可以完混沌而召和平。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121753', '忍得住耐得过，则得自在之境', '语云：“登山耐侧路，踏雪耐危桥。”一耐字极有意味，如倾险之人情，坎坷之世道，若不得一耐字撑持过去，几何不堕入榛莽坑堑哉？', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121754', '心体莹然，不失本真', '夸逞功业，炫耀文章，皆是靠外物作人。不知心体莹然本来不失，即无寸功只字亦自有堂堂正正作人处。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121755', '忙里偷闲，闹中取静', '忙里要偷闲，须先向闲时讨个把柄；闹中要取静，须先从静处立个主宰：不然未有不因境而迁，随事而靡者。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121756', '为天地立心，为生民立命，为子孙造福', '不昧己心，不尽人情，不竭物力；三者可以为天地立心，为生民立命，为子孙造福。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121757', '为官公廉，居家恕俭', '居官有二语，曰“唯公则生明，唯廉则生威”；居家有二语，曰“唯恕则情平，唯俭则用足”。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121758', '处富知贫，居安思危', '处富贵之地，要知贫贱的痛痒；当少壮之时，须念衰老的辛酸。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121759', '清浊并包，善恶兼容', '持身不可太皎洁，一切污辱垢秽要茹纳得；与人不可太分明，一切善恶贤愚要包容得。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121760', '勿仇小人，勿媚君子', '休与小人仇仇，小人自有对头；休向君子谄媚，君子原无私惠。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121761', '疾病易医，魔障难除', '纵欲之病可医，而势理之病难医，事物之障可除，而义理之障难除。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121762', '金须百炼，矢不轻发', '磨砺当如百炼之金，急就者非邃养；施为宜似千钧之弩，轻发者无宏功。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121763', '宁为小人所毁，勿为君子所容', '宁为小人所忌毁，毋为小人所媚悦；宁为君子所责备，毋为君子所包容。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121764', '好利者害显而浅，好名者害隐而深', '好利者逸出道义之外，其害显而浅；好名者窜入于道义之中，其害隐而深。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121765', '忘恩报怨，刻薄之尤', '受人之恩虽深不报，怨则浅亦报之；闻人之恶虽隐不疑，善则显亦疑之。此刻之极，薄之尤也，宜切戒之。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121766', '谗言如云蔽日，甘言如风侵肌', '谗夫毁士，如寸云蔽日，不久自明；媚子阿人，似隙风侵肌，不觉其损。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121767', '戒高绝之行，忌褊急之衷', '山之高峻处无木，而溪谷回环则草木丛生；水之湍急处无鱼，而渊潭停蓄则鱼鳖聚集。此高绝之行，褊急之衷。君子重有戒焉。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121768', '虚圆立业，偾事失机', '建功立业者，多虚圆之士；偾事失机者，必执拗之人。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121769', '处世要道，不即不离', '处世不宜与俗同，亦不宜与俗异；作事不宜令人厌，亦不宜令人喜。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121770', '老当益壮，大器晚成', '日既暮而犹烟霞绚烂，岁将晚而更橙桔芳馨，故末路晚年君子更宜精神百倍。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121771', '藏才隐智，任重致远', '鹰立如睡，虎行似病，正是他攫人噬人手段处。故君子要聪明不露，才华不逞，才有肩鸿任钜的力量。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121772', '喜忧安危，勿介于心', '毋忧拂意，毋喜快心，毋恃久安，毋惮初难。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121773', '宴乐、声色、名位，三者不可过贪', '饮宴之乐多，不是个好人家；声华之习胜，不是个好士子；名位之念重，不是个好臣士。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121774', '乐极生悲，苦尽甘来', '世人以心肯处为乐，欲被乐心引在苦处；达士以心拂处为乐，终为苦心换得乐来。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121775', '过满则溢，过刚则折', '居盈满者，如水之将溢未溢，切忌再加上一滴；处危急者，如木之将折未折，切忌再加一搦。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121776', '冷静观人，理智处世', '冷眼观人，冷耳听语，冷情当感，冷心思理。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121777', '量宽福厚，器小禄薄', '仁人心地宽舒，便福厚而庆长，事事成个宽舒气象；鄙夫念头迫促，便禄薄而泽短，事事得个迫促规模。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121778', '恶不可即就，善不可即亲', '闻恶不可就恶，恐为谗夫泄怒；闻善不可即亲，恐引奸人进身。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121779', '躁性偾事，和平徼福', '性躁心粗者一事无成，心和气平者百福自集。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121780', '酷则失善人，滥则招恶友', '用人不宜刻，刻则思效者去；交友不宜滥，滥则贡谀者来。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121781', '急处站得稳，高处看得准，', '危险径地早回头风斜雨急处要立得脚定，花浓柳艳处要著得眼高，路危径险处要回得头早。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121782', '和衷以济节义，谦德以承功名', '节义之人济以和衷，才不启忿争之路；功名之士承以谦德，方不开嫉妒之门。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121783', '居官有节度，居乡敦旧交', '士大夫居官不可竿牍无节，要使人难见，以杜幸端；居乡不可崖岸太高，要使人易见，以敦旧好。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121784', '事上敬谨，待下宽仁', '大人不可不畏，畏大人则无放逸之心；小民亦不可不畏，畏小民则无豪横之名。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121785', '处逆境时比于下，心怠荒时思于上', '事稍拂逆，便思不如我的人，则怨尤自清；心稍怠荒，便思胜似我的人，则精神自奋。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121786', '不轻诺，不生嗔，不多事，不倦怠', '不可乘喜而轻诺，不可因醉而生嗔，不可乘快而多事，不可因倦而鲜终。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121787', '读书读到乐处，观物观入化境', '善读书者，要读到手舞足蹈处，方不落筌蹄；善观物者，要观到心融神洽时，方不泥迹象。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121788', '勿逞所长以形人之短，', '勿恃所有以凌人之贫天贤一人以诲众人之愚，而世反逞所长以形人之短；天富一人以济众人之困，而世反挟所有以凌人之贫；真天之戮民哉！', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121789', '上智下愚可与论学，中才之人难与下手', '至人何思何虑，愚人不认不知，可与论学亦可与建功。唯中才的人，多一番思虑知识，便多一番臆度猜疑，事事难与下手。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121790', '守口须密，防意须严', '口乃心之门，守口不密，泄尽真机；意乃心之足，防意不严，走尽邪路。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121791', '责人宜宽，责己宜苛', '责人者，原无过于有过之中，则情平；责己者，求有过于无过之内，则德进。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121792', '幼不学，不成器', '子弟者大人之胚胎，秀才者士夫之胚胎。此时若火力不到，陶铸不纯，他日涉世立朝，终难成个令器。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121793', '不忧患难，不畏权豪', '君子处患难而不忧，当宴游而惕虑；遇权豪而不惧，对茕独而惊心。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121794', '浓夭淡久，大器晚成', '桃李虽艳，何如松苍柏翠之坚贞；梨杏虽甘，何时橙黄橘绿之馨冽？信乎！浓夭不及淡久，早秀不如晚成也。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121795', '静中见真境，淡中识本然', '风恬浪静中，见人生之真境；味淡声希处，识心体之本然。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121796', '言者多不顾行，谈者未必真知', '谈山林之乐者，未必真得山林之趣；厌名利之谈者，未必尽忘名利之情。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121797', '无为无作，优游清逸', '钓水逸事也，尚持生杀之柄；奕棋清戏也，且动战争之心。可见喜事不如省事之为适，多能不若无能之全真。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121798', '春色为人间之装饰，', '秋气见天地之真吾茑花茂而山浓谷艳，总是乾坤之幻境；水木落而石瘦崖枯，才见天地之真吾。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121799', '世间之广狭，皆由于自造', '岁月本长，而忙者自促；天地本宽，而卑者自隘；风花雪月本闲，而劳攘者自冗。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121800', '乐贵自然真趣，景物不在多远', '得趣不在多，盆池拳石间烟霞俱足；会景不在远，蓬窗竹屋下风月自赊。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121801', '心静而本体现，水清而月影明', '听静夜之钟声，唤醒梦中之梦；观澄潭之月影，窥见身外之身。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121802', '天地万物，皆是实相', '鸟语虫声，总是传心之诀；花英草色，无非见道之文。学者要天机清澈，胸次玲珑，触物皆有会心处。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121803', '观形不如观心，神用胜过迹用', '人解读有字书，不解读无字书；知弹有弦琴，不知弹无弦琴。以迹用，不以神用，何以得琴书之趣？', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121804', '心无物欲乾坤静，坐有琴书便是仙', '心无物欲，即是秋空霁海；坐有琴书，便成石室丹丘。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121805', '欢乐极兮哀情多，兴味浓后感索然', '宾朋云集，剧饮淋漓乐矣，俄而漏尽烛残香销茗冷，不觉反成呕咽，令人索然无味。天下事率类此，奈何不早回头也。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121806', '知机其神乎，会趣明道矣', '会得个中趣，五湖之烟月尽入寸里；破得眼前机，千古之英雄尽归掌握。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121807', '万象皆空幻，达人须达观', '山河大地已属微尘，而况尘中之尘；血肉之躯且归泡影，而况影外之影。非上上智，无了了心。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121808', '泡沫人生，何争名利', '石火光中争长竞短，几何光阴？蜗牛角上较雌论雄，许大世界？', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121809', '极端空寂，过犹不及', '寒灯无焰，敝裘无温，总是播弄光景；身如槁木，心似死灰，不免坠在顽空。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121810', '得好休时便好休，如不休时终无休', '人肯当下休，便当下了。若要寻个歇处，则婚嫁虽完，事亦不少。僧道虽好，心亦不了。前人云：“如今休会便休去，若觅了时无了时。”见之卓矣。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121811', '冷静观世事，忙中去偷闲', '从冷视热，然后知热处之奔驰无益；从冗入闲，然后从觉闲中之滋味最长。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121812', '不亲富贵，不溺酒食', '有浮云富贵之风，而不必岩栖穴处；无膏盲泉石之癖，而常自醉酒耽诗。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121813', '恬淡适己，身心自在', '竞逐听人，而不嫌尽醉：恬淡适己，而不夸独醒。此释氏所谓“不为法缠，不为空缠，身心两自在”者。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121814', '广狭长短，由于心念', '延促由于一念，宽窄系之寸心；故机闲者一日遥干千古，意广者斗室宽若两间。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121815', '栽花种竹，心境无我', '损之又损，栽花种竹，仅交还乌有先生；忘无可忘，焚香煮茗，总不问白衣童子。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121816', '知足则仙凡异路，善用则生杀自殊', '都来眼前事，知足者仙境，不知足者凡境；总出世上因，善用者生机，不善用者杀机。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121817', '守正安分，远祸之道', '趋炎附势之祸，甚惨亦甚速；栖恬守逸之味，最淡亦最长。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121818', '与闲云为友，以风月为家', '松涧边携杖独行，立处云生破衲；竹窗下枕书高卧，觉时月侵寒毡。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121819', '存道心，消幻业', '色欲火炽，而一念及病时便兴似寒灰；名利饴甘，而一想到死地便味如嚼蜡。故人常忧死虑病，亦可消幻业而长道心。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121820', '退一步宽平一步', '争先的经路窄，退后一步自宽平一步；浓艳的滋味短，清淡一分自悠长一分。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121821', '修养定静工夫，临变方不动乱', '忙处不乱性，须闲处心神养得清；死时不动心，须生时事物看得破。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121822', '隐者无荣辱，道义无炎凉', '隐逸林中无荣辱，道义路上无炎凉。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121823', '去思苦亦乐，随心热亦凉', '热不必除，而除此热恼，身常在清凉台上；穷不可遣，而遣此穷愁，心常居安乐窝中。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121824', '居安思危，处进思退', '进步处便思退步，庶免触藩之祸；着手时先图放手，才脱骑虎之危。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121825', '贫得者虽富亦贫，知足者虽贫亦富', '贫得者分金恨不得玉，封公怨不受侯，权豪自甘乞丐；知足者藜羹旨于膏粱，布袍暖于狐貉，编民不让王公。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121826', '隐者高明，省事平安', '矜名不若逃名趣，练事何如省事闲。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121827', '超越喧寂，悠然自适', '嗜寂者，观白云幽石而通玄；趋荣者，见清歌妙舞而忘倦。唯自得之士，无喧寂无荣枯，无往非自适之天。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121828', '得道无牵系，静躁两无关', '孤云出岫，去留一无所系；朗镜悬空，静躁两不相干。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121829', '浓处味短，淡中趣长', '悠长之趣，不得于 酽，而得于啜菽饮水；惆恨之怀，不生于桔寂，而生于品竹调丝。故知浓处味常短，淡中趣独真也。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121830', '理出于易，道不在远', '神宗曰：“饥来吃饭倦来眠。”诗旨曰：“眼前景致口头语。”尽极高寓于极平，至难出于易；有意者反远，无心者自近也。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121831', '动静合宜，出入无碍', '水流而境无声，得处喧见寂之趣；山高而云不碍，悟出有入无之机。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121832', '执著是苦海，解脱是仙乡', '山林是胜地，一营恋变成市朝；书画是雅事，一贪痴便成商贾。盖心无染著，欲境是仙都；心有系恋，乐境成苦海矣。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121833', '躁极则昏，静极则明', '时当喧杂，则平日所记忆者，皆漫然忘去；境在清宁，则夙昔所遗忘者，又恍尔现前。可见静躁稍分，昏明顿异也。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121834', '卧云弄月，绝俗超尘', '芦花被下，卧雪眠云，保全得一窝夜气；竹叶杯中，吟风弄月，躲离了万丈红尘。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121835', '鄙俗不及风雅，淡泊反胜浓厚', '衮冕行中，著一藜杖的山人，便增一段高风；渔樵路上，著一衮衣的朝士，转添许多俗气。故知浓不胜淡，俗不如雅也。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121836', '出世在涉世，了心在尽心', '出世之道，即在涉世中，不必绝人以逃世；了心之功，即在尽心内，不必绝欲以灰心。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121837', '身放闲处，心在静中', '此身常放在闲处，荣辱得失谁能差遣我；此心常安在静中，是非利害谁能瞒眯我。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121838', '云中世界，静里乾坤', '竹篱下，忽闻犬吠鸡鸣，恍似云中世界；芸窗中，雅听蝉吟鸦噪，方知静里乾坤。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121839', '不希荣达，不畏权势', '我不希荣，何忧乎利禄之香饵？我不竞进，何畏乎仕宦之危机？', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121840', '圣境之下，调心养神', '徜徉于山林泉石之间，而尘心渐息；夷犹于诗书图画之内，而俗气潜消。故君子虽不玩物丧志，亦常借镜调心。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121841', '春之繁华，不若秋之清爽', '春日气象繁华，令人心神骀荡；不若秋日云白风清，兰芳桂馥，水天一色，上下空明，使人神骨俱清也。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121842', '得诗家真趣，悟禅教玄机', '一字不识而有诗意者，得诗家真趣；一偈不参而有禅味者，悟禅教玄机。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121843', '像由心生，像随心灭', '机动的，弓影疑为蛇蝎，寝石视为伏虎，此中浑是杀机；念息的，石虎可作海鸥，蛙声可当鼓吹，触处俱见真机。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121844', '来去自如，融通自在', '身如不系之舟，一任流行坎止；心似既灰之木，何妨刀割香涂。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121845', '忧喜取舍之情，皆是形气用事', '人情听莺啼则喜，闻蛙鸣则厌，见花则思培之，遇草则欲去之，但以形气用事；若以性天视之，何者非自鸣其天机，非自畅其生意也。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121846', '梦幻空华，真如之月', '发落齿疏，任幻形之凋谢；鸟吟花开，识自性之真如。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121847', '欲心生邪念，虚心生正念', '欲其中者，波沸寒潭，山林不见其寂；虚其中者，凉生酷暑，朝市不如其喧。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121848', '富者多忧，贵者多险', '多藏者厚亡，故知富不如贫之无虑；高步者疾颠，故知贵不如贱之常安。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121849', '读易松间，谈经竹下', '读易晓窗，丹砂研松间之露；谈经午案，宝馨宣竹下之风。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121850', '人为乏生趣，天机在自然', '花居盆内终乏生机，鸟落笼中便减天趣；不若山间花鸟错集成文，翱翔自若自是悠然会心。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121851', '烦恼由我起，嗜好自心生', '世人只缘认得我字太真，故多种种嗜好种种烦恼。前人云：“不复知有我，安知物为贵？”又云：“知身不是我，烦恼更何浸？”真破的之言也。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121852', '以失意之思，制得意之念', '自老视少，可以消奔驰角逐之心；自瘁视荣，可以绝纷华靡丽之念。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121853', '世态变化无极，万事必须达观', '人情世态，倏忽万端，不宜认得太真。尧夫云：“昔日所云我而今却是伊，不知今日我又属后来谁？”人常作如是观，便可解却胸中矣。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121854', '闹日取静，冷处热心', '热闹中着一冷眼，便省许多苦心思；冷落处存一热心，便得许多真趣味。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121855', '世间原无绝对，安乐只是寻常', '有一乐境界，就有一不乐的相对待；有一好光景，就有一不好的相乘除。只是寻常家饭素位风光，才是个安乐的窝巢。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121856', '接近自然风光，物我归于一如', '帘栊高敝，看青山绿水吞吐云烟，识乾坤自在；竹树扶疏，任乳燕鸣鸠送迎时序，知物我之两忘。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121857', '生死成败，一任自然', '知成之必败，则求成之心不必太坚；知生之必死，则保生之道不必过劳。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121858', '处世流水落花，身心皆得自在', '古德云：“竹影扫阶尘不动，月轮穿沼水无痕。”吾儒云：“水流任急境常静，花落虽频意自闲。”人常持此意，以应事接物，身心何等自在。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121859', '勘破乾坤妙趣，识见天地文章', '林间松韵，石上泉声，静里听来识天地自然鸣佩；草际烟光，水心云影，闲中观去见乾坤最上文章。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121860', '猛兽易服，人心难制', '眼看西晋之荆榛，犹矜白刃；身属北邙之狐兔，尚惜黄金。语云：“猛兽易伏，人心难降；谷壑易填、人心难满”。信哉！', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121861', '心地能平稳安静，触处皆青山绿水', '心地上无风涛，随在皆青山绿水；性天中有化育，触处见鱼跃鸢飞。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121862', '生活自适其性，贵人不若平民', '峨冠大带之士，一旦睹轻蓑小笠飘飘然逸也，未必不动其咨嗟；长筵广席之豪，一旦遇疏帘净几悠悠焉静也，未必不增其眷恋。人奈何驱以火牛诱以风马，而不思自适其性哉？', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121863', '处世忘世，超物乐天', '鱼得水逝而相忘乎水，鸟乘风飞而不知有风，识此可以超物景可以乐天机。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121864', '人生本无常，盛衰何可恃', '狐眠败砌，兔走荒台，尽是当年歌舞之地；露冷黄花，烟迷衰草，悉属旧时争战之场，盛衰何常？强弱安在？念此令人心灰！', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121865', '宠辱不惊，去留无意', '宠辱不惊，闲看庭前花开花落；去留无意，漫随天外云卷云舒。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121866', '苦海茫茫，回头是岸', '晴空朗月，何处不可翱翔？而飞蛾独投夜烛；清泉绿果，何物不可饮啄？而鸱鸮偏嗜腐鼠。噫！世之不为飞蛾鸱鸮者，几何人哉？', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121867', '求心内之佛，却心外之法', '才就筏便思舍筏，方是无事道人；若骑驴又复觅驴，终为不了禅师。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121868', '以冷情当事，如汤之消雪', '权贵龙骧，英雄虎战，以冷眼视之，如蚁聚膻，如蝇竞血；是非蜂起，得失猬兴，以冷情当之，如冶化金，如汤消雪。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121869', '彻见真性，自达圣境', '羁锁于物欲，觉吾生之可哀；夷犹于性真，觉吾生之可乐。知其可哀，则尘情立破；知其可乐，则圣境自臻。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121870', '心月开朗，水月无碍', '胸中即无半点物欲，已如雪消炉焰冰消日；眼前自有一段空明，时见月在青天影在波。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121871', '要以我转物，勿以物役我', '无风月花柳不成造化，无情嗜欲好不成心体。只以我转物，不以物役我，则欲嗜莫非天机，尘情即是理境矣。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121872', '就身了身，以物付物', '就一身了一身者，方能以万物付万物；还天于天下者，方能出世于世间。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121873', '不可徒劳身心，当乐风月之趣', '人生太闲则别念窃生，太忙则真性不见。故士君子不可不抱身心之忧，亦不可不耽风月之趣。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121874', '何处无妙境，何处无净土', '人心多从动处失真，若一念不生，澄然静坐；云兴而悠然共逝，雨滴而冷然具清；鸟啼而欣然有会，花落而潇然自得。何地无真境，何物无真机。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121875', '顺逆一视，欣戚两忘', '子生而母危，镪积而盗窥，何喜非忧也；贫可以节用，病可以保身，何忧非喜也，故达人当顺逆一视，而欣戚两忘。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121876', '风迹月影，过而不留', '耳根似飙谷投音，过而不留，则是非具谢；心境如月池浸色，空而不著，则物我两忘。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121877', '世间皆乐，苦自心生', '世人为荣利缠缚，动曰尘世苦海，不知云白山青，川行石立，花迎鸟笑，谷笑樵讴，世亦不尘、海亦不苦、彼自尘苦其心尔。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121878', '月盈则亏，履满者戒', '花看半开，酒饮微醉，此中大有佳趣。若至烂漫酕醄，便成恶境矣。履盈满者，宜思之。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121879', '体任自然，不染世法', '山肴不受世间灌溉，野禽不受世间豢养，其味皆香而且冽，吾人能不为世法所点染，其臭味不迥然别乎！', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121880', '观物须有自得，勿徒留连光景', '栽花种竹，玩鹤观鱼，亦要有段自得处，若徒留连光景，玩弄物华，亦吾儒之口耳，释代之顽空而已，有何佳趣？', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121881', '陷于不义，生不若死', '山林之士，清苦而逸趣自饶，农野之人，鄙略而天真浑具。若一失身市井驵侩，不若转死沟壑神骨犹清。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121882', '非分之收获，陷溺之根源', '非分之福，无故之获，非造物之钓饵，即人世之机阱。此处著眼不高，鲜不堕彼术中矣。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121883', '把握要点，卷舒自在', '人生原是一傀儡，只要根蒂在于一线不乱，卷舒自由行止在我，一毫不受他人提掇，便超出此场中矣！', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121884', '利害乃世之常，不若无事为福', '一事起则一害生，故天下常以无事为福，读前人诗云：“劝君莫话封侯事，一将功成万骨枯。”又云：“天下常令万事平，匣中不错千年死。”虽有雄心猛气，不觉化为冰霰矣。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121885', '茫茫世间，矛盾之窟', '淫奔之妇娇而为尼，热中之人激而入道，清净之门，常为淫邪之渊薮也如此。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121886', '身居局中，心在局外', '波浪兼天。舟中不知惧，而舟外者寒心；猖狂骂坐，席上不知警，而席外者咋舌。故君子身虽在事上，心要超事外也。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121887', '减繁增静，安乐之基', '人生减省一分便超脱了一分，如交游减便免纷扰，言语减便寡愆尤，思虑减则精神不耗，聪明减则混沌可完，彼不求日减而求日增者真桎梏此生哉！', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121888', '满腔和气，随地春风', '天运之寒暑易避，人世之炎凉难除；人世之炎凉易除，吾心之冰炭难去。去得此中之冰炭，则满腔皆和气，自随地有春风矣。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121889', '超越口耳之嗜欲，得见人生之真趣', '茶不求精而壶也不燥，酒不求冽而樽也不空；素琴无弦而常调，短笛无腔而自适；纵难超越羲皇，亦可匹俦嵇阮。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121890', '万事皆缘，随遇而安', '释氏随缘，吾儒素位，四字是渡海的浮囊。盖世路茫茫，一念求全则万绪纷起，随遇而安则无入不得矣。', '洪应明', '5', '3', 'H', null);
INSERT INTO `article` VALUES ('1271056071519121891', '论语', '史记世家曰：“孔子名丘，字仲尼。其先宋人。父叔梁纥，母颜氏。以鲁襄公二十二年，庚戌之岁，十一月庚子，生孔子于鲁昌平乡陬邑。为儿嬉戏，常陈俎豆，设礼容。及长，为委吏，料量平；为司职吏，畜蕃息。适周，问礼于老子，既反，而弟子益进。昭公二十五年甲申，孔子年三十五，而昭公奔齐，鲁乱。于是适齐，为高昭子家臣，以通乎景公。', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519121892', '论语', '公欲封以尼溪之田，晏婴不可，公惑之。孔子遂行，反乎鲁。定公元年壬辰，孔子年四十三，而季氏强僭，其臣阳虎作乱专政。故孔子不仕，而退修诗、书、礼、乐，弟子弥众。九年庚子，孔子年五十一。公山不狃以费畔季氏，召，孔子欲往，而卒不行。定公以孔子为中都宰，一年，四方则之，遂为司空，又为大司寇。十年辛丑，相定公会齐侯于夹谷，齐人归鲁侵地。十二年癸卯，使仲由为季氏宰，堕三都，收其甲兵。孟氏不肯堕成，围之不克。十四年乙巳，孔子年五十六，摄行相事，诛少正卯，与闻国政。三月，鲁国大治。齐人归女乐以沮之，季桓子受之。郊又不致膰俎于大夫，孔子行。适卫，主于子路妻兄颜浊邹家。适陈，过匡，匡人以为阳虎而拘之。既解，还卫，主蘧伯玉家，见南子。去适宋，司马桓魋欲杀之。又去，适陈，主司城贞子家。居三岁而反于卫，灵公不能用。晋赵氏家臣佛肸以中牟畔，召孔子，孔子欲往，亦不果。将西见赵简子，至河而反，又主蘧伯玉家。灵公问陈，不对而行，复如陈。季桓子卒，遗言谓康子必召孔子，其臣止之，康子乃召冉求。孔子如蔡及叶。楚昭王将以书社地封孔子，令尹子西不可，乃止。又反乎卫，时灵公已卒，卫君辄欲得孔子为政。而冉求为季氏将，与齐战有功，康子乃召孔子，而孔子归鲁，实哀公之十一年丁巳，而孔子年六十八矣。然鲁终不能用孔子，孔子亦不求仕，乃叙书传礼记。删诗正乐，序易彖、系、象、说卦、文言。弟子盖三千焉，身通六艺者七十二人。十四年庚申，鲁西狩获麟，孔子作春秋。明年辛酉，子路死于卫。十六年壬戌、四月己丑，孔子卒，年七十三，葬鲁城北泗上。弟子皆服心丧三年而去，惟子<a href=\"https://.99di/character/8d21.html\" target=\"_blank\">贡庐于冢上，凡六年，孔子生鲤，字伯鱼，先卒。伯鱼生急，字子思，作中庸。”<figure></figure><details></details><footer></footer>', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519121893', '论语', '何氏曰：“鲁论语二十篇。齐论语别有问王、知道，凡二十二篇，其二十篇中章句，颇多于鲁论。古论出孔氏壁中，分尧曰下章子张问以为一篇，有两子张，凡二十一篇，篇次不与齐鲁论同。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519121894', '论语', '程子曰：“论语之书，成于有子曾子之门人，故其书独二子以子称。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519121895', '论语', '程子曰：“读论<q>99lib?</q>语：有读了全然无事者；有读了后其中得一两句喜者；有读了后知好之者；有读了后直有不知手之舞之足之蹈之者。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519121896', '论语', '程子曰：“今人不会读书。如读论语，未读时是此等人，读了后又只是此等人，便是不曾读。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519121897', '论语', '程子曰：“颐自十七八读论语，当时已晓文义。读之愈久，但觉意味深长。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519121898', '论语', '读孟子法', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519121899', '论语', '程子曰：“学者当以论语孟子为本。论语孟子既治，则六经可不治而明矣。读书者当观圣人所以作经之意，与圣人所以用心，圣人之所以至于圣人，而吾之所以未至者，所以未得者。句句而求之，昼诵而味之，中夜而思之，平其心，易其气，阙其疑，则圣人之意可见矣。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519121900', '论语', '程子曰：“凡看文字，须先晓其文义，然后可以求其意。未有不晓文义而见意者也。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519121901', '论语', '程子曰：“学者须将论语中诸弟子问处便作自己问，圣人答处便作今日耳闻，自然有得。虽99lｉｂ?孔孟复生，不过以此教人。若能于语孟中深求玩味，将来涵养成甚生气质！”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519121902', '论语', '程子曰：“凡看语孟，且须熟读玩味。须将圣人言语切己，不可只作一场话说。人只看得二书切己，终身尽多也。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519121903', '论语', '程子曰：“论孟只剩读着，便自意足。学者须是玩味。若以语言解着，意便不足。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519121904', '论语', '或问：“且将论孟紧要处看，如何？”程子曰：“固是好，但终是不浃洽耳。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519121905', '论语', '程子曰：“孔子言语句句是自然，孟子言语句句是事实。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519121906', '论语', '程子曰：“学者先读论语孟子，如尺度权衡相似，以此>藏书网去量度事物，自然见得长短轻重。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519121907', '论语', '程子曰：“读论语孟子而不知道，所谓‘虽多，亦奚以为’。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519121908', '论语', '卷一 学而第一', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519121909', '论语', '子曰：“学而时习之，不亦说乎？有朋自远方来，不亦乐乎？人不知而不愠，不亦君子乎？”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519121910', '论语', '有子曰：“其为人也孝弟，而好犯上者，鲜矣；不好犯上，而好作乱者，未之有也。君子务本，本立而道生。孝弟也者，其为仁之本与！”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519121911', '论语', '子曰：“巧言令色，鲜矣仁！”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519121912', '论语', '曾子曰：“吾日三省吾身：为人谋而不忠乎？与朋友交而不信乎？传不习乎？”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519121913', '论语', '子曰：“道千乘之国：敬事而信，节用而爱人，使民以时。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519121914', '论语', '子曰：“弟子入则孝，出则弟，谨而信，泛爱众，而亲仁。行有余力，则以学文。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519121915', '论语', '子夏曰：“贤贤易色，事父母能竭其力，事君能致其身，与朋友交言而有信。虽曰未学，吾必谓之学矣。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519121916', '论语', '子曰：“君子不重则不威，学则不固。主忠信。无友不如己者。过则勿惮改。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519121917', '论语', '曾子曰：“慎终追 .\" >远，民德归厚矣。”>', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519121918', '论语', '子禽问于子贡曰：“夫子至于是邦也，必闻其政，求之与？抑与之与？”子贡曰：“夫子温、良、恭、俭、让以得之。夫子之求之也，其诸异乎人之求之与？”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519121919', '论语', '子曰：“父在，观其志；父没，观其行；三年无改于父之道，可谓孝矣。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519121920', '论语', '有子曰：“礼之用，和为贵。先王之道斯为美，小大由之。有所不行，知和而和，不以礼节之，亦不可行也。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519121921', '论语', '有子曰：“信近于义，言可复也； 606d.\" >恭近于礼，远耻辱也；因不失其亲，亦可宗也。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519121922', '论语', '子曰99ｌib?：“君子食无求饱，居无求安，敏于事而慎于言，就有道而正焉，可谓好学也已。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519121923', '论语', '子贡曰：“贫而无谄，富而无骄，何如？”子曰：“可也。未若贫而乐，富而好礼者也。”子贡曰：“诗云：‘如切如磋，如琢如磨。’其斯之谓与？”子曰：“赐也，始可与言诗已矣！告诸往而知来者。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519121924', '论语', '子曰：“不患人之不己知，患不知人也。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519121925', '论语', '卷一 为政第二', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519121926', '论语', '子曰：“为政以德，譬如北辰，居其所而众星共?之。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519121927', '论语', '子曰：“诗三百，一言以蔽之，曰‘思无邪’。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519121928', '论语', '子曰：“道之以政，齐之以刑，民免而无耻；道之以德，齐之以礼，有耻且格。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519121929', '论语', '子曰：“吾十有五而志于学，三十而立，四十而不惑，五十而知天命，六十而耳顺，七十而从心所欲，不踰矩。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519121930', '论语', '孟懿子问孝。子曰：“无违。”樊迟御，子告之曰：“孟孙问孝于我，我对曰‘无违’。”樊迟曰：“何谓也？”子曰：“生，事之以礼；死，葬之以礼，祭之以礼。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519121931', '论语', '孟武伯问孝。子曰：“父母唯其疾之忧。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519121932', '论语', '子游问孝。子曰：“今之孝者，是谓能养。至于犬马，皆能有养；不敬，何以别乎？”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519121933', '论语', '子夏问孝。子曰：“色难。有事弟子服其劳，有酒食先生馔，曾是以为孝乎？”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519121934', '论语', '子曰：“吾与回言终日，不违如愚。退而省其私，亦足以发。回也，不愚。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519121935', '论语', '子曰：“视其所以，观其所由，察其所安。人焉廋哉？人焉廋哉？”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519121936', '论语', '子曰：“温故而知新，可以为师矣。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519121937', '论语', '子曰：“君子不器。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519121938', '论语', '子贡问君子。子曰：“先行其言，而后从之。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519121939', '论语', '子曰：“君子周而不比，小人比而不周。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519121940', '论语', '子曰：“学而不思则罔，思而不学则殆。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519121941', '论语', '子曰：“攻乎异端，斯害也已！”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519121942', '论语', '子曰：“由！诲女知之乎？知之为知之，不知为不知，是知也。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519121943', '论语', '子张学干禄。子曰：“多闻阙疑，慎言其余，则寡尤；多见阙殆，慎行其 4f59.\" >余，则寡悔。言寡尤，行寡悔，禄在其中矣。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519121944', '论语', '哀公问曰：“何为则民服？”孔子对曰：“举直错诸枉，则民服；举枉错诸直，则民不服。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519121945', '论语', '季康子问：“使民敬、忠以劝，如之何？”子曰：“临之以庄则敬，孝慈则忠，举善而教不能，则劝。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519121946', '论语', '或谓孔子曰：“子奚不为政？”子曰：“书云：‘孝乎惟孝、友于兄弟，施于有政。’是亦为政，奚其为为政？”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519121947', '论语', '子曰：“人而无信，不知其可也。大车无輗，小车无軏，其何以行之哉？”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519121948', '论语', '子张问：“十世可知也？”子曰：“殷因于夏礼，所损益，可知也；周因于殷礼，所损益，可知也；其或继周者，虽百世可知也。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519121949', '论语', '子曰：“非其鬼而祭之，谄也。见义不为，无勇也。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519121950', '论语', '卷二 八佾第三', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519121951', '论语', '孔子谓季氏：“八佾舞于庭，是可忍也，孰不可忍也？”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519121952', '论语', '三家者以雍彻。子曰：“‘相维辟公，天子穆穆’，奚取于三家之堂？”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519121953', '论语', '子曰：“人而不仁，如礼何？人而不仁，如乐何？”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519121954', '论语', '林放问礼之本。子曰：“大哉问！礼，与其奢也，宁俭；丧，与其易也，宁戚。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519121955', '论语', '子曰：“夷狄之有君，不如诸夏之亡也。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519121956', '论语', '季氏旅于泰山。子谓冉有曰：“女弗能救与？”对曰：“不能。”子曰：“呜呼！曾谓泰山，不如林放乎？”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519121957', '论语', '子曰：“君子无所争，必也射乎！揖让而升，下而饮，其争也君子。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519121958', '论语', '子夏问曰：“‘巧笑倩兮，美目盼兮，素以为绚兮。’何谓也？”子曰：“绘事后素。”曰：“礼后乎？”子曰：“起予者商也！始可与言诗已矣。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519121959', '论语', '子曰：“夏礼，吾能言之，杞不足征也；殷礼，吾能言之，宋不足征也。文献不足故也，足则吾能征之矣。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519121960', '论语', '子曰：“禘自既灌而往者，吾不欲观之矣。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519121961', '论语', '或问禘之说。子曰：“不知也。知其说者之于天下也，其如示诸斯乎！”指其掌。 祭如在，祭神如神在。子曰：“吾不与祭，如不祭。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519121962', '论语', '王孙贾问曰：“与其媚于奥，宁媚于灶，何谓也？”子曰：“不然，获罪于天，无所祷也。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519121963', '论语', '子曰：“周监于二代，郁郁乎文哉！吾从周。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519121964', '论语', '子入大庙，每事问。或曰：“孰谓鄹人之子知礼乎？入大庙，每事问。”子闻之曰：“是礼也。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519121965', '论语', '子曰：“射不主皮，为力不同科，古之道也。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519121966', '论语', '子贡欲去告朔之饩羊。子曰：“赐也，尔爱其羊，我爱其礼。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519121967', '论语', '子曰：“事君尽礼，人以为谄也。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519121968', '论语', '定公问：“君使臣，臣事君，如之何？”孔子对曰：“君使臣以礼，臣事君以忠。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519121969', '论语', '子曰：“关雎，乐而不淫，哀而不伤。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519121970', '论语', '哀公问社于宰我。宰我对曰：“夏后氏以松，殷人以柏，周人以栗，曰使民战栗。”子闻之曰：“成事不说，遂事不谏，既往不咎。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519121971', '论语', '子曰：“管仲之器小哉！”或曰：“管仲俭乎？”曰：“管氏有三归，官事不摄，焉得俭？”“然则管仲知礼乎？”曰：“邦君树塞.门，管氏亦树塞门；邦君为两君之好，有反坫，管氏亦有反坫。管氏而知礼，孰不知礼？”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519121972', '论语', '子语鲁大师乐。曰：“乐其可知也：始作，翕如也；从之，纯如也，皦如也，绎如也，以成。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519121973', '论语', '仪封人请见。曰：“君子之至于斯也，吾未尝不得见也。”从者见之。出曰：“二三子，何患于丧乎？天下之无道也久矣，天将以夫子为木铎。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519121974', '论语', '子谓韶，尽美矣，又尽善也。”谓武，“尽美矣，未尽善也”。', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519121975', '论语', '子曰：“居上不宽，为礼不敬，临丧不哀，吾何以观之哉？”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519121976', '论语', '卷二 里仁第四', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519121977', '论语', '子曰：“里仁为美。择不处仁焉得知？”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519121978', '论语', '子曰：“不仁者不可以久处约，不可以长处乐。仁者安仁，知者利仁。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519121979', '论语', '子曰：“唯仁者能好人，能恶人。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519121980', '论语', '子曰：“苟志于仁矣，无恶也。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519121981', '论语', '子曰：“富与贵是人之所欲也，不以其道得之，不处也；贫与贱是人之所恶也，不以其道得之，不去也。君子去仁，恶乎成名？君子无终食之间违仁，造次必于是，颠沛必于是。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519121982', '论语', '子曰：“我未见好仁者，恶不仁者。好仁者，无以尚之；恶不仁者，其为仁矣，不使不仁者加乎其身。有能一日用其力于仁矣乎？我未见力不足者。盖有之矣，我未之见也。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519121983', '论语', '子曰：“人之过也，各于其党。观过，斯知仁矣。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519121984', '论语', '子曰：“朝闻道，夕死可矣。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519121985', '论语', '子曰：“士志于道，而耻恶衣恶食者，未足与议也。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519121986', '论语', '子曰：“君子之于天下也，无适也，无莫也，义之与比。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519121987', '论语', '子曰：“君子怀德，小人怀土；君子怀刑，小人怀惠。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519121988', '论语', '子曰：“放于利而行，多怨。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519121989', '论语', '子曰：“能以礼让为国乎？何有？不能以礼让为国，如礼何？”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519121990', '论语', '子曰：“不患无位，患所以立；不患莫己知，求为可知也。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519121991', '论语', '子曰：“参乎！吾道一以贯之。”曾子曰：“唯。”子出。门人问曰：“何谓也？”曾子曰：“夫子之道，忠恕而已矣。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519121992', '论语', '子曰：“君子喻于义，小人喻于利。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519121993', '论语', '子曰：“见贤思齐焉，见不贤而内自省也。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519121994', '论语', '子曰：“事父母几谏。见志不从，又敬不违，劳而不怨。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519121995', '论语', '子曰：“父母在，不远游。游必有方。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519121996', '论语', '子曰：“三年无改于父之道，可谓孝矣。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519121997', '论语', '子曰：“父母之年，不可不知也。一则以喜，一则以惧。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519121998', '论语', '子曰：“古者言之不出，耻躬之不逮也。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519121999', '论语', '子曰：“以约失之者，鲜矣。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122000', '论语', '子曰：“君子欲讷于言，而敏于行。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122001', '论语', '子曰：“德不孤，必有邻。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122002', '论语', '子游曰：“事君数，斯辱矣，朋友数，斯疏矣。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122003', '论语', '卷三 公冶长第五', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122004', '论语', '子谓公冶长，“可妻也。虽在缧绁之中，非其罪也”。以其子妻之。', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122005', '论语', '子谓南容，“邦有道，不废；邦无道，免于刑戮”。以其兄之子妻之。', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122006', '论语', '子谓子贱，“君子哉若人！鲁无君子者，斯焉取斯？”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122007', '论语', '子贡问曰：“赐也何如？”子曰：“女器也。”曰：“何器也？”曰：“瑚琏也。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122008', '论语', '或曰：“雍也，仁而不佞。”子曰：“焉用佞？御人以口给，屡憎于人。不知其仁，焉用佞？”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122009', '论语', '子使漆雕开仕。对曰：“吾斯之未能信。”子说。', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122010', '论语', '子曰：“道不行，乘桴浮于海。从我者其由与？”子路闻之喜。子曰：“由也好勇过我，无所取材。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122011', '论语', '孟武伯问：“子路仁乎？”子曰：“不知也。”又问。<samp>?99ｌib?</samp>子曰：“由也，千乘之国，可使治其赋也，不知其仁也。”“求也何如？”子曰：“求也，千室之邑，百乘之家，可使为之宰也，不知其仁也。”“赤也何如？”子曰：“赤也，束带立于朝，可使与宾客言也，不知其仁也。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122012', '论语', '子谓子贡曰：“女与回也孰愈？”对曰：“赐也何敢望回。回也闻一以知十，赐也闻一以知二。”子曰：“弗如也！吾与女弗如也。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122013', '论语', '宰予昼寝。子曰：“朽木不可雕也，粪土之墙不可杇也，于予与何诛。”子曰：“始吾于人也，听其言而信其行；今吾于人也，听其言而观其行。于予与改是。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122014', '论语', '子曰：“吾未见刚者。”或对曰：“申枨。”子曰：“枨也欲，焉得刚？”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122015', '论语', '子贡曰：“我不欲人之加诸我也，吾亦欲无加诸人。”子曰：“赐也，非尔所及也。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122016', '论语', '子贡曰：“夫子之文章，可得而闻也；夫子之言性与天道，不可得而闻也。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122017', '论语', '子路有闻，未之能行，唯恐<var>.9９lib?</var>有闻。', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122018', '论语', '子贡问曰：“孔文子何以谓之文也？”子曰：“敏而好学，不耻下问，是以谓之文也。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122019', '论语', '子谓子产，“有君子之道四焉：其行己也恭，其事上也敬，其养民也惠，其使民也义。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122020', '论语', '子曰：“晏平仲 5584.\" >善与人交，久而敬之。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122021', '论语', '子曰：“臧文仲居蔡，山节藻梲，何如其知也？”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122022', '论语', '子张问曰：“令尹子文三仕为令尹，无喜色；三已之，无愠色。旧令尹之政，必以告新令尹。何如？”子曰：“忠矣。”曰：“仁矣乎？”曰：“未知，焉得仁？”“崔子弒齐君，陈文子有马十乘，弃而违之。至于他邦，则曰：‘犹吾大夫崔子也。’违之。之一邦，则又曰：‘犹吾大夫崔子也。’违之。何如？”子曰：“清矣。”曰：“仁矣乎？”曰：“未知。焉得仁？”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122023', '论语', '季文子三思而后行。子闻之，曰：“再，斯可矣。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122024', '论语', '子曰：“宁武子邦有道则知，邦无道则愚。其知可及也，其愚不可及也。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122025', '论语', '子在陈曰：“归与！归与！吾党之小子狂简，斐然成章，不知所以裁之。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122026', '论语', '子曰：“伯夷、叔齐不念旧恶，怨是用希。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122027', '论语', '子曰：“孰谓微生高直？或乞醯焉，乞诸其邻而与之。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122028', '论语', '子曰：“巧言、令色、足恭，左丘明耻之，丘亦耻<bdo></bdo>之。匿怨而友其人，左丘明耻之，丘亦耻之。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122029', '论语', '颜渊、季路侍。子曰：“盍各言尔志？”子路曰：“愿车马、衣轻裘，与朋友共。敝之而无憾。”颜渊曰：“愿无伐善，无施劳。”子路曰：“愿闻子之志。”子曰：“老者安之，朋友信之，少者怀之。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122030', '论语', '子曰：“已矣乎！吾未见能见其过而内自讼者也。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122031', '论语', '子曰：“十室之邑，必有忠信如丘者焉，不如丘之好学也。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122032', '论语', '卷三 雍也第六', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122033', '论语', '子曰：“雍也可使南面。”仲弓问子桑伯子，子曰：“可也简。”仲弓曰：“居敬而行简，以临其民，不亦可乎？居简而行简，无乃大简乎？”子曰：“雍之言然。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122034', '论语', '哀公问：“弟子孰为好学？”孔子对曰：“有颜回者好学，不迁怒，不贰过。不幸短命死矣<s>?</s>！今也则亡，未闻好学者也。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122035', '论语', '子华使 4e8e.\" >于齐，冉子为其母请粟。子曰：“与之釜。”请益。曰：“与之庾。”冉子与之粟五秉。子曰：“赤之适齐也，乘肥马，衣轻裘。吾闻之也，君子周急不继富。”原思为之宰，与之粟九百，辞。子曰：“毋！以与尔邻里乡党乎！”?９9lib?', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122036', '论语', '子谓仲弓曰：“犁牛之子骍且角，虽欲勿用，山川其舍诸？”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122037', '论语', '子曰：“回也，其心三月不违仁，其余则日月至焉而已矣。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122038', '论语', '季康子问：“仲由可使从 653f.\" >政也与？”子曰：“由也果，于从政乎何有？”曰：“赐也，可使从政也与？”曰：“赐也达，于从政乎何有？”曰：“求也，可使从政也与？”曰：“求也艺，于从政乎何有？”<bdi>.</bdi>', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122039', '论语', '季氏使闵子骞为费宰。闵子骞曰：“善为我辞焉。如有复我者，则吾必在汶上矣。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122040', '论语', '伯牛有疾，子问之，自牖执其手，曰：“亡之，命矣夫！斯人也而有斯疾也！斯人也而有斯疾也！”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122041', '论语', '子曰：“贤哉，回也！一箪食，一瓢饮，在陋巷。人不堪其忧，回也不改其乐。贤哉，回也！”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122042', '论语', '冉求曰：“非不说子之道，力不足也。”子曰：“力不足者，中道而废。今女画。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122043', '论语', '子谓子夏曰：“女为君子儒，无为小人儒。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122044', '论语', '子游为武城宰。子曰：“女得人焉尔乎？”曰：“有澹台灭明者，行不由径。非公事，未尝至于偃之室也。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122045', '论语', '子曰：“孟之反不伐，奔而殿。将入门，策其马，曰：‘非敢后也，马不进也。’”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122046', '论语', '子曰：“不有祝鮀之佞而有宋朝之美，难乎免于今之世矣！”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122047', '论语', '子曰：“谁能出不由户？何莫由斯道也？”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122048', '论语', '子曰：“质胜文则野，文胜质则史。文质彬彬，然后君子。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122049', '论语', '子曰：“人之生也直，罔之生也幸而免。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122050', '论语', '子曰：“知之者不如好之者，好之者不如乐之者。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122051', '论语', '子曰：“中人以上，可以语上也；中人以下，不可以语上也。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122052', '论语', '樊迟问知。子曰：“务民之义，敬鬼神而远之，可谓知矣。”问仁。曰：“仁者先难而后获，可谓仁矣。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122053', '论语', '子曰：“知者乐水，仁者乐山；知者动，仁者静；知者乐，仁者寿。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122054', '论语', '子曰：“齐一变，至于鲁；鲁一变，至于道。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122055', '论语', '子曰：“觚不觚，觚哉！觚哉！”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122056', '论语', '宰我问曰：“仁者，虽告之曰：‘井有仁焉。’其从之也？”子曰：“何为其然也？君子可逝也，不可陷也；可欺也，不可罔也。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122057', '论语', '子曰：“君子博学于文，约之以礼，亦可以弗畔矣夫！”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122058', '论语', '子见南子，子路不说。夫子矢之<q></q>曰：“予所否者，天厌之！天厌之！”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122059', '论语', '子曰：“中庸之为德也，其至矣乎！民鲜久矣。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122060', '论语', '子贡曰：“如有博施于民而能济众，何如？可谓仁乎？”子曰：“何事于仁，必也>圣乎！尧舜其犹病诸！夫仁者，己欲立而立人，己欲达而达人。能近取譬，可谓仁之方也已。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122061', '论语', '卷四 述而第七', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122062', '论语', '子曰：“述而不作，信而好古，窃比于我老彭。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122063', '论语', '子曰：“默而识之，学而不厌，诲人不倦，何有于我哉？”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122064', '论语', '子曰：“德之不修，学之不讲，闻义不能徙，不善不能改，是吾忧也。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122065', '论语', '子之燕居，申申如也，夭夭如也。', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122066', '论语', '子曰：“甚矣吾衰也！久矣吾不复梦见周公。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122067', '论语', '子曰：“志于道，据于德，依于仁，游 4e8e.\" >于艺。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122068', '论语', '子曰：“自行束修以上，吾未尝无诲焉。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122069', '论语', '子曰：“不愤不启，不悱不发，举一隅不以三隅反，则不复也。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122070', '论语', '子食于有丧者之侧，未尝饱也。于是日哭，则不歌。', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122071', '论语', '子谓颜渊曰：“用之则行，舍之则藏，唯我与尔有是夫！”子路曰：“子行三军，则谁与？”子曰：“暴虎冯河，死而无悔者，吾不与也。必也临事而惧，好谋而成者也。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122072', '论语', '子曰：“富而可求也，虽执鞭之士，吾亦为之。如不可99ｌｉｂ.求，从吾所好。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122073', '论语', '子之所慎：齐，战，疾。', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122074', '论语', '子在齐闻韶，三月不知肉味。曰：“不图为乐之至于斯也！”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122075', '论语', '冉有曰：“夫子为卫君乎？”子贡曰：“诺。吾将问之。”入，曰：“伯夷、叔齐何人也？”曰：“古之贤人也。”曰：“怨乎？”曰：“求仁而<big></big>得仁，又何怨。”出，曰：“夫子不为也。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122076', '论语', '子曰：“饭疏食饮水，曲肱而枕之，<q></q>乐亦在其中矣。不义而富且贵，于我如浮云。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122077', '论语', '子曰：“加我数年，五十以学易，可以无大过矣。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122078', '论语', '子所雅言，诗、书、执礼，皆雅言也。', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122079', '论语', '叶公问孔子于子路，子路不对。子曰：“女奚不曰，其为人也，发愤忘食，乐以忘忧，不知老之将至云尔。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122080', '论语', '子曰：“我非生而知之者，好古，敏以求之者也。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122081', '论语', '子不语怪，力，乱，神。', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122082', '论语', '子曰：“三人行，必有我师焉。择其善者而从之，其不善者而改之。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122083', '论语', '子曰：“天生德于予，桓魋其如予何？”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122084', '论语', '子曰：“二三子以我为隐乎？吾无隐乎尔。吾无行而不与二三子者，是丘也。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122085', '论语', '子以四教：文，行，忠，信。', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122086', '论语', '子曰：“圣人，吾不得而见之矣；得见君子者，斯可矣。”子曰：“ 善人，吾不得而见之矣；得见有恒者，斯可矣。亡而为有，虚而为盈，约而为泰，难乎有恒矣。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122087', '论语', '子钓而不纲，弋不射宿。', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122088', '论语', '子曰：“盖有不知而作之者，我无是也。多闻择其善者而从之，多见而识之，知之次也。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122089', '论语', '互乡难与言，童子见，门人惑。子曰：“与其进也，不与其退也，唯何甚！人洁己以进，与其洁也，不保其往也。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122090', '论语', '子曰：“仁远乎哉？我欲仁，斯仁至矣。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122091', '论语', '陈司败问昭公知礼乎？孔子曰：“知礼。”孔子退，揖巫马期而进之，曰：“吾闻君子不党，君子亦党乎？君取于吴为同姓，谓之吴孟子。君而知礼，孰不知礼？”巫马期以告。', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122092', '论语', '子曰：“丘也幸，苟有过，人必知之。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122093', '论语', '子与人歌而善，必使反之，而后和之。', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122094', '论语', '子曰：“文，莫吾犹人也。躬行君子，则吾未之有得。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122095', '论语', '子曰：“若圣与仁，则吾岂敢？抑为之不厌，诲人不倦，则可谓云尔已矣。”公西华曰：“正唯弟子不能学也。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122096', '论语', '子疾病，子路请祷。子曰：“有诸？”子路对曰：“有之。诔曰：‘ 祷尔于上下神只。’”子曰：“丘之祷久矣。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122097', '论语', '子曰：“奢则不孙，俭则固。与其不孙也，宁固。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122098', '论语', '子曰：“君子坦荡荡，小人长戚戚。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122099', '论语', '子温而厉，威而不猛，恭而安。', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122100', '论语', '卷四 泰伯第八', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122101', '论语', '子曰：“泰<q></q>伯，其可谓至德也已矣！三以天下让，民无得而称焉。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122102', '论语', '子曰：“恭而无礼则劳，慎而无礼则葸，勇而无礼则乱，直而无礼则绞。君子笃于亲，则民兴于仁；故旧不遗，则民不偷。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122103', '论语', '曾子有疾，召门弟子曰：“启予足！启予手！诗云‘战战兢兢，如临深渊，如履薄冰。’而今而后，吾知免夫！小子！”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122104', '论语', '曾子有疾，孟敬子问之。曾子言曰：“鸟之将死，其鸣也哀；人之将死，其言也善。君子所贵乎道者三：动容貌，斯远暴慢矣；正颜色，斯近信矣；出辞气，斯远鄙倍矣。笾豆之事，则有司存。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122105', '论语', '曾子曰：“以能问于不能，以多问于寡；有若无，实若虚，犯而不校， 6614.\" >昔者吾友尝从事于斯矣。”藏书网', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122106', '论语', '曾子曰：“可以托六尺之孤，可以寄百里之命，临大节而不可夺也。君子人与？君子人也。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122107', '论语', '曾子曰：“士不可以不弘毅，任重而道远。仁以为己任，不亦重乎？死而后已，不亦远乎？”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122108', '论语', '子曰：“兴于诗，立于礼。成于乐。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122109', '论语', '子曰：“民可使由之，不可使知之。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122110', '论语', '子曰：“好勇疾贫，乱也。人而不仁，疾之已甚，乱也。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122111', '论语', '子曰：“如有周公之才之美，使骄且吝，其余不足观也已。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122112', '论语', '子曰：“三年学，不至于谷，不易得也。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122113', '论语', '子曰：“笃信好学，守死善道。危邦不入，乱邦不居。天下有道则见，无道则隐。邦有道，贫且贱焉，耻也；邦无道，富且贵焉，耻也。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122114', '论语', '子曰：“不在其位，不谋其政。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122115', '论语', '子曰：“师挚之始，关雎之乱，洋洋乎！盈耳哉。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122116', '论语', '子曰：“狂而不直，侗而不愿，悾悾而不信，吾不知之矣。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122117', '论语', '子曰：“学如不及，犹恐失之。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122118', '论语', '子曰：“巍巍乎！舜禹之有天下也，而不与焉。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122119', '论语', '子曰：“大哉，尧之为君也！巍巍乎！唯天为大，唯尧则之。荡荡乎！民无能名焉。巍巍乎！其有成功也；焕乎，其有文章！”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122120', '论语', '舜有臣五人而天下治。武王曰：“予有乱臣十人。”孔子曰：“才难，不其然乎？唐虞之际，于<cite>藏书网</cite>斯为盛。有妇人焉，九人而已。三分天下有其二，以服事殷。周之德，其可谓至德也已矣。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122121', '论语', '子曰：“禹，吾无间然矣。菲饮食，而致孝乎鬼神；恶衣服，而致美乎黻冕；卑宫室，而尽力乎沟洫。禹，吾无间然矣。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122122', '论语', '卷五 子罕第九', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122123', '论语', '子罕言利，与命，与仁。', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122124', '论语', '达巷党人曰：“大哉孔子！博学而无所成名。”子闻之，谓门弟子曰：“吾何执？执御乎？执射乎？吾执御矣。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122125', '论语', '子曰：“麻冕，礼也；今也纯，俭。吾从众。拜下，礼也；今拜乎上，泰也。虽违众，吾从下。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122126', '论语', '子绝四：毋意，毋必，毋固，毋我。', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122127', '论语', '子畏于匡。曰：“文王既没，文不在兹乎？天之将丧斯文也，后死者不得与于斯文也；天之未丧斯文也，匡人其如予何？”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122128', '论语', '大宰问于子贡曰：“夫子圣者与？何其多能也？”子贡曰：“固天纵之将圣，又多能也。”子闻之，曰：“大宰知我乎！吾少也贱，故多能鄙事。君子多乎哉？不多也。”牢', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122129', '论语', '曰：“子云，‘吾不试，故艺’。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122130', '论语', '子曰：“吾有知乎哉？无知也。有鄙夫问于我，空空如也，我叩其两端而竭焉。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122131', '论语', '子曰：“凤鸟不至，河不.９9liｂ?出图，吾已矣夫！”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122132', '论语', '子见齐衰者、冕衣裳者与 77bd.\" >瞽者，见之，虽少必作；过之，必趋。', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122133', '论语', '颜渊喟然叹曰：“仰之弥高，钻之弥坚；瞻之在前，忽焉在后。夫子循循然善<bdi>.９９ｌｉｂ.</bdi>诱人，博我以文，约我以礼。欲罢不能，既竭吾才，如有所立卓尔。虽欲从之，末由也已。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122134', '论语', '子疾病，子路使门人为臣。病闲，曰：“久矣哉！由之行诈也，无臣而为有臣。吾谁欺？欺天乎？且予与其死于臣之手也，无宁死于二三子之手乎？且予纵不得大葬，予死于道路乎？”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122135', '论语', '子贡曰：“有美玉于斯，韫匮而藏诸？求善贾而沽诸？”子曰：“沽之哉！沽之哉！我待贾者也。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122136', '论语', '子欲居九夷。或曰：“陋，如之何！”子曰：“君子居之，何陋之有？”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122137', '论语', '子曰：“吾自卫反鲁，然后乐正，雅颂各得其所。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122138', '论语', '子曰：“出则事公卿，入则事父兄，丧事不敢不勉，不为酒困，何有于我哉？”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122139', '论语', '子在川上，曰：“逝者如斯夫！不舍昼夜。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122140', '论语', '子曰：“吾未见好德如好色者也。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122141', '论语', '子曰：“譬如为山，未成一篑，止，吾止也；譬如平地，虽覆一篑，进，吾往也。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122142', '论语', '子曰：“语之而不惰者，其回也与！”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122143', '论语', '子谓颜渊，曰：“惜乎！吾见其进也，未见其止也。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122144', '论语', '子曰；“苗而不秀者有矣夫！秀而不实者有矣夫！”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122145', '论语', '子曰：“后生可畏，焉知来者之不如今也？四十、五十而无闻焉，斯亦不足畏也已。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122146', '论语', '子曰：“法语之言，能无从乎？改之为贵。巽与之言，能无说乎？绎之为贵。说而不绎，从而不改，吾末如之何也已矣。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122147', '论语', '子曰：“主忠信，毋友不如己者，过则勿惮改。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122148', '论语', '子曰：“三军可夺帅也，匹夫不可夺志也。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122149', '论语', '子曰：“衣敝缊袍，与衣狐貉者立，而不耻者，其由也与？‘不忮不求，何用不臧？’”子路终身诵之。子曰：“是道也，何足以臧？”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122150', '论语', '子曰：“岁寒，然后知松柏之后雕也。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122151', '论语', '子曰：“知者不惑，仁者不忧，勇者不惧。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122152', '论语', '子曰：“可与共学，未可与适道；可与适道，未可与立；可与立，未可与权。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122153', '论语', '“唐棣之华，偏其反而。岂不尔思？室是远而。”子曰：“未之思也，夫何远之有？”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122154', '论语', '卷五 乡党第十', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122155', '论语', '孔子于乡党，恂恂如也，似不能言者。其在宗庙朝廷，便便言，唯谨尔。', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122156', '论语', '朝，与下大夫言，侃侃如也；与上大夫言，誾誾如也。君在，踧踖如也。与与如也。', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122157', '论语', '君召使摈，色勃如也，足躩如也。揖所与立，左右手。衣前后，襜如也。趋进，翼如也。宾退，必复命曰：“宾不顾矣。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122158', '论语', '入公门，鞠躬如也，如不容。立不中门，行不履阈。过位，色勃如也，足躩如也，其言似不足者。摄齐升堂，鞠躬如也，屏气似不息者。出，降一等，逞颜色，怡怡如也。没阶趋，翼如藏书网也。复其位，踧踖如也。', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122159', '论语', '执圭，鞠躬如也，如不胜。上如揖，下如授。勃如战色，足缩缩，如有循。享礼，有容色。私觌，愉愉如也。', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122160', '论语', '君子不以绀緅饰。红紫不以为亵服。当暑，袗絺绤，必表而出之。缁衣羔裘，素衣麑裘，黄衣狐裘。亵裘长。短右袂。狐貉之厚以居。去丧，无所不佩。非帷裳，必杀之。羔裘玄冠不以吊。吉月，必朝服而朝。', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122161', '论语', '齐，必有明衣，布。必有寝衣，长一身有半。齐，必变食，居必迁坐。', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122162', '论语', '食不厌精，脍不厌细。食饐而餲，鱼馁而肉败，不<big></big>食。色恶，不食。臭恶，不食。失饪，不食。不时，不食。割不正，不食。不得其酱，不食。肉虽多，不使胜食气。惟酒无量，不及乱。沽酒市脯不食。不撤姜食。不多食。祭于公，不宿肉。祭肉不出三日。出三日，不食之矣。食不语，寝不言。虽疏食菜羹，瓜祭，必齐如也。', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122163', '论语', '席不正，不坐。', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122164', '论语', '乡人饮酒，杖者出，斯bbr>.</abbr>出矣。乡人傩，朝服而立于阼阶。', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122165', '论语', '问人于他邦，再拜而送之。康子馈药，拜而受之。曰：“丘未达，不敢尝。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122166', '论语', '厩焚。子退朝，曰：“伤人乎？”不问马。', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122167', '论语', '君赐食，必正席先尝之；君赐腥，必熟而荐之；君赐生，必畜之。侍食于君，君祭，先饭。疾，君视之，东首，加朝服，拖绅。君命召，不俟驾行矣。入太庙，每事问。', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122168', '论语', '朋友死，无所归。曰：“于我殡。”朋友之馈，虽车马，非祭肉，不拜。', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122169', '论语', '寝不尸，居不容。见齐衰者，虽狎，必变。见冕者与瞽者，虽亵，必以貌。凶服者式之。式负版者。有盛馔，必变色而作。迅雷风烈，必变。', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122170', '论语', '升车，必正立执绥。车中，不内顾，不疾言，不亲指。', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122171', '论语', '色斯举矣，翔而后集。曰：“山梁雌雉，时哉！时哉！”子路共之，三嗅而作。', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122172', '论语', '卷六 先进第十一', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122173', '论语', '子曰：“先进于礼乐，野人也；后进于礼乐，君子也。如用之，则吾从先进。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122174', '论语', '子曰：“从我于陈、蔡者，皆不及门也。”德行：颜渊，闵子骞，冉伯牛，仲弓。言语：宰我，子贡。政事：冉有，季路。文学：子游，子夏。', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122175', '论语', '子曰：“回也非助我者也，于吾言无所不说。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122176', '论语', '子曰：“孝哉闵子骞！人不间于其父母昆弟之言。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122177', '论语', '南容三复白圭，孔子以其兄之子妻之。', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122178', '论语', '季康子问：“弟子孰为好学？”孔子对曰：“有颜回者好学，不幸短命死矣！今也则亡。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122179', '论语', '颜渊死，颜路请子之车以为之椁。子曰：“才不才，亦各言其子也。鲤也死，有棺而无椁。吾不徒行以为之椁。以吾从大夫之后，不可徒行也。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122180', '论语', '颜渊死。子曰：“噫！天丧予！天丧予！”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122181', '论语', '颜渊死，子哭之恸。从者曰：“子恸矣。”曰：“有恸乎？非夫人之为恸而谁为！”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122182', '论语', '颜渊死，门人欲厚葬之，子曰：“不可。”门人厚葬之。子曰：“回bbr>9９lｉｂ?</abbr>也视予犹父也，予不得视犹子也。非我也，夫二三子也。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122183', '论语', '季路问事鬼神。子曰：“未能事人，焉能事鬼？”敢问死。曰：“未知生，焉知死？”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122184', '论语', '闵子侍侧，誾誾如也；子路，行行如也；冉有、子贡，侃侃如也。子乐。“若由也，不得其死然。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122185', '论语', '鲁人为长府。闵子骞曰：“仍旧贯，如之何？何必改作？”子曰：“    夫人不言，言必有中。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122186', '论语', '子曰：“由之瑟奚为于丘之门？”门人不敬子路。子曰：“由也升堂矣，未入于室也。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122187', '论语', '子贡问：“师与商也孰贤？”子曰：“师也过，商也不及。”曰：“ 然则师愈与？”子曰：“过犹不及。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122188', '论语', '季氏富于周公，而求也为之聚敛而附益之。子曰：“非吾徒也。小子鸣鼓而攻之，可也。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122189', '论语', '柴也愚，参也鲁，师也辟，由也喭。', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122190', '论语', '子bbr></abbr>曰：“回也其庶乎，屡空。赐不受命，而货殖焉，亿则屡中。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122191', '论语', '子张问善人之道。子曰：“不践迹，亦不入于室。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122192', '论语', '子曰：“论笃是与，君子者乎？色庄者乎？”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122193', '论语', '子路问：“闻斯行诸？”子曰：“有父兄在，如之何其闻斯行之？”冉有问：“闻斯行诸？”子曰：“闻斯行之。”公西华曰：“由也问闻斯行诸，子曰‘有父兄在’；求也问闻斯行诸，子曰‘闻斯行之’。赤也惑，敢问。”子曰：“求也退，故进之；由也兼人，故退之。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122194', '论语', '子畏于匡，颜渊后。子曰：“吾以女为死矣。”曰：“子在，回何敢死？”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122195', '论语', '季子然问：“仲由、冉求可谓大臣与？”子曰：“吾以子为异之问，曾由与求之问。所谓大臣者：以道事君，不可则止。今由与求也，可谓具臣矣。”曰：“然则从之者与？”子曰：“弒父与君，亦不从也。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122196', '论语', '子路使子羔为费宰。子曰：“贼夫人之子。”子路曰：“有民人焉，有社稷焉。何必读书，然后为学？”子曰：“是故恶夫佞者。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122197', '论语', '子路、曾皙、冉有、公西华侍坐。子曰：“以吾一日长乎尔，毋吾以也。居则曰：“不吾知也！’如或知尔，则何以哉？”子路率尔而对曰：“千乘之国，摄乎大国之间，加之以师旅，因之以饥馑；由也为之，比及三年，可使有勇，且知方也。”夫子哂之。“求！尔何如？”对曰：“方六七', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122198', '论语', '十，如五六十，求也为之，比及三年，可使足民。如其礼乐，以俟君子。”“赤！尔何如？”对曰：“非曰能之，愿学焉。宗庙之事，如会同，端章甫，愿为小相焉。”“点！尔何如？”鼓瑟希，铿尔，舍瑟而作。对曰：“异乎三子者之撰。”子曰：“何伤乎？亦各言其志也。”曰：“莫春者，春服既成。冠者五六人，童子六七人，浴乎沂，风乎舞雩，咏而归。”夫子喟然叹曰：“吾与点也！”三子者出，曾皙后。曾皙曰：“夫三子者之言何如？”子曰：“亦各言其志也已矣。”曰：“夫子何哂由也？”曰：“为国以礼，其言不让，是故哂之。”“唯求<cite></cite>则非邦也与？”“安见方六七十如五六十而非邦也者？”“唯赤则非邦也与？”“宗庙会同，非诸侯而何？赤也为之小，孰能为之大？”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122199', '论语', '卷六 颜渊第十二', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122200', '论语', '颜渊问仁。子曰：“克己复礼为仁。一日克己复礼，天下归仁焉。为仁由己，而由人乎哉？”颜渊曰：“请问其<var>.９9ｌｉｂ.</var>目。”子曰：“非礼勿视，非礼勿听，非礼勿言，非礼勿动。”颜渊曰：“回虽不敏，请事斯语矣。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122201', '论语', '仲弓问仁。子曰：“出门如见大宾，使民如承大祭。己所不欲，勿施于人。在邦无怨，在家无怨。”仲弓曰：“雍虽不敏，请事斯语矣。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122202', '论语', '司马牛问仁。子曰：“仁者其言也讱。”曰：“其言也讱，斯谓之仁已乎？”子曰：“为之难，言之得无讱乎？”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122203', '论语', '司马牛问君子。子曰：“君子不忧不惧。”曰：“不忧不惧，斯谓之君子已乎？”子曰：“内省不疚，夫何忧何惧？”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122204', '论语', '司马牛忧曰：“人皆有兄弟，我独亡。”子夏曰：“商闻之矣：死生有命，富贵在天。君子敬而无失，与人恭而有礼。四海之内，皆兄弟也。君子何患乎无兄弟也？”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122205', '论语', '子张问明。子曰：“浸润之谮，肤受之愬，不行焉。可谓明也已矣。浸润之谮肤受之愬不行焉，可谓远也已矣。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122206', '论语', '子贡问政。子曰：“足食。足兵。民信之矣。”子贡曰：“必不得已而去，于斯三者何先？”曰：“去兵。”子贡曰：“必不得已而去，于斯二者何先？”曰：“去食。自古皆有死，民无信不立。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122207', '论语', '棘子成曰：“君子质而已矣，何以文为？”子贡曰：“惜乎！夫子之说，君子也。驷不及舌。文犹质也，质犹文也。虎豹之(革享)，犹犬', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122208', '论语', '羊之(革享)。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122209', '论语', '哀公问于有若曰：“<cite>藏书网</cite>年饥，用不足，如之何？”有若对曰：“盍彻乎？”曰：“二，吾犹不足，如之何其彻也？”对曰：“百姓足，君孰与不足？百姓不足，君孰与足？”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122210', '论语', '子张问崇德、辨惑。子曰：“主忠信，徙义，崇德也。爱之欲其生，恶之欲其死。既欲其生，又欲其死，是惑也。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122211', '论语', '齐景公问政于孔子。孔子对曰：“君君，臣臣，父父，子子。”公曰：“善哉！信如君不君，臣不臣，父不父，子不子，虽有粟，吾得而食诸？”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122212', '论语', '子曰：“片言可以折狱者，其由也与？”子路无宿诺。', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122213', '论语', '子曰：“听讼，吾犹人也，必也使无讼乎！”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122214', '论语', '子张问政。子曰：“居之无倦，行之以忠。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122216', '论语', '子曰：“君子成人之美，不成人之恶。小人反是。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122217', '论语', '季康子问政于孔子。孔子对曰：“政者，正也。子帅以正，孰敢不正？”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122218', '论语', '季康子患盗，问于孔子。孔子对曰：“苟子之不欲，虽赏之不窃。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122219', '论语', '季康子问政于孔子曰：“如杀无道，以就有道，何如？”孔子对曰：“子为政，焉用杀？子欲善，而民善矣。君子之德风，小人之德草。草上之风，必偃。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122220', '论语', '子张问：“士何如斯可谓之达矣？”子曰：“何哉，尔所谓达者？”子张对曰：“在邦必闻，在家必闻。”子曰：“是闻也，非达也。夫达也者，质直而好义，察言而观色，虑以下人。在邦必达，在家必达。夫闻也者，色取仁而行违，居之不疑。在邦必闻，在家必闻。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122221', '论语', '樊迟从游于舞雩之下，曰：“敢问崇德、修慝、辨惑。”子曰：“善哉问！先事后得，非崇德与？攻其恶，无攻人之恶，非修慝与？一朝之忿，忘其身，以', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122222', '论语', '及其亲，非惑与？”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122223', '论语', '樊迟问仁。子曰：“爱人。”问知。子曰：“知人。”樊迟未达。子曰：“举直错诸枉，能使枉者直。”樊迟退，见子夏。曰：“乡也吾见于夫子而问知，子曰，‘举直错诸枉，能使枉者直’，何谓也？”子夏曰：“富哉言乎！舜有天下，选于众，举皋陶，不仁者远矣。汤有天下，选于众，举伊尹，不仁者远矣。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122224', '论语', '子贡问友。子曰：“忠告而善道之，不可则止，无自辱焉。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122225', '论语', '曾子曰：“君子以文会友，以友辅仁。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122226', '论语', '卷七 子路第十三', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122227', '论语', '子路问政。子曰：“先之，劳之。”请益。曰：“无倦。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122228', '论语', '仲弓为季氏宰.，问政。子曰：“先有司，赦小过，举贤才。”曰：“ 焉知贤才而举之？”曰：“举尔所知。尔所不知，人其舍诸？”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122229', '论语', '子路曰：“卫君待子而为政，子将奚先？”子曰：“必也正名乎！”子路曰：“有是哉，子之迂也！奚其正？”子曰：“野哉由也！君子于其所不知，盖阙如也。名不正，则言不顺；言不顺，则事不成；事不成，则礼乐不兴；礼乐不兴，则刑罚不中；刑罚不中，则民无所措手足。故君子名之必可言也，言之必可行也。君子于其言，无所苟而已矣。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122230', '论语', '樊迟请学稼，子曰：“吾不如老农。”请学为圃。曰：“吾不如老圃。”樊迟出。子曰：“小人哉，樊须也！上好礼，则民莫敢不敬；上好义，则民莫敢不服；上好信，则民莫敢不用情。夫如是，则四方之民襁负其子而至矣，焉用稼？”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122231', '论语', '子曰：“诵诗三百，授之以政，不达；使于四方，不能专对；虽多，亦奚以为？”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122232', '论语', '子曰：“其身正，不令而行；其不正，虽令不从。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122233', '论语', '子曰：“鲁卫之政，兄弟也。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122234', '论语', '子谓卫公子荆，“善居室。始有，曰：‘苟合矣。’少有，曰：‘苟完矣。’富有，曰：‘苟美矣。’”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122235', '论语', '子适卫，冉有仆。子曰：“庶矣哉！”冉有曰：“既庶矣。又何加焉？”曰：“富之。”曰：“既富矣，又何加焉？”曰：“教之。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122236', '论语', '子曰：“苟有用我者。期月而已可也，三年有成。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122237', '论语', '子曰：“善人为邦百年，亦可以胜残去杀矣。诚哉是言也！”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122238', '论语', '子曰：“如有王者，必世而后仁。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122239', '论语', '子曰：“苟正其身矣，于从政乎何有？不能正其身，如正人何？”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122240', '论语', '冉子退朝。子曰：“何晏也？”对曰：“有政。”子曰：“其事也。如有政，虽不吾以，吾其与闻之。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122241', '论语', '定公问：“一言而可以兴邦，有诸？”孔子对曰：“言不可以若是其几也。人之言曰：‘为君难，为臣不易。’如知为君之难也，不几乎一言而兴邦乎？”曰：“一言而丧邦，有诸？”孔子对曰：“言不可以若是其几也。人之言曰：‘予无乐乎为君，唯<bdi></bdi>其言而莫予违也。’如其善而莫之违也，不亦善乎？如不善而莫之违也，不几乎一言而丧邦乎？”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122242', '论语', '叶公问政。子曰：“近者说，远者来。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122243', '论语', '子夏为莒父宰，问政。子曰：“无欲速，无见小利。欲速，则不达；见小利，则大事不成。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122244', '论语', '叶公语孔子曰：“吾党有直躬者，其父攘羊，而子证之。”孔子曰：“吾党之直者异于是。父为子隐，子为父隐，直在其中矣。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122245', '论语', '樊迟问仁。子曰：“居处恭，执事敬，与人忠。虽之夷狄，不可弃也。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122246', '论语', '子贡问曰：“何如斯可谓之士矣？”子曰：“行己有耻，使于四方，不辱君命，可谓士矣。”曰：“敢问其次。”曰：“宗族称孝焉，乡党称弟焉。”曰：“敢问其次。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122247', '论语', '曰：“言必信，行必果，硁硁然小人哉！抑亦可以为次矣。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122248', '论语', '曰：“今之从政者何如？”子曰：“噫！', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122249', '论语', '斗筲之人，何足算也。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122250', '论语', '子曰：“不得中行而与之，必也狂狷乎！狂者进取，狷者有所不为也。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122251', '论语', '子曰：“南人有言曰：‘人而无恒，不可以作巫医。’善夫！”“不恒其德，或承之羞。”子曰：“不占而已矣。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122252', '论语', '子曰：“君子和而不同，小人同而不和。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122253', '论语', '子贡问曰：“乡人皆好之，何如？”子曰：“未可也。”“乡人皆恶之，何如？”子曰：“未可也。不如乡人之善者好之，其不善者恶之。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122254', '论语', '子曰：“君子易事而难说也：说之不以道，不说也；及其使人也，器之。小人难事而易说也：说之虽不以道，说也；及其使人也，求备焉。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122255', '论语', '子曰：“君子泰而不骄，小人骄而不泰。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122256', '论语', '子曰：“刚毅、木讷，近仁。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122257', '论语', '子路问曰：“何如斯可谓之士矣？”子曰：“切切、偲偲、怡怡如也，可谓士矣。朋友切切、偲偲，兄弟怡怡。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122258', '论语', '子曰：“善人教民七年，亦可以即戎矣。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122259', '论语', '子曰：“以不教民战，是谓弃之。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122260', '论语', '卷七 宪问第十四', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122261', '论语', '宪问耻。子曰：“邦有道，谷；邦无道，谷，耻也。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122262', '论语', '“克、伐、怨、欲不行焉，可以为仁矣？”子曰：“可以为难矣，仁则吾不知也。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122263', '论语', '子曰：“士而怀居，不足以为士矣。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122264', '论语', '子曰：“邦有道，危言危行；邦无道，危行言孙。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122265', '论语', '子曰：“有德者，必有言。有言者，不必有德。仁者，必有勇。勇者，不必有仁。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122266', '论语', '南宫适问于孔子曰：“羿善射，奡荡舟，俱不得其死然；禹稷躬稼，而有天下。”夫子不答，南宫适出。子曰：“君子哉若人！尚德哉若人！”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122267', '论语', '子曰：“君子而不仁者有矣夫，未有小人而仁者也。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122268', '论语', '子曰：“爱之，能勿劳乎？忠焉，能勿诲乎？”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122269', '论语', '子曰：“为命：裨谌草创之，世叔讨论之，行人子羽修饰之，东里子产润色之。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122270', '论语', '或问子产。子曰：“惠人也。”问子西。曰：“彼哉！彼哉！”问管仲。曰：“人也。夺伯氏骈邑三百，饭疏食，没齿，无怨言。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122271', '论语', '子曰：“贫而无怨难，富而无骄易。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122272', '论语', '子曰：“孟公绰，为赵魏老则优，不可以为滕薛大夫。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122273', '论语', '子路问成人。子曰：?“若臧武仲之知，公绰之不欲，卞庄子之勇，冉求之艺，文之以礼乐，亦可以为成人矣。”曰：“今之成人者何必然？见利思义，见危授命，久要不忘平生之言，亦可以为成人矣。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122274', '论语', '子问公叔文子于公明贾曰：“信乎夫子不言、不笑、不取乎？”公明贾对曰：“以告者过也。夫子时然后言，人不厌其言；乐然后笑，人不厌其笑；义然后取，人不厌其取。”子曰：“其然，岂其然乎？”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122275', '论语', '子曰：“臧武仲以防求为后于鲁，虽曰不要君，吾不信也。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122276', '论语', '子曰：“晋文公谲而不正，齐桓公正而不谲。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122277', '论语', '子路曰：“桓公杀公子纠，召忽死之，管仲不死。”曰：“未仁乎？”子曰：“桓公九合诸侯，不以兵车，管仲之力也。如其仁！如其仁！”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122278', '论语', '子贡曰：“管仲非仁者与？桓公杀公子纠，不能死，又相之。”子曰：“管仲相桓公，霸诸侯，一匡天下，民到于今受其赐。微管仲，吾其被发左衽矣。岂若匹夫匹妇之为谅也，自经于沟渎，而莫?9９ｌｉｂ.之知也。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122279', '论语', '公叔文子之臣大夫僎，与文子同升诸公。子闻之曰：“可以为文矣。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122280', '论语', '子言卫灵公之无道也，康子曰：“夫如是，奚而不丧？”孔子曰：“仲叔圉治宾客，祝鮀治宗庙，王孙贾治军旅。夫如是，奚其丧？”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122281', '论语', '子曰：“其言之不怍，则为之也难。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122282', '论语', '陈成子弒简公。孔子沐浴而朝，告于哀公曰：“陈恒弒其君，请讨之。”公曰：“告夫三子！”孔子曰：“以吾从大夫之后，不敢不告也。君曰‘告夫三子’者。”之三子告，不可。孔子曰：“以吾从大夫之后，不敢不告也。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122283', '论语', '子路问事君。子曰：“勿欺也，而犯之。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122284', '论语', '子曰：“君子上达，小人下达。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122285', '论语', '子曰：“古之学者为己，今之学者为人。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122286', '论语', '蘧伯玉使人于孔子。孔子与之坐而问焉，曰：“夫子何为？”对曰：“夫子欲寡其过而未能也。”使者出。子曰：“使乎！使乎！”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122288', '论语', '曾子曰：“君子思不出其位。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122289', '论语', '子曰：“君子耻其言而过其行。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122290', '论语', '子曰：“君子道者三，我无能焉：仁者不忧，知者不惑，勇者不惧。”子贡曰：“夫子自道也。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122291', '论语', '子贡方人。子曰：“赐也贤乎哉？夫我则不暇。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122292', '论语', '子曰：“不患人之不己知，患其不能也。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122293', '论语', '子曰：“不逆诈，不亿不信。抑亦先觉者，是贤乎！”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122294', '论语', '微生亩谓孔子曰：“丘何为是栖栖者与？无乃为佞乎？”孔子曰：“', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122295', '论语', '非敢为佞也，疾固也。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122296', '论语', '子曰99lｉｂ?：“骥不称其力，称其德也。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122297', '论语', '或曰：“以德报怨，何如？”子曰：“何以报德？以直报怨，以德报德。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122298', '论语', '子曰：“莫我知也夫！”子贡曰：“何为其莫知子也？”子曰：“不怨天，不尤人。下学而上达。知我者，其天乎！”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122299', '论语', '公伯寮愬子路于季孙。子服景伯以告，曰：“夫子固有惑志于公伯寮，吾力犹能肆诸市朝。”子曰：“道之将行也与？命也。道之将废也与？命也。公伯寮其如命何！”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122300', '论语', '子曰：“贤者辟世，其次辟地，其次辟色，其次辟言。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122301', '论语', '子曰：“作者七人矣。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122302', '论语', '子路宿于石门。晨门曰：“奚自？”子路曰：“自孔氏。”曰：“是知其不可而为之者与？”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122303', '论语', '子击磬于卫。有荷蒉而过孔氏之门者，曰：“有心哉！击磬乎！”既而曰：“鄙哉！硁硁乎！莫己知也，斯己而已矣。深则厉，浅则揭。”子曰：“果哉！末之难矣。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122304', '论语', '子张曰：“书云：‘高宗谅阴，三年不言。’何谓也？”子曰：“何必高宗，古之人皆然。君薨，百官总己以听于冢宰，三年。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122305', '论语', '子曰：“上好礼，则民易使也。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122306', '论语', '子路问君子。子曰：“修己以敬。”曰：“如斯而已乎？”曰：“修己以安人。”曰：“如斯而已乎？”曰：“修己以安百姓。修己以安百姓，尧舜其犹病诸！”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122307', '论语', '原壤夷俟。子曰：“幼而不孙弟，长而无述焉，老而不死，是为贼！”以杖叩其胫。阙党童子将命。或问之曰：“益者与？”子曰：“吾见其居于位也，见其与先生并行也。非求益者也，欲速成者也。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122308', '论语', '卷八 卫灵公第十五', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122309', '论语', '卫灵公问陈于孔子。孔子对曰：“俎豆之事，则尝闻之矣；军旅之事，未之学也。”明日遂行。在陈绝粮，从者病<q>.</q>，莫能兴。', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122310', '论语', '子路愠见曰：“君子亦有穷乎？”子曰：“君子固穷，小人穷斯滥矣。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122311', '论语', '子曰：“赐也，女以予为多学而识之者与？”对曰：“然，非与？”曰：“非也，予一以贯之。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122312', '论语', '子曰：“由！知德者鲜矣。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122313', '论语', '子曰：“无为而治者，其舜也与？夫何为哉，恭己正南面而已矣。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122314', '论语', '子张问行。子曰：“言忠信，行笃敬，虽蛮貊之邦行矣；言不忠信，行不笃敬，虽州里行乎哉？立<bdo></bdo>，则见其参于前也；在舆，则见其倚于衡也。夫然后行。”子张书诸绅。', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122315', '论语', '子曰：“直哉史鱼！邦有道，如矢；邦无道，如矢。”君子哉蘧伯玉    ！邦有道，则仕；邦无道，则可卷而怀之。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122316', '论语', '子曰：“可与言而不与之言，失人；不可与言而与之言，失言。知者不失人，亦不失言。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122317', '论语', '子曰：“志士仁人，无求生以害仁，有杀身以成仁。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122318', '论语', '子贡问为仁。子曰：“工欲善其事，必先利其器。居是邦也，事其大夫之贤者，友其士之仁者。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122319', '论语', '颜渊问为邦。子曰：“行夏之时，乘殷之辂，服周之冕，乐则韶舞。放郑声，远佞人。郑声淫，佞人殆。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122320', '论语', '子曰：“人无远虑，必有近忧。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122321', '论语', '子曰：“已矣乎！吾未见好德如好色者也。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122322', '论语', '子曰：“臧文仲其窃位者与？知柳下惠之贤，而不与立也。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122323', '论语', '子曰：“躬自厚而薄责于人，则远怨矣。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122324', '论语', '子曰：“不曰‘如之何如之何’者，吾末如之何也已矣。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122325', '论语', '子曰：“群居终日，言不及义，好行小慧，难矣哉！”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122326', '论语', '子曰：“君子义以为质，礼以行之，孙以出之，信以成之。君子哉！”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122327', '论语', '子曰：“君子病无能焉，不病人之不己知也。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122328', '论语', '子曰：“君子疾没世而名不称焉。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122329', '论语', '子曰：“君子求诸己，小人求诸人。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122330', '论语', '子曰：“君子矜而不争，群而不党。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122331', '论语', '子曰：“君子不以言举人，不以人废言。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122332', '论语', '子贡问曰：“有一言而可以终身行之者乎？”子曰：“其恕乎！己所不欲，勿施于人。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122333', '论语', '子曰：“吾之于人也，谁毁谁誉？如有所誉者，其有所试矣。斯民也，三代之所以直道而行也。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122334', '论语', '子曰：“吾犹及史之阙文也，有马者借人乘之。今亡矣夫！”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122335', '论语', '子曰：“巧言乱德，小不忍则乱大谋。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122336', '论语', '子曰：“众恶之，必察焉；众好之，必察焉。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122337', '论语', '子曰：“人能弘道，非道弘人。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122338', '论语', '子曰：“过而不 6539.\" >改，是谓过矣。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122339', '论语', '子曰：“吾尝终日不食，终夜不寝，以思，无益，不如学也。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122340', '论语', '子曰：“君子谋道不谋食。耕也，馁在其中矣；学也，禄在其中矣。君子忧道不忧贫。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122341', '论语', '子曰：“知及之，仁不能守之；虽得之，必失之。知及之，仁能守之。不庄以莅之，则民不敬。知及之，仁能守之，庄以莅之。动之不以礼，未善也。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122342', '论语', '子曰：“君子不可小知，而可大受也；小人不可大受，而可小知也。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122343', '论语', '子曰：“民之于仁也，甚于水火。水火，吾见蹈而死者矣，未见蹈仁而死者也。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122344', '论语', '子曰：“当仁不让于师。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122345', '论语', '子曰：“君子贞而不谅。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122346', '论语', '子曰：“事君，敬其事而后其食。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122347', '论语', '子曰：“有教无类。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122348', '论语', '子曰：“道不同，不相为谋。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122349', '论语', '子曰：“辞达而已矣。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122350', '论语', '师冕见，及阶，子曰：“阶也。”及席，子曰：“席也。”皆坐，子告之曰：“某在斯，某在斯。”师冕出。子张问曰：“与师言之道与？”子曰：“然。固相师之道也。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122351', '论语', '卷八 季氏第十六', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122352', '论语', '季氏将伐颛臾。冉有、季路见于孔子曰：“季氏将有事于颛臾。”孔子曰：“求！无乃尔是过与？夫颛臾，昔者先王以为东蒙主，且在邦域之中矣，是社稷之臣也。何以伐为？”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122353', '论语', '冉有曰：“夫子欲之，吾二臣者皆不欲也。”孔子曰：“求！周任有言曰：“陈力就列，不能者止。’危而不持，颠而不扶，则将焉用彼相矣？且尔言过矣。虎兕出于柙，龟玉毁于椟中，是谁之过与？”冉有曰：“今夫颛臾，固而近于费。今不取，后世必为子孙忧。”孔子曰：“求！君子疾夫舍曰欲之，而必为之辞。丘也闻有国有家者，不患寡而患不均，不患贫而患不安。盖均无贫，和无寡，安无倾。夫如是，故远人不服，则修文德<bdo></bdo>以来之。既来之，', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122354', '论语', '则安之。今由与求也，相夫子，远人不服而不能来也；邦分崩离析而不能守也。而谋动干戈于邦内。吾恐季孙之忧，不在颛臾，而在萧墙之内也。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122355', '论语', '孔子曰：“天下有道，则礼乐征伐自天子出；天下无道，则礼乐征伐自诸侯出。自诸侯出，盖十世希不失矣；自大夫出，五世希不失矣；陪臣执国命，三世希不失矣。天下有道，则政不在大夫。天下有道，则庶人不议。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122356', '论语', '孔子曰：“禄之去公室，五世矣；政逮于大夫，四世矣；故夫三桓之子孙，微矣。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122357', '论语', '孔子曰：“益者三友，损者三友。友直，友谅，友多闻，益矣。友便辟，友善柔，友便佞，损矣。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122358', '论语', '孔子曰：“益者三乐，损者三乐。乐节礼乐， 4e50.\" >乐道人之善，乐多bbr></abbr>贤友，益矣。乐骄乐，乐佚游，乐宴乐，损矣。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122359', '论语', '孔子曰：“侍于君子有三愆：言未及之而言谓之躁，言及之而不言谓之隐，未见颜色而言谓之瞽。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122360', '论语', '孔子曰：“君子有三戒：少之时，血气未定，戒之在色；及其壮也，血气方刚，戒之在斗；及<bdo></bdo>其老也，血气既衰，戒之在得。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122361', '论语', '孔子曰：“君子有三畏：畏天命，畏大人，畏圣人之言。小人不<big></big>知天命而不畏也，狎大人，侮圣人之言。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122362', '论语', '孔子曰：“生而知之者，上也；学而知之者，次也；困而学之，又其次也；困而不学，民斯为下矣。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122363', '论语', '孔子曰：“君子有九思：视思明，听思聪，色思温，貌思恭，言思忠，事思敬，疑思问，忿思难，见得思义。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122364', '论语', '孔子曰：“见善如不及，见不善如探汤。吾见其人矣，吾闻其语矣。隐居以求其志，行义以达其道。吾闻其语矣，未见其人也。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122365', '论语', '‘诚不以富，亦只以异。’齐景公有马千驷，死之日，民无德而称焉。伯夷叔齐饿于首阳之下，民到于今称之。其斯之谓与？', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122366', '论语', '陈亢问于伯鱼曰：“子亦有异闻乎？”对曰：“未也。尝独立，鲤趋而过庭。曰：‘学诗乎？’对曰：‘未也。’‘不学诗，无以言。’鲤退而学诗。他日又独立，鲤趋而过庭。', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122367', '论语', '曰：‘学礼乎？’对曰：‘未也。’‘不学礼，无以立。’鲤退而学礼。闻斯二者。”陈亢退而喜曰：“问一得三，闻诗，闻礼，又闻君子之远其子也。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122368', '论语', '邦君之妻，君称之曰 592b.\" >夫人，夫人自称曰小童；邦人称之曰君夫人，称诸异邦曰寡小君；异邦人称之亦曰君夫人。', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122369', '论语', '卷九 阳货第十七', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122370', '论语', '阳货欲见孔子，孔子不见，归孔子豚。孔子时其亡也，而往拜之，遇诸涂。谓孔子曰：“来！予与尔言。”曰：“怀其宝而迷其邦，可谓仁乎？”曰：“不可。”“好从事而亟失时，可谓知乎？”曰：“不可。”“日月逝矣，岁不我与。”孔子曰：“诺。吾将仕矣。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122371', '论语', '子曰：“性相近也，习相远也。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122372', '论语', '子曰：“唯上知与下愚不移。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122373', '论语', '子之武城，闻弦歌之声。夫子莞尔而笑，曰：“割鸡焉用牛刀？”子游对曰：“昔者偃也闻诸夫子曰：‘君子学道则爱人，小人学道则易使也。’”子曰：“二三子！偃之言是也。前言戏之耳。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122374', '论语', '公山弗扰以费畔，召，子欲往。子路不说，曰：“末之也已，何必公山氏之之也。”子曰：“夫召我者而岂徒哉？如有用我者，吾其为东周乎？”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122375', '论语', '子张问仁于孔子。孔子曰：“能行五者于天下，为仁矣。”请问之。曰：“恭、宽、信、敏、惠。恭则不侮，宽则得众，信则人任焉，敏则有功，惠则足以使人。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122376', '论语', '佛肸召，子欲往。子路曰：“昔者由也闻诸夫子曰：‘亲于其身为不善者，君子不入也。’佛肸以中牟畔，子之往也，如之何！”子曰：“然。有是言也。不曰坚乎，磨而不磷；不曰白乎，涅而不缁。吾岂匏瓜也哉？焉能系而不食？”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122377', '论语', '子曰：“由也，女闻六言六蔽矣乎？”对曰：“未也。”“居！吾语女。好仁不好学，其蔽也愚；好知不好学，其蔽也荡；好信不好学，其蔽也贼；好直不好学，其蔽也绞；好勇不好学，其蔽也乱；好刚不好学，其蔽也狂。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122378', '论语', '子曰：“小子！何莫学夫诗？诗，可以兴，可以观，可以群，可以怨。迩之事父，远之?事君。多识于鸟兽草木之名。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122379', '论语', '子谓伯鱼曰：“女为周南召南矣乎？<u>?</u>人而不为周南召南，其犹正墙面而立也与？”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122380', '论语', '子曰：“礼云礼云，玉帛云乎哉？乐云乐云，钟鼓云乎哉？”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122381', '论语', '子曰：“色厉而内荏，譬诸小人，其犹穿窬之盗也与？”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122382', '论语', '子曰：“乡原，德之贼也。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122383', '论语', '子曰：“道听而涂说，德之弃也。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122384', '论语', '子曰：“鄙夫！可与事君也与哉？其未得之也，患得之；既得之，患失之。苟患失之，无所不至矣。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122385', '论语', '子曰：“古者民有三疾，今也或是之亡也。古之狂也肆，今之狂也荡；古之矜也廉，今之矜也忿戾；古之愚也直，今之愚也诈而已矣。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122386', '论语', '子曰：“巧言令色，鲜矣仁。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122387', '论语', '子曰：“恶紫之夺朱也，恶郑声之乱雅乐也，恶利口之覆邦家者。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122388', '论语', '子曰：“予欲无言。”子贡曰：“子如不言，则小子何述焉？”子曰：“天何言哉？四时行焉，百物生焉，天何言哉？”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122389', '论语', '孺悲欲见孔子，孔子辞以疾。将命者出户，取瑟而歌。使之闻之。', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122390', '论语', '宰我问：“三年之丧，期已久矣。君子三年不为礼，礼必坏；三年不为乐，乐必崩。旧谷既没，新谷既升，钻燧改火，期可已矣。”子曰：“食夫稻，衣夫锦，于女安乎？”曰：“安。”“女安则为之！夫', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122391', '论语', '君子之居丧，食旨不甘，闻乐不乐，居处不安，故不为也。今女安，则为之！”宰我出。子曰：“予 4e4b.\" >之不仁也！子生三年，然后免于父母之怀。夫三年之99ｌｉｂ?', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122392', '论语', '丧，天下之通丧也。予也，有三年之爱于其父母乎？”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122393', '论语', '子曰：“饱食终日，无所用心，难矣哉！不有博弈者乎，为之犹贤乎已。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122394', '论语', '子路曰：“君子尚勇乎？”子曰：“君子义以为上。君子有勇而无义为乱，小人有勇而无义为盗。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122395', '论语', '子贡曰：“君子亦有恶乎？”子曰：“有恶：恶称人之恶者，恶居下流而讪上者，恶勇而无礼者，恶果敢而窒者。”曰：“赐也亦有恶乎？”“恶徼以为知者，恶不孙以为勇者，恶讦以为直者。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122396', '论语', '子曰：“唯女子与小人为难养也，近之则不孙，远之则怨。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122397', '论语', '子曰：“年四十而见恶焉，其终也已。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122398', '论语', '卷九 微子第十八', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122399', '论语', '微子去之，箕子为之奴，比干谏而死。孔子曰：“殷有三仁焉。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122400', '论语', '柳下惠为士师，三黜。人曰：“子未可以去乎？”曰：“直道而事人，焉往而不三黜？枉道而事人，何必去父母之邦。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122401', '论语', '齐景公待孔子，曰：“若季氏则吾不能，以季、孟之闲待之。”曰：“吾老矣，不能<tt>.９9ｌiｂ?t>用也。”孔子行。', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122402', '论语', '齐人归女乐，季桓子受之。三日不朝，孔子行。', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122403', '论语', '楚狂接舆歌而过孔子曰：“凤兮！凤兮！何德之衰？往者不可谏，来者犹可追。已而，已而！今之从政者殆而！”孔子下，欲与之言。趋而辟之，不得与之言<bdi></bdi>。', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122404', '论语', '长沮、桀溺耦而耕，孔子过之，使子路问津焉。长沮曰：“夫执舆者为谁？”子路曰：“为孔丘。”曰：“是鲁孔丘与？”曰：“是也。”曰：“是知津矣。”问于桀溺，桀溺曰：“子为谁？”曰：“为仲由。”曰：“是鲁孔丘之徒与？”对曰：“然。”曰：“滔滔者天下皆是也，而谁以易之？且而与其从辟人之士也，岂若从辟世之士哉？”耰而不辍。子路行以告。夫子怃然曰：“鸟兽不可与同群，吾非斯人之徒与而谁与？天下有道，丘不与易也。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122405', '论语', '子路从而后，遇丈人，以杖荷莜。子路问曰：“子见夫子乎？”丈人曰：“四体不勤，五谷不分。孰为夫子？”植其杖而芸。子路拱而立。止子路宿，杀鸡为黍而食之，见其二子焉。明日，子路行以告。子曰：“隐者也。”使子路反见之。至则行矣。子路曰：“不仕无义。长幼之节，不可废也；君臣之义，如之何其废之？欲洁其身，而乱大伦。君子之仕也，行其义也。道之不行，已知之矣。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122406', '论语', '逸民：伯夷、叔齐、虞仲、夷逸、朱张、柳下惠、少连。子曰：“不降其志，不辱其身，伯夷、叔齐与！”谓：“柳下惠、少连，降志辱身矣。言中伦，行中虑，其斯而已矣。”谓：“虞仲bbr></abbr>、夷逸，隐居放言。身中清，废中权。”“我则异于是，无可无不可。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122407', '论语', '大师挚适齐，亚饭干适楚，三饭缭适蔡，四饭缺适秦。鼓方叔入于河，播(上兆下鼓)武入于汉，少师阳、击磬襄，入于海。', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122408', '论语', '周公谓鲁公曰：“君子不施其亲，不使大臣怨乎不以。故旧无大故，则不弃也。无求备于一人。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122409', '论语', '周有八士：伯达、伯适、仲突、仲忽、叔夜、叔夏、季随、季騧。', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122410', '论语', '卷十 子张第十九', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122411', '论语', '子张曰：“士见危致命，见得思义，祭思敬，丧思哀，其可已矣。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122412', '论语', '子张曰：“执德不弘，信道不笃，焉能为有？焉能为亡？”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122413', '论语', '子夏之门人问交于子张。子张曰：“子夏云何？”对曰：“子夏曰：‘可者与之，其不可者拒之。’”子张曰：“异乎吾所闻：君子尊贤而容众，嘉善而矜不能。我之大贤与，于人何所不容？我之不贤与，人将拒我，如之何其拒人也？”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122414', '论语', '子夏曰：“虽小道，必有可观者焉；致远恐泥，是以君子不为也。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122415', '论语', '子夏曰：“日知其所亡，月无忘其所能，可谓好学也已矣。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122416', '论语', '子夏曰：“博学而笃志，切问而近思，仁在其中矣。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122417', '论语', '子夏曰：“百工居肆以成其事，君子学以致其道。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122418', '论语', '子夏曰：“小人之过也必文。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122419', '论语', '子夏曰：“君子有三变：望之俨然，即之也温，听其言也厉。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122420', '论语', '子夏曰：“君子信而后劳其民，未信则以为厉己也；信而后谏，未信则以为谤己也。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122421', '论语', '子夏曰：“大德不踰闲，小德出入可也。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122422', '论语', '子游曰：“子夏之门人小子，当洒扫、应对、进退，则可矣。抑末也，本之则无。如之何？”子夏闻之曰：“噫！言游过矣！君子之道，孰先传焉？孰后倦焉？譬诸草木，区以别矣。君子之道，焉可诬也？有始有卒者，其惟圣人乎！”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122423', '论语', '子夏曰：“仕而优则学，学而优则仕。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122424', '论语', '子游曰：“丧致乎哀而止。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122425', '论语', '子游曰：“吾友张也，为难能也。然而未仁。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122426', '论语', '曾子曰：“堂堂乎张也，难与并为仁矣。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122427', '论语', '曾子曰：“吾闻诸夫子：人未有自致者也，必也亲丧乎！”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122428', '论语', '曾子曰：“吾闻诸夫子：孟庄子之孝也，其他可能也；其不改父之臣，与父之政??，是难能也。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122429', '论语', '孟氏使阳肤为士师，问于曾子。曾子曰：“上失其道，民散久矣。如得其情，则哀矜而勿喜。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122430', '论语', '子贡曰：“纣之不善，不如是之甚也。是以君子恶居下流，天下之恶皆归焉。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122431', '论语', '子贡曰：“君子之过也，如日月之食焉：过也，人皆见之；更也，人皆仰之。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122432', '论语', '卫公孙朝问于子贡曰：“仲尼焉学？”子贡曰：“文武之道，未坠于地，在人。贤者识其大者，不贤者识其小者，莫不有文武之道焉。夫子焉不学？而亦何常师之有？”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122433', '论语', '叔孙武叔语大夫于朝，曰：“子贡贤于仲尼。”子服景伯以告子贡。子贡曰：“譬之宫墙，赐之墙也及肩，窥见室家之好。夫子之墙数仞，不得其门而入，不见宗庙之美，百官之富。得其门者或寡矣。夫子之云，不亦宜乎！”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122434', '论语', '叔孙武叔毁仲尼。子贡曰：“无以为也，仲尼不可毁也。他人之贤者，丘陵也，犹可踰也；仲尼，日 6708.\" >月也，无得而踰焉。人虽欲自绝，其何伤于日月乎？多见其不知量也！”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122435', '论语', '陈子禽谓子贡曰：“子为恭也，仲尼岂贤于子乎？”子贡曰：“君子一言以为知，一言以为不知，言不可不慎也。夫子之不可及也，犹天之不可阶而升也。夫子之得邦家者，所谓立之斯立，道之斯行，绥之斯来，动之斯和。', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122436', '论语', '其生也荣，其死也哀，如之何其可及也。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122437', '论语', '卷十 尧曰第二十', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122438', '论语', '尧曰：“咨！尔舜！天之历数在尔躬。允执其中。四海困穷，天禄永终。”舜亦以命禹。曰：“予小子履，敢用玄牡，敢昭告于皇皇后帝：有罪不敢赦。帝臣不蔽，简在帝心。', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122439', '论语', '朕躬有罪，无以万方；万方有罪，罪在朕躬。”周有大赉，善人是富。“虽有周亲，不如仁人。百姓有过，在予一人。”谨权量，审法度，修废官，四方之政行焉。兴灭国，继绝世，<u></u>举逸民，天下之民归心焉。所重：民、食、丧、祭。宽则得众，信则民任焉，敏则有功，公则说。', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122440', '论语', '子张问于孔子曰：“何如斯可以从政矣？”子曰：“尊五美，屏四恶，斯可以从政矣。”子张曰：“何谓五美？”子曰：“君子惠而不费，劳而藏书网不怨，欲而不贪，泰而不骄，威而不猛。”子张曰：“何谓惠而不费？”子曰：“因民之所利而利之，斯不亦惠而不费乎？择可劳而劳之，又谁怨？欲仁而得仁，又焉贪？君子无众寡，无小大，无敢慢，斯<bdo></bdo>不亦泰而不骄乎？君子正其衣冠，尊其瞻视，俨然人望而畏之，斯不亦威而不猛乎？”子张曰：“何谓四恶？”子曰：“不教而杀谓之虐；不戒视成谓之暴；慢令致期谓之贼；犹之与人也，出纳之吝，谓之有司。”', '孔子', '4', '4', 'K', null);
INSERT INTO `article` VALUES ('1271056071519122441', '论语', '子曰：“不知命，无以为君子也。不知礼，无以立也。不知言，无以知人也。', '孔子', '4', '4', 'K', null);
