INSERT INTO `article` VALUES (1, '宴山亭', '裁剪冰绡，轻叠数重，淡着燕脂匀注。新样靓妆，艳溢香融，羞杀蕊珠宫女。易得凋零，更多少、无情风雨。愁苦，问院落凄凉，几番春暮？凭寄离恨重重，者双燕何曾，会人言语？天遥地远，万水千山，知他故宫何处？怎不思量？除梦里有曾去。无据，和梦也新来不做。', '赵佶', NULL, 1);
INSERT INTO `article` VALUES (2, '木兰花', '城上风光莺语乱，城下烟波春拍岸。绿杨芳草几时休？泪眼愁肠先已断。情怀渐觉成衰晚，鸾镜朱颜惊暗换。昔年多病厌芳尊，今日芳尊惟恐浅。', '钱惟演', NULL, 1);
INSERT INTO `article` VALUES (3, '苏幕遮', '碧云天，黄叶地，秋色连波，波上寒烟翠。山映斜阳天接水，芳草无情，更在斜阳外。黯乡魂，追旅思。夜夜除非，好梦留人睡。明月楼高休独倚，酒入愁肠，化作相思泪。', '范仲淹', NULL, 1);
INSERT INTO `article` VALUES (4, '御街行', '纷纷坠叶飘香砌。夜寂静，寒声碎。真珠帘卷玉楼空，天淡银河垂地。年年今夜，月华如练，长是人千里。　愁肠已断无由醉，酒未到，先成泪。残灯明灭枕头攲，谙尽孤眠滋味。都来此事，眉间心上，无计相回避。　', '范仲淹', NULL, 1);
INSERT INTO `article` VALUES (5, '千秋岁', '数声鶗鳺，又报芳菲歇。惜春更选残红折，雨轻风色暴，梅子青时节。永丰柳，无人尽日花飞雪。　莫把幺弦拨，怨极弦能说。天不老，情难绝，心似双丝网，中有千千结。夜过也，东窗未白孤灯灭。', '张先', NULL, 1);
INSERT INTO `article` VALUES (6, '菩萨蛮', '哀筝一弄湘江曲，声声写尽湘波绿。纤指十三弦，细将幽恨传。当筵秋水慢，玉柱斜飞雁。弹到断肠时，春山眉黛低。醉垂鞭，张先双蝶绣罗裙，东池宴初相见。朱粉不深匀，闲花淡淡春。细看诸处好，人人道柳腰身。昨日乱山昏，来时衣上云。', '张先', NULL, 1);
INSERT INTO `article` VALUES (7, '一丛花', '伤高怀远几时穷？无物似情浓。离愁正引千丝乱，更东陌，飞絮蒙蒙。嘶骑渐遥，征尘不断，何处认郎踪？　双鸳池沼水溶溶，南北小桡通。梯横画阁黄昏后，又还是，斜月帘栊。沉恨细思，不如桃杏，犹解嫁东风。　', '张先', NULL, 1);
INSERT INTO `article` VALUES (8, '天仙子', '水调数声持酒听，午醉醒来愁未醒。送春春去几时回？临晚镜，伤流景，往事后期空记省。沙上并禽池上暝，云破月来花弄影。重重帘幕密遮灯，风不定，人初静，明月落红应满径。', '张先', NULL, 1);
INSERT INTO `article` VALUES (9, '青门引', '乍暖还轻冷，风雨晚来方定。庭轩寂寞近清明，残花中酒，又是去年病。楼头画角风吹醒，入夜重门静。那堪更被明月，隔墙送过秋千影。　', '张先', NULL, 1);
INSERT INTO `article` VALUES (10, '浣溪沙', '一曲新词酒一杯，去年天气旧池台，夕阳西下几时回？无可奈何花落去，似曾相识燕归来，小园香径独徘徊。', '晏殊', NULL, 1);
INSERT INTO `article` VALUES (11, '浣溪沙', '一向年光有限身，等闲离别易消魂，酒筵歌席莫辞频。满目山河空念远，落花风雨更伤春，不如怜取眼前人。', '晏殊', NULL, 1);
INSERT INTO `article` VALUES (12, '清乐平', '　红笺小字，说尽平生意。鸿雁在云鱼在水，惆怅此情难寄。　斜阳独倚西楼，遥山恰对帘钩。人面不知何处，绿波依旧东流。', '晏殊', NULL, 1);
INSERT INTO `article` VALUES (13, '清乐平', '　金风细细，叶叶梧桐坠。绿酒初尝人易醉，一枕小窗浓睡。　紫薇朱槿花残，斜阳却照阑干。双燕欲归时节，银屏昨夜微寒。', '晏殊', NULL, 1);
INSERT INTO `article` VALUES (14, '木兰花', '燕鸿过后莺归去，细算浮生千万绪。长于春梦几多时，散似秋云无觅处。闻琴解佩神仙侣，挽断罗衣留不住。劝君莫作独醒人，烂醉花间应有数。', '晏殊', NULL, 1);
INSERT INTO `article` VALUES (15, '木兰花', '池塘水绿风微暖，记得玉真初见面。重头歌韵响琤琮，入破舞腰红乱旋。玉钩栏下香阶畔，醉后不知斜日晚。当时共我赏花人，点检如今无一半。', '晏殊', NULL, 1);
INSERT INTO `article` VALUES (16, '木兰花', '绿杨芳草长亭路，年少抛人容易去。楼头残梦五更钟，花底离愁三月雨。无情不似多情苦，一寸还成千万缕。天涯地角有穷时，只有相思无尽处。', '晏殊', NULL, 1);
INSERT INTO `article` VALUES (17, '踏莎行', '祖席离歌，长亭别宴，香尘已隔犹回面。居人匹马映林嘶，行人去棹依波转。画阁魂消，高楼目断，斜阳只送平波远。无穷无尽是离愁，天涯地角寻思遍。', '晏殊', NULL, 1);
INSERT INTO `article` VALUES (18, '踏莎行', '小径红稀，芳郊绿遍，高台树色阴阴见。春风不解禁杨花，蒙蒙乱扑行人面。翠叶藏莺，朱帘隔燕，炉香静逐游丝转。一场愁梦酒醒时，斜阳却照深深院。', '晏殊', NULL, 1);
INSERT INTO `article` VALUES (19, '蝶恋花', '六曲阑干偎碧树,杨柳风轻,展尽黄金缕。谁把钿筝移玉柱，穿帘海燕双飞去。满眼游丝兼落絮,红杏开时,一霎清明雨。浓睡觉来莺乱语，惊残好梦无寻处。', '晏殊', NULL, 1);
INSERT INTO `article` VALUES (20, '凤箫吟', '锁离愁连绵无际，来时陌上初熏，绣帏人念远，暗垂珠露，泣送征轮。长行长在眼，更重重、远水孤云。但望极楼高，尽日目断王孙。　消魂池塘别后，曾行处、绿妒轻裙。恁时携素手,乱花飞絮里,缓步香茵。朱颜自改，向年年、芳意长新。遍绿野、嬉游醉眼，莫负青春。', '韩缜', NULL, 1);
INSERT INTO `article` VALUES (21, '木兰花', '东城渐觉风光好，彀皱波纹迎客棹。绿杨烟外晓云轻，红杏枝头春意闹。浮生长恨欢娱少，肯爱千金轻一笑？为君持酒劝斜阳，且向花间留晚照。', '宋祁', NULL, 1);
INSERT INTO `article` VALUES (22, '采桑子', '群芳过后西湖好，狼籍残红，飞絮蒙蒙，垂柳阑干尽日风。笙歌散尽游人去，始觉春空，垂下帘栊，双燕归来细雨中。', '欧阳修', NULL, 1);
INSERT INTO `article` VALUES (23, '诉衷情', '清晨帘幕卷轻霜，呵手试梅妆。都缘自有离恨，故画作远山长。思往事，惜流芳，易成伤。拟歌先敛，欲笑还颦，最断人肠。', '欧阳修', NULL, 1);
INSERT INTO `article` VALUES (24, '踏莎行', '候馆梅残，溪桥柳细，草薰风暖摇征辔。离愁渐远渐无穷，迢迢不断如春水。寸寸柔肠，盈盈粉泪，楼高莫近危阑倚。平芜尽处是春山，行人更在春山外。', '欧阳修', NULL, 1);
INSERT INTO `article` VALUES (25, '蝶恋花', '庭院深深深几许？杨柳堆烟,帘幕无重数。玉勒雕鞍游冶处,楼高不见章台路。雨横风狂三月暮，门掩黄昏,无计留春住。泪眼问花花不语,乱红飞过秋千去。', '欧阳修', NULL, 1);
INSERT INTO `article` VALUES (26, '蝶恋花', '谁道闲情抛弃久？每到春来,惆怅还依旧。日日花前常病酒,不辞镜里朱颜瘦。河畔青芜堤上柳，为问新愁,何事年年有？独立小桥风满袖,平林新月人归后。', '欧阳修', NULL, 1);
INSERT INTO `article` VALUES (27, '蝶恋花', '几日行云何处去？忘了归来,不道春将暮。百草千花寒食路,香车系在谁家树？泪眼倚楼频独语。双燕来时,陌上相逢否？撩乱春愁如柳絮,依依梦里无寻处。', '欧阳修', NULL, 1);
INSERT INTO `article` VALUES (28, '木兰花', '别后不知君远近，触目凄凉多少闷！渐行渐远渐无书，水阔鱼沉何处问？夜深风竹敲秋韵，万叶千声皆是恨。故攲单枕梦中寻，梦又不成灯双烬。', '欧阳修', NULL, 1);
INSERT INTO `article` VALUES (29, '浪淘沙', '把酒祝东风，且共从容。垂杨紫陌洛城东，总是当时携手处，游遍芳丝。聚散苦匆匆，此恨无穷。今年花胜去年红，可惜明年花更好，知与谁同？', '欧阳修', NULL, 1);
INSERT INTO `article` VALUES (30, '青玉案', '一年春事都来几？早过了、三之二。绿暗红嫣浑可事。绿杨庭院，暖风帘幕，有个人憔悴。买花载酒长安市，又争似，家山见桃李？不枉东风吹客泪，相思难表，梦魂无据，惟有归来是。', '欧阳修', NULL, 1);
INSERT INTO `article` VALUES (31, '曲玉管', '陇首云飞，江边日晚，烟波满目凭阑久。一望关河萧索，千里清秋，忍凝眸。杳杳神京，盈盈仙子，别来锦字终难偶。断雁无凭，冉冉飞下汀洲、思悠悠。暗想当初，有多少、幽欢佳会；岂知聚散难期，翻成雨恨云愁。阻追游，每登山临水，惹起平生心事，一场消黯，永日无言，却下层楼。', '柳永', NULL, 1);
INSERT INTO `article` VALUES (32, '雨霖铃', '寒蝉凄切，对长亭晚，骤雨初歇。都门帐饮无绪，留恋处、兰舟催发。执手相看泪眼，竟无语凝噎。念去去、千里烟波，暮霭沉沉楚天阔。　多情自古伤离别，更那堪、冷落清秋节！今宵酒醒何处？杨柳岸、晓风残月。此去经年，应是良辰好景虚设。便纵有千种风情，更与何人说？', '柳永', NULL, 1);
INSERT INTO `article` VALUES (33, '蝶恋花', '伫倚危楼风细细，望极春愁，黯黯生天际。草色烟光残照里，无言谁会凭阑意？　拟把疏狂图一醉，对酒当歌，强乐还无味。衣带渐宽终不悔，为伊消得人憔悴。　', '柳永', NULL, 1);
INSERT INTO `article` VALUES (34, '采莲令', '月华收,云淡霜天曙。西征客、此时情苦。翠娥手,送临歧、轧轧开朱户。千娇面、盈盈伫立,无言有泪,断肠争忍回顾？一叶兰舟,便凭急桨凌波去。贪行色、岂知离绪,万般方寸，但饮恨、脉脉同谁语？更回首、重城不见，寒江天外，隐隐两三烟树。　', '柳永', NULL, 1);
INSERT INTO `article` VALUES (35, '浪淘沙慢', '梦觉透窗风一线,寒流灯吹息。那堪酒醒,又闻空阶夜雨频滴。嗟因循、久作天涯客。负佳人、几许盟言，便忍把、从前欢会，陡顿翻成忧戚。愁极，再三追思,洞房深处,几度饮散歌阑，香暖鸳鸯被。岂暂时疏散，费伊心力。殢云尤雨，有万般千种，相怜相惜。恰到如今,天长漏永,无端自家疏隔。知何时、却拥秦云态？愿低帏昵枕，轻轻细说与，江乡夜夜，数寒更思忆。　', '柳永', NULL, 1);
INSERT INTO `article` VALUES (36, '定风波', '自春来、惨绿愁红，芳心是事可可。日上花梢，莺穿柳带，犹压香衾卧。暖酥消，腻云亸，终日厌厌倦梳裹。无那。恨薄情一去，音书无个。早知恁么。悔当初、不把雕鞍锁。向鸡窗、只与蛮笺象管，拘束教吟课。镇相随，莫抛躲。针线闲拈伴伊坐。和我，免使年少光阴虚过。', '柳永', NULL, 1);
INSERT INTO `article` VALUES (38, '少年游', '晚秋天。一霎微雨洒庭轩。槛菊萧疏,井梧零乱,惹残烟。凄然。望江关，飞云黯淡夕阳间。当时宋玉悲感，向此临水与登山。远道迢递,行人凄楚,倦听陇水潺湲。正蝉吟败叶，蛩响衰草，相应喧喧。　孤馆度日如年。风露渐变，悄悄至更阑。长天净，绛河清浅，皓月婵娟。思绵绵，夜永对景，那堪屈指，暗想从前。未名未禄，绮陌红楼，往往经岁迁延。帝里风光好,当年少日,暮宴朝欢。况有狂朋怪侣，遇当歌、对酒竞留连。别来迅景如梭，旧游似梦，烟水程何限。念利名、憔悴长萦绊。追往事、空惨愁颜。漏箭移、稍觉轻寒。渐呜咽画角数声残。对闲窗畔，停灯向晓，抱影无眠。', '柳永', NULL, 1);
INSERT INTO `article` VALUES (39, '夜半乐', '冻云黯淡天气，扁舟一叶，乘兴离江渚。渡万壑千岩，越溪深处。怒涛渐息，樵风乍起，更闻商旅相呼，片帆高举。泛画鹢、翩翩过南浦。　望中洒旆闪闪，一簇烟村，数行霜树。残日下渔人鸣榔归去。败荷零落，衰杨掩映，岸边两两三三，浣纱游女。避行客，含羞笑相语。　到此因念，绣阁轻抛，浪萍难驻。叹后约、丁宁竟何据。惨离怀、空恨岁晚归期阻。凝泪眼、杳杳神京路。断鸿声远长天暮。　', '柳永', NULL, 1);
INSERT INTO `article` VALUES (40, '玉胡蝶', '望处雨收云断,凭栏悄悄,目送秋光。晚景萧疏，堪动宋玉悲凉。水风轻、蘋花渐老，月露冷、梧叶飘黄。遗情伤。故人何在？烟水茫茫。难忘。文期洒会，几孤风月，屡变星霜。海阔山遥，未知何处是潇湘？念双燕、难凭远信，指暮天、空识归航。黯相望。断鸿声里，立尽斜阳。　', '柳永', NULL, 1);
INSERT INTO `article` VALUES (41, '八声甘州', '对潇潇暮雨洒江天，一番洗清秋。渐霜风凄紧，关河冷落，残照当楼。是处红衰翠减，苒苒物华休。惟有长江水，无语东流。　不忍登高临远，望故乡渺邈，归思难收。叹年来踪迹，何事苦淹留？想佳人妆楼凝望，误几回天际识归舟？争知我、倚阑干处，正恁凝愁？　', '柳永', NULL, 1);
INSERT INTO `article` VALUES (42, '迷神引', '一叶扁舟轻帆卷,暂泊楚江南岸。孤城暮角,引胡笳怨。水茫茫，平沙雁，旋惊散。烟敛寒林簇，画屏展。天际遥山小，黛眉浅。旧赏轻抛，到此成游宦。觉客程劳,年光晚。异乡风物,忍萧索、当愁眼。帝城赊，秦楼阻，旅魂乱。芳草连空阔，残照满。佳人无消息，断云远。', '柳永', NULL, 1);
INSERT INTO `article` VALUES (43, '竹马子', '登孤垒荒凉，危亭旷望，静临烟渚。对雌霓挂雨，雄风拂槛，微收残暮。渐觉一叶惊秋，残蝉噪晚，素商时序。览景想肖欢，指神京、非零非烟深处。向此成追感，新愁易积，故人难聚。凭高尽日凝伫，赢得消魂无语。极目霁霭霏，暝鸦零乱，萧索江城暮。南楼画角，又送残阳去。', '柳永', NULL, 1);
INSERT INTO `article` VALUES (44, '桂枝香', '登临送目，正故国晚秋，天气初肃。千里澄江似练，翠峰如簇。归帆去棹斜阳里，背西风，酒旗斜矗。彩舟云淡，星河鹭起，画图难足。念往昔、繁华竞逐，叹门外楼头，悲恨相续。千古凭高，对此漫嗟荣辱。六朝旧事如流水，但寒烟、衰草凝绿。至今商女，时时犹唱，后庭遗曲。', '王安石', NULL, 1);
INSERT INTO `article` VALUES (45, '千秋岁引', '别馆寒砧，孤城画角，一派秋声入寥廓。东归燕从海上去，南来雁向沙头落。楚台风，庚楼月，宛如昨。缧缧无奈，被些名利缚，无奈被他情担阁，可惜风流总闲却。当初漫留华表语，而今误我秦楼约。梦阑时，酒醒后，思量着。　', '王安石', NULL, 1);
INSERT INTO `article` VALUES (50, '鹧鸪天', '彩袖殷勤捧玉钟，当年拚却醉颜红。舞低杨柳楼心月，歌尽桃花扇底风。从别后，忆相逢，几回魂梦与君同。今宵剩把银釭照，犹恐相逢是梦中。', '晏几道', NULL, 1);
INSERT INTO `article` VALUES (55, '阮郎归', '旧香残粉似当初，人情恨不如。一春犹有数行书，秋来书更疏。衾凤冷，枕鸳孤，愁肠待酒舒。梦魂纵有也成虚，那堪和梦无。', '晏几道', NULL, 1);
INSERT INTO `article` VALUES (56, '阮郎归', '天边金掌露成霜，云随雁字长。　绿杯红袖趁重阳，人情似故乡。兰佩紫，菊簪黄，殷勤理旧狂。　欲将沉醉换悲凉，清歌莫断肠。', '晏几道', NULL, 1);
INSERT INTO `article` VALUES (82, '水龙吟', '次韵林圣予《惜春》　问春何苦勿勿,带风伴雨如驰骤。幽葩细萼,小园低槛，壅培未就。吹尽繁红，占春长久，不如垂柳。算春长不老,人愁春老,愁只是、人间有。春恨十常八九，忍轻孤、芳醪经口。那知自是、桃花结子，不因春瘦。世上功名，老来风味，春归时候。最多情犹有，尊前青眼，相逢依旧。情愁忧患都说尽，烟柳浮云始作闲。', '晁补之', NULL, 1);
INSERT INTO `article` VALUES (83, '临江仙', '梦后楼台高锁，酒醒帘暮低垂。去年春恨却来时，落花人独立，微雨燕双飞。记得小蘋初见，两重心字罗衣。琵琶弦上说相思，当时明月在，曾照彩云归。', '晏几道', NULL, 1);
INSERT INTO `article` VALUES (84, '蝶恋花', '醉别西楼醒不记,春梦秋云,聚散真容易。斜月半窗还少睡，画屏闲展吴山翠。衣上酒痕诗里字,点点行行,总是凄凉意。红烛自怜无好计，夜寒空替人垂泪。', '晏几道', NULL, 1);
INSERT INTO `article` VALUES (87, '生查子', '关山魂梦长，塞雁音书少。两鬓可怜青，只为相思老。归傍碧纱窗，说与人人道：真个别离难，不似相逢好。', '晏几道', NULL, 1);
INSERT INTO `article` VALUES (88, '木兰花', '东风又作无情计，艳粉娇红吹满地。碧楼帘影不遮愁，还似去年今日意。谁知错管春残事，到处登临曾费泪。此时金盏直须深，看尽落花能几醉。', '晏几道', NULL, 1);
INSERT INTO `article` VALUES (89, '木兰花', '秋千院落重帘暮，彩笔闲来题绣户。墙头丹杏雨馀花，门外绿杨风后絮。朝云信断知何处？应作襄王春梦去。紫骝认得旧游踪，嘶过画桥东畔路。', '晏几道', NULL, 1);
INSERT INTO `article` VALUES (90, '清平乐', '留人不住，醉解兰舟去。一棹碧涛春水路，过尽晓莺啼处。渡头杨柳青青，枝枝叶叶离情。此后锦书休寄，画楼云雨无凭。', '晏几道', NULL, 1);
INSERT INTO `article` VALUES (93, '六么令', '绿阴春尽，飞絮绕香阁。晚来翠眉宫样，巧把远山学。一寸狂心未说，已向横波觉。　画帘遮匝，新翻曲妙，暗许闲人带偷掐。前度书多隐语，意浅愁难答。不消红蜡，闲云归后，月在庭花旧阑角。　', '晏几道', NULL, 1);
INSERT INTO `article` VALUES (94, '御街行', '街南绿树春饶絮，雪满游春路。树头花艳杂娇云，树底人家朱户。北楼闲上，疏帘高卷，直见街南树。　阑干倚尽犹慵去，几度黄昏雨。晚春盘马踏青苔，曾傍绿阴深驻。落花犹在，香屏空掩，人面知何处？　', '晏几道', NULL, 1);
INSERT INTO `article` VALUES (95, '虞美人', '曲阑干外天如水，昨夜还曾倚。初将明月比佳期，长向月圆时候、望人归。罗衣著破前香在，旧意谁教攻。一春离恨懒调弦，犹有两行闲泪、宝筝前。', '晏几道', NULL, 1);
INSERT INTO `article` VALUES (96, '留春令', '画屏天畔，梦回依约，十洲云水。手捻红笺寄人书，写无限、伤春事。别浦高楼曾漫倚，对江南千里。楼下分流水声中，有当日、凭高泪。', '晏几道', NULL, 1);
INSERT INTO `article` VALUES (97, '思远人', '丙辰中秋，欢饮达旦，作此篇兼怀子由明月几时有，把酒问青天。不知天上宫阙，今夕是何年。我欲乘风归去。惟恐琼楼玉宇，高处不胜寒，起舞弄清影，何似在人间。　转朱阁，低绮户，照无眠。不应有恨，何事长向别时圆？人有悲欢离合，月有阴睛圆缺，此事古难全。但愿人长久，千里共婵娟。', '晏几道', NULL, 1);
INSERT INTO `article` VALUES (99, '水龙吟', '次韵章质夫《杨花词》似花还似非花，也无人惜从教坠。抛家傍路，思量却是，无情有思。萦损柔肠，困酣娇眼，欲开还闭。梦随风万里，寻郎去处，又还被莺呼起。不恨此花飞尽，恨西园、落红难缀。晓还雨过，遗踪何在？一池萍碎。春色三分，二分尘土，一分流水。细看来不是杨花，点点是离人泪。', '苏轼', NULL, 1);
INSERT INTO `article` VALUES (100, '永遇乐', '彭城夜宿燕子楼，梦盼盼，因作此词明月如霜，好风如水，清景无限。曲港跳鱼，圆荷泻璐，寂寞无人见。紞如三鼓，铿然一叶，黯黯梦云惊断。夜茫茫、重寻无处，觉来小园行遍。天涯倦客,山中归路,望断故园心眼。燕子楼空，佳人何在？空锁楼中燕。古今如梦，何曾梦觉，但有旧欢新怨。异时对、黄楼夜景，为余浩叹。', '苏轼', NULL, 1);
INSERT INTO `article` VALUES (101, '洞仙歌', '冰肌玉骨，自清凉无汗。水殿风来暗香满。绣帘开，一点明月窥人，人未寝，倚枕钗横鬓乱。　起来携素手，庭户无声，时见疏星渡河汉。试问夜如何？夜已三更。金波淡，玉绳低转。但屈指西风几时来？又不道流年暗中偷换。', '苏轼', NULL, 1);
INSERT INTO `article` VALUES (102, '卜算子', '缺月挂疏桐，漏断人初静。　谁见幽人独住来，飘渺孤鸿影。惊起却回头，有恨无人省。　拣尽寒枝不肯栖，寂寞沙洲冷。', '苏轼', NULL, 1);
INSERT INTO `article` VALUES (103, '青玉案', '三年枕上吴中路，遣黄犬，随君去。若到松江呼小渡，莫惊鸳鹭，四桥尽是、老子经行处。辋川图上看春暮，常记高人右丞句。作个归期天定许，春衫犹是，小蛮针线，曾湿西湖雨。', '苏轼', NULL, 1);
INSERT INTO `article` VALUES (104, '临江仙', '夜饮东坡醒复醉,归来仿佛三更。家童鼻息已雷鸣,敲门都不应，倚杖听江声。长恨此身非我有,何时忘却营营。夜阑风静彀纹平,小舟从此逝，江海寄余生。', '苏轼', NULL, 1);
INSERT INTO `article` VALUES (105, '定风波', '莫听穿林打叶声，何妨吟啸且徐行。竹杖芒鞋轻胜马，谁怕！一蓑烟雨任平生。　料峭春风吹酒醒，微冷，山头斜照却相迎。回首向来萧瑟处，归去，也无风雨也无晴。', '苏轼', NULL, 1);
INSERT INTO `article` VALUES (106, '贺新郎', '乳燕飞华屋。悄无人、桐阴转午，晚凉新浴。手弄生绡白团扇，扇手一时似玉。渐困倚、孤眠清熟。帘外谁来推绣户？枉教人梦断瑶台曲。又却是，风敲竹。　石榴半吐红巾蹙。待浮花浪蕊都尽，伴君幽独。秾艳一枝细看取，芳心千重似束。又恐被、秋风惊绿。若待得君来向此，花前对酒不忍触。共粉泪，两簌簌。　', '苏轼', NULL, 1);
INSERT INTO `article` VALUES (107, '望海潮', '梅英疏淡，冰澌溶泄，东风暗换年华。金谷俊游，铜驼巷陌，新晴细履平沙。长忆误随车，正絮翻蝶舞，芳思交加。柳下桃蹊，乱分春色到人家。西园夜饮鸣笳。有华灯碍月，飞盖妨花。兰苑未空，行人渐老，重来是事堪喈。烟暝酒旗斜，但倚楼极目，时见栖鸦。无奈归心，暗随流水到天涯。　', '秦观', NULL, 1);
INSERT INTO `article` VALUES (108, '八六子', '倚危亭，恨如芳草，萋萋剗尽还生。念柳外青骢别后，水边红袂分时，怆然暗惊。无端天与娉婷。夜月一帘幽梦，春风十里柔情。　怎奈何欢娱渐随流水，素弦声断，翠绡香减；那堪片片，飞花弄晚。蒙蒙残雨笼晴。正销凝，黄鹂又啼数声。', '秦观', NULL, 1);
INSERT INTO `article` VALUES (109, '满庭芳', '山抹微云，天粘衰草，画角声断谯门。暂停征棹，聊共引离尊。多少蓬莱旧事，空回首，烟霭纷纷。斜阳外，寒鸦万点，流水绕孤村。　销魂、当此际，香囊暗解，罗带轻分。谩赢得青楼、薄倖名存。此去何时见也？襟袖上、空惹啼痕。伤情处，高楼望断，灯火已黄昏。　', '秦观', NULL, 1);
INSERT INTO `article` VALUES (110, '满庭芳', '晓色云开，春随人意，骤雨才过还晴。古台芳榭，飞燕蹴红英。舞困榆钱自落，秋千外、绿水桥平。东风里，朱门映柳，低按小秦筝。　多情。行乐处，珠钿翠盖，玉辔红樱。渐酒空金榼，花困蓬瀛。豆蔻梢头旧恨，十年梦、屈指堪惊。凭阑久，疏烟淡日，寂寞下芜城。　', '秦观', NULL, 1);
INSERT INTO `article` VALUES (111, '减安木兰花', '天涯旧恨，独自凄凉人不问。　欲见回肠，断尽金炉小篆香。黛蛾长敛，任是春风吹不展。　困倚危楼，过尽飞鸿字字愁。', '秦观', NULL, 1);
INSERT INTO `article` VALUES (112, '浣溪沙', '漠漠轻寒上小楼，晓阴无赖似穷秋，淡烟流水画屏幽。自在飞花轻似梦，无边丝雨细如愁，宝帘闲挂小银钩。', '秦观', NULL, 1);
INSERT INTO `article` VALUES (113, '阮郎归', '湘天风雨破寒初，深沉庭院虚。　丽谯吹罢小单于，迢迢清夜徂。乡梦断，旅魂孤，峥嵘岁又除。　衡阳犹有雁传书，郴阳和雁无。', '秦观', NULL, 1);
INSERT INTO `article` VALUES (114, '绿头鸭', '晚云收，淡天一片琉璃。烂银盘、来从海底，皓色千里澄辉。莹无尘、素娥淡伫,静可数、丹桂参差。玉露初零,金风未澶，一年无似此佳时。露坐久、疏萤时度，乌鹊正南飞。瑶台玲，阑干凭暖，欲下迟迟。　念佳人、音尘别后,对此应解相思。最关情漏声正永,暗断肠、花阴偷移。料得来宵，清光未减，阴睛天气又争知。共凝恋、如今别后，还是隔年期。人强健，清尊素影，长愿相随。　', '晁元礼', NULL, 1);
INSERT INTO `article` VALUES (115, '蝶恋花', '欲减罗衣寒未去,不卷珠帘,人在深深处。红杏枝头花几许？啼痕止恨清明雨。尽日沉烟香一缕,宿酒醒迟,恼破春情绪。飞燕又将归信误，小屏风上西江路。', '赵令畤', NULL, 1);
INSERT INTO `article` VALUES (116, '蝶恋花', '卷絮风头寒欲尽,坠粉飘香,日日红成阵。新酒又添残酒困，今春不减前春恨。蝶去莺飞无处问,隔水高楼,望断双鱼信。恼乱横波秋一寸，斜阳只与黄昏近。', '赵令畤', NULL, 1);
INSERT INTO `article` VALUES (117, '清平乐', '春风依旧，着意隋堤柳。搓得鹅几黄欲就，天气清明时候。去年紫陌青门，今宵雨云魂。断送一生憔悴，只消几个黄昏。', '赵令畤', NULL, 1);
INSERT INTO `article` VALUES (118, '水龙吟', '次韵林圣予《惜春》　问春何苦勿勿,带风伴雨如驰骤。幽葩细萼,小园低槛，壅培未就。吹尽繁红，占春长久，不如垂柳。算春长不老,人愁春老,愁只是、人间有。春恨十常八九，忍轻孤、芳醪经口。那知自是、桃花结子，不因春瘦。世上功名，老来风味，春归时候。最多情犹有，尊前青眼，相逢依旧。', '晁补之', NULL, 1);
INSERT INTO `article` VALUES (119, '夜游宫', '叶下斜阳照水，卷轻浪、沉沉千里。桥上酸风射眸子。立多时，看黄昏灯火市。　古屋寒窗底，听几片、井桐飞坠。不恋单衾再三起。有谁知，为萧娘书一纸？', '周邦彦', NULL, 1);
INSERT INTO `article` VALUES (120, '青玉案', '凌波不过横塘路，但目送、芳尘去。锦瑟华年谁与度？月桥花院，琐窗朱户，只有春知处。飞云冉冉蘅皋暮，彩笔新题断肠句。试问闲愁都几许？一川烟草，满城风絮，梅子黄时雨。', '贺铸', NULL, 1);
INSERT INTO `article` VALUES (122, '感皇恩', '淡妆多态，更的的、频回眄睐。便认得琴心先许，欲绾合双带。记画堂、风月逢迎，轻颦浅笑娇无奈。向睡鸭炉边，翔鸳屏里，羞把香罗暗解。　自过了烧灯后，都不见踏青挑菜。几回凭双燕，丁宁深意，往来却恨重帘碍。约何时再，正春浓酒困，人闲昼永无聊赖。厌厌睡起，犹有花梢日在。　', '贺铸', NULL, 1);
INSERT INTO `article` VALUES (123, '浣溪沙', '不信芳春厌老人，老人几度送馀春，惜春行乐莫辞频。巧笑艳歌皆我意，恼花颠酒拼君瞋，物情惟有醉中真。', '贺铸', NULL, 1);
INSERT INTO `article` VALUES (124, '浣溪沙', '楼角初消一缕霞，淡黄杨柳暗栖鸦，玉人和月摘梅花。笑捻粉香归洞户，更垂帘幕护窗纱，东风寒似夜来些。', '贺铸', NULL, 1);
INSERT INTO `article` VALUES (125, '石州慢', '薄雨收寒，斜照弄晴，春意空阔。长亭柳色才黄，远客一枝先折。烟横水漫，映带几点归鸿，平沙消尽龙沙雪。犹记出关来，恰而今时节。　将发，画楼芳酒，红泪清歌，便成轻别。回首经年，杳杳音尘都绝。欲知方寸，共有几许新愁？芭蕉不展丁香结。枉望断天涯，两厌厌风月。', '贺铸', NULL, 1);
INSERT INTO `article` VALUES (126, '蝶恋花', '几许伤春春复暮,杨柳清阴,偏碍游丝度。天际小山桃叶步，白蘋花满湔裙处。竟日微吟长短句,帘影灯昏,心寄胡琴语。数点雨声风约住，朦胧淡月云来去。', '贺铸', NULL, 1);
INSERT INTO `article` VALUES (128, '天门谣', '烟络横林，山沉远照，迤逦黄昏钟鼓。烛映帘栊，蛩催机杼，共苦清秋风露。不眠思妇，齐应和、几声砧杵。惊动天涯倦宦，锓囗囗岁华行暮。　当年酒狂自负，谓东君、以春相付。流浪征骖北道，客樯南浦，幽恨无人晤语。赖明月曾知旧游处，好伴云来，还将梦去。', '贺铸', NULL, 1);
INSERT INTO `article` VALUES (129, '望湘人', '厌莺声到枕，花气动帘，醉魂愁相半。被惜余熏，带惊剩眼，几许伤春春晚。泪竹痕鲜，佩兰香老，湘天浓暖。记小江风月佳时，屡约非烟游伴。须信鸾弦易断，奈云和再鼓，曲中人远。认罗袜无踪，旧处弄波清浅。青翰棹舣，白蘋洲畔，尽目临皋飞观。不解寄、一字相思，幸有归来双燕。', '贺铸', NULL, 1);
INSERT INTO `article` VALUES (130, '绿头鸭', '玉人家，画楼珠箔临津。托微风彩箫流怨，断肠马上曾闻。宴堂开、艳妆丝里，调琴思、认歌颦。麝蜡烟浓，玉莲漏短，更衣不待酒初醺。绣屏掩、枕鸳相就，香气渐暾暾。回廊影、疏钟淡月，几许消魂？翠钗分、银笺封泪，舞鞋从此生尘。任兰舟载将离恨，转南浦、北西醺。记取明年，蔷薇谢后，佳期应未误行云。凤城远、楚梅香嫩，先寄一枝春。青门外，只凭芳草，寻访郎君。　', '贺铸', NULL, 1);
INSERT INTO `article` VALUES (131, '石州慢', '寒水依痕，春意渐回，沙际烟阔。溪梅睛照生香，冷蕊数枝争发。天涯旧恨，试看几许消魂？长亭门外山重叠。不尽眼中青，是愁来时节。　情切，画楼深闭，想见东风，暗消肌雪。孤负枕前云雨，尊前花月。心期切处，更有多少凄凉，殷勤留在归时说。到得再相逢，恰经年离别。', '张元干', NULL, 1);
INSERT INTO `article` VALUES (132, '兰陵王', '卷珠箔，朝雨轻阴乍阁。阑干外、烟柳弄睛，芳草侵阶映红药。东风妒花恶，吹落梢头嫩萼。屏山掩、沉水倦熏，中酒心情怯杯勺。寻思旧京洛，正年少疏狂，歌笑迷著。障泥油壁催梳掠，曾驰道同载，上林携手，灯夜初过早共约，又争信飘泊。寂寞，念行乐。甚粉淡衣襟，音断弦索，琼枝壁月春如昨。怅别华表，那回双鹤。相思除是，向醉里、暂忘却。　', '张元干', NULL, 1);
INSERT INTO `article` VALUES (133, '贺新郎', '睡起流莺语，掩苍苔，房栊向晚，乱红无数。吹尽残花无人见，惟有垂杨自舞。渐暖霭、初回轻暑，宝扇重寻明月影，暗尘侵、上有乘鸾女。惊旧恨，遽如许。江南梦断横江渚。浪粘天、葡萄涨绿，半空烟雨。无限楼前沧波意，谁采频花寄取？但怅望、兰舟容与，万里云帆何时到？送孤鸿、目断千山阻。谁为我，唱金缕。', '叶梦得', NULL, 1);
INSERT INTO `article` VALUES (134, '虞美人', '落花已作风前舞，又送黄昏雨。晓来庭院半残红，惟有游丝，千丈袅晴空。殷勤花下同携手，更尽杯中酒。美人不用敛蛾眉，我亦多情，无奈酒阑时。', '叶梦得', NULL, 1);
INSERT INTO `article` VALUES (135, '点绛唇', '新月娟娟，夜寒江静山衔斗。起来搔首，梅影横窗瘦。好个霜天，闲却传杯手。君知否？乱鸦啼后，归兴浓如酒。', '汪藻', NULL, 1);
INSERT INTO `article` VALUES (136, '喜迁莺', '晓光催角，听宿鸟未惊，邻鸡先觉。迤逦烟村，马嘶人起，残月尚穿林薄。泪痕带霜微凝，酒力冲寒犹弱。叹倦客，悄不禁重染，风尘京洛。　追念人别后，心事万重，难觅孤鸿托。翠幌娇深，曲屏香暖，争念岁华飘泊。怨月恨花烦恼，不是不曾经著。者情味、望一成消减，新来还恶。　', '刘一生', NULL, 1);
INSERT INTO `article` VALUES (137, '高阳台·除夜', '频听银签，重然绛蜡，年华衮衮惊心。饯旧迎新，能消几刻光阴？老来可惯通宵饮？待不眠、还怕寒侵。掩清尊、多谢梅花，伴我微吟。邻娃已试春妆了，更蜂腰簇翠，燕股横金。句引东风，也知芳思难禁。朱颜那有年年好，逞艳游、赢取如今。恣登临、残雪楼台，迟日园林。', '韩疁', NULL, 1);
INSERT INTO `article` VALUES (138, '汉宫春', '潇洒江梅,向竹梢疏处,横两三枝。东君也不爱惜，雪压霜欺。无情燕子，怕春寒、轻失花期。却是有、年年塞雁，归来曾见开时。　清浅小溪如练，问玉堂何似，茅舍疏篱？伤心故人去后、冷落新诗。微云淡月，对江天、分付他谁。空白忆、清香未减，风流不在人知。', '李邴', NULL, 1);
INSERT INTO `article` VALUES (139, '临江仙', '高咏楚词酬午日，天涯节序匆匆。榴花不似舞裙红,无人知此意,歌罢满帘风。万事一身伤老矣，戎葵凝笑墙东。酒杯深浅去年同,试浇桥下水,今夕到湘中。', '陈与义', NULL, 1);
INSERT INTO `article` VALUES (140, '临江仙', '忆昔午桥桥上饮,坐中多是豪英。长沟流月去无声，杏花疏影里,吹笛到天明。二十馀年如一梦,此身虽在堪惊，头登小阁看新晴。古今多少事,渔唱起三更。', '陈与义', NULL, 1);
INSERT INTO `article` VALUES (141, '苏武慢', '雁落平沙，烟笼寒冰，古垒鸣笳声断。青山隐隐，败叶萧萧，天际暝鸦零乱。楼上黄昏,片帆千里归程,年华将晚。望碧云空暮，佳人何处？梦魂俱远。忆旧游，邃馆朱扉，小园香径，尚想桃花人面。书盈锦轴，恨满金徽，难写寸心幽怨。两地离愁，一尊芳酒，凄凉危栏倚遍。尽迟留，凭仗西风，吹干泪眼。', '蔡伸', NULL, 1);
INSERT INTO `article` VALUES (142, '柳梢青', '数声鶗鳺，可怜又是、春归时节。满面东风，海棠铺绣，梨花飘雪。丁香露泣残枝，算未比、愁肠寸结。自是休文，多情多感，不干风月。', '蔡伸', NULL, 1);
INSERT INTO `article` VALUES (143, '鹧鸪天', '一点残红欲尽时，乍凉秋气满屏帏。梧桐叶上三更雨，叶叶声声是别离。调宝瑟，拨金猊，那时同唱鹧鸪词。如今风雨西楼夜，不听清歌也垂泪。', '周紫芝', NULL, 1);
INSERT INTO `article` VALUES (144, '踏沙行', '情似游丝，人如飞絮，泪珠阁定空相觑。一溪烟柳万丝垂，无因系得兰舟住。烟过斜阳，草迷烟渚，如今已是愁无数。明朝且做莫思量，如何过得今宵去。', '周紫芝', NULL, 1);
INSERT INTO `article` VALUES (145, '帝台春', '芳草碧色，萋萋便南陌。暖絮乱红，也似知人，春愁无力。忆得——盈盈拾发言翠侣，共携赏、凤城寒食。　到今来，海角逢春，天涯为客。愁旋释，还似织；泪暗则，而今已拚了，忘则怎生便忘得。又还问鳞鸿，试重寻消息。', '李甲', NULL, 1);
INSERT INTO `article` VALUES (146, '忆王孙', '萋萋芳草忆王孙，柳外楼高空断魂，杜宇声声不忍闻。欲黄昏，雨打梨花深闭门。', '李重元', NULL, 1);
INSERT INTO `article` VALUES (147, '忆秦娥', '千里草，萋萋尽处遥山小。遥山小，行人远似，此山多少？天若有情天亦老，此情说便说不了。说不了，一声唤起，又惊春晓。', '万俟咏', NULL, 1);
INSERT INTO `article` VALUES (148, '三台', '见梨花初带夜月，海棠半含朝雨。内苑春、不禁过青门，御沟涨、潜通南浦。东风静，细柳垂金缕，望凤阙非烟非雾。好时代、朝野多欢，遍九陌、太平箫鼓。乍莺儿百啭断续，燕子飞来飞去。近绿水、台榭映秋千，斗草聚、双双游女。饧香更、酒冷踏青路；会暗识、夭桃朱户。向晚骤、宝马雕鞍，醉襟惹、乱花飞絮。正轻寒轻暖漏水，半阴半晴云暮。禁火天、已是试新妆，岁华到、三分佳处。清明看、汉蜡传宫炬，散翠烟、飞入槐府。敛兵卫、阊阖门开，住传宣、又还休务。', '万俟咏', NULL, 1);
INSERT INTO `article` VALUES (149, '二郎神', '闷来弹鹊，又搅碎、一帘花影。漫试著春衫，还思纤手，熏彻金猊烬冷。动是愁端如何向？但怪得新来多病。嗟旧日沈腰，如今潘鬓，怎堪临镜？重省别时泪湿，罗衣犹凝。料为我厌厌，日高慵起，账托春醒未醒。雁足不来，马蹄难驻，门掩一庭芳景。空伫立，尽日阑干，倚遍昼长人静。　', '徐伸', NULL, 1);
INSERT INTO `article` VALUES (150, '江神子慢', '玉台挂秋月，铅素浅、梅花傅香雪。冰姿洁，金莲衬、小小凌波罗袜。雨初歇，楼外孤鸿声渐远，远山外行人音信绝。此恨对语犹难，那堪更寄书说。教人红消翠减，觉衣宽金缕，都为轻别。太情切，消魂处画角黄昏时节。声呜咽。落尽庭花草，春去也。银蟾迥，无情圆又缺。恨伊不似馀香，惹鸳鸯结。', '田为', NULL, 1);
INSERT INTO `article` VALUES (151, '蓦山溪', '洗妆真态，不作铅花御。竹外一枝斜，想佳人、天寒日暮。黄昏院落，无处着清香，风细细，雪垂垂，何况江头路。　月边疏影，梦到消魂处。结子欲黄时，又须作、廉纤细雨。孤芳一世，供断有情愁，消瘦损，东阳也，试问花知否？　', '曹组', NULL, 1);
INSERT INTO `article` VALUES (152, '蓦山溪', '浮烟冷雨，今日还重九。秋去又秋来，但黄花、年年如旧。平台戏马，无处问英雄；茅舍底，竹篱东，伫立时搔首。　客来何有？草草三杯酒。一醉万缘空，莫贪伊、金印如斗。病翁老矣，谁共赋归来？芟垅麦，网溪鱼，未落他人后。　', '间子翚', NULL, 1);
INSERT INTO `article` VALUES (153, '贺新郎', '篆缕消金鼎。醉沉沉、庭阴转午，画堂人静。芳草王孙知何处？惟有杨花糁径。渐玉枕、腾腾春醒，帘外残红春已透，镇无聊、殢酒厌厌病。云鬓乱，未忺整。江南旧事休重省，遍天涯寻消问息，断鸿难倩。月满西楼凭阑久，依旧归期未定。又只恐、瓶沉金井。嘶骑不来银烛暗，枉教人立尽梧桐影。谁伴我，对鸾镜。', '李玉', NULL, 1);
INSERT INTO `article` VALUES (155, '烛影摇红', '青楼春晚，昼寂寂、梳匀又懒。乍听得、鸦啼莺哢，惹起新愁无限。记年时偷掷春心，花前隔雾遥相见。便角枕题诗，宝钗贳酒，共醉青苔深院。怎忘得、回廊下，携手处、花明月满。如今但暮雨，蜂愁蝶恨，小窗闲对芭蕉展。却谁拘管？尽无言、闲品秦筝，泪满参差雁。腰肢渐小，心与杨花共远。　', '廖世美', NULL, 1);
INSERT INTO `article` VALUES (156, '南浦', '风悲画角，听单于、三弄落谯门。投宿囗囗征骑，飞雪满孤村。酒市渐阑灯火，正敲窗、乱叶舞纷纷。送数声惊雁，乍离烟水，嘹唳度寒云。好在半胧淡月，到如今、无处不消魂。故国梅花归梦，愁损绿罗裙。为问暗香闲艳，也相思、万点付啼痕。算翠屏应是，两眉馀恨倚黄昏。　', '鲁逸仲', NULL, 1);
INSERT INTO `article` VALUES (157, '满江红', '怒发冲寇，凭阑处、潇潇雨歇。抬望眼、仰天长啸，壮怀激烈。三十功名尘与土，八千里路云和月。莫等闲、白了少年头，空悲切。靖康耻，犹未雪；臣子憾，何时灭。驾长车踏破、贺兰山缺。壮志饥餐胡虏肉，笑谈渴饮匈奴血。待从头、收拾旧山河，朝天阙。　', '岳飞', NULL, 1);
INSERT INTO `article` VALUES (158, '烛影摇红', '双阙中天，凤楼十二春寒浅。去年元夜奉宸游，曾侍瑶池宴。玉殿珠帘尽卷，拥群仙、蓬壶阆苑。五云深处，万烛光中，揭天丝管。驰隙流年，恍如一瞬星霜换。今宵谁念泣孤臣，回首长安远。可是尘缘未断，漫惆怅、华胥梦短。满怀幽恨，数点寒灯，几声归雁。', '张抡', NULL, 1);
INSERT INTO `article` VALUES (159, '水龙吟', '夜来风雨匆匆，故园定是花无几。愁多怨极，等闲孤负，一年芳意。柳困桃慵，杏青梅小，对人容易。算好春长在，好花长见，原只是、人憔悴。回首池南旧事，恨星星、不堪重记。如今但有，看花老眼，伤时清泪。不怕逢花瘦，只愁怕、老来风味。待繁红乱处，留云借月，也须拚醉。', '程垓', NULL, 1);
INSERT INTO `article` VALUES (160, '六州歌头', '　长淮望断，关塞莽然平。征尘暗，霜风劲，悄边声。黯消凝，追想当年事，殆天数，非人力；洙泗上，弦歌地，亦膻腥。隔水毡乡，落日牛羊下，区脱纵横。看名王宵猎，骑火一川明，笳鼓悲鸣，遣人惊。　　念腰间箭，匣中剑，空埃蠹，竟何成！时易失，心徒壮，岁将零，渺神京。干羽方怀远，静烽燧，且休兵。冠盖使，纷驰鹜，若为情？闻道中原遗老，常南望翠葆霓旌。使行人到此，忠愤气填膺，有泪如倾。', '张孝祥', NULL, 1);
INSERT INTO `article` VALUES (161, '念奴娇', '洞庭青草，近中秋、更无一点风色。玉界琼田三万顷，著我扁舟一叶。素月分辉，银河共影，表里俱澄澈。怡然心会，妙处难与君说。　应念岭海经年,孤光自照,肝胆皆冰雪。短发萧骚襟袖冷，稳泛沧浪空阔。尽挹西江，细斟北斗，万象为宾客。扣舷独啸，不知今夕何夕！', '张孝祥', NULL, 1);
INSERT INTO `article` VALUES (162, '六州歌头', '东风著意，先上小桃枝。红粉腻，娇如醉，倚朱扉。记年时，隐映新生妆面，临水岸，春将半，云日暖，斜桥转，夹城西。草软莎平，跋马垂杨渡，玉勒争嘶。认蛾眉，凝笑脸，薄拂燕脂，绣户曾窥，恨依依。共携手处，香如雾，红随步，怨春迟。消瘦损，凭谁问？只花知，泪空垂。旧日堂前燕，和烟雨又双飞。人自老，春长暮霭，目断武陵溪，往事难追。', '韩元吉', NULL, 1);
INSERT INTO `article` VALUES (163, '好事近', '凝碧旧池头，一听管弦凄切。多少梨园声在，总不堪华发。杏花无处避春愁，也傍野烟发。惟有御沟声断，似知人呜咽。', '韩元吉', NULL, 1);
INSERT INTO `article` VALUES (164, '瑞鹤仙', '郊原初过雨，风数叶零乱，风定犹舞。斜阳挂深树，映浓愁浅黛。遥山媚妩。来时旧路，尚岩花、娇黄半吐。到今日惟有、溪边流水，见人如故。无语，邮亭深静，下马还寻，旧曾题处。无聊倦旅，伤离恨，最愁苦。纵收香藏镜，他年重到，人面桃花在否？念沉沉小阁幽窗，有时梦去。', '袁去华', NULL, 1);
INSERT INTO `article` VALUES (165, '剑器近', '夜来雨，赖倩得东风吹住。海棠正妖绕处，且留取。悄庭户，试细听莺啼燕语，分明共人愁绪，怕春去。佳树翠阴初转午。重帘未卷，乍睡起，寂寞看风絮。偷弹清泪寄烟波，见江头故人，为言憔悴如许。彩笺无数，去却寒暄，到了浑无定据。断肠落日千山暮。　', '袁去华', NULL, 1);
INSERT INTO `article` VALUES (166, '安公子', '弱柳千丝缕，嫩黄匀遍鸦啼处。寒入罗衣春尚浅，过一番风雨。问燕子来时，绿水桥边路，曾画楼、见个人人否？料静掩云窗，尘满哀弦危柱。　庚信愁如许，为谁都著眉端聚。独立东风弹泪眼，寄烟波东去。念永昼春闲，人倦如何度？闲傍枕、百转黄鹂语。唤觉来厌厌，残照依然花坞。　', '袁去华', NULL, 1);
INSERT INTO `article` VALUES (167, '瑞鹤仙', '脸霞红印枕，睡觉来、冠儿还是不整。屏闲麝煤冷，但眉峰压翠，泪珠弹粉。堂深昼永，燕交飞、风帘露井。恨无人说与，相思近日，带围宽尽。重省，残灯朱幌，淡月纱窗，那时风景。阳台路迥，云雨梦，便无准。待归来，先指花梢教看，欲把心期细问。问因循过了青春，怎生意稳？', '陆淞', NULL, 1);
INSERT INTO `article` VALUES (168, '卜算子', '驿外断桥边，寂寞开无主。已是黄昏独自愁，更著风和雨。无意苦争春，一任群芳妒。零落成泥碾作尘，只有香如故。', '陆游', NULL, 1);
INSERT INTO `article` VALUES (169, '水龙吟', '闹花深处楼台，画帘半卷东风软。春归翠陌，平莎茸嫩，垂杨金浅。迟日催花，淡云阁雨，轻寒轻暖。恨芳菲世界，游人未赏，都付与莺和燕。　寂寞凭高念远，向南楼、一声归雁。金钗斗草，青丝勒马，风流云散。罗绶分香，翠绡封泪，几多幽怨？正消魂又是，疏烟淡月，子规声断。', '陈亮', NULL, 1);
INSERT INTO `article` VALUES (170, '忆秦娥', '楼阴缺，阑于影卧东厢月。东厢月，一天风露，杏花如雪。隔烟催漏金叫咽，罗帏黯淡灯花结。灯花结，片时春梦，江南天阔。', '范成大', NULL, 1);
INSERT INTO `article` VALUES (171, '眼儿媚', '萍乡道中乍晴，卧舆中困甚，小憩柳塘。酣酣日脚紫烟浮，妍暖破轻裘。困人天色，醉人花气，午梦扶头。春慵恰似春塘水，一片彀纹愁。溶溶曳曳，东风无力，欲避还休。', '范成大', NULL, 1);
INSERT INTO `article` VALUES (173, '霜天晓角', '绿树听鹈鴂，更那堪、鹧鸪声住，杜鹃声切！啼到春归无寻处，苦恨芳菲都歇。算未抵人间离别：马上琵琶关塞黑，更长门、翠辇辞金阙。看燕燕，送归妾。　将军百战声名裂，向河梁、回头万里，故人长绝。易水萧萧西风冷，满座衣冠似雪，正壮士悲歌未徹。啼鸟还知如许恨，料不啼清泪长啼血。谁共我，醉明月！', '范成大', NULL, 1);
INSERT INTO `article` VALUES (174, '念奴娇', '野棠花落，又匆匆、过了清明时节。剗地东风期客梦，一枕云屏寒怯。曲岸持觞，垂杨系马，此地曾轻别。楼空人去，旧游飞燕能说。　闻道绮陌东头,行人长见,帘底纤纤月。旧恨春江流不尽，新恨云山千叠。料得明朝，尊前重见，镜花难折。也应惊问，近来多少华发？　', '辛弃疾', NULL, 1);
INSERT INTO `article` VALUES (175, '汉宫春·立春日', '春已归来,看美人头上,袅袅春幡。无端风雨，未肯收尽馀寒。年时燕子，料今宵梦到西园。浑未辨、黄柑荐酒，更传青韭堆盘。却笑东风,从此便熏梅染柳,更传些闲。闲时又来镜里，转变朱颜。清愁不断，问何人会解连环。生怕见、花开花落，朝来塞雁先还。', '辛弃疾', NULL, 1);
INSERT INTO `article` VALUES (176, '贺新郎', '凤尾龙香拔。自开元、霓裳曲罢，几番风月。最苦浔阳江头客，画舸亭亭待发。记出塞、黄云堆雪。马上离愁三万里，望昭阳、宫殿孤鸿没，弦解语，恨难说。辽阳驿使音尘绝，琐窗寒、轻拢慢捻，泪珠盈睫。推手含情还却手，一抹梁州哀彻。千古事云飞烟灭。贺老定场无消息，想沉香亭北繁华歇，弹到此，为呜咽。', '辛弃疾', NULL, 1);
INSERT INTO `article` VALUES (177, '水龙吟', '楚天千里清秋，水随天去秋无际。遥岑远目，献愁供恨，玉簪螺髻。落日楼头，断鸿声里，江南游子。把吴钩看了，阑干拍遍，无人会、登临意。休说鲈鱼堪脍，尽西风，季鹰归未？求田问舍，怕应羞见，刘郎才气。可惜流年，忧愁风雨，树犹如此。倩何人唤取，红巾翠袖，揾英雄泪！', '辛弃疾', NULL, 1);
INSERT INTO `article` VALUES (178, '摸鱼儿', '更能消、几番风雨，匆匆春又归去。惜春长怕花开早，何况落红无数。春且住！见说道天涯芳草无归路。怨春不语，算只有殷勤，画檐蛛网，终日惹飞絮。长门事,准拟佳期又误,蛾眉曾有人妒。千金纵买相如赋，脉脉此情谁诉？君莫舞！君不见、玉环飞燕皆尘土。闲愁最苦，休去倚危阑，斜阳正在，烟柳断肠处。', '辛弃疾', NULL, 1);
INSERT INTO `article` VALUES (179, '永遇乐', '千古江山，英雄无觅、孙仲谋处。舞榭歌台，风流总被、雨打风吹去。斜阳草树，寻常巷陌，人道寄奴曾住。想当年，金戈铁马，气吞万里如虎。元嘉草草,封狼居胥,赢得仓皇北顾。四十三年，望中犹记、灯火扬州路。可堪回首、佛狸祠下，一片神鸦社鼓。凭谁问，廉颇老矣，尚能饭否？　', '辛弃疾', NULL, 1);
INSERT INTO `article` VALUES (180, '木兰花慢', '老来情味减，对别酒，怯流年。况屈指中秋，十分好月，不照人圆。无情水，都不管，共西风、只管送归船。秋晚莼鲈江上，夜深儿女灯前。征衫，便好去朝天。玉殿正思贤。想夜半承明，留教视草，却遣筹边。长安故人问我，道愁肠殢酒只依然。目断秋霄落雁，醉来时响空弦。　', '辛弃疾', NULL, 1);
INSERT INTO `article` VALUES (181, '祝英台近·晚春', '宝钗分，桃叶渡，烟柳暗南浦。怕上层楼，十日九风雨。断肠片片飞红，都无人管，更谁劝、啼莺声住？髻边觑，应把花卜归期，才簪又重数。罗帐灯昏，哽咽梦中语。是他春带愁来，春归何处？却不解、带将愁去。　', '辛弃疾', NULL, 1);
INSERT INTO `article` VALUES (182, '青玉案·元夕', '东风夜放花千树，更吹落，星如雨。宝马雕车香满路，凤箫声动，玉壶光转，一夜鱼龙舞。蛾儿雪柳黄金缕，笑语盈盈暗香去。众里寻他千百度，蓦然回首，那人却在，灯火阑珊处。', '辛弃疾', NULL, 1);
INSERT INTO `article` VALUES (183, '鹧鸪天', '枕箪溪堂冷欲秋，断云依水晚来收。红莲相倚浑如醉，白鸟无言定自愁。书咄咄，且休休，一丘一壑也风流。不知筋力衰多少，但觉新来懒上楼。', '辛弃疾', NULL, 1);
INSERT INTO `article` VALUES (184, '菩萨蛮', '郁孤台下清江水，中间多少行人泪。西北是长安，可怜无数山。青山遮不住，毕竟东流去。江晚正愁余，山深闻鹧鸪。', '辛弃疾', NULL, 1);
INSERT INTO `article` VALUES (185, '点绛唇', '丁未冬，过吴松作燕雁无心，太湖西畔随云去。数峰清苦，商略黄昏雨。第四桥边，拟共天随住。今何许？凭栏怀古，残柳参差舞。', '姜夔', NULL, 1);
INSERT INTO `article` VALUES (186, '鹧鸪天', '肥水东流无尽期，当初不合种相思。梦中未比丹青见，暗里忽惊山鸟啼。春未绿，鬓先丝，人间别久不成悲。准教岁岁红莲夜，两处沉吟各自知。', '姜夔', NULL, 1);
INSERT INTO `article` VALUES (187, '踏莎行', '燕燕轻盈，莺莺娇软，分明又向华胥见。夜长争得薄情知？春初早被相识染。别后书辞，别时针线，离魂暗逐郎行远。淮南皓月冷千山，冥冥归去无人管。', '姜夔', NULL, 1);
INSERT INTO `article` VALUES (188, '庆宫春', '双桨莼波，一蓑松雨，暮愁渐满空阔。呼我盟鸥，翩翩欲下，背人还过木末。那回归去，荡云雪、孤舟夜发。伤心重见，依约眉山，黛痕低压。　采香径里春寒，老子婆娑，自歌谁答？垂虹西望，飘然引去，此兴平生难遏。酒醒波远，正凝想、明珰素袜。如今安在？惟有阑干，伴人一霎。　', '姜夔', NULL, 1);
INSERT INTO `article` VALUES (189, '齐天乐', '庚郎先自吟愁赋，凄凄更闻私语。露湿铜铺，苔侵石井，都是曾听伊处。正思妇无眠，起寻机杼。曲曲屏山，夜凉独自甚情绪？西窗又吹暗雨，为谁频断续，相和砧杵？候馆迎秋，离宫吊月，别有伤心无数。豳诗漫与，笑篱落呼灯，世间儿女。写入琴丝，一声声更苦。', '姜夔', NULL, 1);
INSERT INTO `article` VALUES (191, '琵琶仙', '芳莲坠粉，疏桐吹绿，庭院暗雨乍歇。无端抱影销魂处，还见篠墙萤暗，藓阶蛩切。送客重寻西去路，问水面、琵琶谁拔？最可惜一片江山，总付与啼鴂。长恨相逢未款，而今何事，又对西风离别？渚寒烟淡，棹移人远，漂渺行舟如叶。想文君望久，倚竹愁生步罗袜。归来后翠尊双饮,下了珠帘,玲珑闲看月。', '姜夔', NULL, 1);
INSERT INTO `article` VALUES (192, '念奴娇', '闹红一舸，记来时尝与鸳鸯为侣。三十六陂人未到，水佩风裳无数。翠叶吹凉，玉容销酒，更洒菰蒲雨。嫣然摇动，冷香飞上诗句。日暮青盖亭亭,情人不见,争忍凌波去？只恐舞衣寒易落，愁入西凤南浦。高柳垂阴，老鱼吹浪，留我花间住。田田多少，几回沙际归路。', '姜夔', NULL, 1);
INSERT INTO `article` VALUES (193, '扬州慢', '淮左名都，竹西佳处，解鞍少驻初程。过春风十里，尽荠麦青青。自胡马窥江去后，废池乔木，犹厌言兵。渐黄昏、清角吹寒，都在空城。　杜郎俊赏，算而今、重到须惊。纵豆蔻词工，青楼梦好，难赋深情。二十四桥仍在，波心荡、冷月无声。念桥边红药，年年知为谁生？　', '姜夔', NULL, 1);
INSERT INTO `article` VALUES (194, '长亭怨慢', '予颇喜自制曲，初率意为长短句，然后协以律，故前後阕多不同。桓大司马云「昔年种柳，依依汉南，今看摇落，凄怆江潭，树犹如此，人何以堪」。此语予深爱之。　渐吹尽，枝头香絮，是处人家，绿深门户。远浦萦回，暮帆零乱向何许？阅人多矣，谁得似长亭树？树若有情时，不会得青青如此！日暮，望高城不见，只见乱山无数。韦郎去也，怎忘得玉箫分付。第一是早早归来，怕红萼无人为主。算空有并刀，难剪离愁千缕。', '姜夔', NULL, 1);
INSERT INTO `article` VALUES (197, '淡黄柳', '苔枝缀玉，有翠禽小小，枝上同宿。客里相逢,篱角黄昏,无言自倚修竹。昭君不惯胡沙远，但暗忆、江南江北。想佩环、月夜归来，化作此花幽独。　犹记深宫旧事，那人正睡里，飞近蛾绿。莫似春风，不管盈盈，早与安排金屋。还教一片随波去，又却怨、玉龙哀曲。等恁时、重觅幽香，已入小窗横幅。', '姜夔', NULL, 1);
INSERT INTO `article` VALUES (198, '翠楼吟', '月冷龙沙，尘清虎落，今年汉酺初赐。新翻胡部曲，听毡幕元戎歌吹。层楼高峙，看槛曲萦红，檐牙飞翠。人姝丽，粉香吹下，夜寒风细。　此地宜有词仙，拥素云黄鹤，与君游戏。玉梯凝望久，但芳草萋萋千里。天涯情味，仗酒祓清愁，花消英气。西山外，晚来还卷，一帘秋霁。', '姜夔', NULL, 1);
INSERT INTO `article` VALUES (199, '杏花天', '绿丝低拂鸳鸯浦，想桃叶，当时唤渡。又将愁眼与春风,待去,倚兰桡更少驻。金陵路，莺吟燕舞。算潮水知人最苦。满汀芳草不成归,日暮,更移舟向甚处？', '姜夔', NULL, 1);
INSERT INTO `article` VALUES (200, '一萼红', '古城阴，有官梅几许，红萼未宜簪。池面冰胶,墙腰雪老,云意还又沉沉。翠藤共闲穿径竹，渐笑语、惊起卧沙禽。野老林泉，故王台榭，呼唤登临。　南去北来何事，荡湘云楚水，目极伤心。朱户黏鸡，金盘簇燕，空叹时序侵寻。记曾共西楼雅集，想垂柳、还袅万丝金。待得归鞍到时，只怕春深。　', '姜夔', NULL, 1);
INSERT INTO `article` VALUES (201, '霓裳中序第一', '亭皋正望极，乱落江莲归未得。多病却无气力，况纨扇渐疏，罗衣初索。流光过隙，叹杏梁双燕如客。　人何在？一帘淡月，仿佛照颜色。　幽寂，乱蛩吟壁，动庚信清愁似织。沉思年少浪迹,笛里关山,柳下坊陌。坠红无信息，漫暗水、涓涓溜碧。飘零久，而今何意，醉卧酒垆侧！', '姜夔', NULL, 1);
INSERT INTO `article` VALUES (202, '小重山', '柳暗花明春事深,小阑红芍药,已抽簪。雨馀风软碎鸣禽,迟迟日,犹带一分阴。往事莫沉吟，身闲时序好、且凳临。旧游无处不堪寻，无寻处、惟有少年心。', '章良能', NULL, 1);
INSERT INTO `article` VALUES (203, '唐多令', '芦叶满汀洲，寒沙带浅流。二十年重过南楼。柳下系船犹未稳，能几日，又中秋。黄鹤断矶头，故人曾到否？旧江山浑是新愁。欲买桂花同载酒，终不似，少年游。', '刘过', NULL, 1);
INSERT INTO `article` VALUES (204, '木兰花', '春风只在园西畔，荠菜花繁胡蝶乱。冰池晴绿照还空，香径落红吹已断。意长翻恨游丝短，尽日相思罗带缓。宝奁如月不欺人，明日归来君试看。', '严仁', NULL, 1);
INSERT INTO `article` VALUES (205, '风入松', '一春长费买花钱，日日醉湖边。玉骢惯识西湖路，骄嘶过、沽酒楼前。红杏香中箫鼓，绿杨影里秋千。暖风十里丽人天，花压鬓云偏。画船载取春归去，余情付、湖水湖烟。明日重扶残醉，来寻陌上花钿。', '俞国宝', NULL, 1);
INSERT INTO `article` VALUES (206, '满庭芳', '月洗高梧，露溥幽草，宝钗楼外秋深。土花沿翠，萤火坠墙阴。静听寒声断续，微韵转，凄咽悲沉。争求侣、殷勤劝织，促破晓机心。　儿时曾记得，呼灯灌穴，敛步随音。任满身花影，独自追寻。携向华堂戏斗。亭台小笼巧妆金。今休说，从渠床下，凉夜伴孤吟。　', '张镃', NULL, 1);
INSERT INTO `article` VALUES (207, '宴山亭', '幽梦初回，重阴未开，晓色催成疏雨。竹槛气寒，蕙畹声摇，新绿暗通南浦。未有人行，才半启回廊朱户。无绪，空望极霓旌，锦书难据。　苔径追忆曾游，念谁伴秋千，彩绳芳柱。犀帘黛卷，凤枕云孤，应也几番凝伫。怎得伊来，花雾绕、小堂深处。留住，直到老不教归去。', '张镃', NULL, 1);
INSERT INTO `article` VALUES (208, '绮罗香', '做冷欺花，将烟困柳，千里偷催春暮。尽日冥迷，愁里欲飞还住。惊粉重蝶宿西园，喜泥润、燕归南浦。最妨她佳约风流，钿车不到杜陵路。沉沉江上望极，还被春潮晚急，难寻官渡。隐约遥峰，和泪谢娘眉妩。临断岸、新绿生时，是落红、带愁流处。记当日门掩梨花，剪灯深夜语。　', '史达祖', NULL, 1);
INSERT INTO `article` VALUES (209, '双双燕', '过春社了，度帘幕中间，去年尘冷。差池欲住，试入旧巢相并。还相雕梁藻井，又软语商量不定。飘然快拂花梢。翠尾分开红影。　芳径，芹泥雨润。爱贴地争飞，竞夸轻俊。红楼归晚，看足柳昏花暝。应自栖香正稳，便忘了天涯芳信。愁损翠黛双蛾，日日画阑独凭。', '史达祖', NULL, 1);
INSERT INTO `article` VALUES (210, '东风第一枝', '巧沁兰心，偷粘草甲，东风欲障新暖。漫疑碧瓦难留，信知暮寒犹浅。行天入境，做弄出、轻松纤软。料故园、不卷重帘，误了乍来双燕。　青未了、柳回白眼，红欲断、杏开素面。旧游忆著山阴，后盟遂妨上苑。寒炉重熨，便放漫春衫针线。怕凤靴挑菜归来，万一灞桥相见。', '史达祖', NULL, 1);
INSERT INTO `article` VALUES (211, '喜迁莺', '月波疑滴，望玉壶天近，了无尘隔。翠眼圈花,冰丝织练,黄道宝光相直。自怜诗酒瘦，难应接许多春色。最无赖，是随香趁烛，曾伴狂客。　踪迹，漫记忆，老了杜郎，忍听东风笛。柳院灯疏，梅厅雪在，谁与细倾克碧？旧情拘未定，犹自学当年游历，怕万一，误玉人夜寒帘隙。　', '史达祖', NULL, 1);
INSERT INTO `article` VALUES (212, '三姝媚', '烟光摇缥瓦，望晴檐多风，柳花如洒。锦瑟横床,想泪痕尘影,凤弦常下。倦出犀帷，频梦见、王孙骄马。讳道相思，偷理绡衩。惆怅南楼遥夜，记翠箔张灯，枕肩歌罢。又入铜驼，遍旧家门巷，首询声价。可惜东风，将恨与闲花俱谢。记取崔徵模样。归来暗写。　', '史达祖', NULL, 1);
INSERT INTO `article` VALUES (213, '秋霁', '江水苍苍，望倦柳愁荷，共感秋色。废阁先凉,古帘空暮,雁程最嫌风力。故园信息，爱渠入眼南山碧。念上国，谁是、脍鲈江汉未归客。还又岁晚、瘦骨临风，夜闻秋声，吹动岑寂。露蛩悲、青灯冷屋，翻书愁上鬓毛白。年少俊游浑断得，但可怜处，无奈苒苒魂惊,采香南浦,剪梅烟驿。', '史达祖', NULL, 1);
INSERT INTO `article` VALUES (214, '夜合花', '柳锁莺魂,花翻蝶梦,自知愁染潘郎。轻衫未揽，犹将泪点偷藏。忘前事，怯流光，早春窥、酥雨池塘。向消凝里，梅开半面，情满徐妆。风丝一寸柔肠，曾在歌边惹恨，烛底萦香。芳机瑞锦，如何未织鸳鸯。人扶醉，月依墙，是当初、谁敢疏狂！把闲言语，花房夜久，各自思量。　', '史达祖', NULL, 1);
INSERT INTO `article` VALUES (216, '玉胡蝶', '秋江带雨，寒沙萦水，人瞰画阁愁独。烟蓑散响惊诗思，还被乱鸥飞去，秀句难续。冷眼尽归图画上,认隔岸微茫云屋；想半属渔市樵村,欲暮竞然竹。须信风流未老，凭持尊酒，慰此凄凉心目。一鞭南陌，几篙官渡，赖有歌眉舒绿。只匆匆残照，早觉闲愁挂乔木。应难奈故人天际，望彻淮山，相思无雁足。', '史达祖', NULL, 1);
INSERT INTO `article` VALUES (217, '生查子', '繁灯夺霁华，戏鼓侵明发。物色旧时同，情味中年别。浅画镜中眉，深拜楼中月。人散市声收，渐入愁时节。', '刘克庄', NULL, 1);
INSERT INTO `article` VALUES (218, '贺新郎', '深院榴花吐，画帘开、綀衣纨扇，午风清暑。儿女纷纷夸结束，新样钗符艾虎。早已有游人观渡。老大逢场慵作戏，任陌头年少争旗鼓，溪雨急，浪花舞。灵均标致高如许，忆生平、既纫兰佩，更怀椒醑。谁信骚魂千载后，波底垂涎角黍。又说是、蛟馋龙怒。把似而今醒到了，料当年，醉死差无苦，聊一笑。', '刘克庄', NULL, 1);
INSERT INTO `article` VALUES (219, '贺新郎', '湛湛长空黑，更那堪、斜风细雨，乱愁如织。老眼平生空四海，赖有高楼百尺。看浩荡千崖秋色。白发书生神州泪，尽凄凉不向牛山滴。追往事，去无迹。少年自负凌云笔，到而今春华落尽，满怀萧瑟。常恨世人新意少，爱说南朝狂客。把破帽年年拈出。若对黄花孤负酒,怕黄花也笑人岑寂。鸿去北,日西匿。', '刘克庄', NULL, 1);
INSERT INTO `article` VALUES (220, '木兰花', '年年跃马长安市，客舍似家家似寄。青钱换酒日无何，红烛呼卢宵不寐。易挑锦妇机中字，难得玉人心下事。男儿西北有神州，莫滴水西桥畔泪。', '刘克庄', NULL, 1);
INSERT INTO `article` VALUES (221, '江城子', '画楼帘暮卷新晴，掩银屏，晓寒轻。坠粉飘香，日日唤愁生。暗数十年湖上路，能几度，着娉婷。　年华空自感漂零，拥春醒？天阔云闲，无处觅萧声。载酒买花年少事，浑不似、旧心情。　', '卢祖皋', NULL, 1);
INSERT INTO `article` VALUES (222, '宴清都', '春讯飞琼管，风日薄，度墙啼鸟声乱。江城次第，笙歌翠合，绮罗香暖。溶溶涧渌冰泮，醉梦里，年华暗换。料黛眉，重锁隋堤，芳心还动梁苑。新来雁阔云音，鸾分鉴影，无计重见。春啼细雨，笼愁淡月，恁时庭院。离肠未语先断，算犹有凭高望眼。更那堪衰草边天，飞梅弄晚。', '卢祖皋', NULL, 1);
INSERT INTO `article` VALUES (223, '南乡子', '生怕倚阑干，阁下溪声阁外山。惟有旧时山共水，依然，暮雨朝云去不还。应是蹑飞鸾，月下时时整佩环。月又渐低霜又下，更阑，折得梅花独自看。', '潘牥', NULL, 1);
INSERT INTO `article` VALUES (224, '瑞鹤仙', '湿云粘雁影，望征唱片，愁迷离绪难整。千金买光景，但疏钟催晓，乱鸦啼螟。花悰暗省，许多情，相逢梦境。便行云都不归来，也合寄将音信。孤迥。盟鸾心在，跨鹤程高，后期无准。情丝等剪，翻若得旧时恨。怕天教何处，参差双燕，还染残朱剩粉。对菱花与说相思，看谁瘦损？', '陆壑', NULL, 1);
INSERT INTO `article` VALUES (225, '渡江云', '羞经颦浅恨，晚风未落，片绣点重茵。旧堤分燕尾，桂棹轻鸥，宝勒倚残云。千丝怨碧，渐路入、仙坞迷津。肠漫回，隔花时见、背面楚腰身。逡巡，题门惆怅，堕履牵萦。数幽期难准，还始觉留情缘眼，宽带因春。明朝事与孤冷，做满湖风雨愁人。山黛暝，尘波澹绿无痕。', '吴文英', NULL, 1);
INSERT INTO `article` VALUES (226, '夜合花', '白鹤江入京，泊葑门，有感。柳暝河桥,莺清台苑,短策频惹春香。当时夜泊，温柔便入深乡。词韵窄，酒杯长，剪蜡花、壶箭催忙。共追游处，凌波翠陌，连棹横塘。十年一梦凄凉，似西湖燕去，吴馆巢荒。重来万感，依前唤酒银罂。溪雨急，岸花狂，趁残鸦飞过苍茫。故人楼上，凭谁指与芳草斜阳？', '吴文英', NULL, 1);
INSERT INTO `article` VALUES (227, '霜叶飞', '断烟离绪，关心事，斜阳红隐霜树。半壶秋水荐黄花，香噀西风雨。纵玉勒、轻飞迅羽，凄凉谁吊荒台古。记醉踏南屏，彩扇咽寒蝉，倦梦不知蛮素。聊对旧节传杯，尘管，断阕经岁慵赋。小蟾斜影转东篱，夜冷残蛩语。早白发、缘愁万缕，惊从卷乌纱去，漫细将、茱萸看，但约明年，翠微高处。　', '吴文英', NULL, 1);
INSERT INTO `article` VALUES (228, '宴清都', '绣幄鸳鸯柱，红情密、腻云低护秦树。芳草兼倚，花梢钿合，锦屏人妒。东风睡足交枝，正梦枕瑶钗燕股。障滟蜡、满照欢丛，蟾冷落羞度。人间万感幽单，华清惯浴，春盎风露。连鬟并暖，同心共结，向承恩处。凭谁为歌长恨？暗殿锁、秋灯夜语。叙旧期、不负春盟，红朝翠暮。', '吴文英', NULL, 1);
INSERT INTO `article` VALUES (230, '齐天乐', '小娉婷清铅素靥，蜂黄暗偷晕，翠翘攲鬓。昨夜冷中庭，月下相认，睡浓更苦凄风紧。惊回心未稳，送晓色、一壶葱茜，才知花梦准。湘娥化作此幽芳，凌波路，古岸云沙遗恨。临砌影，寒香乱、冻梅藏韵。熏炉畔、旋移傍枕，还又见、玉人垂绀鬓。料唤赏、清华池馆，台杯须满引。', '吴文英', NULL, 1);
INSERT INTO `article` VALUES (231, '浣溪沙', '门隔花深旧梦游，夕阳无语燕归愁，玉纤香动小帘钩。落絮无声春堕泪，行云有影月含羞，东风临夜冷于秋。', '吴文英', NULL, 1);
INSERT INTO `article` VALUES (232, '浣溪沙', '波面铜花冷不收，玉人垂钓理纤钩，月明池阁夜来秋。江燕话归成晓别，水花红减似春休，西风梧井叶先愁。', '吴文英', NULL, 1);
INSERT INTO `article` VALUES (233, '点绛唇', '卷尽愁云，素娥临夜新梳洗。暗尘不起，酥润凌波地。辇路重来，仿佛灯前事。情如水，小楼熏被，春梦笙歌里。', '吴文英', NULL, 1);
INSERT INTO `article` VALUES (234, '祝英台', '采幽香，巡古苑，竹冷翠微路。斗草溪根，沙印小莲步。自怜两鬓清霜，一年寒食，又身在云山深处。昼闲度，因甚天也悭春，轻阴便成雨？绿暗长亭，归梦趁风絮。有情花影阑干，莺声门径，解留我霎时凝伫。', '吴文英', NULL, 1);
INSERT INTO `article` VALUES (235, '祝英台', '剪红情，裁绿意，花信上钗股。残日东风，不放岁华去。有人添烛西窗，不眠侵晓，笑声转新年莺语。旧尊俎，玉纤曾擘黄柑，柔香系幽素。归梦湖边，还迷镜中路。可怜千点吴霜，寒消不尽，又相对落梅如雨。', '吴文英', NULL, 1);
INSERT INTO `article` VALUES (236, '澡兰香', '盘丝系腕，巧篆垂簪，玉隐绀纱睡觉。银瓶露井，彩囗云窗，往事少年依约。为当时曾写榴裙，伤心红绡褪萼。黍梦光阴，渐老汀洲烟箬。莫唱江南古调，怨抑难招，楚江沉魄。薰风燕乳，暗雨槐黄，午镜澡兰帘幕。念秦楼、也拟人归，应剪菖蒲自酌。但怅望一缕新蟾，随人天角。', '吴文英', NULL, 1);
INSERT INTO `article` VALUES (237, '风入松', '听风听雨过清明，愁草瘗花铭。楼前绿暗分携路，一丝柳、一寸柔情。料峭春寒中酒，交加晓梦啼莺。西园日日扫林亭，依旧赏新晴。黄蜂频扑秋千索，有当时、纤手香凝。惆怅双鸳不到，幽阶一夜苔生。', '吴文英', NULL, 1);
INSERT INTO `article` VALUES (238, '莺啼序', '残寒正欺病酒,掩沉香绣户。燕来晚、飞入西城,似说春事迟暮。画船载、清明过却，晴烟冉冉吴宫树。念羁情、游荡随风，化为轻絮。十载西湖，傍柳系马,趁娇尘软雾。溯红渐、招入仙溪,锦儿偷寄幽素。倚银屏春宽梦窄，断红湿、歌纨金缕。螟堤空,轻把斜阳,总还鸥鹭。幽兰旋老，杜若还生，水乡尚寄旅。别后访、六桥无信,事往花委,瘗玉埋香，几番风雨。长波妒盼，遥山羞黛,渔灯分影克江宿。记当时、短楫桃根渡,青楼仿佛。临分败壁题诗，泪墨惨淡尘土。危亭望极，草色天涯，叹鬓侵半苧。暗点检、离痕欢唾，尚染鲛绡，嚲凤迷归，破鸾慵舞。殷勤待写，书中长恨，蓝霞辽海沉过雁。漫相思、弹入哀筝柱。伤心千里江南，怨曲重招，断魂在否？', '吴文英', NULL, 1);
INSERT INTO `article` VALUES (239, '惜黄花慢', '送客吴皋，正试霜夜冷，枫落长桥。望天不尽，背城渐杳，离亭黯黯，恨水迢迢。翠香零落红衣老，暮愁锁，残柳眉梢。念瘦腰,沈郎旧日,曾系兰桡。仙人凤咽琼箫，怅断魂送远，九辩难招。醉鬟留盼，小窗剪烛,歌云载恨,飞上银霄。素秋不解随船去，败红趁一叶寒涛。梦翠翘，怨鸿料过南谯。', '吴文英', NULL, 1);
INSERT INTO `article` VALUES (240, '高阳台', '宫粉雕痕，仙云堕影，无人野水荒湾。西石埋香，金沙锁骨连环。南楼不恨吹横笛，恨晓风千里关山。半飘零、庭上黄昏，月冷阑干。寿阳空理愁鸾，问谁调玉髓，暗补香瘢？细雨归鸿，孤山无限春寒。离魂难倩招清些，梦缟衣解溪边。最愁人、啼鸟晴明，叶底清圆。', '吴文英', NULL, 1);
INSERT INTO `article` VALUES (241, '高阳台', '丰乐楼分韵得“如”字修竹凝妆，垂杨驻马，凭阑浅画成图。山色谁题？楼前有雁斜书。东风紧送斜阳下，弄旧寒、晚酒醒馀。自消凝，能几花前，顿老相如？伤春有在高楼上，在灯前攲枕，雨外熏炉。怕舣船，临流可奈清臞？飞红若到西湖底，搅翠澜、总是愁鱼。莫重来、吹尽香绵，泪满平芜。', '吴文英', NULL, 1);
INSERT INTO `article` VALUES (242, '三姝媚', '湖山经醉惯,渍春衫,啼痕酒痕无限。又客长安，叹断襟零袂，涴尘谁浣。紫曲门荒，沿败井、风摇青蔓。对语东邻，犹是曾巢，谢堂双燕。春梦人间须断，但怪得当年，梦缘能短。绣屋秦筝，傍海棠偏爱，夜深开宴。舞歇歌沉，花未减、红颜先变。伫久河桥欲去，斜阳泪满。', '吴文英', NULL, 1);
INSERT INTO `article` VALUES (243, '八声甘州', '渺空烟四远，是何年、青天坠长星。幻苍崖云树，名娃金屋，残霸宫城。箭径酸风射眼，腻水染花腥。时靸双鸳响，廊叶秋声。宫里吴王沉醉，倩五湖倦客，独钓醒醒。问苍波无语，华发奈山青。水涵空、阑干高处，送乱鸦、斜日落渔汀。连呼酒、上琴台去，秋与云平。', '吴文英', NULL, 1);
INSERT INTO `article` VALUES (244, '踏莎行', '润玉笼绡，檀樱倚扇，绣圈犹带脂香浅。榴心空叠舞裙红，艾枝应愁鬟乱。午梦千山,窗阴一箭,香凝瘢新褪红丝腕。隔江人在雨声中，晚风菰叶生秋怨。', '吴文英', NULL, 1);
INSERT INTO `article` VALUES (245, '瑞鹤仙', '晴丝牵绪乱，对沧江斜日，花飞人远。垂杨暗吴苑，正旗亭烟冷，河桥风暖。兰情蕙盼，惹相思、春根酒畔。又争知、吟骨萦消，渐把旧衫重剪。凄断流红千浪，缺月孤楼，总难留燕。歌尘凝扇，待凭信，拚分钿。试挑灯欲写，还依不忍，笺幅偷和泪卷。寄残云剩雨蓬莱，也应梦见。', '吴文英', NULL, 1);
INSERT INTO `article` VALUES (246, '鹧鸪天', '池上红衣伴倚阑，栖鸦常带夕阳还。殷云度雨疏桐落，明月生凉宝扇闲。乡梦窄，水天宽，小窗愁黛淡秋山。吴鸿好为传归信，杨柳阊门屋数间。', '吴文英', NULL, 1);
INSERT INTO `article` VALUES (247, '夜游宫', '人去西楼雁杳,叙别梦、扬州一觉。云淡星疏楚山晓。听啼乌,立河桥，话未了。雨外蛩声早，细织就霜丝多少？说与萧娘未知道。向长安，对秋灯，几人老？', '吴文英', NULL, 1);
INSERT INTO `article` VALUES (248, '贺新郎', '乔木生云气。访中兴、英雄陈迹，暗追前事。战舰东风悭借便，梦断神州故里。旋小筑、吴宫闲地，华表月明归夜鹤，叹当时、花竹今如此，枝上露，溅清泪。遨头小簇行春队，步苍苔、寻幽别墅，问梅开未？重唱梅边新度曲，催发寒梢冻蕊。此心与东君同意，后不如今今非昔，两无言相对沧浪水，怀此恨，寄残醉。', '吴文英', NULL, 1);
INSERT INTO `article` VALUES (249, '唐多令', '何处合成愁？离人心上秋，纵芭蕉、不雨也飕飕。都道晚凉天气好，有明月，怕登楼。年事梦中休，花空烟水流，燕辞归、客尚淹留。垂柳不萦裙带住，漫长是系行舟。', '吴文英', NULL, 1);
INSERT INTO `article` VALUES (250, '湘春夜月', '近清明，翠禽枝上消魂。可惜一片清歌，都付与黄昏。欲共柳花低诉。怕柳花轻薄，不解伤春。衾楚乡旅宿，柔情别绪，谁与温存？空尊夜泣，青山不语，残照当门。翠玉楼前，惟是有、一陂湘水，摇荡湘云。天长梦短，问甚时重见桃根？这次第、算人间没个并刀，剪断心上愁痕。', '黄孝迈', NULL, 1);
INSERT INTO `article` VALUES (251, '大有·九日', '戏马台前，采花篱下，问岁华、还是重九。恰归来、南山翠色依旧。帘栊昨夜听风雨，都不似登临时候。一片宋玉情怀，十分卫郎清瘦。红萸佩，空对酒。砧杵动微寒，暗欺罗袖。秋已无多，早是败荷衰柳。强整帽檐攲侧，曾经向天涯搔首。几回忆、故国莼鲈，霜前雁后。', '潘希白', NULL, 1);
INSERT INTO `article` VALUES (252, '青玉案', '年年社日停针线，怎忍见、双飞燕？今日江城春已半，一身犹在，乱山深处，寂寞溪桥畔。春衫著破谁针线？点点行行泪痕满。落日解鞍芳草岸，花无人戴，酒无人劝，醉也无人管。', '黄公绍', NULL, 1);
INSERT INTO `article` VALUES (253, '摸鱼儿', '对西风、鬓摇烟碧，参差事流水。紫丝罗带鸳鸯结，的的镜盟钗誓。浑不记，漫手织回文，几度欲心碎。安花著叶，奈雨覆云翻，情宽分窄，石上玉簪脆。朱楼外,愁压空云欲坠,月痕犹照无寐。阴晴也只随天意，枉了玉消香碎。君且醉，君不见、长门青草春风泪。一时左计，悔不早荆钗，暮天修竹，头白倚寒翠。', '朱嗣发', NULL, 1);
INSERT INTO `article` VALUES (254, '兰陵王', '送春去，春去人间无路。秋千外，芳草连天，谁谴风沙暗南浦。依依甚意绪？漫忆海门飞絮。乱鸦过、斗转城荒，不见来时试灯处。春去谁最苦？但箭雁沉边，梁燕无主，杜鹃声里长门暮。想玉树凋土，泪盘如露。咸阳送客屡回顾，斜日未能度。春去，尚来否？正江令恨别，庚信愁赋，苏堤尽日风和雨。叹神游故国，花记前度。人生流落，顾孺子，共夜语。', '刘辰翁', NULL, 1);
INSERT INTO `article` VALUES (255, '宝鼎现', '红妆春骑，踏月影竿旗穿市。望不尽、楼台歌舞，习习香尘莲步底。箫声断、约彩鸾归去，未怕金吾呵醉。甚辇路、喧阗且止，听得念奴歌起。父老犹记宣和事，抱铜仙、清泪如水。还转盼、沙河多丽。滉漾明光连邸第，帘影冻、散红光成绮。月浸葡萄十里，看往来，神仙才子，肯把菱花扑碎。肠断竹马儿童，空见说、三千乐指。等多时春不归来，到春时欲睡。又说向灯前拥髻，暗滴鲛珠坠。便当日亲见霓裳，天上人间梦里。', '刘辰翁', NULL, 1);
INSERT INTO `article` VALUES (256, '永遇乐', '壁月初晴，黛云远淡，春事谁主？禁苑娇寒，湖堤倦暖，前度遽如许。香尘暗陌，华灯明昼，长是懒携去。谁知道、断烟禁夜，满城似愁风雨。宣和旧日，临安南渡，芳景犹自如故。细帙离离,风鬟三五,能赋词最苦。江南无路，鄜州今夜，此苦又谁知否？空相对公、残釭无寐，满村社鼓。', '刘辰翁', NULL, 1);
INSERT INTO `article` VALUES (257, '摸鱼儿', '怎知他、春归何处？相逢且尽尊酒。少年袅袅天涯恨，长结西湖烟柳。休回首，但细雨断桥，憔悴人归后。东风似旧，向前度桃花，刘郎能记，花复认郎否？君且住,草草留君剪韭,前宵正恁时候。深杯欲共歌声滑，翻湿春衫半袖。空眉皱，看白发尊前，已似人人有。临分把手，叹一笑论文，清狂顾曲，此会几时又？', '刘辰翁', NULL, 1);
INSERT INTO `article` VALUES (258, '高阳台', '照野旌旗，朝天车马，平沙万里天低。宝带金章，尊前茸风欺。秦关汴水经行地，想登临都付新诗。纵英游、叠鼓清笳，骏马名姬。酒酣应对燕山雪，正冰河月冻，晓陇云飞。投老残年，江南谁念方回？东风渐绿西湖岸，雁已还人未南归。归关情、折尽梅花，难寄相思。　', '周密', NULL, 1);
INSERT INTO `article` VALUES (259, '瑶华', '朱钿宝囗，天上飞琼，比人间春别。江南北上，终未见、漫拟梨云梅雪。淮山春晚，问谁识、芳心高洁？消几番、花落花开，老了玉关豪杰。金壶剪送琼枝，看一骑红尘，香度瑶阙。韶华正好，应自喜、初乱长安蜂蝶。杜郎老矣，想旧事花须能说。记少年一梦扬州，二十四桥明月。', '周密', NULL, 1);
INSERT INTO `article` VALUES (260, '玉京秋', '烟水阔，高林弄残照，晚蜩凄切。碧砧度韵，银床飘叶。衣湿桐阴露冷，采凉花时赋秋雪。叹轻别，一襟幽事，砌虫能说。客思吟商还怯，怨歌长、琼壶暗缺。翠扇恩疏，红衣香褪，翻成消歇。玉骨西风，恨最恨、闲却新凉时节。楚箫咽，谁寄西楼淡月。', '周密', NULL, 1);
INSERT INTO `article` VALUES (262, '曲游春', '楚江湄，湘娥再见，无言洒清泪，淡然春意。空独倚东风，芳思谁寄？凌波路冷秋无际。　香云随步起，漫记得、汉宫仙掌，亭亭明月底。冰丝写怨更多情，骚人恨枉赋芳兰幽芷。春思远，谁叹赏国香风味？相露，一枝灯影里。', '周密', NULL, 1);
INSERT INTO `article` VALUES (263, '瑞鹤仙', '绀烟迷雁迹，渐碎鼓零钟，街喧初息。风檠背寒壁，放冰蟾，飞到蛛丝帘隙。琼瑰暗泣，念乡关、霜华似织。漫将身、化鹤归来，忘却旧游端的。欢极蓬壶蕖浸，花院梨溶，醉连春夕。柯云罢弈，樱桃在，梦难觅。劝清光、乍可幽窗相照，休照红楼夜笛。怕人间、换谱伊凉，素娥未识。', '蒋捷', NULL, 1);
INSERT INTO `article` VALUES (264, '贺新郎', '梦冷黄金屋。叹秦筝、斜鸿阵里，素弦尘扑。化作娇莺飞归去，犹认纱窗旧绿。正过雨、荆桃如菽。此恨难平君知否？似琼台、涌起弹棋局。消瘦影，嫌明烛。鸳楼碎泻东西玉。问芳踪、何时再展？翠钗难卜。待把宫眉横云样，描上生绡画幅。怕不是新来妆束。彩扇红牙今都在，恨无人解听开元曲。空掩袖，倚寒竹。', '蒋捷', NULL, 1);
INSERT INTO `article` VALUES (265, '女冠子', '蕙花香也，雪晴池馆如画。春风飞到，宝钗楼上，一片笙箫，琉璃光射。而今灯漫挂，不是暗尘明月，那时元夜。况年来、心懒意怯，羞与蛾儿争耍。江城人悄初更打，问繁华谁解，再向天公借？剔残红灺，但梦里隐隐，钿车罗帕。吴笺银粉砑，待把旧家风景，写成闲话。笑绿鬟邻女，倚窗犹唱，夕阳西下。', '蒋捷', NULL, 1);
INSERT INTO `article` VALUES (266, '高阳台', '接叶巢莺，平波卷絮，断桥斜日归船。能几番游？看花又是明年。东风且伴蔷薇住，到蔷薇、春已堪怜。更凄然，万绿西冷，一抹荒烟。当年燕子知何处？但苔深韦曲，草暗斜川。见说新愁，如今也到鸥边。无心再续笙歌梦，掩重门、浅醉闲眠。莫开帘，怕见飞花，怕听啼鹃。', '张炎', NULL, 1);
INSERT INTO `article` VALUES (267, '渡江云', '山空天入海，倚楼望极，风急暮潮初。一帘鸠外雨，几处闲田，隔水动春锄。新烟禁柳，想如今、绿到西湖。犹记得、当年深隐，门掩两三株。　愁余，荒洲古溆，断梗疏萍，更漂流何？空自觉围羞带减，影怯烟孤。长疑即见桃花面，甚近来翻无书。书纵远，如何梦也都无。　', '张炎', NULL, 1);
INSERT INTO `article` VALUES (268, '八声甘州', '记玉关、踏雪事清游，寒气脆貂裘。傍枯林古道，长河饮马，此意悠悠。短梦依然江表，老泪洒西州。一字列题处，落叶都愁。载取白云归去，问谁留楚佩，弄影中洲？折芦花赠远，零落一身秋。向寻常、野桥流水，待招来、不是旧沙鸥。空怀感，有斜阳处，却怕登楼。　', '张炎', NULL, 1);
INSERT INTO `article` VALUES (269, '解连环', '楚江空晚，恨离群万里，恍然惊散。自顾影、却下寒塘，正沙净草枯，水平天远。写不成书，只寄得相思一点。料因循误了，残毡拥雪，故人心眼。　谁怜旅愁荏苒，漫长门夜悄,锦筝弹怨,想伴侣、犹宿芦花，也曾念春前，去程应转。暮雨相呼，怕蓦地、玉关重见。未羞他、双燕归来，画帘半卷。　', '张炎', NULL, 1);
INSERT INTO `article` VALUES (270, '疏影', '碧圆自洁，向浅洲远浦，亭亭清绝。犹有遗簪,不展秋心,能卷几多炎热？鸳鸯密语同倾盖，且莫与、浣纱人说。恐怨歌、忽断花风，碎却翠云千叠。　回首当年汉舞，怕飞去漫皱，留仙裙折。恋恋青衫，犹染枯香，还叹鬓丝飘雪。盘心清露如铅水,又一夜西风吹折。喜净看、匹练飞光,倒泻半湖明月。', '张炎', NULL, 1);
INSERT INTO `article` VALUES (271, '月下笛', '万里孤云，清游渐远，故人何处？寒窗梦里，犹记经行旧时路。连昌约略无多柳，第一是难听夜雨。漫惊回凄悄，相看烛影，拥衾无语。张绪归何暮？半零落依依，断桥鸥鹭。天涯倦旅，此时心事良苦。只愁重洒西州泪，问杜曲人家在否？恐翠袖天寒，犹倚梅花那树。', '张炎', NULL, 1);
INSERT INTO `article` VALUES (273, '天香', '渐新痕悬柳，淡彩穿花，依约破初暝。便有团圆意，深深拜，相逢谁在香径？画眉未稳，料素娥、犹带离恨。最堪爱、一曲银钩小，宝奁挂秋冷。千古盈亏休问，叹慢磨玉斧，难补金镜。太液池犹在，凄凉处、何人重赋清景？故山夜水，试待他、窥户端正。看云外山河，还老桂花旧影。', '王沂孙', NULL, 1);
INSERT INTO `article` VALUES (274, '齐天乐', '一襟馀恨宫魂断，年年翠阴庭树。乍咽凉柯，还移暗叶，重把离愁深诉。西窗过雨，怪瑶佩流空，玉筝调柱。镜暗妆残，为谁娇鬓尚如许？铜仙铅泪，似斜阳几度？馀音更苦，甚独抱清商，顿成凄楚。漫想熏风，柳丝千万缕。　', '王沂孙', NULL, 1);
INSERT INTO `article` VALUES (275, '长亭怨慢', '泛孤艇东皋过遍，尚记当日，绿阴门掩。屐齿莓苔，酒痕罗袖事何限？欲寻前迹，空惆怅成秋苑。自约赏花人，别后总风流云散。　水远，怎知流水外，却是乱山尤远。天涯梦短，想忘了绮疏雕槛。望不尽冉冉斜阳，抚乔木年华将晚。但数点红英，犹识西园凄婉。', '王沂孙', NULL, 1);
INSERT INTO `article` VALUES (276, '高阳台', '残雪庭阴，轻寒帘影，霏霏玉管春葭。小帖金泥，不知春是谁家？相思一夜窗前梦，奈个人、水隔天遮。但凄然，满树幽香，满地横斜。江南自是离愁苦，况游骢古道，归雁平沙。怎得银笺，殷勤说与年华。如今处处生芳草，纵凭高、不见天涯。更消他，几度东风，几度飞花。', '王沂孙', NULL, 1);
INSERT INTO `article` VALUES (278, '法曲献仙音', '江空不渡,恨蘼芜杜若,零落无数。远道荒寒，婉娩流年，望望美人迟暮。风烟雨雪阴晴晚，更何须春风千树。尽孤城、落木萧萧，日夜江声流去。日晏山深闻笛,恐他年流落,与子同赋。事阔心违，交淡媒劳，蔓草沾衣多露。汀洲窈窕馀醒寐，遗佩环、浮沉澧浦。有白鸥淡月微波，寄语逍遥容与。', '王沂孙', NULL, 1);
INSERT INTO `article` VALUES (279, '六丑', '似东风老大，那复有当时风气。有情不收，江山身是寄，浩荡何世？但忆临官道，暂来不住，便出门千里。痴心指望回风坠，扇底相逢，钗头微缀。他家万条千缕，解遮亭障驿，不隔江水。瓜洲曾檥，等行人岁岁。日下长秋，城乌夜起。帐庐好在春睡，共飞归湖上，草青无地。愔愔雨、春心如腻、欲待化、丰乐楼前帐饮，青门都废。何人念、流落无几。点点抟作雪绵松润，为君囗泪。紫萸香慢姚云文　近重阳、偏多风雨，绝怜此日暄明。问秋香浓未，待携客、出西城。正自羁怀多感，怕荒台高处，更不胜情。向尊前又忆、漉酒插花人，只座上已无老兵。凄清，浅醉还醒。愁不肯、与诗平。记长楸走马，雕弓笮柳，前事休评。紫萸一枝传赐，梦谁到、汉家陵。尽乌纱、便随风去，要天知道，华发如此星星，歌罢涕零。　', '彭元逊', NULL, 1);
INSERT INTO `article` VALUES (280, '金明池', '天阔云高，溪横水远，晚日寒生轻晕。闲阶静、杨花渐少，朱门掩、莺声犹嫩。悔匆匆、过却清明，旋占得、馀芳已成幽恨。却几日阴沉，连宵慵困，起来韶华都尽。　怨入双眉闲斗损，乍品得情怀，看承全近。深深态、无非自许，厌厌意、终羞人问。争知道、梦里蓬莱，待忘了馀香，时传音信。纵留得莺花，东风不住，也则眼前愁闷。　', '僧挥', NULL, 1);
INSERT INTO `article` VALUES (281, '凤凰台上忆吹箫', '香冷金猊，被翻红浪，起来慵自梳头。任宝奁尘满，日上帘钩。生怕离怀别苦，多少事、欲说还休。新来瘦，非干病酒，不是悲秋。休休，这回去也，千万遍阳关，也则难留。念武陵人远，烟锁秦楼。惟有楼前流水，应念我、终日凝眸。凝眸处，从今又添，一段新愁。', '李清照', NULL, 1);
INSERT INTO `article` VALUES (282, '醉花阴', '薄雾浓云愁水昼，瑞脑消金兽。佳节又重阳，玉枕纱厨，半夜凉初透。东篱把酒黄昏后，有暗香盈袖。莫道不消魂？薄雾浓云愁水昼，瑞脑消金。', '李清照', NULL, 1);
INSERT INTO `article` VALUES (283, '声声慢', '寻寻觅觅，冷冷清清，凄凄惨惨戚戚。乍暖还寒时侯，最难将息。三杯两盏淡酒，怎敌他、晚来风急。雁过也，最伤心，却是旧时相识。满地黄花堆积，惟悴损、如今有谁堪摘。守着窗儿，独自怎生得黑？梧桐更兼细雨，到黄昏、点点滴滴。这次第，怎一个愁字了得。', '李清照', NULL, 1);
INSERT INTO `article` VALUES (284, '念奴娇', '萧条庭院，有斜风细雨，重门须闭。宠柳娇花寒食近，种种恼人天气。险韵诗成，扶头酒醒，别是闲滋味。征鸿过尽，万千心事难寄。　楼上几日春寒,帘垂四面,玉阑干慵倚。被冷香消新梦觉，不许愁人不起。清露晨流，新桐初引，多少游春意。日高烟敛，更看今日晴未。', '李清照', NULL, 1);
INSERT INTO `article` VALUES (285, '永遇乐', '落日溶金，暮云合璧，人在何处？染柳烟浓，吹梅笛怨，春意知几许？元宵佳期节，融和天气，次第岂无风雨。来相召、香车宝马，谢他酒朋诗侣。　中州盛日，闺门多暇，记得偏重三五。铺翟冠儿,捻金雪柳,簇带争济楚。如今憔悴，风鬟雾鬓，怕见夜间出去。不如向帘儿底下，听人笑语。', '李清照', NULL, 1);
INSERT INTO `article` VALUES (286, '佳人', '绝代有佳人，幽居在空谷。自云良家子，零落依草木。关中昔丧乱，兄弟遭杀戮。官高何足论，不得收骨肉。世情恶衰歇，万事随转烛。夫婿轻薄儿，新人美如玉。合昏尚知时，鸳鸯不独宿。但见新人笑，那闻旧人哭！在山泉水清，出山泉水浊。侍婢卖珠回，牵萝补茅屋。摘花不插发，采柏动盈掬。天寒翠袖薄，日暮倚修竹。', '杜甫', NULL, 1);
INSERT INTO `article` VALUES (287, '梦李白二首之一', '死别已吞声，生别常恻恻。江南瘴疠地，逐客无消息。故人入我梦，明我长相忆。君今在罗网，何以有羽翼？恐非平生魂，路远不可测。魂来枫林青，魂返关塞黑。落月满屋梁，犹疑照颜色。水深波浪阔，无使蛟龙得。', '杜甫', NULL, 1);
INSERT INTO `article` VALUES (288, '梦李白二首之二', '浮云终日行，游子久不至。三夜频梦君，情亲见君意。告归常局促，苦道来不易。江湖多风波，舟楫恐失坠。出门搔白首，若负平生志。冠盖满京华，斯人独憔悴。孰云网恢恢，将老身反累。千秋万岁名，寂寞身后事。', '杜甫', NULL, 1);
INSERT INTO `article` VALUES (289, '送别', '下马饮君酒，问君何所之。君言不得意，归卧南山陲。但去莫复闻，白云无尽时。', '王维', NULL, 1);
INSERT INTO `article` VALUES (290, '送綦毋潜落第还乡', '圣代无隐者，英灵尽来归。遂令东山客，不得顾采薇。既至金门远，孰云吾道非？江淮度寒食，京洛缝春衣。置酒长安道，同心与我违。行当浮桂棹，未几拂荆扉。远树带行客，孤城当落晖。吾谋适不用，勿谓知音稀。', '王维', NULL, 1);
INSERT INTO `article` VALUES (291, '青溪', '言入黄花川，每逐青溪水。随山将万转，趣途无百里。声喧乱石中，色静深松里。漾漾泛菱荇，澄澄映葭苇。我心素已闲，清川澹如此。请留盘石上，垂钓将已矣。', '王维', NULL, 1);
INSERT INTO `article` VALUES (292, '渭川田家', '斜光照墟落，穷巷牛羊归。野老念牧童，倚杖候荆扉。雉［句隹］麦苗秀，蚕眠桑叶稀。田夫荷锄立，相见语依依。即此羡闲逸，怅然吟式微。', '王维', NULL, 1);
INSERT INTO `article` VALUES (293, '西施咏', '艳色天下重，西施宁久微。朝为越溪女，暮作吴宫妃。贱日岂殊众，贵来方悟稀。邀人傅脂粉，不自著罗衣。君宠益娇态，君怜无是非。当时浣纱伴，莫得同车归。持谢邻家子，效颦安可希！', '王维', NULL, 1);
INSERT INTO `article` VALUES (294, '秋登兰山寄张五', '北山白云里，隐者自怡悦。相望始登高，心随雁飞灭。愁因薄暮起，兴是清秋发。时见归村人，沙行渡头歇。天边树若荠，江畔洲如月。何当载酒来，共醉重阳节。', '孟浩然', NULL, 1);
INSERT INTO `article` VALUES (295, '夏日南亭怀辛大', '山光忽西落，池月渐东上。散发乘夜凉，开轩卧闲敞。荷风送香气，竹露滴清响。欲取鸣琴弹，恨无知音赏。感此怀故人，中宵劳梦想。', '孟浩然', NULL, 1);
INSERT INTO `article` VALUES (296, '宿业师山房待丁大不至', '夕阳度西岭，群壑倏已暝。松月生夜凉，风泉满清听。樵人归欲尽，烟鸟栖初定。之子期宿来，孤琴候萝径。', '孟浩然', NULL, 1);
INSERT INTO `article` VALUES (297, '同从弟南斋玩月忆山阴崔少府', '高卧南斋时，开帷月初吐。清辉淡水木，演漾在窗户。苒苒几盈虚，澄澄变今古。美人清江畔，是夜越吟苦。千里其如何，微风吹兰杜。', '王昌龄', NULL, 1);
INSERT INTO `article` VALUES (298, '寻西山隐者不遇', '绝顶一茅茨，直上三十里。扣关无僮仆，窥室惟案几。若非巾柴车，应是钓秋水。差池不相见，黾勉空仰止。草色新雨中，松声晚窗里。及兹契幽绝，自足荡心耳。虽无宾主意，颇得清净理。兴尽方下山，何必待之子。', '邱为', NULL, 1);
INSERT INTO `article` VALUES (299, '春泛若耶溪', '幽意无断绝，此去随所偶。晚风吹行舟，花路入溪口。际夜转西壑，隔山望南斗。潭烟飞溶溶，林月低向后。生事且弥漫，愿为持竿叟。', '綦毋潜', NULL, 1);
INSERT INTO `article` VALUES (300, '宿王昌龄隐居', '清溪深不测，隐处唯孤云。松际露微月，清光犹为君。茅亭宿花影，药院滋苔纹。余亦谢时去，西山鸾鹤群。', '常建', NULL, 1);
INSERT INTO `article` VALUES (301, '与高适薛据登慈恩寺浮图', '塔势如涌出，孤高耸天宫。登临出世界，磴道盘虚空。突兀压神州，峥嵘如鬼工。四角碍白日，七层摩苍穹。下窥指高鸟，俯听闻惊风。连山若波涛，奔凑如朝东。青槐夹驰道，宫馆何玲珑！秋色从西来，苍然满关中。五陵北原上，万古青蒙蒙。净理了可悟，胜因夙所宗。誓将挂冠去，觉道资无穷。', '岑参', NULL, 1);
INSERT INTO `article` VALUES (302, '贼退示官吏并序', '癸卯岁，西原贼入道州，焚烧杀掠，几尽而去。明年，贼又攻永州，破邵，不犯此州边鄙而退，岂力能制敌欤？盖蒙其伤怜而已！诸史何为忍苦征敛！故作诗一篇以示官吏。昔岁逢太平，山林二十年。泉源在庭户，洞壑当门前。井税有常期，日晏犹得眠。忽然遭时变，数岁亲戎旃。今来典斯郡，山夷又纷然。城小贼不屠，人贫伤可怜。是以陷邻境，此州独见全。使臣将王命，岂不如贼焉！令彼征敛者，迫之如火煎。谁能绝人命，以作时世贤。思欲委符节，引竿自刺船。将家就鱼麦，归老江湖边。', '元结', NULL, 1);
INSERT INTO `article` VALUES (303, '郡斋雨中与诸文士燕集', '兵卫森画戟，宴寝凝清香。海上风雨至，逍遥池阁凉。烦疴近消散，嘉宾复满堂。自惭居处崇，未睹斯民康。理会是非遣，性达形迹忘。鲜肥属时禁，蔬果幸见尝。俯饮一杯酒，仰聆金玉章。神欢体自轻，意欲凌风翔。吴中盛文史，群彦今汪洋。方知大蕃地，岂曰财赋强。', '韦应物', NULL, 1);
INSERT INTO `article` VALUES (304, '初发扬子寄元大校书', '凄凄去亲爱，泛泛入烟雾。归棹洛阳人，残钟广陵树。今朝为此别，何处还相遇。世事波上舟，沿洄安得住。', '韦应物', NULL, 1);
INSERT INTO `article` VALUES (305, '寄全椒山中道士', '今朝郡斋冷，忽念山中客。涧底束荆薪，归来煮白石。欲持一瓢酒，远慰风雨夕。落叶满空山，何处寻行迹。', '韦应物', NULL, 1);
INSERT INTO `article` VALUES (306, '长安遇冯著', '客从东方来，衣上灞陵雨。问客何为来，采山因买斧。冥冥花正开，扬扬燕新乳。昨别今已春，鬓丝生几缕。', '韦应物', NULL, 1);
INSERT INTO `article` VALUES (307, '夕次盱眙县', '落帆逗淮镇，停舫临孤驿。浩浩风起波，冥冥日沈夕。人归山郭暗，雁下芦洲白。独夜忆秦关，听钟未眠客。', '韦应物', NULL, 1);
INSERT INTO `article` VALUES (308, '东郊', '吏舍局终年，出郊旷清曙。杨柳散和风，青山澹吾虑。依丛适自憩，缘涧还复去。微雨霭芳原，春鸠鸣何处？乐幽心屡止，遵事迹犹遽。终罢斯结庐，慕陶真可庶。', '韦应物', NULL, 1);
INSERT INTO `article` VALUES (309, '送杨氏女', '永日方戚戚，出行复悠悠。女子今有行，大江溯轻舟。尔辈苦无恃，抚念益慈柔。幼为长所育，两别泣不休。对此结中肠，义往难复留！自小阙内训，事姑贻我忧。赖兹托令门，仁恤庶无尤。贫俭诚所尚，资从岂待周？孝恭遵妇道，容止顺其猷。别离在今晨，见尔当何秋。居闲始自遣，临感忽难收。归来视幼女，零泪缘缨流。', '韦应物', NULL, 1);
INSERT INTO `article` VALUES (310, '晨诣超师院读禅经', '汲井漱寒齿，清心拂尘服。闲持贝叶书，步出东斋读。真源了无取，忘迹世所逐。遗言冀可冥，缮性何由熟？道人庭宇静，苔色连深竹。日出雾露馀，青松如膏沐。澹然离言说，悟悦心自足。', '柳宗元', NULL, 1);
INSERT INTO `article` VALUES (311, '溪居', '久为簪组累，幸此南夷谪。闲依农圃邻，偶似山林客。晓耕翻露草，夜榜响溪石。来往不逢人，长歌楚天碧。', '柳宗元', NULL, 1);
INSERT INTO `article` VALUES (312, '塞上曲', '蝉鸣空桑林，八月萧关道。出塞复入塞，处处黄芦草。从来幽并客，皆向沙场老。莫学游侠儿，矜夸紫骝好。', '王昌龄', NULL, 1);
INSERT INTO `article` VALUES (313, '塞下曲', '饮马渡秋水，水寒风似刀。平沙日未没，黯黯见临洮。昔日长城战，咸言意气高。黄尘足今古，白骨乱蓬蒿。', '王昌龄', NULL, 1);
INSERT INTO `article` VALUES (314, '关山月', '明月出天山，苍茫云海间。长风几万里，吹度玉门关。汉下白登道，胡窥青海湾。由来征战地，不见有人还。戍客望边色，思归多苦颜。高楼当此夜，叹息未应闲。', '李白', NULL, 1);
INSERT INTO `article` VALUES (315, '子夜四时歌', '秦地罗敷女，采桑绿水边。素手青条上，红妆白日鲜。蚕饥妾欲去，五马莫留连。', '李白', NULL, 1);
INSERT INTO `article` VALUES (316, '子夜四时歌', '镜湖三百里，菡萏发荷花。五月西施采，人看隘若耶。回舟不待月，归去越王家。', '李白', NULL, 1);
INSERT INTO `article` VALUES (317, '子夜四时歌', '长安一片月，万户捣衣声。秋风吹不尽，总是玉关情。何日平胡虏，良人罢远征？', '李白', NULL, 1);
INSERT INTO `article` VALUES (318, '子夜四时歌', '明朝驿使发，一夜絮征袍。素手抽针冷，那堪把剪刀。裁缝寄远道，几日到临洮？', '李白', NULL, 1);
INSERT INTO `article` VALUES (319, '长干行', '妾发初覆额，折花门前剧。郎骑竹马来，绕床弄青梅。同居长干里，两小无嫌猜。十四为君妇，羞颜未尝开。低头向暗壁，千唤不一回。十五始展眉，愿同尘与灰。常存抱柱信，岂上望夫台！十六君远行，瞿塘滟预堆。五月不可触，猿鸣天上哀。门前迟行迹，一一生绿苔。苔深不能扫，落叶秋风早。八月蝴蝶来，双飞西园草。感此伤妾心，坐愁红颜老。早晚下三巴，预将书报家。相迎不道远，直至长风沙。', '李白', NULL, 1);
INSERT INTO `article` VALUES (320, '烈女操', '梧桐相待老，鸳鸯会双死。贞妇贵殉夫，舍生亦如此。波澜誓不起，妾心井中水。', '孟郊', NULL, 1);
INSERT INTO `article` VALUES (321, '游子吟', '慈母手中线，游子身上衣。临行密密缝，意恐迟迟归。谁言寸草心，报得三春辉？', '孟郊', NULL, 1);
INSERT INTO `article` VALUES (322, '登幽州台歌', '前不见古人，后不见来者。念天地之悠悠，独怆然而涕下！', '陈子昂', NULL, 1);
INSERT INTO `article` VALUES (323, '古意', '男儿事长征，少小幽燕客。赌胜马蹄下，由来轻七尺。杀人莫敢前，须如猬毛磔。黄云陇底白雪飞，未得报恩不能归。辽东小妇年十五，惯弹琵琶解歌舞。今为羌笛出塞声，使我三军泪如雨！', '李颀', NULL, 1);
INSERT INTO `article` VALUES (324, '送陈章甫', '四月南风大麦黄，枣花未落桐叶长。青山朝别暮还见，嘶马出门思故乡。陈侯立身何坦荡，虬须虎眉仍大颡。腹中贮书一万卷，不肯低头在草莽。东门酤酒饮我曹，心轻万事皆鸿毛。醉卧不知白日暮，有时空望孤云高。长河浪头连天黑，津口停舟渡不得。郑国游人未及家，洛阳行子空叹息。闻道故林相识多，罢官昨日今如何？', '李颀', NULL, 1);
INSERT INTO `article` VALUES (325, '琴歌', '主人有酒欢今夕，请奏鸣琴广陵客。月照城头乌半飞，霜凄万树风入衣。铜炉华烛烛增辉，初弹渌水后楚妃。一声已动物皆静，四座无言星欲稀。清淮奉使千馀里，敢告云山从此始？', '李颀', NULL, 1);
INSERT INTO `article` VALUES (326, '听董大弹胡笳声兼寄语弄房给事', '蔡女昔造胡笳声，一弹一十有八拍。胡人落泪沾边草，汉使断肠对归客。古戍苍苍烽火寒，大荒沈沈飞雪白。先拂声弦后角羽，四郊秋叶惊［扌戚］［扌戚］。董夫子，通神明，深山窃听来妖精。言迟更速皆应手，将往复旋如有情。空山百鸟散还合，万里浮云阴且晴。嘶酸雏雁失群夜，断绝胡儿恋母声。川为静其波，鸟亦罢其鸣。乌孙部落家乡远，逻娑沙尘哀怨生。幽音变调忽飘洒，长风吹林雨堕瓦。迸泉飒飒飞木末，野鹿呦呦走堂下。长安城连东掖垣，凤凰池对青琐门。高才脱略名与利，日夕望君抱琴至。更多精彩图书、手机资源尽在飞库电脑访问:www.feiku.com手机访问:wap.feiku.com第二章?唐诗300首 51-10', '李颀', NULL, 1);
INSERT INTO `article` VALUES (327, '听安万善吹筚篥歌', '南山截竹为筚篥，此乐本自龟兹出。流传汉地曲转奇，凉州胡人为我吹。傍邻闻者多叹息，远客思乡皆泪垂。世人解听不解赏，长飙风中自来往。枯桑老柏寒飕［风留］，九雏鸣凤乱啾啾。龙吟虎啸一时发，万籁百泉相与秋。忽然更作渔阳掺，黄云萧条白日暗。变调如闻杨柳春，上林繁花照眼新。岁夜高堂列明烛，美酒一杯声一曲。', '李颀', NULL, 1);
INSERT INTO `article` VALUES (328, '夜归鹿门山歌', '山寺钟鸣昼已昏，渔梁渡头争渡喧。人随沙路向江村，余亦乘舟归鹿门。鹿门月照开烟树，忽到庞公栖隐处。岩扉松径长寂寥，惟有幽人自来去。', '孟浩然', NULL, 1);
INSERT INTO `article` VALUES (329, '庐山谣寄卢侍御虚舟', '我本楚狂人，凤歌笑孔丘。手持绿玉杖，朝别黄鹤楼。五岳寻仙不辞远，一生好入名山游。庐山秀出南斗傍，屏风九叠云锦张。影落明湖青黛光，金阙前开二峰长。银河倒挂三石梁，香炉瀑布遥相望。回崖沓障凌苍苍。翠影红霞映朝日，鸟飞不到吴天长。登高壮观天地间，大江茫茫去不黄。黄云万里动风色，白波九道流雪山。好为庐山谣，兴因庐山发。闲窥石镜清我心，谢公行处苍苔没。早服还丹无世情，琴心三叠道初成。遥见仙人彩云里，手把芙蓉朝玉京。先期汗漫九垓上，愿接卢敖游太清。', '李白', NULL, 1);
INSERT INTO `article` VALUES (330, '梦游天姥吟留别', '海客谈瀛洲，烟涛微茫信难求。越人语天姥，云霓明灭或可睹。天姥连天向天横，势拔五岳掩赤城。天台四万八千丈，对此欲倒东南倾。我欲因之梦吴越，一夜飞渡镜湖月。湖月照我影，送我至剡溪。谢公宿处今尚在，渌水荡漾清猿啼。脚著谢公屐，身登青云梯。半壁见海日，空中闻天鸡。千岩万壑路不定，迷花倚石忽已暝。熊咆龙吟殷岩泉，栗深林兮惊层巅。云青青兮欲雨，水澹澹兮生烟。裂缺霹雳，丘峦崩摧。洞天石扇，訇然中开。青冥浩荡不见底，日月照耀金银台。霓为衣兮风为马，云之君兮纷纷而来下。虎鼓瑟兮鸾回车，仙之人兮列如麻。忽魂悸以魄动，恍惊起而长嗟。惟觉时之枕席，失向来之烟霞。世间行乐亦如此，古来万事东流水。别君去兮何时还？且放白鹿青崖间。须行即骑访名山。安能摧眉折腰事权贵，使我不得开心颜！', '李白', NULL, 1);
INSERT INTO `article` VALUES (331, '金陵酒肆留别', '风吹柳花满店香，吴姬压酒唤客尝。金陵子弟来相送，欲行不行各尽觞。请君试问东流水，别意与之谁短长？', '李白', NULL, 1);
INSERT INTO `article` VALUES (332, '宣州谢［月兆］楼饯别校书叔云', '弃我去者，昨日之日不可留。乱我心者，今日之日多烦忧！长风万里送秋雁，对此可以酣高楼。蓬莱文章建安骨，中间小谢又清发。俱怀逸兴壮思飞，欲上青天览明月。抽刀断水水更流，举杯销愁愁更愁。人生在世不称意，明朝散发弄扁舟。', '李白', NULL, 1);
INSERT INTO `article` VALUES (333, '走马川行奉送封大夫出师西征', '君不见走马川行雪海边，平沙莽莽黄入天。轮台九月风夜吼，一川碎石大如斗。随风满地石乱走，匈奴草黄马正肥。金山西见烟尘飞，汉家大将西出师。将军金甲夜不脱，半夜军行戈相拨。风头如刀面如割，马毛带雪汗气蒸。五花连钱旋作冰，幕中草檄砚水凝。虏骑闻之应胆慑，料知短兵不敢接。车师西门伫献捷！', '岑参', NULL, 1);
INSERT INTO `article` VALUES (334, '轮台歌奉送封大夫出师西征', '轮台城头夜吹角，轮台城北旄头落。羽书昨夜过渠黎，单于已在金山西。戍楼西望烟尘黑，汉兵屯在轮台北。上将拥旄西出征，平明吹笛大军行。四边伐鼓雪海涌，三军大呼阴山动。虏塞兵气连云屯，战场白骨缠草根。剑河风急雪片阔，沙口石冻马蹄脱。亚相勤王甘苦辛，誓将报主静边尘。古来青史谁不见，今见功名胜古人。', '岑参', NULL, 1);
INSERT INTO `article` VALUES (335, '白雪歌送武判官归京', '北风卷地白草折，胡天八月即飞雪。忽如一夜春风来，千树万树梨花开。散入珠帘湿罗幕，狐裘不暖锦衾薄。将军角弓不得控，都护铁衣冷犹著。瀚海阑干百丈冰，愁云黪淡万里凝。中军置酒饮归客，胡琴琵琶与羌笛。纷纷暮雪下辕门，风掣红旗冻不翻。轮台东门送君去，去时雪满天山路。山回路转不见君，雪上空留马行处。', '岑参', NULL, 1);
INSERT INTO `article` VALUES (336, '韦讽录事宅观曹将军画马图', '国初以来画鞍马，神妙独数江都王。将军得名三十载，人间又见真乘黄。曾貌先帝照夜白，龙池十日飞霹雳。内府殷红玛瑙盘，婕妤传诏才人索。盘赐将军拜舞归，轻纨细绮相追飞。贵戚权门得笔迹，始觉屏障生光辉。昔日太宗拳毛［马呙］，近时郭家狮子花。今之新图有二马。复令识者久叹嗟。此皆骑战一敌万，缟素漠漠开风沙。其余七匹亦殊绝，迥若寒空杂烟雪。霜蹄蹴踏长楸间，马官厮养森成列。可怜九马争神骏，顾视清高气深稳。借问苦心爱者谁，后有韦讽前支盾。忆昔巡幸新丰宫，翠花拂天来向东。腾骧磊落三万匹，皆与此图筋骨同。自从献宝朝河宗，无复射蛟江水中。君不见，金粟堆前松柏里。龙媒去尽鸟呼风。', '杜甫', NULL, 1);
INSERT INTO `article` VALUES (337, '丹青引赠曹霸将军', '将军魏武之子孙，于今为庶为青门。英雄割据虽已矣，文采风流今尚存。学书初学卫夫人，但恨无过王右军。丹青不知老将至，富贵于我如浮云。开元之中常引见，承恩数上南熏殿。凌烟功臣少颜色，将军下笔开生面。良相头上进贤冠，猛将腰间大羽箭。褒公鄂公毛发动，英姿飒爽犹酣战。先帝天马玉花骢，画工如山貌不同。是日牵来赤墀下，迥立阊阖生长风。诏谓将军拂绢素，意匠惨淡经营中。斯须九重真龙出，一洗万古凡马空。玉花却在御榻上，榻上庭前屹相向。至尊含笑催赐金，圉人太仆皆惆怅。弟子韩干早入室，亦能画马穷殊相。干惟画肉不画骨，忍使骅骝气凋丧。将军画善盖有神，偶逢佳士亦写真。即今漂泊干戈际，屡貌寻常行路人。涂穷反遭俗眼白，世上未有如公贫。但看古来盛名下，终日坎［土禀］缠其身！', '杜甫', NULL, 1);
INSERT INTO `article` VALUES (338, '寄韩谏议', '今我不乐思岳阳，身欲奋飞病在床。美人娟娟隔秋水，濯足洞庭望八荒。鸿飞冥冥日月白，青枫叶赤天雨霜。玉京群帝集北斗，或骑麒麟翳凤凰。芙蓉旌旗烟雾落，影动倒景摇潇湘。星宫之君醉琼浆，羽人稀少不在旁。似闻昨者赤松子，恐是汉代韩张良。昔随刘氏定长安，帷幄未改神惨伤。国家成败吾岂敢，色难腥腐餐枫香。周南留滞古所惜，南极老人应寿昌。美人胡为隔秋水，焉得置之贡玉堂？', '杜甫', NULL, 1);
INSERT INTO `article` VALUES (339, '古柏行', '孔明庙前有老柏，柯如青铜根如石。双皮溜雨四十围，黛色参天二千尺。君臣已与时际会，树木犹为人爱惜。云来气接巫峡长，月出寒通雪山白。忆昨路绕锦亭东，先主武侯同［门必］宫。崔嵬枝干郊原古，窈窕丹青户牖空。落落盘踞虽得地，冥冥孤高多烈风。扶持自是神明力，正直元因造化功。大厦如倾要梁栋，万牛回首丘山重。不露文章世已惊，未辞剪伐谁能送？苦心岂免容蝼蚁？香叶终经宿鸾凤。志士幽人莫怨嗟，古来材大难为用！', '杜甫', NULL, 1);
INSERT INTO `article` VALUES (340, '观公孙大娘弟子舞剑器行并序', '大历二年十月十九日夔府别驾元持宅见临颍李十二娘舞剑器，壮其蔚［足支］。问其所师，曰：余公孙大娘弟子也。开元三载，余尚童稚，记于郾城观公孙氏舞剑器浑脱。浏漓顿挫，独出冠时。自高头宜春梨园二伎坊内人，洎外供奉，晓是舞者，圣文神武皇帝初，公孙一人而已。玉貌锦衣，况余白首！今兹弟子亦匪盛颜。既辨其由来，知波澜莫二。抚事慷慨，聊为剑器行。昔者吴人张旭善草书书帖，数尝於邺县见公孙大娘舞西河剑器，自此草书长进，豪荡感激。即公孙可知矣！昔有佳人公孙氏，一舞剑器动四方。观者如山色沮丧，天地为之久低昂。霍如羿射九日落，矫如群帝骖龙翔。来如雷霆收震怒，罢如江海凝清光。绛唇珠袖两寂寞，晚有弟子传芬芳。临颍美人在白帝，妙舞此曲神扬扬。与余问答既有以，感时抚事增惋伤。先帝侍女八千人，公孙剑器初第一。五十年间似反掌，风尘［氵项］洞昏王室。梨园子弟散如烟，女乐馀姿映寒日。金粟堆前木已拱，瞿塘石城草萧瑟。玳筵急管曲复终，乐极哀来月东出。老夫不知其所往，足茧荒山转愁疾。', '杜甫', NULL, 1);
INSERT INTO `article` VALUES (341, '石鱼湖上醉歌并序', '漫叟以公田米酿酒，因休暇，则载酒于湖上，时取一醉；欢醉中，据湖岸，引臂向鱼取酒，使舫载之，遍饮坐者。意疑倚巴丘，酌於君山之上，诸子环洞庭而坐，酒舫泛泛然，触波涛而往来者，乃作歌以长之。石鱼湖，似洞庭，夏水欲满君山青。山为樽，水为沼，酒徒历历坐洲鸟。长风连日作大浪，不能废人运酒舫。我持长瓢坐巴丘，酌饮四座以散愁。', '元结', NULL, 1);
INSERT INTO `article` VALUES (342, '山石', '山石荦确行径微，黄昏到寺蝙蝠飞。升堂坐阶新雨足，芭蕉叶大栀子肥。僧言古壁佛画好，以火来照所见稀。铺床拂席置羹饭，疏粝亦足饱我饥。夜深静卧百虫绝，清月出岭光入扉。天明独去无道路，出入高下穷烟霏。山红涧碧纷烂漫，时见松枥皆十围。当流赤足蹋涧石，水声激激风吹衣。人生如此自可乐，岂必局束为人［革几］！嗟哉吾党二三子，安得至老不更归！', '韩愈', NULL, 1);
INSERT INTO `article` VALUES (343, '八月十五夜赠张功曹', '纤云四卷天无河，清风吹空月舒波。沙平水息声影绝，一杯相属君当歌。君歌声酸辞且苦，不能听终泪如雨。洞庭连天九嶷高，蛟龙出没猩鼯号。十生九死到官所，幽居默默如藏逃。下床畏蛇食畏药，海气湿蛰熏腥臊。昨者州前槌大鼓，嗣皇继圣登夔皋。赦书一日行万里，罪从大辟皆除死。迁者追回流者还，涤瑕荡垢清朝班。州家申名使家抑，坎轲只得移荆蛮。判司卑官不堪说，未免捶楚尘埃间。同时辈流多上道，天路幽险难追攀。君歌且休听我歌，我歌今与君殊科。一年明月今宵多，人生由命非由他。有酒不饮奈明何！', '韩愈', NULL, 1);
INSERT INTO `article` VALUES (344, '谒衡岳庙遂宿岳寺题门楼', '五岳祭秩皆三公，四方环镇嵩当中。火维地荒足妖怪，天假神柄专其雄。喷云泄雾藏半腹，虽有绝顶谁能穷？我来正逢秋雨节，阴气晦昧无清风。潜心默祷若有应，岂非正直能感通！须臾静扫众峰出，仰见突兀撑青空。紫盖连延接天柱，石廪腾掷堆祝融。森然魄动下马拜，松柏一迳趋灵宫。纷墙丹柱动光彩，鬼物图画填青红。升阶伛偻荐脯酒，欲以菲薄明其衷。庙内老人识神意，睢盱侦伺能鞠躬。手持杯［王交］导我掷，云此最吉馀难同。窜逐蛮荒幸不死，衣食才足甘长终。侯王将相望久绝，神纵欲福难为功！夜投佛寺上高阁，星月掩映云［日童］［日龙］。猿鸣钟动不知曙，杲杲寒日生于东。', '韩愈', NULL, 1);
INSERT INTO `article` VALUES (345, '石鼓歌', '张生手持石鼓文，劝我识作石鼓歌。少陵无人谪仙死，才薄将奈石鼓何！周纲凌迟四海沸，宣王愤起挥天戈。大开明堂受朝贺，诸侯剑佩鸣相磨。搜于岐阳骋雄俊，万里禽兽皆遮罗。镌功勒成告万世，凿石作鼓隳嵯峨。从臣才艺咸第一，拣选撰刻留山阿。雨淋日炙野火燎，鬼物守护烦［扌为］呵。公从何处得纸本？毫发尽备无差讹。辞严义密读难晓，字体不类隶与蝌。年深岂免有缺画，快剑砍断生蛟鼍。鸾翔凤翥众仙下，珊瑚碧树交枝柯。金绳铁索锁钮壮，古鼎跃水龙腾梭。陋儒编诗不收入，二雅褊迫无委蛇。孔子西行不到秦，掎摭星宿遗羲娥。嗟予好古生苦晚，对此涕泪双滂沱。忆昔初蒙博士征，其年始改称元和。故人从军在右辅，为我度量掘臼科。濯冠沐浴告祭酒，如此至宝存岂多！毡包席裹可立致，十鼓只载数骆驼。荐诸太庙比郜鼎，光价岂止百倍过！圣恩若许留太学，诸生讲解得切磋。观经鸿都尚填咽，坐见举国来奔波。剜苔剔藓露节角，安置妥帖平不颇。大厦深檐与盖覆，经历久远期无佗。中朝大官老于事，讵肯感激徒□（“妍”右上一横改为“合”）婀。牧童敲火牛砺角，谁复著手为摩挲？日销月铄就埋没，六年西顾空吟哦。羲之俗书趁姿媚，数纸尚可博白鹅。继周八代争战罢，无人收拾理则那。方今太平日无事，柄任儒术崇丘轲。安能以此上论列，愿借辩口如悬河。石鼓之歌止于此，呜呼吾意其蹉跎！', '韩愈', NULL, 1);
INSERT INTO `article` VALUES (346, '渔翁', '渔翁夜傍西岩宿，晓汲清湘燃楚烛。烟销日出不见人，［矣欠］乃一声山水绿。回看天际下中流，岩上无心云相逐。', '柳宗元', NULL, 1);
INSERT INTO `article` VALUES (347, '长恨歌', '汉皇重色思倾国，御宇多年求不得。杨家有女初长成，养在深闺人未识。天生丽质难自弃，一朝选在君王侧。回眸一笑百媚生，六宫粉黛无颜色。春寒赐浴华清池，温泉水滑洗凝脂。侍儿扶起娇无力，始是新承恩泽时。云鬓花颜金步摇，芙蓉帐暖度春宵。春宵苦短日高起，从此君王不早朝。承欢侍宴无闲暇，春从春游夜专夜。后宫佳丽三千人，三千宠爱在一身。金星妆成娇侍夜，玉楼宴罢醉和春。姊妹弟兄皆列士，可怜光彩生门户。遂令天下父母心，不重生男重生女。骊宫高处入青云，仙乐风飘处处闻。缓歌慢舞凝丝竹，尽日君王看不足。渔阳鼙鼓动地来，惊破霓裳羽衣曲。九重城阙烟尘生，千乘万骑西南行。翠华摇摇行复止，西出都门百馀里。六军不发无奈何，宛转蛾眉马前死。花钿委地无人收，翠翘金雀玉搔头。君王掩面救不得，回看血泪相和流。黄埃散漫风萧索，云栈萦纡登剑阁。峨嵋山下少人行，旌旗无光日色薄。蜀江水碧蜀山青，圣主朝朝暮暮情。行宫见月伤心色，夜雨闻铃肠断声。天旋地转回龙驭，到此踌躇不能去。马嵬坡下泥土中，不见玉颜空死处。君臣相顾尽沾衣，东望都门信马归。归来池苑皆依旧，太液芙蓉未央柳。芙蓉如面柳如眉，对此如何不泪垂！春风桃李花开日，秋雨梧桐叶落时。西宫南内多秋草，落叶满阶红不扫。梨园子弟白发新，椒房阿监青娥老。夕殿萤飞思悄然，孤灯挑尽未成眠。迟迟钟鼓初长夜，耿耿星河欲曙天。鸳鸯瓦冷霜华重，翡翠衾寒谁与共？悠悠生死别经年，魂魄不曾来入梦。临邛道士鸿都客，能以精诚致魂魄。为感君王辗转思，遂教方士殷勤觅。排空驭气奔如电，升天入地求之遍。上穷碧落下黄泉，两处茫茫皆不见。忽闻海上有仙山，山在虚无缥缈间。楼阁玲珑五云起，其中绰约多仙子。中有一人字太真，雪肤花貌参差是。金阙西厢叩玉扃，转教小玉报双成。闻道汉家天子使，九华帐里梦魂惊。揽衣推枕起徘徊，珠箔银屏迤逦开。云鬓半偏新睡觉，花冠不整下堂来。风吹仙袂飘飘举，犹似霓裳羽衣舞。玉容寂寞泪阑干，梨花一枝春带雨。含情凝睇谢君王，一别音容两渺茫。昭阳殿里恩爱绝，蓬莱宫中日月长。回头下望人寰处，不见长安见尘雾。唯将旧物表深情，钿合金钗寄将去。钗留一股合一扇，钗擘黄金合分钿。但教心似金钿坚，天上人间会相见。临别殷勤重寄词，词中有誓两心知。七月七日长生殿，夜半无人私语时。在天愿作比翼鸟，在地愿为连理枝。天长地久有时尽，此恨绵绵无绝期！', '白居易', NULL, 1);
INSERT INTO `article` VALUES (348, '琵琶行并序', '元和十年，予左迁九江郡司马。明年秋，送客湓浦口，闻船中夜弹琵琶者，听其音，铮铮然有京都声；问其人，本长安倡女，尝学琵琶於穆曹二善才。年长色衰，委身为贾人妇。遂命酒，使快弹数曲，曲罢悯然。自叙少小时欢乐事，今漂沦憔悴，转徙於江湖间。予出官二年恬然自安，感斯人言，是夕，始觉有迁谪意，因为长句歌以赠之，凡六百一十六言，命曰琵琶行。浔言江头夜送客，枫叶荻花秋瑟瑟。主人下马客在船，举酒欲饮无管弦。醉不成欢惨将别，别时茫茫江浸月。忽闻水上琵琶声，主人忘归客不发。寻声暗问弹者谁，琵琶声停欲语迟。移船相近邀相见，添酒回灯重开宴。千呼万唤始出来，犹抱琵琶半遮面。转轴拨弦三两声，未成曲调先有情。弦弦掩抑声声思，似诉平生不得志。低眉信手续续弹，说尽心中无限事。轻拢慢捻抹复挑，初为霓裳后六么。大弦嘈嘈如急雨，小弦切切如私语。嘈嘈切切错杂弹，大珠小珠落玉盘。间关莺语花底滑，幽咽泉流水下滩。水泉冷涩弦凝绝，凝绝不通声渐歇。别有幽愁暗恨生，此时无声胜有声。银瓶乍破水浆迸，铁骑突出刀枪鸣。曲终收拨当心画，四弦一声如裂帛。东船西舫悄无言，唯见江心秋月白。沈吟放拨插弦中，整顿衣裳起敛容。自言本是京城女，家在虾蟆陵下住。十三学得琵琶成，名属教坊第一部。曲罢曾教善才服，妆成每被秋娘妒。五陵年少争缠头，一曲红绡不知数。钿头银篦击节碎，血色罗裙翻酒污。今年欢笑复明年，秋月春风等闲度。弟走从军阿姨死，暮去朝来颜色故。门前冷落车马稀，老大嫁作商人妇。商人重利轻别离，前月浮梁买茶去。去来江口守空船，绕船月明江水寒。夜深忽梦少年事，梦啼妆泪红阑干。我闻琵琶已叹息，又闻此语重唧唧。同是天涯沦落人，相逢何必曾相识！我从去年辞帝京，谪居卧病浔阳城。浔阳地僻无音乐，终岁不闻丝竹声。住近湓江地低湿，黄芦苦竹绕宅生。其间旦暮闻何物？杜鹃啼血猿哀鸣。春江花朝秋月夜，往往取酒还独倾。岂无山歌与村笛，呕哑嘲哳难为听！今夜闻君琵琶语，如听仙乐耳暂明。莫辞更坐弹一曲，为君翻作琵琶行。感我此言良久立，却坐促弦弦转急。凄凄不似向前声，满座重闻皆掩泣。座中泣下谁最多，江州司马青衫湿！', '白居易', NULL, 1);
INSERT INTO `article` VALUES (349, '韩碑', '元和天子神武姿，彼何人哉轩与羲。誓将上雪列圣耻，坐法宫中朝四夷。淮西有贼五十载，封狼生［豸区］［豸区］生罴。不据山河据平地，长戈利矛日可麾。帝得圣相相曰度，贼斫不死神扶持。腰悬相印作都统，阴风惨澹天王旗。□（上朔下心］武古通作牙爪，仪曹外郎载笔随。行军司马智且勇，十四万众犹虎貔。入蔡缚贼献太庙，功无与让恩不訾。帝曰汝度功第一，汝从事愈宜为辞。愈拜稽首蹈且舞，金石刻画臣能为。古者世称大手笔，此事不系于职司。当仁自古有不让，言讫屡颔天子颐。公退斋戒坐小阁，濡染大笔何淋漓！点窜尧典舜典字，涂改清庙生民诗。文成破体书在纸，清晨再拜铺丹墀。表曰臣愈昧死上，咏神圣功书之碑。碑高三丈字如斗，负以灵鳌蟠以螭。句奇语重喻者少，谗之天子言其私。长绳百尺拽碑倒，粗沙大石相磨治。公之斯文若元气，先时已入人肝脾。汤盘孔鼎有述作，今无其器存其辞。呜呼圣皇及圣相，相与［火亘］赫流淳熙。公之斯文不示后，曷与三五相攀追。愿书万本诵万过，口角流沫右手胝。传之七十有二代，以为封禅玉检明堂基。', '李商隐', NULL, 1);
INSERT INTO `article` VALUES (350, '燕歌行并序', '开元二十六年，客有从御史大夫张公出塞而还者，作燕歌行以示适，感征戍之事，因而和焉。汉家烟尘在东北，汉将辞家破残贼。男儿本自重横行，天子非常赐颜色。［扌从］金伐鼓下榆关，旌旆逶迤碣石间。校尉羽书飞瀚海，单于猎火照狼山。山川萧条极边土，胡骑凭陵杂风雨。战士军前半死生，美人帐下犹歌舞。大漠穷秋塞草衰，孤城落日斗兵稀。身当恩遇常轻敌，力尽关山未解围。铁衣远戍辛勤久，玉筋应啼别离后。少妇城南欲断肠，征人蓟北空回首。边庭飘摇那可度，绝域苍茫更何有！杀气三时作阵云，寒声一夜传刁斗。相看白刃血纷纷，死节从来岂顾勋？君不见沙场征战苦，至今犹忆李将军！', '高适', NULL, 1);
INSERT INTO `article` VALUES (351, '古从军行', '白日登山望烽火，黄昏饮马傍交河。行人刁斗风沙暗，公主琵琶幽怨多。野云万里无城郭，雨雪纷纷连大漠。胡雁哀鸣夜夜飞，胡儿眼泪双双落。闻道玉门犹被遮，应将性命逐轻车。年年战骨埋荒外，空见葡萄入汉家。', '李颀', NULL, 1);
INSERT INTO `article` VALUES (352, '洛阳女儿行', '洛阳女儿对门居，才可容颜十五馀。良人玉勒乘骢马，侍女金盘脍鲤鱼。画阁朱楼尽相望，红桃绿柳垂檐向。罗帷送上七香车，宝扇迎归九华帐。狂夫富贵在青春，意气骄奢剧季伦。自怜碧玉亲教舞，不惜珊瑚持与人。春窗曙灭九微火，九微片片飞花琐。戏罢曾无理曲时，妆成只是薰香坐。城中相识尽繁华，日夜经过赵李家。谁怜越女颜如玉，贫贱江头自浣纱！', '王维', NULL, 1);
INSERT INTO `article` VALUES (353, '老将行', '少年十五二十时，步行夺得胡马骑。射杀山中白额虎，肯数邺下黄须儿！一身转战三千里，一剑曾当百万师。汉兵奋迅如霹雳，虏骑崩腾畏蒺藜。卫青不败由天幸，李广无功缘数奇。自从弃置便衰朽，世事蹉跎成白首。昔时飞箭无全目，今日垂杨生左肘。路旁时卖故侯瓜，门前学种先生柳。苍茫古木连穷巷，寥落寒山对虚牖。誓令疏勒出飞泉，不似颍川空使酒。贺兰山下阵如云，羽檄交驰日夕闻。节使三河募年少，诏书五道出将军。试拂铁衣如雪色，聊持宝剑动星文。愿得燕弓射大将，耻令越甲鸣吾君。莫嫌旧日云中守，犹堪一战取功勋！', '王维', NULL, 1);
INSERT INTO `article` VALUES (354, '桃源行', '渔舟逐水爱山春，两岸桃花夹古津。坐看红树不知远，行尽青溪不见人。山口潜行始隈［阝奥］，山开旷望旋平陆。遥看一处攒云树，近入千家散花竹。樵客初传汉姓名，居人未改秦衣服。居人共住武陵源，还从物外起田园。月明松下房栊静，日出云中鸡犬喧。惊闻俗客争来集，竞引还家问都邑。平明闾巷扫花开，薄暮渔樵乘水入。初因避地去人间，及至成仙遂不还。峡里谁知有人事？世中遥望空云山。不疑灵境难闻见，尘心未尽思乡县。出洞无论隔山水，辞家终拟长游衍。自谓经过旧不迷，安知峰壑今来变？当时只记入山深，青溪几曲到云林。春来遍是桃花水，不辨仙源何处寻。', '王维', NULL, 1);
INSERT INTO `article` VALUES (355, '蜀道难', '噫吁戏，危乎高哉！蜀道之难难于上青天！蚕丛及鱼凫，开国何茫然！尔来四万八千岁，始与秦塞通人烟。西当太白有鸟道，可以横绝峨眉巅。地崩山摧壮士死，然后天梯石栈方钩连。上有六龙回日之高标，下有冲波逆折之回川。黄鹤之飞尚不得，猿猱欲度愁攀援。青泥何盘盘，百步九折萦岩峦。扪参历井仰胁息，以手抚膺坐长叹。问君西游何时还？畏途□（繁体“谗”换山旁）岩不可攀！但见悲鸟号古木，雄飞雌从绕林间。又闻子规啼，夜月愁空山。蜀道之难难于上青天！使人听此凋朱颜。连峰去天不盈尺，枯松倒挂倚绝壁。飞湍瀑流争喧［兀豕］，冰崖转石万壑雷。其险也如此！嗟尔远道之人，胡为乎来哉？剑阁峥嵘而崔嵬。一夫当关，万夫莫开。所守或匪亲，化为狼与豺。朝避猛虎，夕避长蛇。磨牙吮血，杀人如麻。锦城虽云乐，不如早还家。蜀道之难难于上青天！侧身西望常咨嗟！', '李白', NULL, 1);
INSERT INTO `article` VALUES (356, '长相思二首之一', '长相思，在长安。络纬秋啼金井阑，微霜凄凄簟色寒。孤灯不明思欲绝，卷帷望月空长叹。美人如花隔云端。上有青冥之长天，下有渌水之波澜。天长路远魂飞苦，梦魂不到关山难。长相思，摧心肝！', '李白', NULL, 1);
INSERT INTO `article` VALUES (357, '长相思二首之二', '日色已尽花含烟，月明欲素愁不眠。赵瑟初停凤凰柱，蜀琴欲奏鸳鸯弦。此曲有意无人传，愿随春风寄燕然。忆君迢迢隔青天。昔日横波目，今成流泪泉。不信妾肠断，归来看取明镜前。', '李白', NULL, 1);
INSERT INTO `article` VALUES (358, '行路难三首之一', '金樽清酒斗十千，玉盘珍羞值万钱。停杯投箸不能食，拔剑四顾心茫然。欲渡黄河冰塞川，将登太行雪满山。闲来垂钓碧溪上，忽复乘舟梦日边。行路难，行路难！多歧路，今安在？长风破浪会有时，直挂云帆济沧海。', '李白', NULL, 1);
INSERT INTO `article` VALUES (359, '行路难三首之二', '大道如青天，我独不得出。羞逐长安社中儿，赤鸡白狗赌梨栗。弹剑作歌奏苦声，曳裾王门不称情。淮阴市井笑韩信，汉朝公卿忌贾生。君不见，昔时燕家重郭隗，拥彗折节无嫌猜剧辛乐毅感恩分，输肝剖胆效英才。昭王白骨萦蔓草，谁人更扫黄金台？行路难，归去来！', '李白', NULL, 1);
INSERT INTO `article` VALUES (360, '行路难三首之三', '有耳莫洗颍川水，有口莫食首阳蕨。含光混世贵无名，何用孤高比云月？吾观自古贤达人，功成不退皆殒身。子胥既弃吴江上，屈原终投湘水滨。陆机雄才岂自保？李斯税驾苦不早。华亭鹤唳讵可闻，上蔡苍鹰何足道！君不见，吴中张翰称达生，秋风忽忆江东行。且乐生前一杯酒，何须身后千载名！', '李白', NULL, 1);
INSERT INTO `article` VALUES (361, '将进酒', '君不见，黄河之水天上来，奔流到海不复回。君不见，高堂明镜悲白发，朝如青丝暮成雪。人生得意须尽欢，莫使金樽空对月！天生我材必有用，千金散尽还复来。烹羊宰牛且为乐，会须一饮三百杯！岑夫子，丹丘生，将进酒，君莫停！与君歌一曲，请君为我侧耳听！钟鼓馔玉不足贵，但愿长醉不愿醒！古来圣贤皆寂寞，惟有饮者留其名！陈王昔时宴平乐，斗酒十千恣欢谑。主人何为言少钱？径须沽取对君酌。五花马，千金裘，呼儿将出换美酒，与尔同消万古愁！', '李白', NULL, 1);
INSERT INTO `article` VALUES (362, '兵车行', '车辚辚，马萧萧，行人弓箭各在腰。耶娘妻子走相送，尘埃不见咸阳桥。牵衣顿足拦道哭，哭声直上干云霄！道旁过者问行人，行人但云点行频。或从十五北防河，便至四十西营田。去时里正与裹头，归来头白还戍边！边亭流血成海水，武皇开边意未已。君不闻，汉家山东二百州，千村万落生荆杞！纵有健妇把锄犁，禾生陇亩无东西。况复秦兵耐苦战，被驱不异犬与鸡。长者虽有问，役夫敢申恨？且如今年冬，未休关西卒。县官急索租，租税从何出？信知生男恶，反是生女好。生女犹得嫁比邻，生男埋没随百草！君不见，青海头，古来白骨无人收。新鬼烦冤旧鬼哭，天阴雨湿声啾啾！', '杜甫', NULL, 1);
INSERT INTO `article` VALUES (363, '丽人行', '三月三日天气新，长安水边多丽人。态浓意远淑且真，肌理细腻骨肉匀。绣罗衣裳照暮春，蹙金孔雀银麒麟。头上何所有？翠微盍叶垂鬓唇。背后何所见？珠压腰［衤及］稳称身。就中云幕椒房亲，赐名大国虢与秦。紫驼之峰出翠釜，水精之盘行素鳞。犀箸餍饫久未下，鸾刀缕切空纷纶。黄门飞［革空］不动尘，御厨络绎送八珍。箫鼓哀吟感鬼神，宾从杂沓实要津。后来鞍马何逡巡，当轩下马入锦茵。杨花雪落覆白苹，青鸟飞去衔红巾。炙手可热势绝伦，慎莫近前丞相嗔！', '杜甫', NULL, 1);
INSERT INTO `article` VALUES (364, '哀江头', '少陵野老吞生哭，春日潜行曲江曲。江头宫殿锁千门，细柳新蒲为谁绿？忆昔霓旌下南苑，苑中景物生颜色。昭阳殿里第一人，同辇随君侍君侧。辇前才人带弓箭，白马嚼啮黄金勒。翻身向天仰射云，一箭正坠双飞翼。明眸皓齿今何在？血污游魂归不得！清渭东流剑阁深，去住彼此无消息。人生有情泪沾臆，江水江花岂终极？黄昏胡骑尘满城，欲往城南望城北。', '杜甫', NULL, 1);
INSERT INTO `article` VALUES (365, '哀王孙', '长安城头头白乌，夜飞延秋门上呼。又向人家啄大屋，屋底达官走避胡。金鞭断折九马死，骨肉不待同驰驱。腰下宝［“决”换王旁］青珊瑚，可怜王孙泣路隅！问之不肯道姓名，但道困苦乞为奴。已经百日窜荆棘，身上无有完肌肤。高帝子孙尽隆准，龙种自与常人殊。豺狼在邑龙在野，王孙善保千金躯。不敢长语临交衢，且为王孙立斯须。昨夜东风吹血腥，东来橐驼满旧都。朔方健儿好身手，昔何勇锐今何愚！窃闻天子已传位，圣德北服南单于。花门□（“嫠”下女换刀）面请雪耻，慎勿出口他人狙！哀哉王孙慎勿疏，五陵佳气无时无。', '杜甫', NULL, 1);
INSERT INTO `article` VALUES (366, '经邹鲁祭孔子而叹之', '夫子何为者，栖栖一代中。地犹鄹氏邑，宅即鲁王宫。叹凤嗟身否？伤麟怨道穷。今看两楹奠，当与梦时同。', '唐玄宗', NULL, 1);
INSERT INTO `article` VALUES (367, '望月怀远', '海上生明月，天涯共此时。情人怨遥夜，竟夕起相思！灭烛怜光满，披衣觉露滋。不堪盈手赠，还寝梦佳期。', '张九龄', NULL, 1);
INSERT INTO `article` VALUES (368, '送杜少府之任蜀州', '城阙辅三秦，风烟望五津。与君离别意，同是宦游人。海内存知己，天涯若比邻。无为在歧路，儿女共沾巾。', '王勃', NULL, 1);
INSERT INTO `article` VALUES (369, '在狱咏蝉并序', '余禁所禁垣西，是法厅事也。有古槐数株焉，虽生意可知，同殷仲文之古树，而听讼斯在，即周召伯之甘棠。每至夕照低阴，秋蝉疏引，发声幽息，有切尝闻；岂人心异於曩时，将虫响悲於前听？嗟乎！声以动容，德以象贤，故洁其身也，禀君子达人之高行；蜕其皮也，有仙都羽化之灵姿。候时而来，顺阴阳之数；应节为变，审藏用之机。有目斯开，不以道昏而昧其视；有翼自薄，不以俗厚而易其真。吟乔树之微风，韵资天纵；饮高秋之坠露，清畏人知。仆失路艰虞，遭时徽［纟墨］，不哀伤而自怨，未摇落而先衰。闻蟪蛄之流声，悟平反之已奏；见螳螂之抱影，怯危机之未安。感而缀诗，贻诸知己。庶情沿物应，哀弱羽之飘零；道寄人知，悯馀声之寂寞。非谓文墨，取代幽忧云尔。西路蝉声唱，南冠客思侵。那堪玄鬓影，来对白头吟！露重飞难进，风多响易沉。无人信高洁，谁为表予心？', '骆宾王', NULL, 1);
INSERT INTO `article` VALUES (370, '和晋陵路丞早春游望', '独有宦游人，偏惊物候新。云霞出海曙，梅柳渡江春。淑气催黄鸟，晴光转绿苹。忽闻歌古调，归思欲沾巾。', '杜审言', NULL, 1);
INSERT INTO `article` VALUES (371, '杂诗', '闻道黄龙戍，频年不解兵。可怜闺里月，长在汉家营。少妇今春意，良人昨夜情。谁能将旗鼓，一为取龙城？', '沈全期', NULL, 1);
INSERT INTO `article` VALUES (372, '题大庾岭北驿', '阳月南飞雁，传闻至此回。我行殊未已，何日复归来？江静潮初落，林昏瘴不开。明朝望乡处，应见陇头梅。', '宋之问', NULL, 1);
INSERT INTO `article` VALUES (373, '次北固山下', '客路青山外，行舟绿水前。潮平两岸阔，风正一帆悬。海日生残夜，江春入旧年。乡书何处达？归雁洛阳边。', '王湾', NULL, 1);
INSERT INTO `article` VALUES (374, '题破山寺后禅院', '清晨入古寺，初日照高林。曲径通幽处，禅房花木深。山光悦鸟性，潭影空人心。万籁此俱寂，惟馀钟磬音。', '常建', NULL, 1);
INSERT INTO `article` VALUES (375, '寄左省杜拾遗', '联步趋丹陛，分曹限紫微。晓随天仗入，暮惹御香归。白发悲花落，青云羡鸟飞。圣朝无阙事，自觉谏书稀。', '岑参', NULL, 1);
INSERT INTO `article` VALUES (376, '赠孟浩然', '吾爱孟夫子，风流天下闻。红颜弃轩冕，白首卧松云。醉月频中圣，迷花不事君。高山安可仰，徒此挹清芬。第三章?唐诗300首 101-150', '李白', NULL, 1);
INSERT INTO `article` VALUES (377, '渡荆门送别', '渡远荆门外，来从楚国游。山随平野尽，江入大荒流。月下飞天镜，云生结海楼。仍怜故乡水，万里送行舟。', '李白', NULL, 1);
INSERT INTO `article` VALUES (378, '送友人', '青山横北郭，白水绕东城。此地一为别，孤蓬万里征。浮云游子意，落日故人情。挥手自兹去，萧萧班马鸣。', '李白', NULL, 1);
INSERT INTO `article` VALUES (379, '听蜀僧浚弹琴', '蜀僧抱绿绮，西下峨眉峰。为我一挥手，如听万壑松。客心洗流水，馀响入霜钟。不觉碧山暮，秋云暗几重。', '李白', NULL, 1);
INSERT INTO `article` VALUES (380, '夜泊牛渚怀古', '牛渚西江夜，青天无片云。登舟望秋月，空忆谢将军。余亦能高咏，斯人不可闻。明朝挂帆席，枫叶落纷纷。', '李白', NULL, 1);
INSERT INTO `article` VALUES (381, '月夜', '今夜［鹿阝］州月，闺中只独看。遥怜小儿女，未解忆长安。香雾云鬟湿，清辉玉臂寒。何时倚虚幌，双照泪痕干？', '杜甫', NULL, 1);
INSERT INTO `article` VALUES (382, '春望', '国破山河在，城春草木深。感时花溅泪，恨别鸟惊心。烽火连三月，家书抵万金。白头搔更短，浑欲不胜簪。', '杜甫', NULL, 1);
INSERT INTO `article` VALUES (383, '春宿左省', '花隐掖垣暮，啾啾栖鸟过。星临万户动，月傍九霄多。不寝听金钥，因风想玉珂。明朝有封事，数问夜如何？', '杜甫', NULL, 1);
INSERT INTO `article` VALUES (384, '至德二载甫自京金光门出，问道归凤翔。乾元初从左拾遗移华州掾。与亲', '故别，因出此门。有悲往事。此道昔归顺，西郊胡正繁。至今残破胆，应有未招魂。近得归京邑，移官岂至尊？无才日衰老，驻马望千门。', '杜甫', NULL, 1);
INSERT INTO `article` VALUES (385, '月夜忆舍弟', '戍鼓断人行，秋边一雁声。露从今夜白，月是故乡明。有弟皆分散，无家问死生。寄书长不达，况乃未休兵。', '杜甫', NULL, 1);
INSERT INTO `article` VALUES (386, '天末怀李白', '凉风起天末，君子意如何？鸿雁几时到，江湖秋水多。文章憎命达，魑魅喜人过。应共冤魂语，投诗赠汨罗。', '杜甫', NULL, 1);
INSERT INTO `article` VALUES (387, '奉济驿重送严公四韵', '远送从此别，青山空复情。几时杯重把，昨夜月同行。列郡讴歌惜，三朝出入荣。将村独归处，寂寞养残生。', '杜甫', NULL, 1);
INSERT INTO `article` VALUES (388, '别房太尉墓', '他乡复行役，驻马别孤坟。近泪无干土，低空有断云。对棋陪谢傅，把剑觅徐君。唯见林花落，莺啼送客闻。', '杜甫', NULL, 1);
INSERT INTO `article` VALUES (389, '旅夜书怀', '细草微风岸，危樯独夜舟。星垂平野阔，月涌大江流。名岂文章著？官应老病休。飘飘何所似，天地一沙鸥。', '杜甫', NULL, 1);
INSERT INTO `article` VALUES (390, '登岳阳楼', '昔闻洞庭水，今上岳阳楼。吴楚东南坼，乾坤日夜浮。亲朋无一字，老病有孤舟。戎马关山北，凭轩涕泗流。', '杜甫', NULL, 1);
INSERT INTO `article` VALUES (391, '辋川闲居赠裴秀才迪', '寒山转苍翠，秋水日潺［氵爰］。倚杖柴门外，临风听暮蝉。渡头馀落日，墟里上孤烟。复值接舆醉，狂歌五柳前。', '王维', NULL, 1);
INSERT INTO `article` VALUES (392, '山居秋暝', '空山新雨后，天气晚来秋。明月松间照，清泉石上流。竹喧归浣女，莲动下渔舟。随意春芳歇，王孙自可留。', '王维', NULL, 1);
INSERT INTO `article` VALUES (393, '归嵩山作', '清川带长薄，车马去闲闲。流水如有意，暮禽相与还。荒城临古渡，落日满秋山。迢递嵩高下，归来且闭关。', '王维', NULL, 1);
INSERT INTO `article` VALUES (394, '终南山', '太乙近天都，连山接海隅。白云回望合，青霭入看无。分野中峰变，阴晴众壑殊。欲投人处宿，隔水问樵夫。', '王维', NULL, 1);
INSERT INTO `article` VALUES (395, '酬张少府', '晚年惟好静，万事不关心。自顾无长策，空知返旧林。松风吹解带，山月照弹琴。君问穷通理，渔歌入浦深。', '王维', NULL, 1);
INSERT INTO `article` VALUES (396, '过香积寺', '不知香积寺，数里入云峰。古木无人径，深山何处钟？泉声咽危石，日色冷青松。薄暮空潭曲，安禅制毒龙。', '王维', NULL, 1);
INSERT INTO `article` VALUES (397, '送梓州李使君', '万壑树参天，千山响杜鹃。山中一夜雨，树杪百重泉。汉女输［木童］布，巴人讼芋田。文翁翻教授，不敢倚先贤。', '王维', NULL, 1);
INSERT INTO `article` VALUES (398, '汉江临眺', '楚塞三湘接，荆门九派通。江流天地外，山色有无中。郡邑浮前浦，波澜动远空。襄阳好风日，留醉与山翁。', '王维', NULL, 1);
INSERT INTO `article` VALUES (399, '终南别业', '中岁颇好道，晚家南山陲。兴来美独往，胜事空自知。行到水穷处，坐看云起时。偶然值林叟，谈笑无还期。', '王维', NULL, 1);
INSERT INTO `article` VALUES (400, '望洞庭湖赠张丞相', '八月湖水平，涵虚混太清。气蒸云梦泽，波撼岳阳城。欲济无舟楫，端居耻圣明。坐观垂钓者，空有羡鱼情。', '孟浩然', NULL, 1);
INSERT INTO `article` VALUES (401, '与诸子登岘山', '人事有代谢，往来成古今。江山留胜迹，我辈复登临。水落鱼梁浅，天寒梦泽深。羊公碑字在，读罢泪沾襟。', '孟浩然', NULL, 1);
INSERT INTO `article` VALUES (402, '清明日宴梅道士房', '林卧愁春尽，开轩览物华。忽逢青鸟使，邀入赤松家。丹灶初开火，仙桃正发花。童颜若可驻，何惜醉流霞！', '孟浩然', NULL, 1);
INSERT INTO `article` VALUES (403, '岁暮归南山', '北阙休上书，南山归敝庐。不才明主弃，多病故人疏。白发催年老，青阳逼岁除。永怀愁不寐，松月夜窗墟。', '孟浩然', NULL, 1);
INSERT INTO `article` VALUES (404, '过故人庄', '故人具鸡黍，邀我至田家。绿树村边合，青山郭外斜。开轩面场圃，把酒话桑麻。待到重阳日，还来就菊花。', '孟浩然', NULL, 1);
INSERT INTO `article` VALUES (405, '秦中感秋寄远上人', '一丘尝欲卧，三径苦无资。北土非吾愿，东林怀我师。黄金燃桂尽，壮志逐年衰。日夕凉风至，闻蝉但益悲。', '孟浩然', NULL, 1);
INSERT INTO `article` VALUES (406, '宿桐庐江寄广陵旧游', '山暝听猿愁，沧江急夜流。风鸣两岸叶，月照一孤舟。建德非吾土，维扬忆旧游。还将两行泪，遥寄海西头。', '孟浩然', NULL, 1);
INSERT INTO `article` VALUES (407, '留别王侍御维', '寂寂竟何待，朝朝空自归。欲寻芳草去，惜与故人违。当路谁相假，知音世所稀。只应守寂寞，还掩故园扉。', '孟浩然', NULL, 1);
INSERT INTO `article` VALUES (408, '早寒江上有怀', '木落雁南渡，北风江上寒。我家襄水曲，遥隔楚云端。乡泪客中尽，孤帆天际看。迷津欲有问，平海夕漫漫。', '孟浩然', NULL, 1);
INSERT INTO `article` VALUES (409, '秋日登吴公台上寺远眺', '古台摇落后，秋日望乡心。野寺人来少，云峰水隔深。夕阳依旧垒，寒磬满空林。惆怅南朝事，长江独至今。', '刘长卿', NULL, 1);
INSERT INTO `article` VALUES (410, '送李中丞归汉阳别业', '流落征南将，曾驱十万师。罢归无旧业，老去恋明时。独立三边静，轻生一剑知。茫茫江汉上，日暮复何之。', '刘常卿', NULL, 1);
INSERT INTO `article` VALUES (411, '饯别王十一南游', '望君烟水阔，挥手泪沾巾。飞鸟没何处，青山空向人。长江一帆远，落日五湖春。谁见汀洲上，相思愁白苹？', '刘长卿', NULL, 1);
INSERT INTO `article` VALUES (412, '寻南溪常山道人隐居', '一路经行处，莓苔见履痕。白云依静渚，春草闭闲门。过雨看松色，随山到水源。溪花与禅意，相对亦忘言。', '刘长卿', NULL, 1);
INSERT INTO `article` VALUES (413, '新年作', '乡心新岁切，天畔独潸然。老至居人下，春归在客先。岭猿同旦暮，江柳共风烟。已似长沙傅，从今又几年？', '刘长卿', NULL, 1);
INSERT INTO `article` VALUES (414, '送僧归日本', '上国随缘住，来途若梦行。浮天沧海远，去世法舟轻。水月通禅寂，鱼龙听梵声。惟怜一灯影，万里眼中明。', '钱起', NULL, 1);
INSERT INTO `article` VALUES (415, '谷口书斋寄杨补阙', '泉壑带茅茨，云霞生薜帷。竹怜新雨后，山爱夕阳时。闲鹭栖常早，秋花落更迟。家童扫萝径，昨与故人期。', '钱起', NULL, 1);
INSERT INTO `article` VALUES (416, '淮上喜会梁川故人', '江汉曾为客，相逢每醉还。浮云一别后，流水十年间。欢笑情如旧，萧疏鬓已斑。何因北归去，淮上对秋山。', '韦应物', NULL, 1);
INSERT INTO `article` VALUES (417, '赋得暮雨送李胄', '楚江微雨里，建业暮钟时。漠漠帆来重，冥冥鸟去迟。海门深不见，浦树远含滋。相送情无限，沾襟比散丝。', '韦应物', NULL, 1);
INSERT INTO `article` VALUES (418, '酬程延秋夜即事见赠', '长簟迎风早，空城澹月华。星河秋一雁，砧杵夜千家。节候看应晚，心期卧亦赊。向来吟秀句，不觉已鸣鸦。', '韩翃', NULL, 1);
INSERT INTO `article` VALUES (419, '阙题', '道由白云尽，春与青溪长。时有落花至，远隋流水香。闲门向山路，深柳读书堂。幽映每白日，清辉照衣裳。', '刘脊虚', NULL, 1);
INSERT INTO `article` VALUES (420, '江乡故人偶集客舍', '天秋月又满，城阙夜千重。还作江南会，翻疑梦里逢。风枝惊暗鹊，露草覆寒虫。羁旅长堪醉，相留畏晓钟。', '戴叔伦', NULL, 1);
INSERT INTO `article` VALUES (421, '李端公', '故关衰草遍，离别正堪悲！路出寒云外，人归暮雪时。少孤为客早，多难识君迟。掩泪空相向，风尘何处期？', '卢纶', NULL, 1);
INSERT INTO `article` VALUES (422, '喜见外弟又言别', '十年离乱后，长大一相逢。问姓惊初见，称名忆旧容。别来沧海事，语罢暮天钟。明日巴陵道，秋山又几重。', '李益', NULL, 1);
INSERT INTO `article` VALUES (423, '云阳馆与韩绅宿别', '故人江海别，几度隔山川。乍见翻疑梦，相悲各问年。孤灯寒照雨，深竹暗浮烟。更有明朝恨，离杯惜共传。', '司空曙', NULL, 1);
INSERT INTO `article` VALUES (424, '喜外弟卢纶见宿', '静夜四无邻，荒居旧业贫。雨中黄叶树，灯下白头人。以我独沉久，愧君相访频。平生自有分，况是蔡家亲！', '司空曙', NULL, 1);
INSERT INTO `article` VALUES (425, '贼平后送人北归', '世乱同南去，时清独北还。他乡生白发，旧国见青山。晓月过残垒，繁星宿故关。寒禽与衰草，处处伴愁颜。', '司空曙', NULL, 1);
INSERT INTO `article` VALUES (426, '蜀先主庙', '天地英雄气，千秋尚凛然！势分三足鼎，业复五铢钱。得相能开国，生儿不象贤。凄凉蜀故妓，来舞魏宫前。第四章?唐诗300首 151-200', '刘禹锡', NULL, 1);
INSERT INTO `article` VALUES (427, '没蕃故人', '前年伐月支，城下没全师。蕃汉断消息，死生长别离。无人收废帐，归马识残旗。欲祭疑君在，天涯哭此时。', '张籍', NULL, 1);
INSERT INTO `article` VALUES (428, '赋得古原草送别', '离离原上草，一岁一枯荣。野火烧不尽，春风吹又生。远芳侵古道，晴翠接荒城。又送王孙去，萋萋满别情。', '白居易', NULL, 1);
INSERT INTO `article` VALUES (429, '旅宿', '旅馆无良伴，凝情自悄然。寒灯思旧事，断雁警愁眠。远梦归侵晓，家书到隔年。沧江好烟月，门系钓鱼船。', '杜牧', NULL, 1);
INSERT INTO `article` VALUES (430, '秋日赴阙题潼关驿楼', '红叶晚萧萧，长亭酒一瓢。残云归太华，疏雨过中条。树色随山迥，河声入海遥。帝乡明日到，犹自梦渔樵。', '许浑', NULL, 1);
INSERT INTO `article` VALUES (431, '早秋', '遥夜泛清瑟，西风生翠萝。残萤栖玉露，早雁拂银河。高树晓还密，远山晴更多。淮南一叶下，自觉老烟波。', '许浑', NULL, 1);
INSERT INTO `article` VALUES (432, '蝉', '本以高难饱，徒劳恨费声。五更疏欲断，一树碧无情。薄宦梗犹泛，故园芜已平。烦君最相警，我亦举家清。', '李商隐', NULL, 1);
INSERT INTO `article` VALUES (433, '风雨', '凄凉宝剑篇，羁泊欲穷年。黄叶仍风雨，青楼自管弦。新知遭薄俗，旧好隔良缘。心断新丰酒，销愁斗几千。', '李商隐', NULL, 1);
INSERT INTO `article` VALUES (434, '落花', '高阁客竟去，小园花乱飞。参差连曲陌，迢递送斜晖。肠断未忍扫，眼穿仍欲归。芳心向春尽，所得是沾衣。', '李商隐', NULL, 1);
INSERT INTO `article` VALUES (435, '凉思', '客去波平槛，蝉休露满枝。永怀当此节，倚立自移时。北斗兼春远，南陵寓使迟。天涯占梦数，疑误有新知。', '李商隐', NULL, 1);
INSERT INTO `article` VALUES (436, '北青萝', '残阳西入崦，茅屋访孤僧。落叶人何在？寒云路几层？独敲初夜磬，闲倚一枝藤。世界微尘里，吾宁爱与憎。', '李商隐', NULL, 1);
INSERT INTO `article` VALUES (437, '送人东游', '荒戍落黄叶，浩然离故关。高风汉阳渡，初日郢门山。江上几人在？天涯孤棹还。何当重相见，樽酒慰离颜？', '温庭筠', NULL, 1);
INSERT INTO `article` VALUES (438, '灞上秋居', '灞原风雨定，晚见雁行频。落叶他乡树，寒灯独夜人。空园白露滴，孤壁野僧邻。寄卧郊扉久，何年致此身？', '马戴', NULL, 1);
INSERT INTO `article` VALUES (439, '楚江怀古', '露气寒光集，微阳下楚丘。猿啼洞庭树，人在木兰舟。广泽生明月，苍山夹乱流。云中君不见，竟夕自悲秋。', '马戴', NULL, 1);
INSERT INTO `article` VALUES (440, '书边事', '调角断清秋，征人倚戍楼。春风对青冢，白日落梁州。大漠无兵阻，穷边有客游。蕃情似此水，长愿向南流。', '张乔', NULL, 1);
INSERT INTO `article` VALUES (441, '巴山道中除夜有怀', '迢递三巴路，羁危万里身。乱山残雪夜，孤独异乡春。渐与骨肉远，转於僮仆亲。那堪正飘泊，明日岁华新。', '崔涂', NULL, 1);
INSERT INTO `article` VALUES (442, '孤雁', '几行归塞尽，片影独何之？暮雨相呼失，寒塘欲下迟。渚云低暗渡，关月冷相随。未必逢［矢曾］缴，孤飞自可疑。', '崔涂', NULL, 1);
INSERT INTO `article` VALUES (443, '春宫怨', '早被婵娟误，欲妆临镜慵。承恩不在貌，教妾若为容。风暖鸟声碎，日高花影重。年年越溪女，相忆采芙蓉。', '杜荀鹤', NULL, 1);
INSERT INTO `article` VALUES (444, '章台夜思', '清瑟怨遥夜，绕弦风雨哀。孤灯闻楚角，残月下章台。芳草已云暮，故人殊未来。乡书不可寄，秋雁又南回。', '韦庄', NULL, 1);
INSERT INTO `article` VALUES (445, '寻陆鸿渐不遇', '移家虽带郭，野径入桑麻。近种篱边菊，秋来未著花。扣门无犬吠，欲去问西家。报到山中去，归来每日斜。', '僧皎然', NULL, 1);
INSERT INTO `article` VALUES (446, '黄鹤楼', '昔人已乘黄鹤去，此地空馀黄鹤楼。黄鹤一去不复返，白云千载空悠悠。晴川历历汉阳树，芳草萋萋鹦鹉洲。日暮乡关何处是，烟波江上使人愁。', '崔颢', NULL, 1);
INSERT INTO `article` VALUES (447, '行经华阴', '迢□（“绕”换山旁）太华俯咸京，天外三峰削不成。武帝祠前云欲散，仙人掌上雨初晴。河山北枕秦关险，驿树西连汉［田寺］平。借问路傍名利客，无如此处学长生。', '崔颢', NULL, 1);
INSERT INTO `article` VALUES (448, '望蓟门', '燕台一去客心惊，箫鼓喧喧汉将营。万里寒光生积雪，三边曙色动危旌。沙场烽火侵胡月，海畔云山拥蓟城。少小虽非投笔吏，论功还欲请长缨。', '祖咏', NULL, 1);
INSERT INTO `article` VALUES (449, '送魏万之京', '朝闻游子唱骊歌，昨夜微霜初度河。鸿雁不堪愁里听，云山况是客中过。关城树色催寒近，御苑砧声向晚多。莫见长安行乐处，空令岁月易蹉跎。', '李颀', NULL, 1);
INSERT INTO `article` VALUES (450, '九日登望仙台呈刘明府', '汉文皇帝有高台，此日登临曙色开。三晋云山皆北向，二陵风雨自东来。关门令尹谁能识？河上仙翁去不回。且欲竟寻彭泽宰，陶然共醉菊花杯。', '崔曙', NULL, 1);
INSERT INTO `article` VALUES (451, '送李少府贬峡中王少府贬长沙', '嗟君此别意何如？驻马衔杯问谪居。巫峡啼猿数行泪，衡阳归雁几封书。青枫江上秋帆远，白帝城边古木疏。圣代即今多雨露，暂时分手莫踌躇。', '高适', NULL, 1);
INSERT INTO `article` VALUES (452, '奉和中书舍人贾至早朝大明宫', '鸡鸣紫陌曙光寒，莺啭皇州春色阑。金阙晓钟开万户，玉阶仙仗拥千官。花迎剑佩星初落，柳拂旌旗露未干。独有凤凰池上客，阳春一曲和皆难。', '岑参', NULL, 1);
INSERT INTO `article` VALUES (453, '和贾舍人早朝大明宫之作', '绛帻鸡人送晓筹，尚衣方进翠云裘。九天阊阖开宫殿，万国衣冠拜冕旒。日色才临仙掌动，香烟欲傍衮龙浮。朝罢须裁五色诏，佩声归向凤池头。', '王维', NULL, 1);
INSERT INTO `article` VALUES (454, '奉和圣制从蓬莱向兴庆阁道中留春雨中春望之作应制', '渭水自萦秦塞曲，黄山旧绕汉宫斜。銮舆迥出千门柳，阁道回看上苑花。云里帝城双凤阙，雨中春树万人家。为乘阳气行时令，不是宸游玩物华。', '王维', NULL, 1);
INSERT INTO `article` VALUES (455, '积雨辋川庄作', '积雨空林烟火迟，蒸藜炊黍饷东□（“淄”去三点水加草头）。漠漠水田飞白鹭，阴阴夏木啭黄鹂。山中习静观朝槿，松下清斋折露葵。野老与人争席罢，海鸥何事更相疑。', '王维', NULL, 1);
INSERT INTO `article` VALUES (456, '酬郭给事', '洞门高阁霭馀辉，桃李阴阴柳絮飞。禁里疏钟官舍晚，省中啼鸟吏人稀。晨摇玉佩趋金殿，夕奉天书拜琐闱。强欲从君无那老，将因卧病解朝衣。', '王维', NULL, 1);
INSERT INTO `article` VALUES (457, '蜀相', '丞相祠堂何处寻？锦官城外柏森森。映阶碧草自春色，隔叶黄鹂空好音。三顾频烦天下计，两朝开济老臣心。出师未捷身先死，长使英雄泪满襟！', '杜甫', NULL, 1);
INSERT INTO `article` VALUES (458, '客至', '舍南舍北皆春水，但见群鸥日日来。花径不曾缘客扫，蓬门今始为君开。盘飧市远无兼味，樽酒家贫只旧醅。肯与邻翁相对饮，隔篱呼取尽馀杯！', '杜甫', NULL, 1);
INSERT INTO `article` VALUES (459, '野望', '西山白雪三城戍，南浦清江万里桥。海内风尘诸弟隔，天涯涕泪一身遥。唯将迟暮供多病，未有涓埃答圣朝。跨马出郊时极目，不堪人事日萧条！', '杜甫', NULL, 1);
INSERT INTO `article` VALUES (460, '闻官军收河南河北', '剑外忽传收蓟北，初闻涕泪满衣裳。却看妻子愁何在，漫卷诗书喜欲狂。白日放歌须纵酒，青春作伴好还乡！即从巴峡穿巫峡，便下襄阳向洛阳。', '杜甫', NULL, 1);
INSERT INTO `article` VALUES (461, '登高', '风急天高猿啸哀，渚清沙白鸟飞回。无边落木萧萧下，不尽长江滚滚来。万里悲秋常作客，百年多病独登台。艰难苦恨繁霜鬓，潦倒新停浊酒杯。', '杜甫', NULL, 1);
INSERT INTO `article` VALUES (462, '登楼', '花近高楼伤客心，万方多难此登临。锦江春色来天地，玉垒浮云变古今。北极朝庭终不改，西山寇盗莫相侵！可怜后主还祠庙，日暮聊为梁父吟。', '杜甫', NULL, 1);
INSERT INTO `article` VALUES (463, '宿府', '清秋幕府井梧寒，独宿江城蜡炬残。永夜角声悲自语，中天月色好谁看？风尘荏苒音书绝，关塞萧条行陆难。已忍伶俜十年事，强移栖息一枝安。', '杜甫', NULL, 1);
INSERT INTO `article` VALUES (464, '阁夜', '岁暮阴阳催短景，天涯霜雪霁寒霄。五更鼓角声悲壮，三峡星河影动摇。野哭千家闻战伐，夷歌数处起渔樵。卧龙跃马终黄土，人事音书漫寂寥。', '杜甫', NULL, 1);
INSERT INTO `article` VALUES (465, '咏怀古迹五首之一', '支离东北风尘际，漂泊西南天地间。三峡楼台淹日月，五溪衣服共云山。羯胡事主终无赖，词客哀时且未还。庾信平生最萧瑟，暮年诗赋动江关。', '杜甫', NULL, 1);
INSERT INTO `article` VALUES (466, '咏怀古迹五首之二', '摇落深知宋玉悲，风流儒雅亦吾师。怅望千秋一洒泪，萧条异代不同时。江山故宅空文藻，云雨荒台岂梦思！最是楚宫俱泯灭，舟人指点到今疑。', '杜甫', NULL, 1);
INSERT INTO `article` VALUES (467, '咏怀古迹五首之三', '群山万壑赴荆门，生长明妃尚有村。一去紫台连朔漠，独留青冢向黄昏。画图省识春风面，环佩空归月下魂。千载琵琶作胡语，分明怨恨曲中论。', '杜甫', NULL, 1);
INSERT INTO `article` VALUES (468, '咏怀古迹五首之四', '蜀主征吴幸三峡，崩年亦在永安宫。翠华想像空山里，玉殿虚无野寺中。古庙杉松巢水鹤，岁时伏腊走村翁。武侯祠屋常邻近，一体君臣祭祀同。', '杜甫', NULL, 1);
INSERT INTO `article` VALUES (469, '咏怀古迹五首之五', '诸葛大名垂宇宙，宗臣遗像肃清高。三分割据纡筹策，万古云霄一羽毛。伯仲之间见伊吕，指挥若定失萧曹。运移汉祚终难复，志决身歼军务劳。', '杜甫', NULL, 1);
INSERT INTO `article` VALUES (470, '江州重别薛六柳八二员外', '生涯岂料承优诏？世事空知学醉歌。江上月明胡雁过，淮南木落楚山多。寄身且喜沧洲近，顾影无如白发何！今日龙钟人共老，愧君犹遣慎风波。', '刘长卿', NULL, 1);
INSERT INTO `article` VALUES (471, '长沙过贾谊宅', '三年谪宦此栖迟，万古惟留楚客悲。秋草独寻人去后，寒林空见日斜时。汉文有道恩犹薄，湘水无情吊岂知？寂寂江山摇落处，怜君何事到天涯！', '刘长卿', NULL, 1);
INSERT INTO `article` VALUES (472, '自夏口至鹦洲夕望岳阳寄源中丞', '汀洲无浪复无烟，楚客相思益渺然。汉口夕阳斜渡鸟，洞庭秋水远连天。孤城背岭寒吹角，独戍临江夜泊船。贾谊上书忧汉室，长沙谪去古今怜。', '刘长卿', NULL, 1);
INSERT INTO `article` VALUES (473, '赠阙下裴舍人', '二月黄鹂飞上林，春城紫禁晓阴阴。长乐钟声花外尽，龙池柳色雨中深。阳和不散穷途恨，霄汉长怀捧日心。献赋十年犹未遇，羞将白发对华簪。', '钱起', NULL, 1);
INSERT INTO `article` VALUES (474, '寄李儋元锡', '去年花里逢君别，今日花开又一年。世事茫茫难自料，春愁黯黯独成眠。身多疾病思田里，邑有流亡愧俸钱。闻道欲来相问讯，西楼望月几回圆？', '韦应物', NULL, 1);
INSERT INTO `article` VALUES (475, '同题仙游观', '仙台初见五城楼，风物凄凄宿雨收。山色遥连秦树晚，砧声近报汉宫秋。疏松影落空坛静，细草香闲小洞幽。何用别寻方外去，人间亦自有丹丘！第五章?唐诗300首 201-250', '韩翃', NULL, 1);
INSERT INTO `article` VALUES (476, '春思', '莺啼燕语报新年，马邑龙堆路几千。家住层城邻汉苑，心随明月到胡天。机中锦字论长恨，楼上花枝笑独眠。为问天戎窦车骑，何时返旆勒燕然？', '皇甫冉', NULL, 1);
INSERT INTO `article` VALUES (477, '晚次鄂州', '云开远见汉阳城，犹是孤帆一日程。估客昼眠知浪静，舟人夜语觉潮生。三湘愁鬓逢秋色，万里归心对月明。旧业已随征战尽，更堪江上鼓鼙声。', '卢纶', NULL, 1);
INSERT INTO `article` VALUES (478, '登柳州城楼寄漳汀封连四州刺史', '城上高楼接大荒，海天愁思正茫茫。惊风乱［风占）芙蓉水，密雨斜侵薜荔墙。岭树重遮千里目，江流曲似九回肠。共来百越文身地，犹自音书滞一乡。', '柳宗元', NULL, 1);
INSERT INTO `article` VALUES (479, '西塞山怀古', '王浚楼船下益州，金陵王气黯然收。千寻铁锁沈江底，一片降幡出石头。人世几回伤往事？山形依旧枕寒流。从今四海为家日，故垒萧萧芦荻秋。', '刘禹锡', NULL, 1);
INSERT INTO `article` VALUES (480, '遣悲怀三首之一', '谢公最小偏怜女，自嫁黔娄百事乖。顾我无衣搜荩箧，泥他沽酒拔金钗。野蔬充膳甘长藿，落叶添薪仰古槐。今日俸钱过十万，与君营奠复营斋。', '元稹', NULL, 1);
INSERT INTO `article` VALUES (481, '遣悲怀三首之二', '昔日戏言身后事，今朝都到眼前来。衣裳已施行看尽，针线犹存未忍开。尚想旧情怜婢仆，也曾因梦送钱财。诚知此恨人人有，贫贱夫妻百事哀。', '元稹', NULL, 1);
INSERT INTO `article` VALUES (482, '遣悲怀三首之三', '闲坐悲君亦自悲，百年都是几多时？邓攸无子寻知命，潘岳悼亡犹费词。同穴□（上“穴”下“目”）冥何所望，他生缘会更难期。惟将终夜长开眼，报答平生未展眉。', '元稹', NULL, 1);
INSERT INTO `article` VALUES (483, '自河南经乱，关内阻饥，兄弟离散，各在一处。因望月有感，聊书所怀', '，寄上浮梁大兄，于潜七兄，乌江十五兄，兼示符离及下［圭阝］弟妹。时难年荒世业空，弟兄羁旅各西东。田园寥落干戈后，骨肉流离道路中。吊影分为千里雁，辞根散作九秋蓬。共看明月应垂泪，一夜乡心五处同。', '白居易', NULL, 1);
INSERT INTO `article` VALUES (484, '锦瑟', '锦瑟无端五十弦，一弦一柱思华年。庄生晓梦迷蝴蝶，望帝春心托杜鹃。沧海月明珠有泪，蓝田日暖玉生烟。此情可待成追忆，只是当时已惘然。', '李商隐', NULL, 1);
INSERT INTO `article` VALUES (485, '无题', '昨夜星辰昨夜风，画楼西畔桂堂东。身无彩凤双飞翼，心有灵犀一点通。隔座送钩春酒暖，分曹射覆蜡灯红。嗟余听鼓应官去，走马兰台类转蓬。', '李商隐', NULL, 1);
INSERT INTO `article` VALUES (486, '隋宫', '紫泉宫殿锁烟霞，欲取芜城作帝家。玉玺不缘归日角，锦帆应是到天涯。於今腐草无萤火，终古垂杨有暮鸦。地下若逢陈后主，岂宜重问后庭花？', '李商隐', NULL, 1);
INSERT INTO `article` VALUES (487, '无题二首之一', '来是空言去绝踪，月斜楼上五更钟。梦为远别啼难唤，书被催成墨未浓。蜡照半笼金翡翠，麝熏微度绣芙蓉。刘郎已恨蓬山远，更隔蓬山一万重。', '李商隐', NULL, 1);
INSERT INTO `article` VALUES (488, '无题二首之二', '飒飒东风细雨来，芙蓉塘外有轻雷。金蟾啮锁烧香入，玉虎牵丝汲井回。贾氏窥帘韩掾少，宓妃留枕魏王才。春心莫共花争发，一寸相思一寸灰。', '李商隐', NULL, 1);
INSERT INTO `article` VALUES (489, '筹笔驿', '猿鸟犹疑畏简书，风云常为护储胥。徒令上将挥神笔，终见降王走传车。管乐有才原不忝，关张无命欲何如。他年锦里经祠庙，梁父吟成恨有馀。', '李商隐', NULL, 1);
INSERT INTO `article` VALUES (490, '无题', '相见时难别亦难，东风无力百花残。春蚕到死丝方尽，蜡炬成灰泪始干。晓镜但愁云鬓改，夜吟应觉月光寒。蓬莱此去无多路，青鸟殷勤为探看。', '李商隐', NULL, 1);
INSERT INTO `article` VALUES (491, '春雨', '怅卧新春白袷衣，白门寥落意多违。红楼隔雨相望冷，珠箔飘灯独自归。远路应悲春［日宛］晚，残宵犹得梦依稀。玉［王当］缄札何由达？万里云罗一雁飞。', '李商隐', NULL, 1);
INSERT INTO `article` VALUES (492, '无题二首之一', '凤尾香罗薄几重，碧文圆顶夜深缝。扇裁月魄羞难掩，车走雷声语未通。曾是寂寥金烬暗，断无消息石榴红。斑骓只系垂杨岸，何处西南任好风？', '李商隐', NULL, 1);
INSERT INTO `article` VALUES (493, '无题二首之二', '重帷深下莫愁堂，卧后清宵细细长。神女生涯原是梦，小姑居处本无郎。风波不信菱枝弱，月露谁教桂叶香？直道相思了无益，未妨惆怅是清狂。', '李商隐', NULL, 1);
INSERT INTO `article` VALUES (494, '利洲南渡', '澹然空水对斜晖，曲岛苍茫接翠微。波上马嘶看棹去，柳边人歇待船归。数丛沙草群鸥散，万顷江田一鹭飞。谁解乘舟寻范蠡，五湖烟水独忘机？', '温庭筠', NULL, 1);
INSERT INTO `article` VALUES (495, '苏武庙', '苏武魂销汉使前，古祠高树两茫然。云边雁断胡天月，陇上羊归塞草烟。回日楼台非甲帐，去时冠剑是丁年。茂陵不见封侯印，空向秋波哭逝川。', '温庭筠', NULL, 1);
INSERT INTO `article` VALUES (496, '宫词', '十二楼中尽晓妆，望仙楼上望君王。锁衔金兽连环冷，水滴铜龙昼漏长。云髻罢梳还对镜，罗衣欲换更添香。遥窥正殿帘开处，袍裤宫人扫御床。', '薛逢', NULL, 1);
INSERT INTO `article` VALUES (497, '贫女', '蓬门未识绮罗香，拟托良媒益自伤。谁爱风流高格调？共怜时世俭梳妆。敢将十指夸针巧，不把双眉斗画长。苦恨年年压金线，为他人作嫁衣裳。', '秦韬玉', NULL, 1);
INSERT INTO `article` VALUES (498, '古意呈补阙乔知之', '卢家少妇郁金香，海燕双栖玳瑁梁。九月寒砧催木叶，十年征戍忆辽阳。白狼河北音书断，丹凤城南秋夜长。谁为含愁独不见，更教明月照流黄？', '沈全期', NULL, 1);
INSERT INTO `article` VALUES (499, '鹿柴', '空山不见人，但闻人语响。返景入深林，复照青苔上。', '王维', NULL, 1);
INSERT INTO `article` VALUES (500, '竹里馆', '独坐幽篁里，弹琴复长啸。深林人不知，明月来相照。', '王维', NULL, 1);
INSERT INTO `article` VALUES (501, '送别', '山中相送罢，日暮掩柴扉。春草明年绿，王孙归不归？', '王维', NULL, 1);
INSERT INTO `article` VALUES (502, '相思', '红豆生南国，春来发几枝？愿君多采撷，此物最相思。', '王维', NULL, 1);
INSERT INTO `article` VALUES (503, '杂诗', '君自故乡来，应知故乡事。来日绮窗前，寒梅著花未？', '王维', NULL, 1);
INSERT INTO `article` VALUES (504, '送崔九', '归山深浅去，须尽丘壑美。莫学武陵人，暂游桃源里。', '裴迪', NULL, 1);
INSERT INTO `article` VALUES (505, '终南望馀雪', '终南阴岭秀，积雪浮云端。林表明霁色，城中增暮寒。', '祖咏', NULL, 1);
INSERT INTO `article` VALUES (506, '宿建德江', '移舟泊烟渚，日暮客愁新。野旷天低树，江清月近人。', '孟浩然', NULL, 1);
INSERT INTO `article` VALUES (507, '春晓', '春眠不觉晓，处处闻啼鸟。夜来风雨声，花落知多少？', '孟浩然', NULL, 1);
INSERT INTO `article` VALUES (508, '夜思', '床前明月光，疑是地上霜。举头望明月，低头思故乡。', '李白', NULL, 1);
INSERT INTO `article` VALUES (509, '怨情', '美人卷珠帘，深坐蹙蛾眉。但见泪痕湿，不知心恨谁？', '李白', NULL, 1);
INSERT INTO `article` VALUES (510, '八阵图', '功盖三分国，名成八阵图。江流石不转，遗恨失吞吴。', '杜甫', NULL, 1);
INSERT INTO `article` VALUES (511, '登鹳雀楼', '白日依山尽，黄河入海流。欲穷千里目，更上一层楼。', '王之涣', NULL, 1);
INSERT INTO `article` VALUES (512, '送灵澈', '苍苍竹林寺，杳杳钟声晚。荷笠带斜阳，青山独归远。', '刘长卿', NULL, 1);
INSERT INTO `article` VALUES (513, '弹琴', '泠泠七弦上，静听松风寒。古调虽自爱，今人多不弹。', '刘长卿', NULL, 1);
INSERT INTO `article` VALUES (514, '送上人', '孤云将野鹤，岂向人间住！莫买沃洲山，时人已知处。', '刘长卿', NULL, 1);
INSERT INTO `article` VALUES (515, '秋夜寄邱员外', '怀君属秋夜，散步咏凉天。空山松子落，幽人应未眠。', '韦应物', NULL, 1);
INSERT INTO `article` VALUES (516, '听筝', '鸣筝金粟柱，素手玉房前。欲得周郎顾，时时误拂弦。', '李端', NULL, 1);
INSERT INTO `article` VALUES (517, '新嫁娘', '三日入厨下，洗手作羹汤。未谙姑食性，先遣小姑尝。', '王建', NULL, 1);
INSERT INTO `article` VALUES (518, '玉台体', '昨夜裙带解，今朝［虫喜］子飞。铅华不可弃，莫是藁砧归。', '权德舆', NULL, 1);
INSERT INTO `article` VALUES (519, '江雪', '千山鸟飞绝，万径人踪灭。孤舟蓑笠翁，独钓寒江雪。', '柳宗元', NULL, 1);
INSERT INTO `article` VALUES (520, '行宫', '寥落古行宫，宫花寂寞红。白头宫女在，闲坐说玄宗。', '元稹', NULL, 1);
INSERT INTO `article` VALUES (521, '问刘十九', '绿蚁新醅酒，红泥小火炉。晚来天欲雪，能饮一杯无？', '白居易', NULL, 1);
INSERT INTO `article` VALUES (522, '何满子', '故国三千里，深宫二十年。一声何满子，双泪落君前。', '张祜', NULL, 1);
INSERT INTO `article` VALUES (523, '登乐游原', '向晚意不适，驱车登古原。夕阳无限好，只是近黄昏。', '李商隐', NULL, 1);
INSERT INTO `article` VALUES (524, '寻隐者不遇', '松下问童子，言师采药去。只在此山中，云深不知处。', '贾岛', NULL, 1);
INSERT INTO `article` VALUES (525, '渡汉江', '岭外音书绝，经冬复立春。近乡情更怯，不敢问来人。第六章?唐诗300首 251-300', '李频', NULL, 1);
INSERT INTO `article` VALUES (526, '春怨', '打起黄莺儿，莫教枝上啼。啼时惊妾梦，不得到辽西。', '金昌绪', NULL, 1);
INSERT INTO `article` VALUES (527, '哥舒歌', '北斗七星高，哥舒夜带刀。至今窥牧马，不敢过临洮。', '西鄙人', NULL, 1);
INSERT INTO `article` VALUES (528, '长干行二首之一', '君家何处住，妾住在横塘。停船暂借问，或恐是同乡。', '崔颢', NULL, 1);
INSERT INTO `article` VALUES (529, '长干行二首之二', '家临九江水，来去九江侧。同是长干人，生小不相识。', '崔颢', NULL, 1);
INSERT INTO `article` VALUES (530, '玉阶怨', '玉阶生白露，夜久侵罗袜。却下水晶帘，玲珑望秋月。', '李白', NULL, 1);
INSERT INTO `article` VALUES (531, '塞下曲四首之一', '鹫翎金仆姑，燕尾绣蝥弧。独立扬新令，千营共一呼。', '卢纶', NULL, 1);
INSERT INTO `article` VALUES (532, '塞下曲四首之二', '林暗草惊风，将军夜引弓。平明寻白羽，没在石棱中。', '卢纶', NULL, 1);
INSERT INTO `article` VALUES (533, '塞下曲四首之三', '月黑雁飞高，单于夜遁逃。欲将轻骑逐，大雪满弓刀。', '卢纶', NULL, 1);
INSERT INTO `article` VALUES (534, '塞下曲四首之四', '野幕蔽琼筵，羌戎贺劳旋。醉和金甲舞，雷鼓动山川。', '卢纶', NULL, 1);
INSERT INTO `article` VALUES (535, '江南曲', '嫁得瞿塘贾，朝朝误妾期。早知潮有信，嫁与弄潮儿。', '李益', NULL, 1);
INSERT INTO `article` VALUES (536, '回乡偶书', '少小离家老大回，乡音无改鬓毛衰。儿童相见不相识，笑问客从何处来？', '贺知章', NULL, 1);
INSERT INTO `article` VALUES (537, '桃花溪', '隐隐飞桥隔野烟，石矶西畔问渔船。桃花尽日随流水，洞在清溪何处边？', '张旭', NULL, 1);
INSERT INTO `article` VALUES (538, '九月九日忆山东兄弟', '独在异乡为异客，每逢佳节倍思亲。遥知兄弟登高处，遍插茱萸少一人。', '王维', NULL, 1);
INSERT INTO `article` VALUES (539, '芙蓉楼送辛渐', '寒雨连江夜入吴，平明送客楚山孤。洛阳亲友如相问，一片冰心在玉壶。', '王昌龄', NULL, 1);
INSERT INTO `article` VALUES (540, '闺怨', '闺中少妇不知愁，春日凝妆上翠楼。忽见陌头杨柳色，悔教夫婿觅封侯。', '王昌龄', NULL, 1);
INSERT INTO `article` VALUES (541, '春宫曲', '昨夜风开露井桃，未央前殿月轮高。平阳歌舞新承宠，帘外春寒赐锦袍。', '王昌龄', NULL, 1);
INSERT INTO `article` VALUES (542, '凉州词', '葡萄美酒夜光杯，欲饮琵琶马上催。醉卧沙场君莫笑，古来征战几人回！', '王翰', NULL, 1);
INSERT INTO `article` VALUES (543, '送孟浩然之广陵', '故人西辞黄鹤楼，烟花三月下扬州。孤帆远影碧空尽，惟见长江天际流。', '李白', NULL, 1);
INSERT INTO `article` VALUES (544, '下江陵', '朝辞白帝彩云间，千里江陵一日还。两岸猿声啼不住，轻舟已过万重山。', '李白', NULL, 1);
INSERT INTO `article` VALUES (545, '逢入京使', '故园东望路漫漫，双袖龙钟泪不干。马上相逢无纸笔，凭君传语报平安。', '岑参', NULL, 1);
INSERT INTO `article` VALUES (546, '江南逢李龟年', '岐王宅里寻常见，崔九堂前几度闻。正是江南好风景，落花时节又逢君。', '杜甫', NULL, 1);
INSERT INTO `article` VALUES (547, '滁州西涧', '独怜幽草涧边生，上有黄鹂深树鸣。春潮带雨晚来急，野渡无人舟自横。', '韦应物', NULL, 1);
INSERT INTO `article` VALUES (548, '枫桥夜泊', '月落乌啼霜满天，江枫渔火对愁眠。姑苏城外寒山寺，夜半钟声到客船。', '张继', NULL, 1);
INSERT INTO `article` VALUES (549, '寒食', '春城无处不飞花，寒食东风御柳斜。日暮汉宫传蜡烛，轻烟散入五侯家。', '韩翃', NULL, 1);
INSERT INTO `article` VALUES (550, '月夜', '更深月色半人家，北斗阑干南斗斜。今夜偏知春气暖，虫声新透绿窗纱。', '刘方平', NULL, 1);
INSERT INTO `article` VALUES (551, '春怨', '纱窗日落渐黄昏，金屋无人见泪痕。寂寞空庭春欲晚，梨花满地不开门。', '刘方平', NULL, 1);
INSERT INTO `article` VALUES (552, '征人怨', '岁岁金河复玉关，朝朝马策与刀环。三春白雪归青冢，万里黄河绕黑山。', '柳中庸', NULL, 1);
INSERT INTO `article` VALUES (553, '宫词', '玉楼天半起笙歌，风送宫嫔笑语和。月殿影开闻夜漏，水晶帘卷近秋河。', '顾况', NULL, 1);
INSERT INTO `article` VALUES (554, '夜上受降城闻笛', '回乐峰前沙似雪，受降城外月如霜。不知何处吹芦管，一夜征人尽望乡。', '李益', NULL, 1);
INSERT INTO `article` VALUES (555, '乌衣巷', '朱雀桥边野草花，乌衣巷口夕阳斜。旧时王谢堂前燕，飞入寻常百姓家。', '刘禹锡', NULL, 1);
INSERT INTO `article` VALUES (556, '春词', '新妆宜面下朱楼，深锁春光一院愁。行到中庭数花朵，蜻蜓飞上玉搔头。', '刘禹锡', NULL, 1);
INSERT INTO `article` VALUES (557, '后宫词', '泪湿罗巾梦不成，夜深前殿按歌声。红颜未老恩先断，斜倚薰笼坐到明。', '白居易', NULL, 1);
INSERT INTO `article` VALUES (558, '赠内人', '禁门宫树月痕过，媚眼惟看宿鹭窠。斜拔玉钗灯影畔，剔开红焰救飞蛾。', '张祜', NULL, 1);
INSERT INTO `article` VALUES (559, '集灵台二首之一', '日光斜照集灵台，红树花迎晓露开。昨夜上皇新授□（“录”加竹头），太真含笑入帘来。', '张祜', NULL, 1);
INSERT INTO `article` VALUES (560, '集灵台二首之二', '虢国夫人承主恩，平明骑马入宫门。却嫌脂粉污颜色，淡扫蛾眉朝至尊。', '张祜', NULL, 1);
INSERT INTO `article` VALUES (561, '题金陵渡', '金陵津渡小山楼，一宿行人自可愁。潮落夜江斜月里，两三星火是瓜州。', '张祜', NULL, 1);
INSERT INTO `article` VALUES (562, '宫词', '寂寂花时闭院门，美人相并立琼轩。含情欲说宫中事，鹦鹉前头不敢言。', '朱庆馀', NULL, 1);
INSERT INTO `article` VALUES (563, '近试上张水部', '洞房昨夜停红烛，待晓堂前拜舅姑。妆罢低声问夫婿，画眉深浅入时无？', '朱庆馀', NULL, 1);
INSERT INTO `article` VALUES (564, '将赴吴兴登乐游原', '清时有味是无能，闲爱孤云静爱僧。欲把一麾江海去，乐游原上望昭陵。', '杜牧', NULL, 1);
INSERT INTO `article` VALUES (565, '赤壁', '折戟沈沙铁未销，自将磨洗认前朝。东风不与周郎便，铜雀春深销二乔。', '杜牧', NULL, 1);
INSERT INTO `article` VALUES (566, '泊秦淮', '烟笼寒水月笼沙，夜泊秦淮近酒家。商女不知亡国恨，隔江犹唱《后庭花》。', '杜牧', NULL, 1);
INSERT INTO `article` VALUES (567, '寄扬州韩绰判官', '青山隐隐水迢迢，秋尽江南草未凋。二十四桥明月夜，玉人何处教吹箫？', '杜牧', NULL, 1);
INSERT INTO `article` VALUES (568, '遣怀', '落魄江湖载酒行，楚腰纤细掌中轻。十年一觉扬州梦，赢得青楼薄幸名。', '杜牧', NULL, 1);
INSERT INTO `article` VALUES (569, '秋夕', '银烛秋光冷画屏，轻罗小扇扑流萤。天阶夜色凉如水，坐看牵牛织女星。', '杜牧', NULL, 1);
INSERT INTO `article` VALUES (570, '赠别二首之一', '娉娉袅袅十三馀，豆蔻梢头二月初。春风十里扬州路，卷上珠帘总不如。', '杜牧', NULL, 1);
INSERT INTO `article` VALUES (571, '赠别二首之二', '多情却似总无情，唯觉樽前笑不成。蜡烛有心还惜别，替人垂泪到天明。', '杜牧', NULL, 1);
INSERT INTO `article` VALUES (572, '金谷园', '繁华事散逐香尘，流水无情草自春。日暮东风怨啼鸟，落花犹似坠楼人。', '杜牧', NULL, 1);
INSERT INTO `article` VALUES (573, '夜雨寄北', '君问归期未有期，巴山夜雨涨秋池。何当共剪西窗烛，却话巴山夜雨时？', '李商隐', NULL, 1);
INSERT INTO `article` VALUES (574, '寄令狐郎中', '嵩云秦树久离居，双鲤迢迢一纸笔。休问梁园旧宾客，茂陵秋雨病相如。', '李商隐', NULL, 1);
INSERT INTO `article` VALUES (575, '为有', '为有云屏无限娇，凤城寒尽怕春宵。无端嫁得金龟婿，辜负香衾事早朝。', '李商隐', NULL, 1);
INSERT INTO `article` VALUES (576, '隋宫', '乘兴南游不戒严，九重谁省谏书函？春风举国裁宫锦，半作障泥半作帆。', '李商隐', NULL, 1);
INSERT INTO `article` VALUES (577, '瑶池', '瑶池阿母绮窗开，黄竹歌声动地哀。八骏日行三万里，穆王何事不重来？', '李商隐', NULL, 1);
INSERT INTO `article` VALUES (578, '嫦娥', '云母屏风烛影深，长河渐落晓星沈。嫦娥应悔偷灵药，碧海青天夜夜心。', '李商隐', NULL, 1);
INSERT INTO `article` VALUES (579, '贾生', '宣室求贤访逐臣，贾生才调更无伦。可怜夜半虚前席，不问苍生问鬼神！', '李商隐', NULL, 1);
INSERT INTO `article` VALUES (580, '瑶瑟怨', '冰簟银床梦不成，碧天如水夜云轻。雁声远过潇湘去，十二楼中月自明。', '温庭筠', NULL, 1);
INSERT INTO `article` VALUES (581, '马嵬坡', '玄宗回马杨妃死，云雨难忘日月新。终是圣明天子事，景阳宫井又何人？', '郑畋', NULL, 1);
INSERT INTO `article` VALUES (582, '已凉', '碧阑干外绣帘垂，猩色屏风画折枝。八尺龙须方锦褥，已凉天气未寒时。', '韩偓', NULL, 1);
INSERT INTO `article` VALUES (583, '金陵图', '江雨霏霏江草齐，六朝如梦鸟空啼。无情最是台城柳，依旧烟笼十里堤。', '韦庄', NULL, 1);
INSERT INTO `article` VALUES (584, '陇西行', '誓扫匈奴不顾身，五千貂锦丧胡尘。可怜无定河边骨，犹是深闺梦里人！', '陈陶', NULL, 1);
INSERT INTO `article` VALUES (585, '寄人', '别梦依依到谢家，小廊回合曲阑斜。多情只有春庭月，犹为离人照落花。', '张泌', NULL, 1);
INSERT INTO `article` VALUES (586, '杂诗', '尽寒食雨草萋萋，著麦苗风柳映堤。等是有家归未得，杜鹃休向耳边啼。', '无名氏', NULL, 1);
INSERT INTO `article` VALUES (587, '渭城曲', '渭城朝雨［氵邑］轻尘，客舍青青柳色新。劝君更尽一杯酒，西出阳关无故人。', '王维', NULL, 1);
INSERT INTO `article` VALUES (588, '秋夜曲', '桂魄初生秋露微，轻罗已薄未更衣。银筝夜久殷勤弄，心怯空房不忍归！', '王维', NULL, 1);
INSERT INTO `article` VALUES (589, '长信怨', '奉帚平明金殿开，且将团扇共徘徊。玉颜不及寒鸦色，犹带昭阳日影来。', '王昌龄', NULL, 1);
INSERT INTO `article` VALUES (590, '出塞', '秦时明月汉时关，万里长征人未还。但使龙城飞将在，不教胡马渡阴山！', '王昌龄', NULL, 1);
INSERT INTO `article` VALUES (591, '出塞', '黄河远上白云间，一片孤城万仞山。羌笛何须怨杨柳？春风不度玉门关。', '王之涣', NULL, 1);
INSERT INTO `article` VALUES (592, '清平调三首之一', '云想衣裳花想容，春风拂槛露华浓。若非群玉山头见，会向瑶台月下逢。', '李白', NULL, 1);
INSERT INTO `article` VALUES (593, '清平调三首之二', '一枝红艳露凝香，云雨巫山枉断肠。借问汉宫谁得似？可怜飞燕倚新妆。', '李白', NULL, 1);
INSERT INTO `article` VALUES (594, '清平调三首之三', '名花倾国两相欢，常得君王带笑看。解释春风无限恨，沈香亭北倚阑干。', '李白', NULL, 1);
INSERT INTO `article` VALUES (595, '金缕衣', '劝君莫惜金缕衣，劝君惜取少年时。花开堪折直须折，莫待无花空折枝！', '杜秋娘', NULL, 1);
INSERT INTO `article` VALUES (1271056071519121491, '第一章（天地之始）', '道可道，非常道;名可名，非常名。无，名天地之始；有，名万物之母。故常无，欲以观其妙；常有，欲以观其徼（jiao）。此两者，同出而异名，同谓之玄。玄之又玄，众妙之门。', '老子', 4, 2);
INSERT INTO `article` VALUES (1271056071519121492, '第二章（美之为美）', '天下皆知美之为美，斯恶已。皆知善之为善，斯不善矣。故有无相生，难易相成，长短相较，高下相倾，音声相和（he4），前后相随。是以圣人处无为之事，行不言之教。万物作焉而不辞，生而不有，为而不恃（shi），功成而弗（fu）居，夫唯弗居，是以不去。', '老子', 4, 2);
INSERT INTO `article` VALUES (1271056071519121493, '第三章（圣人之治）', '不尚贤，使民不争；不贵难得之货，使民不为盗；不见可欲，使民心不乱。是以圣人之治，虚其心，实其腹，弱其志，强其骨，常使民无知无欲，使夫智者不敢为也。为无为，则无不为。', '老子', 4, 2);
INSERT INTO `article` VALUES (1271056071519121494, '第四章（象帝之先）', '道冲，而用之或不盈。渊兮似万物之宗。挫其锐，解其纷，和其光，同其尘。湛兮似或存，吾不知谁之子，象帝之先。', '老子', 4, 2);
INSERT INTO `article` VALUES (1271056071519121495, '第五章（天地不仁）', '天地不仁，以万物为刍（chu）狗；圣人不仁，以百姓为刍狗。天地之间，其犹橐（tuo）龠（yue）乎！虚而不屈，动而愈出。多言数穷，不如守中。', '老子', 4, 2);
INSERT INTO `article` VALUES (1271056071519121496, '第六章（玄牝之门）', '谷神不死，是谓玄牝（pin）。玄牝之门，是谓天地根。绵绵若存，用之不勤。', '老子', 4, 2);
INSERT INTO `article` VALUES (1271056071519121497, '第七章（天长地久）', '天长地久。天地所以能长且久者，以其不自生，故能长生。是以圣人后其身而身先，外其身而身存。非以其无私邪？故能成其私。', '老子', 4, 2);
INSERT INTO `article` VALUES (1271056071519121498, '第八章（不争无尤）', '上善若水。水善利万物而不争，处众人之所恶（wu），故几于道。居善地，心善渊，与善仁，言善信，正善治，事善能，动善时。夫唯不争，故无尤（you）。', '老子', 4, 2);
INSERT INTO `article` VALUES (1271056071519121499, '第九章（功遂身退）', '持而盈之，不如其已；揣而锐之，不可长保；金玉满堂，莫之能守；富贵而骄，自遗（yi）其咎（jiu）。功遂身退，天之道。', '老子', 4, 2);
INSERT INTO `article` VALUES (1271056071519121500, '第十章（长而不宰）', '载营魄抱一，能无离乎？专气致柔，能婴儿乎？涤（di）除玄览（jian），能如疵（bi）乎？爱国治民，能无为乎？天门开阖（he），能为雌（ci）乎？明白四达，能无知乎？生之畜之，生而不有，为而不恃（shi），长而不宰，是谓玄德。', '老子', 4, 2);
INSERT INTO `article` VALUES (1271056071519121501, '第十一章（无之为用）', '三十辐共一毂，当其无，有车（ju）之用。埏（shan）埴以为器，当其无，有器之用。凿（zao）户牖（you）以为室，当其无，有室之用；故有之以为利，无之以为用。', '老子', 4, 2);
INSERT INTO `article` VALUES (1271056071519121502, '第十二章（圣人为腹）', '五色令人目盲；五音令人耳聋；五味令人口爽；驰（chi）骋（cheng）畋（tian）猎，令人心发狂；难得之货，令人行妨。是以圣人为腹不为目。故去彼取此。', '老子', 4, 2);
INSERT INTO `article` VALUES (1271056071519121503, '第十三章（宠辱两忘）', '宠辱若惊，贵大患若身。何谓宠辱若惊？宠为下，得之若惊，失之若惊，是谓宠辱若惊。何谓贵大患若身？吾所以有大患者，为吾有身，及吾无身，吾有何患？故贵以身为天下，若可寄天下；爱以身为天下，若可托天下。', '老子', 4, 2);
INSERT INTO `article` VALUES (1271056071519121504, '第十四章（无状之状）', '视之不见名曰夷，听之不闻名曰希，搏之不得名曰微。此三者不可致诘（jie），故混而为一。其上不皦（jiao），其下不昧。绳绳不可名，复归于无物。是谓无状之状，无物之象，是谓惚恍。迎之不见其首，随之不见其后。执古之道以御今之有，能知古始，是谓道纪。', '老子', 4, 2);
INSERT INTO `article` VALUES (1271056071519121505, '第十五章（善为士者）', '古之善为士者，微妙玄通，深不可识。夫唯不可识，故强为之容：豫（yu）焉若冬涉（she）川，犹兮若畏四邻，俨（yan）兮其若容，涣（huan）兮若冰之将释，敦（dun）兮其若朴，旷兮其若谷，混兮其若浊。孰能浊以静之徐清？孰能安以久，动之徐生？保此道者不欲盈。夫唯不盈，故能蔽不新成。', '老子', 4, 2);
INSERT INTO `article` VALUES (1271056071519121506, '第十六章（殆身不殆）', '致虚极，守静笃（du）。万物并作，吾以观复。夫物芸芸，各复归其根。归根曰静，是曰复命。复命曰常，知常曰明。不知常，妄（wang）作凶。知常容，容乃公，公乃王，王乃天，天乃道，道乃久。殁（mo）身不殆（dai）。', '老子', 4, 2);
INSERT INTO `article` VALUES (1271056071519121507, '第十七章（功成事遂）', '太上，下知有之；其次，亲而誉之；其次，畏之；其次，侮（wu）之。信不足焉，有不信焉。悠兮其贵言。功成事遂，百姓皆谓“我自然”。', '老子', 4, 2);
INSERT INTO `article` VALUES (1271056071519121508, '第十八章（道亡有义）', '大道废，有仁义；智慧出，有大伪；六亲不和，有孝慈；国家昏乱，有忠臣。', '老子', 4, 2);
INSERT INTO `article` VALUES (1271056071519121509, '第十九章（绝圣弃智）', '绝圣弃智，民利百倍；绝仁弃义，民复孝慈；绝巧弃利，盗贼无有。此三者，以为文不足。故令有所属：见素抱朴，少私寡欲。', '老子', 4, 2);
INSERT INTO `article` VALUES (1271056071519121510, '第二十章（独异于人）', '绝学无忧。唯之与阿，相去几何？善之与恶，相去若何？人之所畏，不可不畏。荒兮其未央哉！众人熙熙，如享太牢，如春登台；我独泊兮其未兆，如婴儿之未孩。傫傫（lei）兮若无所归！众人皆有余，而我独若遗（yi）。我愚人之心也哉，沌沌兮！俗人昭昭（zhao），我独昏昏。俗人察察，我独闷闷。澹dan兮其若海，飂（liao）兮若无止。众人皆有以，而我独顽且鄙（bi）。我独异于人，而贵食母。', '老子', 4, 2);
INSERT INTO `article` VALUES (1271056071519121511, '第二十一章（惟道是从）', '孔德之容，惟道是从。道之为物，惟恍惟惚。惚兮恍兮，其中有象；恍兮惚兮，其中有物。窈兮冥（ming）兮，其中有精。其精甚真，其中有信。自今及古，其名不去，以阅众甫（fu）。吾何以知众甫之状哉！以此。', '老子', 4, 2);
INSERT INTO `article` VALUES (1271056071519121512, '第二十二章（圣人抱一）', '曲则全，枉则直，洼（wa）则盈，敝则新，少则得，多则惑。是以圣人抱一为天下式。不自见（xian），故明；不自是，故彰（zhang）；不自伐，故有功；不自矜（jin），故长。夫唯不争，故天下莫能与之争。古之所谓“曲则全”者，岂虚言哉！诚全而归之。', '老子', 4, 2);
INSERT INTO `article` VALUES (1271056071519121513, '第二十三章（道亦乐得）', '希言自然。故飘风不终朝，骤雨不终日。孰为此者？天地。天地尚不能久，而况于人乎？故从事于道者，同于道；德者，同于德；失者，同于失。同于道者，道亦乐得之；同于德者，德亦乐得之；同于失者，失亦乐得之。信不足焉，有不信焉。', '老子', 4, 2);
INSERT INTO `article` VALUES (1271056071519121514, '第二十四章（自是不彰）', '企者不立，跨者不行，自见者不明，自是者不彰，自伐者无功，自矜（jin）者不长。其在道也，曰：余（yu）食赘（zhui）行。物或恶（wu）之，故有道者不处。', '老子', 4, 2);
INSERT INTO `article` VALUES (1271056071519121515, '第二十五章（道法自然）', '有物混成，先天地生。寂（ji）兮寥（liao）兮，独立而不改，周行而不殆（dai），可以为天地母。吾不知其名，字之曰道，强为之名曰大。大曰逝，逝曰远，远曰反。故道大，天大，地大，王亦大。域中有四大，而王居其一焉。人法地，地法天，天法道，道法自然。', '老子', 4, 2);
INSERT INTO `article` VALUES (1271056071519121516, '第二十六章（静为躁君）', '重为轻根，静为躁君。是以君子终日行不离辎（zi）重。虽有荣观，燕处超然。奈何万乘之主，而以身轻天下？轻则失本，躁则失君。', '老子', 4, 2);
INSERT INTO `article` VALUES (1271056071519121517, '第二十七章（善行无痕）', '善行无辙（zhe）迹;善言无瑕（xia）谪（zhe）；善数（shu）不用筹（chou）策（ce）；善闭无关楗（jian）而不可开;善结无绳约而不可解。是以圣人常善救人，故无弃人；常善救物，故无弃物。是谓袭（xi）明。故善人者，不善人之师；不善人者，善人之资。不贵其师，不爱其资，虽智大迷，是谓要妙。', '老子', 4, 2);
INSERT INTO `article` VALUES (1271056071519121518, '第二十八章（知雄守雌）', '知其雄，守其雌（ci），为天下溪。为天下溪，常德不离，复归于婴儿。知其白，守其黑，为天下式。为天下式，常德不忒（te），复归于无极。知其荣，守其辱。为天下谷。为天下谷，常德乃足，复归于朴（pu）。朴散则为器，圣人用之，则为官长，故大制不割。', '老子', 4, 2);
INSERT INTO `article` VALUES (1271056071519121519, '第二十九章（圣人无为）', '将欲取天下而为之，吾见其不得已。天下神器，不可为也。为者败之，执者失之。故物或行或随，或歔（xu）或吹，或强或羸（lei），或载或隳（hui）。是以圣人去甚，去奢，去泰。', '老子', 4, 2);
INSERT INTO `article` VALUES (1271056071519121520, '第三十章（以道佐主）', '以道佐（zuo）人主者，不以兵强天下，其事好还。师之所处，荆（jing）棘（ji）生焉。大军之后，必有凶年。善有果而已，不敢以取强。果而勿矜，果而勿伐，果而勿骄。果而不得已，果而勿强。物壮则老，是谓不道，不道早已。', '老子', 4, 2);
INSERT INTO `article` VALUES (1271056071519121521, '第三十一章（有道不处）', '夫佳兵者不祥之器，物或恶之，故有道者不处。君子居则贵左，用兵则贵右。兵者不祥之器，非君子之器，不得已而用之，恬淡为上。胜而不美，而美之者，是乐杀人。夫乐杀人者，则不可得志于天下矣。吉事尚左，凶事尚右。偏将军居左，上将军居右，言以丧礼处之。杀人之众，以哀悲涖（li）之，战胜，以丧礼处之。', '老子', 4, 2);
INSERT INTO `article` VALUES (1271056071519121522, '第三十二章（知止不殆）', '道常无名，朴虽小，天下莫能臣也。侯王若能守之，万物将自宾（bjn）。天地相合，以降甘露，民莫之令而自均。始制有名，名亦既有，夫亦将知止，知止可以不殆。譬（pi）道之在天下，犹川谷之于江海。', '老子', 4, 2);
INSERT INTO `article` VALUES (1271056071519121523, '第三十三章（知人者智）', '知人者智，自知者明。胜人者有力，自胜者强。知足者富，强行者有志。不失其所者久，死而不亡者寿。', '老子', 4, 2);
INSERT INTO `article` VALUES (1271056071519121524, '第三十四章（不自为大）', '大道泛兮，其可左右。万物恃（shi）之而生而不辞，功成不名有，衣养万物而不为主。常无欲，可名于小；万物归焉而不为主，可名为大。以其终不自为大，故能成其大。', '老子', 4, 2);
INSERT INTO `article` VALUES (1271056071519121525, '第三十五章（执道乐往）', '执大象，天下往。往而不害，安平泰。乐（yue）与饵（er），过客止。道之出口。淡乎其无味，视之不足见，听之不足闻，用之不足既。', '老子', 4, 2);
INSERT INTO `article` VALUES (1271056071519121526, '第三十六章（欲歙固张）', '将欲歙（xi）之，必固张之；将欲弱之，必固强之；将欲废之，必固兴之；将欲夺之，必固与之。是谓微明，柔弱胜刚强。鱼不可脱于渊，国之利器不可以示人。', '老子', 4, 2);
INSERT INTO `article` VALUES (1271056071519121527, '第三十七章（道恒无为）', '道常无为而无不为，侯王若能守之，万物将自化。化而欲作，吾将镇之以无名之朴。无名之朴，夫亦将不欲。不欲以静，天下将自定。', '老子', 4, 2);
INSERT INTO `article` VALUES (1271056071519121528, '第三十八章（上德不德）', '上德不德，是以有德；下德不失德，是以无德。上德无为，而无以为；下德为之，而有以为。上仁为之，而无以为；上义为之，而有以为；上礼为之，而莫之应，则攘（rang）臂而扔之。故失道而后德，失德而后仁，失仁而后义，失义而后礼。夫礼者，忠信之薄（bo），而乱之首；前识者，道之华，而愚（yu）之始。是以大丈夫处其厚，不居其薄；处其实，不居其华。故去彼取此。', '老子', 4, 2);
INSERT INTO `article` VALUES (1271056071519121529, '第三十九章（下为高基）', '昔之得一者：天得一以清，地得一以宁；神得一以灵，谷得一以盈，万物得一以生，侯王得一以为天下贞（zhen）。其致之。天无以清，将恐裂；地无以宁，将恐废；神无以灵，将恐歇；谷无以盈，将恐竭（jie）；万物无以生，将恐灭；侯王无以贵高，将恐蹶（jue）。故贵以贱为本，高以下为基。是以侯王自谓孤、寡、不毂（gu）。此非以贱为本邪？非乎？故致数舆（yu）无舆。是故不欲琭（lu）琭如玉，珞（luo）珞如石。', '老子', 4, 2);
INSERT INTO `article` VALUES (1271056071519121530, '第四十章（无中生有）', '反者，道之动；弱者，道之用。天下万物生于有，有生于无。', '老子', 4, 2);
INSERT INTO `article` VALUES (1271056071519121531, '第四十一章（善贷且成）', '上士闻道，勤而行之；中士闻道，若存若亡；下士闻道，大笑之。不笑，不足以为道。故建言有之：明道若昧，进道若退，夷道若颣（lei）。上德若谷，大白若辱，广德若不足，建德若偷，质真若渝（yu）。大方无隅（yu）；大器晚成；大音希声，大象无形，道隐无名。夫唯道，善贷（dai）且成。', '老子', 4, 2);
INSERT INTO `article` VALUES (1271056071519121532, '第四十二章（物损而益）', '道生一，一生二，二生三，三生万物。万物负阴而抱阳，冲气以为和。人之所恶，唯孤、寡、不毂，而王公以为称。故物或损之而益，或益之而损。人之所教，我亦教之。强梁者不得其死，吾将以为教父。', '老子', 4, 2);
INSERT INTO `article` VALUES (1271056071519121533, '第四十三章（不言之教）', '天下之至柔，驰骋天下之至坚。无有入无间，吾是以知无为之有益。不言之教，无为之益，天下希及之。', '老子', 4, 2);
INSERT INTO `article` VALUES (1271056071519121534, '第四十四章（知足不辱）', '名与身孰（shu）亲？身与货孰多？得与亡孰病？是故，甚爱必大费，多藏必厚亡。故知足不辱，知止不殆，可以长久。', '老子', 4, 2);
INSERT INTO `article` VALUES (1271056071519121535, '第四十五章（大成若缺）', '大成若缺，其用不弊。大盈若冲，其用不穷。大直若屈，大巧若拙，大辩若讷（ne）。躁胜寒，静胜热。清静为天下正。', '老子', 4, 2);
INSERT INTO `article` VALUES (1271056071519121536, '第四十六章（知足常足）', '天下有道，却走马以粪。天下无道，戎（rong）马生于郊。祸莫大于不知足；咎（jiu）莫大于欲得。故知足之足，常足矣。', '老子', 4, 2);
INSERT INTO `article` VALUES (1271056071519121537, '第四十七章（不行而知）', '不出户，知天下；不窥（kui）牖（you），见天道。其出弥远，其知弥少。是以圣人不行而知，不见而名，不为而成。', '老子', 4, 2);
INSERT INTO `article` VALUES (1271056071519121538, '第四十八章（为道日损）', '为学日益，为道日损。损之又损，以至于无为。无为而无不为。取天下常以无事，及其有事，不足以取天下。', '老子', 4, 2);
INSERT INTO `article` VALUES (1271056071519121539, '第四十九章（善者吾善）', '圣人常无心，以百姓之心为心。善者吾善之，不善者吾亦善之，德善；信者吾信之，不信者，吾亦信之。德信。圣人在天下，歙（xi）歙；为天下，浑其心。圣人皆孩之。', '老子', 4, 2);
INSERT INTO `article` VALUES (1271056071519121540, '第五十章（出生入死）', '出生入死。生之徒十有三；死之徒十有三；人之生，动之死地，亦十有三。夫何故？以其生生之厚。盖闻善摄（she）生者，陆行不遇兕（si）虎，入军不被甲兵。兕无所投其角，虎无所措其爪，兵无所容其刃。夫何故？以其无死地。', '老子', 4, 2);
INSERT INTO `article` VALUES (1271056071519121541, '第五十一章（尊道贵德）', '道生之，德畜之，物形之，势成之。是以万物莫不尊道而贵德。道之尊，德之贵，夫莫之命而常自然。故道生之，德畜之，长之育之，亭之毒之，养之覆之。生而不有，为而不恃，长而不宰。是谓玄德。', '老子', 4, 2);
INSERT INTO `article` VALUES (1271056071519121542, '第五十二章（天下有始）', '天下有始，以为天下母；既得其母，以知其子; 既知其子，复守其母，没身不殆。塞其兑，闭其门，终身不勤；开其兑，济其事，终身不救；见小曰明，守柔曰强。用其光，复归其明，无遗身殃（yang），是为袭常。', '老子', 4, 2);
INSERT INTO `article` VALUES (1271056071519121543, '第五十三章（盗竽非道）', '使我介然有知，行于大道，唯施是畏。大道甚夷，而民好径。朝甚除，田甚芜，仓甚虚；服文采，带利剑，厌饮食，财货有余，是为盗夸。非道也哉！', '老子', 4, 2);
INSERT INTO `article` VALUES (1271056071519121544, '第五十四章（善抱不脱）', '善建者不拔，善抱者不脱，子孙以祭祀不辍（chuo）；修之于身，其德乃真；修之于家，其德乃余；修之于乡，其德乃长；修之于国，其德乃丰；修之于天下，其德乃普。故以身观身，以家观家，以乡观乡，以国观国，以天下观天下。吾何以知天下之然哉？以此。', '老子', 4, 2);
INSERT INTO `article` VALUES (1271056071519121545, '第五十五章（含德之厚）', '含德之厚，比于赤子。蜂虿（chai4）虺（hui3）蛇不螫（shi4），猛兽不据，攫（jue2）鸟不搏。骨弱筋柔而握固。未知牝（pin）牡（mu）之合而朘（zui1）作，精之至也。终日号而不嗄（sha4），和之至也。知和曰常，知常曰明，益生曰祥，心使气曰强。物壮则老，是谓不道，不道早已。', '老子', 4, 2);
INSERT INTO `article` VALUES (1271056071519121546, '第五十六章（知者不言）', '知者不言，言者不知。塞其兑，闭其门，挫其锐，解其纷，和其光，同其尘，是谓玄同。故不可得而亲，不可得而疏；不可得而利，不可得而害；不可得而贵，不可得而贱。故为天下贵。', '老子', 4, 2);
INSERT INTO `article` VALUES (1271056071519121547, '第五十七章（以正治国）', '以正治国，以奇用兵，以无事取天下。吾何以知其然哉？以此。天下多忌讳（hui），而民弥贫；民多利器，国家滋（zi）昏；人多伎（ji）巧，奇物滋起；法令滋彰，盗贼多有。故圣人云：“我无为而民自化；我好静而民自正；我无事而民自富；我无欲而民自朴。”', '老子', 4, 2);
INSERT INTO `article` VALUES (1271056071519121548, '第五十八章（福祸相倚）', '其政闷闷，其民淳淳；其政察察，其民缺缺。祸兮福之所倚， 福兮祸之所伏。孰知其极？其无正也。正复为奇（ji1），善复为妖。人之迷，其日固久。是以圣人方而不割，廉而不刿（gui4），直而不肆，光而不耀。', '老子', 4, 2);
INSERT INTO `article` VALUES (1271056071519121549, '第五十九章（治人尚啬）', '治人事天，莫若啬（se4）。夫为啬，是谓早服；早服谓之重积德；重积德，则无不克；无不克，则莫知其极；莫知其极，可以有国；有国之母，可以长久。是谓深根固柢（di），长生久视之道。', '老子', 4, 2);
INSERT INTO `article` VALUES (1271056071519121550, '第六十章（以道治国）', '治大国，若烹（peng）小鲜。以道莅（li）天下，其鬼不神；非其鬼不神，其神不伤人；非其神不伤人，圣人亦不伤人。夫两不相伤，故德交归焉。', '老子', 4, 2);
INSERT INTO `article` VALUES (1271056071519121551, '第六十一章（大者宜下）', '大国者下流，天下之交，天下之牝，牝常以静胜牡，以静为下。故大国以下小国，则取小国；小国以下大国，则取大国。故或下以取，或下而取。大国不过欲兼畜人，小国不过欲入事人。夫两者各得其所欲，大者宜为下。', '老子', 4, 2);
INSERT INTO `article` VALUES (1271056071519121552, '第六十二章（万物之奥）', '道者万物之奥，善人之宝，不善人之所保。美言可以市，尊行可以加人。人之不善，何弃之有？故立天子，置三公，虽有拱璧以先驷（si）马，不如坐进此道。古之所以贵此道者何？不曰：求以得，有罪以免邪！故为天下贵。', '老子', 4, 2);
INSERT INTO `article` VALUES (1271056071519121553, '第六十三章（能成其大）', '为无为，事无事，味无味。大小多少，抱怨以德。图难于其易，为大于其细；天下难事，必作于易; 天下大事，必作于细。是以圣人终不为大，故能成其大。夫轻诺必寡信，多易必多难。是以圣人犹难之，故终无难矣。', '老子', 4, 2);
INSERT INTO `article` VALUES (1271056071519121554, '第六十四章（无为无败）', '其安易持，其未兆易谋，其脆易泮（pan），其微易散（san）。为之于未有，治之于未乱。合抱之木，生于毫末；九层之台，起于累土；千里之行，始于足下。为者败之，执者失之。是以圣人无为故无败，无执故无失。民之从事，常于几成而败之。慎终如始，则无败事。是以圣人欲不欲，不贵难得之货; 学不学，复众人之所过。以辅万物之自然，而不敢为。', '老子', 4, 2);
INSERT INTO `article` VALUES (1271056071519121555, '第六十五章（善为道者）', '古之善为道者，非以明民，将以愚之。民之难治，以其智多。故以智治国，国之贼；不以智治国，国之福。知此两者亦稽（qi）式。常知稽式，是谓玄德。玄德深矣远矣，与物反矣！然后乃至大顺。', '老子', 4, 2);
INSERT INTO `article` VALUES (1271056071519121556, '第六十六章（莫能与争）', '江海之所以能为百谷王者，以其善下之，故能为百谷王。是以欲上民，必以言下之；欲先民，必以身后之。是以圣人处上，而民不重；处前，而民不害。是以天下乐推而不厌。以其不争，故天下莫能与之争。', '老子', 4, 2);
INSERT INTO `article` VALUES (1271056071519121557, '第六十七章（我有之宝）', '天下皆谓我道大，似不肖。夫唯大，故似不肖（xiao）。若肖，久矣其细也夫！我有三宝，持而保之: 一曰慈，二曰俭，三曰不敢为天下先。慈故能勇，俭故能广，不敢为天下先，故能成器长。今舍慈且勇，舍俭且广，舍后且先，死矣！夫慈，以战则胜，以守则固。天将救之，以慈卫之。', '老子', 4, 2);
INSERT INTO `article` VALUES (1271056071519121558, '第六十八章（不争之德）', '善为士者不武，善战者不怒，善胜敌者不与，善用人者为之下。是谓不争之德，是谓用人之力，是谓配天之极。', '老子', 4, 2);
INSERT INTO `article` VALUES (1271056071519121559, '第六十九章（哀者胜矣）', '用兵有言：“ 吾不敢为主而为客，不敢进寸而退尺。” 是谓行（heng）无行（heng），攘（rang）无臂，扔无敌，执无兵。祸莫大于轻敌，轻敌几丧吾宝。故抗兵相加，哀者胜矣。', '老子', 4, 2);
INSERT INTO `article` VALUES (1271056071519121560, '第七十章（被褐怀玉）', '吾言甚易知，甚易行。天下莫能知，莫能行。言有宗，事有君。夫唯无知，是以不我知。知我者希，则我者贵。是以圣人被褐（he4）而怀玉。', '老子', 4, 2);
INSERT INTO `article` VALUES (1271056071519121561, '第七十一章（以其病病）', '知，不知，上；不知，知，病。夫唯病病，是以不病。圣人不病，以其病病。是以不病。', '老子', 4, 2);
INSERT INTO `article` VALUES (1271056071519121562, '第七十二章（自爱不贵）', '民不畏威，则大威至。无狎（xia）其所居，无厌其所生。夫唯不厌，是以不厌。是以圣人自知不自见（xian），自爱不自贵。故去彼取此。', '老子', 4, 2);
INSERT INTO `article` VALUES (1271056071519121563, '第七十三章（天网恢恢）', '勇于敢则杀，勇于不敢则活。此两者，或利或害。天之所恶，孰知其故？是以圣人犹难之，天之道，不争而善胜，不言而善应，不召而自来，繟（chan）然而善谋。天网恢恢，疏而不失。', '老子', 4, 2);
INSERT INTO `article` VALUES (1271056071519121564, '第七十四章（民不畏死）', '民不畏死，奈何以死惧之？若使民常畏死，而为奇者，吾得执而杀之，孰敢？常有司杀者杀。夫代司杀者杀，是谓代大匠斫（zhuo）。夫代大匠斫者，希有不伤其手矣。', '老子', 4, 2);
INSERT INTO `article` VALUES (1271056071519121565, '第七十五章（无以生为）', '民之饥，以其上食税之多，是以饥。民之难治，以其上之有为，是以难治。民之轻死，以其上求生之厚，是以轻死。夫唯无以生为者，是贤于贵生。', '老子', 4, 2);
INSERT INTO `article` VALUES (1271056071519121566, '第七十六章（强大处下）', '人之生也柔弱，其死也坚强。万物草木之生也柔脆，其死也枯槁（gao）。故坚强者死之徒，柔弱者生之徒。是以兵强则不胜，木强则兵。强大处下，柔弱处上。', '老子', 4, 2);
INSERT INTO `article` VALUES (1271056071519121567, '第七十七章（不欲见贤）', '天之道，其犹张弓欤（yu）？高者抑（yi）之，下者举之；有余者损之，不足者补之；天之道，损有余而补不足；人之道则不然，损不足以奉有余。孰能有余以奉天下？唯有道者。是以圣人为而不恃，功成而不处，其不欲见贤。', '老子', 4, 2);
INSERT INTO `article` VALUES (1271056071519121568, '第七十八章（柔之胜刚）', '天下莫柔弱于水，而攻坚强者莫之能胜，以其无以易之。弱之胜强，柔之胜刚，天下莫不知，莫能行。是以圣人云：“ 受国之垢（gou），是谓社稷（ji）主；受国不祥，是为天下王。”正言若反。', '老子', 4, 2);
INSERT INTO `article` VALUES (1271056071519121569, '第七十九章（道与善人）', '和大怨，必有余怨，安可以为善？是以圣人执左契（qi），而不责于人。有德司契，无德司彻。天道无亲，常与善人。', '老子', 4, 2);
INSERT INTO `article` VALUES (1271056071519121570, '第八十章（小国寡民）', '小国寡民。使有什（shi）伯之器而不用。使民重死而不远徙（xi3）。虽有舟舆（yu），无所乘之; 虽有甲兵，无所陈之。使民复结绳而用之。甘其食，美其服，安其居，乐其俗。邻国相望，鸡犬之声相闻，民至老死不相往来。', '老子', 4, 2);
INSERT INTO `article` VALUES (1271056071519121571, '第八十一章（善者不辩）', '信言不美，美言不信。善者不辩，辩者不善。知者不博，博者不知。圣人不积，既以为人，己愈有；既以与人，己愈多。天之道，利而不害；圣人之道，为而不争。', '老子', 4, 2);
